INSERT INTO `tbl_category` (`id`, `icon`, `user`, `name`, `img`, `sort_by`, `slug`, `parent`, `view`, `status`, `content`, `hits`, `hot`, `date`, `date_up`, `sitemap`, `title`, `description`, `keywords`, `robot`) VALUES
(39, NULL, 'admin', 'Tin Tức', NULL, 2, 'tin-tuc-bds', 0, 4, 'true', NULL, 50, NULL, '17-9-2018', '2019-10-01', '0.7', 'Tin Tức Thị Trường Bất Động Sản Nhanh Nhất', 'Tin tức chuyên sâu, mới, nóng về thị trường nhà đất, dự án bất động sản, thông tin về các dự án bất động sản mới trên cả nước', 'thông tin bds, thông tin bất động sản, tin tức bất động sản, tin tức bds', 'index, follow'),
(69, NULL, 'Admin', 'Dự án bđs', 'demo.jpg', 1, 'du-an-bds', 0, 2, 'true', NULL, 50, NULL, '2019-06-11', '2019-10-01', '0.9', 'Tổng hợp các dự án biệt thự cao cấp, shophouse trên cả nước', 'Cập nhật thường xuyên, nhanh chóng thông tin chi tiết, đầy đủ, chính xác của các dự án biệt thự cao cấp, shophouse trên cả nước', NULL, 'index, follow'),
(42, NULL, 'admin', 'Liên hệ', NULL, 3, 'lien-he', 0, 12, 'true', NULL, 50, NULL, '21-11-2018', '2019-07-10', '0.5', NULL, NULL, NULL, 'index, follow'),
(71, NULL, 'Admin', 'Giới thiệu', 'demo.jpg', 3, 'gioi-thieu', 0, 1, 'true', '<p><strong>C&Ocirc;NG TY CỔ PHẦN ĐỊA ỐC MAI VIỆT - <a href=\"https://maivietland.vn/\">MAI VIỆT LAND</a></strong> l&agrave; c&ocirc;ng ty chuy&ecirc;n kinh doanh, m&ocirc;i giới bất động sản nghỉ dưỡng từ c&aacute;c Chủ đầu tư lớn tr&ecirc;n thị trường bất động sản Việt Nam. Mai Việt Land c&oacute; Ban L&atilde;nh đạo gi&agrave;u kinh nghiệm, đ&atilde; hoạt động hơn 15 năm trong ng&agrave;nh bất động sản v&agrave; đội ngũ nh&acirc;n vi&ecirc;n nhiệt huyết, năng lực vững v&agrave;ng. Ch&iacute;nh v&igrave; vậy, d&ugrave; mới th&agrave;nh lập nhưng Mai Việt Land nhanh ch&oacute;ng ghi dấu ấn v&agrave; khẳng định năng lực, uy t&iacute;n của m&igrave;nh tr&ecirc;n thị trường. C&ocirc;ng ty đ&atilde; hợp t&aacute;c v&agrave; tham gia ph&acirc;n phối c&aacute;c dự &aacute;n bất động sản nghỉ dưỡng của c&aacute;c Chủ đầu tư lớn, điển h&igrave;nh l&agrave; Tập đo&agrave;n Flamingo - đơn vị c&oacute; quần thể nghỉ dưỡng Flamingo Đại Lải Resort lọt Top 10 Resort đẹp nhất thế giới.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Giới thiệu Mai Việt\" src=\"/public/upload/files/2020/HDF_5339%20(1).jpg\" style=\"height:666px; width:1000px\" /></p>\r\n\r\n<p><a href=\"https://maivietland.vn/\"><strong>Mai Việt Land</strong></a> đang tập trung nỗ lực ph&aacute;t triển nguồn nh&acirc;n lực song song với tiềm lực t&agrave;i ch&iacute;nh, mở rộng quy m&ocirc; c&ocirc;ng ty v&agrave; kh&ocirc;ng ngừng tiếp thu học hỏi để cung cấp cho kh&aacute;ch h&agrave;ng những sản phẩm chất lượng, đạt được ti&ecirc;u ch&iacute; v&agrave;ng v&agrave; gi&aacute; cả hợp l&yacute;. Trong tương lai gần, Mai Việt Land sẽ l&agrave; địa chỉ tin cậy, đơn vị hợp t&aacute;c gi&agrave;u tiềm năng được c&aacute;c Qu&yacute; đối t&aacute;c, Qu&yacute; chủ đầu tư v&agrave; Qu&yacute; kh&aacute;ch h&agrave;ng tin tưởng lựa chọn.&nbsp;Ng&agrave;y 25/7/2019, sau chưa đầy 1 th&aacute;ng ra mắt, Mai Việt Land đ&atilde; vinh dự được vinh danh trong Top 7 đại l&yacute; ti&ecirc;u biểu của Tập đo&agrave;n Flamingo nhờ v&agrave;o những cố gắng v&agrave; th&agrave;nh t&iacute;ch đ&aacute;ng nể của c&ocirc;ng ty.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"CÔNG TY CỔ PHẦN ĐỊA ỐC MAI VIỆT\" src=\"/public/upload/files/gioi-thieu/mai-viet-land-info.jpg\" /></p>\r\n\r\n<h2>Tầm nh&igrave;n</h2>\r\n\r\n<p>Bằng kh&aacute;t vọng dẫn đầu v&agrave; chiến lược kinh doanh, ph&aacute;t triển bền vững, Mai Việt Land phấn đấu trở th&agrave;nh c&ocirc;ng ty kinh doanh bất động sản uy t&iacute;n, thương hiệu h&agrave;ng đầu Việt Nam.</p>\r\n\r\n<h2>Sứ mệnh</h2>\r\n\r\n<p>Đối với kh&aacute;ch h&agrave;ng: Cung cấp c&aacute;c giải ph&aacute;p, dịch vụ bất động sản nghỉ dưỡng hợp l&yacute;, hữu hiệu, gi&uacute;p kh&aacute;ch h&agrave;ng tiết kiệm thời gian, chi ph&iacute;, g&oacute;p phần mang đến cuộc sống ưu việt cho kh&aacute;ch h&agrave;ng. Lu&ocirc;n đặt chữ T&Acirc;M v&agrave; chữ T&Iacute;N l&ecirc;n h&agrave;ng đầu để tạo lập sự tin tưởng, h&agrave;i l&ograve;ng nơi kh&aacute;ch h&agrave;ng.</p>\r\n\r\n<p>Đối với đối t&aacute;c: N&ecirc;u cao tinh thần hợp t&aacute;c t&ocirc;n trọng, b&igrave;nh đẳng, đ&ocirc;i b&ecirc;n c&ugrave;ng c&oacute; lợi, c&ugrave;ng ph&aacute;t triển.</p>\r\n\r\n<p>Đối với nh&acirc;n vi&ecirc;n: X&acirc;y dựng m&ocirc;i trường l&agrave;m việc l&agrave;nh mạnh, năng động, tất cả hỗ trợ, gi&uacute;p đỡ nhau c&ugrave;ng ph&aacute;t triển. C&ocirc;ng ty tạo mọi điều kiện vật chất cũng như tinh thần để nh&acirc;n vi&ecirc;n c&oacute; thể ph&aacute;t huy hết năng lực của m&igrave;nh.</p>\r\n\r\n<p>Đối với x&atilde; hội: Đ&oacute;ng g&oacute;p t&iacute;ch cực v&agrave;o sự ph&aacute;t triển kinh tế x&atilde; hội, tổ chức c&aacute;c hoạt động hướng về cộng đồng, thể hiện tinh thần, tr&aacute;ch nghiệm của một doanh nghiệp lớn tại Việt Nam.</p>\r\n\r\n<h2>Gi&aacute; trị cốt l&otilde;i</h2>\r\n\r\n<p>Uy t&iacute;n: Lấy chữ T&Iacute;N l&agrave; nền tảng trong hợp t&aacute;c, kinh doanh, tạo dựng sự tin tưởng bền vững, l&acirc;u d&agrave;i đối với đối t&aacute;c, kh&aacute;ch h&agrave;ng.</p>\r\n\r\n<p>Chất lượng: Chất lượng từ l&atilde;nh đạo, nh&acirc;n vi&ecirc;n đến c&aacute;c dự &aacute;n cung cấp cho kh&aacute;ch h&agrave;ng.</p>\r\n\r\n<p>Hiệu quả: Hiệu quả của c&ocirc;ng việc l&agrave; thước đo m&agrave; to&agrave;n thể nh&acirc;n vi&ecirc;n Mai Việt Land hướng tới.</p>\r\n\r\n<h2>Cơ cấu tổ chức</h2>\r\n\r\n<p>Mai Việt Land cam kết đồng h&agrave;nh c&ugrave;ng kh&aacute;ch h&agrave;ng v&agrave; đối t&aacute;c để tạo lập c&aacute;c gi&aacute; trị văn h&oacute;a, kinh tế, g&oacute;p phần n&acirc;ng cao chất lượng cuộc sống v&agrave; sự ph&aacute;t triển của ng&agrave;nh bất động sản Việt Nam.</p>\r\n\r\n<p>Tr&acirc;n trọng!</p>', 50, NULL, '2019-07-08', '2020-12-28', '0.6', 'CÔNG TY CỔ PHẦN ĐỊA ỐC MAI VIỆT - INFO', NULL, NULL, 'index, follow'),
(72, NULL, 'Admin', 'Tuyển dụng', 'demo.jpg', 2, 'tuyen-dung', 0, 11, 'true', NULL, 50, NULL, '2019-07-16', '2020-12-18', '0.7', 'Thông Tin Tuyển Dụng Mai Việt Land Nhanh Nhất', 'Cập nhật thông tin, vị trí tuyển dụng của Mai Việt Land', NULL, 'index, follow'),
(74, NULL, 'Admin', 'Tin thị trường', 'demo.jpg', 2, 'tin-thi-truong', 39, 4, 'true', NULL, 50, NULL, '2019-07-31', '2019-10-01', '0.7', 'Cập Nhật Mới Nhất Về Những Chuyển Động Của Thị Trường Bất Động Sản', 'Tin tức mới nhất, chuyên sâu về thị trường bất động sản, xu hướng đầu tư đang thịnh hành, những chính sách, quy định khi mua bán nhà đất', NULL, 'index, follow'),
(75, NULL, 'Admin', 'Tin dự án', 'demo.jpg', 2, 'tin-du-an', 39, 5, 'true', NULL, 50, NULL, '2019-07-31', '2019-10-01', '0.7', 'Cập Nhật Thông Tin Dự Án Nhanh Nhất', 'Tin tức mới nhất về dự án, sự kiện mở bán, cập nhật giá bán, tiến độ thi công, chính sách bán hàng của chủ đầu tư', NULL, 'index, follow'),
(76, NULL, 'Admin', 'Tin nội bộ', 'demo.jpg', 2, 'tin-noi-bo', 39, 6, 'true', NULL, 50, NULL, '2019-07-31', '2019-10-01', '0.6', 'Tin Nội Bộ Mai Việt Land', 'Đưa tin về các hoạt động, sự kiện trong nội bộ công ty, quảng bá rộng rãi những hình ảnh đẹp của nhân viên, môi trường làm việc tại Mai Việt Land tới nhiều người.', NULL, 'index, follow'),
(77, NULL, 'Admin', 'Kiến thức bđs', 'demo.jpg', 2, 'kien-thuc-bds', 0, 7, 'true', NULL, 50, NULL, '2019-08-15', '2019-10-01', '0.7', 'Cung Cấp Kiến Thức Bất Động Sản Từ A-Z', 'Tổng hợp những kiến thức về bất động sản, kinh nghiệm đầu tư, kinh nghiệm mua bán nhà đất, những thủ tục pháp lý cần biết. Giải đáp mọi thắc mắc về bất động sản.', NULL, 'index, follow'),
(78, NULL, 'Admin', 'Kinh nghiệm', 'demo.jpg', 2, 'kinh-nghiem', 77, 8, 'true', NULL, 50, NULL, '2019-08-15', '2019-10-01', '0.7', 'Kinh Nghiệm Đầu Tư Mua Bán Bất Động Sản', 'Chia sẻ kinh nghiệm trong đầu tư, mua bán bất động sản, từ việc lựa chọn dự án đến cách thức đầu tư để bạn có những quyết định đầu tư đúng đắn nhất', NULL, 'index, follow'),
(79, NULL, 'Admin', 'Thủ tục - pháp lý', 'demo.jpg', 2, 'thu-tuc-phap-ly', 77, 9, 'true', NULL, 50, NULL, '2019-08-15', '2019-10-01', '0.7', 'Thủ Tục Pháp Lý Nhà Đất', 'Tổng hợp những điều cần biết về thủ tục pháp lý khi mua bán nhà đất, hỗ trợ, giải đáp những khúc mắc khi thực hiện thủ tục pháp lý.', NULL, 'index, follow'),
(80, NULL, 'Admin', 'Hỏi đáp bđs', 'demo.jpg', 2, 'hoi-dap-bds', 77, 10, 'true', NULL, 50, NULL, '2019-09-03', '2019-10-01', '0.7', 'Giải Đáp Mọi Thắc Mắc Về Bất Động Sản Nhanh Nhất', 'Chuyên gia tư vấn, giải đáp những thắc mắc trong mua, bán nhà đất, cách thức đầu tư, gợi ý dự án tiềm năng, cách giải quyết những vấn đề phát sinh.', NULL, 'index, follow'),
(82, NULL, 'Admin', 'Mai Việt Academy', 'demo.jpg', 2, 'mai-viet-academy', 0, 3, 'true', NULL, 50, NULL, '2020-12-14', '2020-12-15', NULL, NULL, NULL, NULL, 'index, follow');
