INSERT INTO `tbl_comment` (`id`, `name`, `phone`, `email`, `content`, `p_id`, `n_id`, `reply`, `link`, `date`) VALUES
(26, 'Trần Phúc', NULL, NULL, 'Vị trí dự án có giao thông thuận tiện di chuyển không?', NULL, 181, 'Dự án The Matrix One nằm ở vị trí giao điểm của 2 tuyến đường Lê Quang Đạo và Mễ Trì, kết nối với nhiều tuyến đường huyết mạch của thành phố như Phạm Hùng, Xuân Thủy, Hồ Tùng Mậu… Xung quanh dự án cũng tập trung nhiều cơ quan ban, ngành, các khu giải trí, giáo dục như: Trung tâm hội nghị quốc gia, Trụ sở Bộ Ngoại giao, Sân vận động quốc gia Mỹ Đình, Bến xe Mỹ Đình, Tòa nhà KeangNam, BigC Thăng Long...', 'http://maivietland.vn/tin-thi-truong/vi-dau-bat-dong-san-my-dinh-duoc-nhieu-nha-dau-tu-san-don-/181.html', '2020-02-04'),
(27, 'Thu Thủy', NULL, NULL, 'Dự án có vướng mắc gì về pháp lý không? Có cam kết về tiến độ xây dựng không?', 126, NULL, 'Chủ đầu tư đã có đầy đủ giấy phép được phê duyệt liên quan đến quy hoạch và xây dựng của dự án.\r\nTiến độ xây dựng của các đảo khác nhau sẽ là khác nhau, đối với các nhánh Đảo Jade, Sapphire và Topaz dự kiến sẽ bàn giao cho khách hàng vào tháng 12/2019.\r\nMai Việt Land sẽ liên tục cập nhật tiến độ xây dựng của dự án. Mời bạn theo dõi bài viết này để cập nhật những thông tin mới nhất.', 'http://maivietland.vn/du-an-bds/ecopark-grand-the-island.html', '2020-02-04'),
(28, 'Trần Cao Cường', NULL, NULL, 'Chủ đầu tư dự án là đơn vị nào? Đã có tiếng vang hay thành tích gì chưa?', 127, NULL, 'Dự án Flamingo Crown Bay được đầu tư và phát triển bởi Flamingo Group. Tập đoàn đã có hơn 20 năm kinh nghiệm cùng 61 giải thưởng danh giá, trong đó nổi bật là \"Top 10 Nhà phát triển BĐS hàng đầu\" được trao tại tại lễ Trao giải BCI Asia Awards 2019. Flamingo Group là \"cha đẻ\" của nhiều dự án bất động nghỉ dưỡng cao cấp như Flamingo Đại Lải Resort (Top 10 Resort đẹp nhất Thế giới); Flamingo Cat Ba Beach Resort ( đạt 3 giải thưởng cao nhất tại Giải thưởng bất động sản Châu Á Thái Bình Dương (IPA 2019)... Các dự án được Tập đoàn Flamingo kiến tạo đều tạo được tiếng vang lớn, là lựa chọn nghỉ dưỡng, du lịch của nhiều du khách trong và ngoài nước; đặc biệt các dự án đều mang đến cho các nhà đầu tư cơ hội đầu tư tiềm năng, sinh lợi nhuận cao.\r\nFlamingo Crown Bay cũng sẽ là một dự án tiềm năng của Tập đoàn. Với tư duy phát triển mới cùng bề dày kinh nghiệm, tập đoàn sẽ kiến tạo nên một siêu đô thị nghỉ dưỡng đẳng cấp nhất Thanh Hóa.', 'http://maivietland.vn/du-an-bds/flamingo-crown-bay.html', '2020-02-04'),
(30, 'Bích Trần', NULL, NULL, 'Vị trí của dự án này có lợi thế gì?', 127, NULL, 'Dự án Flamingo Crown Bay được triển khai xây dựng tại huyện Hoằng Hóa tỉnh Thanh Hóa, thuộc địa phận xã Hoằng Trường và giáp ranh với các xã Hoằng Phụ, Hoằng Châu, Hoằng Phong. Dự án sở hữu vị trí đặc biệt “tọa sơn hướng hải”, nhìn thẳng ra đường bờ biển Hải Tiến dài 12km với nét đẹp hoang sơ, trong mát.\r\nVới vị trí này, dự án vừa có phong thủy cực đẹp lại vừa được thừa hưởng tiềm năng du lịch có sẵn từ khu du lịch sinh thái và nghỉ dưỡng Hoằng Hóa. \r\nCơ sở hạ tầng tại nơi đây thường xuyên được đầu tư đồng bộ, hoàn thiện và nâng cấp nên hệ thống giao thông rất hiện đại, thuận tiện di chuyển bằng nhiều phương tiện khác nhau với cao tốc Hà Nội – Ninh Bình – Thanh Hóa, cửa khẩu quốc tế Na Mèo, cảng nước sâu Nghi Sơn và cảng hàng không Thọ Xuân.\r\nChính bởi những ưu thế đó, Flamingo Crown Bay có tiềm năng thu hút nhiều du khách và mang giá trị thịnh vượng.', 'http://maivietland.vn/du-an-bds/flamingo-crown-bay.html', '2020-02-07'),
(31, 'Hải Nam Nguyễn', NULL, NULL, 'Tương lai dự án có tiềm năng tăng giá không?', 128, NULL, 'Dự án có tiềm năng tăng giá lớn bởi sở hữu những lợi thế sau:\r\n+ Tọa lạc tại mặt đường Trần Phú (57m), tuyến đường huyết mạch kết nối Hòn Gai với cao tốc Hạ Long - Vân Đồn - Móng Cái.\r\n\r\n+ Chân cầu Cửa Lục 3 & tuyến đường 56m (khởi công Quý III/2020 & hoàn thành 2021) kết nối Trung tâm cũ Hạ Long & Trung tâm HC tương lai Hoành Bồ.\r\n\r\n+ Các khu đất phía sau Dự án được quy hoạch là khu công viên sinh thái, đất trước mặt là công viên cây xanh theo quy hoạch Hạ Long mới ban hành 2019.\r\n\r\n+ Khung giá đất Hạ Long từ 2020 được điều chỉnh tăng 4 - 5 lần.\r\n\r\n+ KDT được triển khai thi công & hoàn thiện nhanh nhất Hà Khánh, tọa lạc tại mặt đường Trần Phú với hạ tầng đồng bộ nên có thể ở ngay từ 2021 (Điểm khác biệt hoàn toàn với các Khu lân cận).\r\n\r\n+ Sáp nhập Hoành Bồ về TP Hạ Long để trở thành trung tâm HC mới; hầm Cửa Lục khởi công 2020; Hạ Long định hướng là TP trực thuộc Trung Ương 2030; các CĐT lớn như Sungroup, FLC, Văn Phú, Hải Phát... đang lập quy hoạch những dự án rất lớn ngay phía trước & phía sau dự án.', 'http://maivietland.vn/du-an-bds/ruby-city-ha-long.html', '2020-02-07'),
(33, 'Huy Hoàng Nguyễn', NULL, NULL, 'Pháp lý dự án thì thế nào?', 127, NULL, 'Dự án Flamingo Crown Bay đã hoàn thiện xong công tác pháp lý, thủ tục giấy tờ và chỉ chờ thời gian công bố chính thức.', 'http://maivietland.vn/du-an-bds/flamingo-crown-bay.html', '2020-02-11'),
(34, 'Hoàng Minh Dũng', NULL, NULL, 'Tôi chỉ muốn mua đất nền, không muốn xây có được không?', 128, NULL, 'Dự án Ruby City Hạ Long là dự án Đất nền DUY NHẤT và CÒN LẠI CUỐI CÙNG của Hạ Long KHÔNG BẮT XÂY DỰNG. Vậy nên anh có thể chỉ đầu tư đất nền mà không cần xây dựng.', 'http://maivietland.vn/du-an-bds/ruby-city-ha-long.html', '2020-02-13'),
(35, 'Đức Minh Hoàng', NULL, NULL, 'Cho tôi tham khảo giá của dự án. Vốn ban đầu cần có khoảng bao nhiêu để sở hữu 1 căn hộ khách sạn?', 129, NULL, 'Nếu anh mua từ bây giờ thì giá cực kỳ tốt. Giá bất động sản tại Hạ Long luôn hợp lý hơn so với Nha Trang, Đà Nẵng nhưng tiềm năng tăng giá cực cao. Chỉ từ 333 triệu là anh có thể sở hữu căn hộ dịch vụ 5 sao phiên bản 257 căn GIỚI HẠN duy nhất tại Bãi Cháy. Hạ Long là thành phố du lịch có hạ tầng giao thông, du lịch phát triển bậc nhất miền Bắc, vậy nên đầu tư BĐS Hạ Long là một lựa chọn thông minh, là tài sản tích lũy sinh lời vĩnh viễn cho thế hệ con cháu mai sau.', 'http://maivietland.vn/du-an-bds/citadines-marina-ha-long.html', '2020-02-17'),
(36, 'Phạm Tiến Minh', NULL, NULL, 'Tỉ lệ lợi nhuận tôi nhận được là bao nhiêu? Tiềm năng tăng giá có cao không?', 129, NULL, 'Khách hàng sẽ được chia sẻ 85% lợi nhuận từ chương trình cho thuê của chủ đầu tư. Citadines Marina Hạ Long được quản lý bởi The Ascott - thương hiệu quản lý vận hành khách sạn hàng đầu, tỷ lệ lấp phòng luôn đạt 90%. Vậy nên dự án sẽ đem lại cho khách hàng nguồn lợi nhuận đều. Bên cạnh đó, Hạ Long là địa điểm thu hút khách du lịch lớn nhất cả nước, bất động sản view biển cũng không có nhiều. Vì vậy, quý khách hàng hoàn toàn có thể yên tâm về tiềm năng tăng giá cực cao của dự án.', 'http://maivietland.vn/du-an-bds/citadines-marina-ha-long.html', '2020-02-21'),
(37, 'Đạt Lê', NULL, NULL, 'Cơ hội nào cho nhà đầu tư?', 127, NULL, 'Là siêu phẩm nghỉ dưỡng 5 sao đẳng cấp nên Flamingo Crown Bay đích thị là cơ hội vàng cho các nhà đầu tư. Đây là dự án nghỉ dưỡng đầu tiên tại Thanh Hóa đáp ứng tiêu chuẩn quốc tế nên chắc chắn sẽ gây tò mò và thu hút nhiều khách hàng mong muốn được trải nghiệm. Chính điều này đã khiến cho dự án có thể thanh khoản dễ dàng và nguồn lợi nhuận siêu hấp dẫn. Bên cạnh đó, chủ đầu tư Flamingo Group uy tín bậc nhất thị trường cùng pháp lý dự án minh bạch cũng là \"bảo chứng\" lòng tin cho nhà đầu tư về một cơ hội đầu tư an toàn, sinh lời bền vững.', 'http://maivietland.vn/du-an-bds/flamingo-crown-bay.html', '2020-02-26'),
(38, 'Duyên Nguyễn', NULL, NULL, 'Dự án khi nào ra hàng?', 127, NULL, 'Dự kiến khoảng giữa T3/2020 sẽ ra hàng ạ. Theo dõi bài dự án này của Mai Việt để cập nhật nhanh nhất tiến độ của dự án ạ. Nếu có thắc mắc hoặc có nhu cầu tư vấn về dự án, chị vui lòng để lại thông tin, số điện thoại hoặc đăng ký nhận bộ tài liệu dự án để được Nhân viên Kinh doanh của Mai Việt giải đáp. Trân trọng.', 'http://maivietland.vn/du-an-bds/flamingo-crown-bay.html', '2020-02-28'),
(39, 'Nguyễn Tuấn', NULL, NULL, 'Dự án rất đẹp, chủ đầu tư thiết kế  rất hợp lý!', 127, NULL, 'Nếu anh Tuấn có mong muốn được tư vấn sâu hơn về dự án, anh vui lòng để lại thông tin số điện thoại, nhân viên kinh doanh của Mai Việt sẽ giải đáp mọi thắc mắc cho anh ạ.', 'http://maivietland.vn/du-an-bds/flamingo-crown-bay.html', '2020-02-29'),
(40, 'Khánh Đặng', NULL, NULL, 'Tôi muốn được tư vấn kỹ hơn về dự án', 127, NULL, 'Dạ anh vui lòng để lại Số điện thoại để chuyên viên kinh doanh của Mai Việt trực tiếp tư vấn chuyên sâu cho anh ạ.', 'http://maivietland.vn/du-an-bds/flamingo-crown-bay.html', '2020-03-05'),
(41, 'Nguyễn Hải Nam', NULL, NULL, 'Shophouse giá tầm bao nhiêu vậy?', 127, NULL, 'Chỉ từ 2 tỷ đồng anh đã có thể sở hữu 1 căn shophouse mặt biển rồi ạ. Anh để lại thông tin để Chuyên viên kinh doanh của Mai Việt gọi tư vấn sâu hơn cho anh về các căn và giá bán ạ.', 'http://maivietland.vn/du-an-bds/flamingo-crown-bay.html', '2020-03-11'),
(42, 'Tuan Nguyen Anh', NULL, NULL, 'sổ đỏ thế nào, đất lâu dài để ở hay đất thuê kinh doanh dịch vụ nhà ở', 127, NULL, 'Dạ dự án có quyền sở hữu 50 năm. Anh có thể vừa kinh doanh, để ở và đầu tư sinh lời ạ. Anh để lại thông tin để Chuyên viên kinh doanh của Mai Việt gọi tư vấn sâu hơn cho anh ạ.', 'http://maivietland.vn/du-an-bds/flamingo-crown-bay.html', '2020-03-13'),
(43, 'Nguyễn Anh Đạt', NULL, NULL, 'Chung cư đẹp quá', 119, NULL, 'Đây là chung cư đẹp và đẳng cấp nhất Mỹ Đình đấy ạ. Nếu anh cần biết thêm thông tin về dự án, anh vui lòng để lại số điện thoại, chuyên viên kinh doanh của Mai Việt sẽ gọi tư vấn chuyên sâu cho anh ạ.', 'http://maivietland.vn/du-an-bds/the-matrix-one-me-tri.html', '2020-03-26'),
(52, 'Tuấn', '0977572947', NULL, 'Tư vấn cho tôi căn shophouse mặt đường lớn', 115, NULL, NULL, 'http://maivietland.vn/du-an-bds/flamingo-night-street.html', '2020-05-23'),
(53, 'Thảo Nguyễn', NULL, NULL, 'Dự án đẹp quá!!!', 115, NULL, NULL, 'http://maivietland.vn/du-an-bds/flamingo-night-street.html', '2020-05-23'),
(95, 'Nguyễn Văn Hưng', NULL, NULL, 'Căn 2 phòng ngủ giá thế nào?', 135, NULL, 'Hiện tại căn 2PN đang có giá từ 2 tỷ ạ. Hiện tại, chủ đầu tư đang có nhiều chính sách ưu đãi tài chính tốt như: Hỗ trợ vay 65% GTCH với LS 0% trong 24 tháng và có Quà tân gia 15 triệu cho căn 2PN ạ. Anh vui lòng để lại số điện thoại để NVKD của Mai Việt liên hệ tư vấn chuyên sâu hơn cho anh ạ.', 'http://maivietland.vn/du-an-bds/the-terra-an-hung.html', '2020-06-26'),
(96, 'Nguyen Hoa', NULL, NULL, 'Toi can tu van', 135, NULL, 'Anh/chị cần tư vấn về căn hộ mấy phòng ngủ ạ? Anh/chị để lại số điện thoại để NVKD của Mai Việt liên hệ tư vấn chuyên sâu cho anh/chị ạ.', 'http://maivietland.vn/du-an-bds/the-terra-an-hung.html', '2020-06-26'),
(98, 'Dang Van Son', NULL, NULL, 'Cho tôi hỏi có căn 2PN nào tài chính 1,5 tỷ có thể mua ko?', 135, NULL, 'Dạ tài chính 1,5 tỷ có thể sở hữu căn 2PN của The Terra - An Hưng ạ. Anh vui lòng để lại số điện thoại để NVKD của Mai Việt liên hệ tư vấn chuyên sâu và chọn căn cho anh ạ.', 'http://maivietland.vn/du-an-bds/the-terra-an-hung.html', '2020-06-26'),
