INSERT INTO `tbl_product` (`id`, `cat_id`, `dis_id`, `user`, `name`, `address`, `hits`, `img`, `price`, `hot`, `tag`, `status`, `detail`, `tab1`, `tab2`, `tab3`, `tab4`, `tab5`, `tab6`, `tab7`, `tab8`, `tab9`, `content1`, `content2`, `content3`, `content4`, `content5`, `content6`, `content7`, `content8`, `content9`, `heading1`, `heading2`, `heading3`, `heading4`, `heading5`, `heading6`, `heading7`, `heading8`, `heading9`, `title`, `date`, `date_up`, `slug`, `description`, `keywords`, `robot`) VALUES
(113, '69', 43, 'Admin', 'The Legend Villas', 'Ngọc Thanh', 1548, 'the-legend-villas.jpg', '8 - 17 tỷ', 'false', NULL, 'true', '<ul>\r\n	<li><strong>Chủ đầu tư:</strong>&nbsp;Tập đo&agrave;n Flamingo Group</li>\r\n	<li><strong>Loại h&igrave;nh:&nbsp;</strong>Biệt thự, Shophose</li>\r\n	<li><strong>Legend garden villas:</strong>\r\n	<ul>\r\n		<li>Quy m&ocirc;: 38 căn&nbsp;</li>\r\n		<li>Tổng diện t&iacute;ch sản phẩm: 5.775 m&sup2;&nbsp;</li>\r\n		<li>100% biệt thự sinh th&aacute;i hướng ra đ&ocirc;̀i nghệ thu&acirc;̣t Art In The Forest&nbsp;</li>\r\n	</ul>\r\n	</li>\r\n	<li><strong>Luxury legend villas:</strong>\r\n	<ul>\r\n		<li>Quy m&ocirc;: 28 căn&nbsp;</li>\r\n		<li>Tổng diện t&iacute;ch sản phẩm: 7.785 m&sup2;&nbsp;</li>\r\n		<li>100% biệt thự sinh th&aacute;i hướng h&ocirc;̀</li>\r\n	</ul>\r\n	</li>\r\n</ul>', 'Tổng quan', 'Vị trí', 'Tiện ích', 'Legend Garden Villas', 'Luxury Legend Villas', 'Đầu tư', NULL, NULL, 'Chính sách bán hàng', '<p>Tập đo&agrave;n Flamingo l&agrave; nh&agrave; đầu tư h&agrave;ng đầu trong lĩnh vực bất động sản nghỉ dưỡng, du lịch, dịch vụ với triết l&yacute; kinh doanh &ldquo;ti&ecirc;n phong &ndash; tận tụy &ndash; bền vững&rdquo;, b&ecirc;n cạnh c&aacute;c mục ti&ecirc;u kinh doanh sinh lời, tập đo&agrave;n cũng l&agrave; đơn vị lu&ocirc;n đề cao vấn đề bảo tồn m&ocirc;i trường sinh th&aacute;i, đề cao vẻ đẹp thi&ecirc;n nhi&ecirc;n, văn h&oacute;a truyền thống, những gi&aacute; tr&iacute; nh&acirc;n văn g&oacute;p phần x&acirc;y dựng x&atilde; hội, cuộc sống tốt đẹp.</p>\r\n\r\n<p>Qua hơn 20 năm h&igrave;nh th&agrave;nh v&agrave; ph&aacute;t triển, Tập đo&agrave;n Flamingo li&ecirc;n tục ghi dấu ấn với c&aacute;c quần thể nghỉ dưỡng xanh hiện đại. Nghệ thuật kiến tr&uacute;c v&agrave; cảm hứng thi&ecirc;n nhi&ecirc;n h&ograve;a trộn, trở th&agrave;nh &ldquo;sợi chỉ đỏ&rdquo; xuy&ecirc;n suốt những sản phẩm nghỉ dưỡng của Flamingo.&nbsp; Mỗi căn biệt thự ở đ&acirc;y đều c&oacute; sự pha trộn giữa gi&aacute; trị hiện đại v&agrave; dấu ấn hoang sơ mang đến những cảm nhận mới lạ, một kh&ocirc;ng gian nghỉ dưỡng c&oacute; một kh&ocirc;ng hai m&agrave; kh&aacute;ch h&agrave;ng kh&ocirc;ng thể t&igrave;m thấy ở nơi n&agrave;o kh&aacute;c.</p>\r\n\r\n<p>Nối tiếp những th&agrave;nh c&ocirc;ng v&agrave; kế thừa tư duy &ldquo;xanh&rdquo;, Tập đo&agrave;n Flamingo đ&atilde; kiến tạo n&ecirc;n <strong><a href=\"https://maivietland.vn/du-an-bds/the-legend-villas.html\">The Legend Villas</a></strong>, hứa hẹn sẽ l&agrave; thỏi nam ch&acirc;m thu h&uacute;t kh&aacute;ch h&agrave;ng giới thượng lưu về đ&acirc;y nghỉ dưỡng v&agrave; l&agrave; sản phẩm đầu tư hấp dẫn của giới bất động sản. Với The Legend Villas, sự khác biệt trong thi&ecirc;́t k&ecirc;́ mang đậm phong cách riêng của từng chủ nhân sẽ tạo nên đẳng c&acirc;́p vượt trội của từng căn biệt thự. Theo lối kiến tr&uacute;c hiện đại, đẳng cấp nhưng được h&ograve;a trộn, chấm ph&aacute; bởi m&agrave;u xanh của thi&ecirc;n nhi&ecirc;n, <strong>The Legend Villas</strong> l&agrave; những biệt thự xanh - một mảnh gh&eacute;p ho&agrave;n hảo cho quần thể nghỉ dưỡng xanh của Flamingo Đại Lải Resort.</p>\r\n\r\n<p><a href=\"https://maivietland.vn/du-an-bds/the-legend-villas.html\"><strong>The Legend Villas</strong></a> c&oacute; hai loại h&igrave;nh biệt thự l&agrave; Luxury Legend Villas (căn đơn lập) v&agrave; Legend Garden Villas (căn song lập).</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"flamingo the legend villas\" src=\"/public/upload/images/flamingo-the-legend-villas/the-legend-villas.jpg\" /></p>', '<p><a href=\"https://maivietland.vn/du-an-bds/the-legend-villas.html\"><strong>The Legend Villas</strong></a> &ndash; Biệt thự huyền thoại, đ&uacute;ng như t&ecirc;n gọi, dự &aacute;n sẽ l&agrave; một &ldquo;si&ecirc;u phẩm&rdquo; l&agrave;m n&ecirc;n huyền thoại cho Flamingo Resort. The Legend Villas nằm trong B&aacute;n đảo trung t&acirc;m hồ tựa sơn, vượng hướng đẹp nhất Flamingo Đại Lải. Vị tr&iacute; n&agrave;y cực k&igrave; hiếm c&oacute;, tiếp x&uacute;c hồ nước trong l&agrave;nh, kh&ocirc;ng gian rộng lớn tho&aacute;ng đ&atilde;ng, lưu th&ocirc;ng kh&iacute; trời v&agrave; thế gi&oacute; m&aacute;t vừa phải. Nếu đến với The Legend Villas v&agrave;o m&ugrave;a h&egrave;, chắc chắn qu&yacute; kh&aacute;ch h&agrave;ng sẽ qu&ecirc;n đi c&aacute;i n&oacute;ng bỏng r&aacute;t của đ&ocirc; thị m&agrave; đắm ch&igrave;m, tận hưởng sự sảng khoải, trong l&agrave;nh của gi&oacute; trời.&nbsp;</p>\r\n\r\n<p>Phải n&oacute;i rằng The Legend Villas được tập đo&agrave;n Flamingo v&agrave; thi&ecirc;n nhi&ecirc;n ưu đ&atilde;i đặc biệt bởi được tọa lạc cạnh khu vực hồ Đại Lải rộng lớn, hữu t&igrave;nh, n&uacute;i non tr&ograve;n trịa, c&acirc;y cối tốt tươi, phong b&igrave;nh s&oacute;ng lặng v&agrave; long sinh khởi sắc. Với vị tr&iacute; phong thủy đắc địa n&agrave;y, gia chủ tương lai của c&aacute;c căn biệt thự The Legend Villas sẽ được hưởng những luồng sinh kh&iacute; an l&agrave;nh, gi&uacute;p sức khỏe cũng như vận mệnh của gia chủ lu&ocirc;n khỏe mạnh, b&igrave;nh an. V&igrave; l&iacute; do đ&oacute;, The Legend Villas l&agrave; nơi an cư l&yacute; tưởng cho kh&aacute;ch h&agrave;ng.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"vị trí flamingo the legend villas\" src=\"/public/upload/images/flamingo-night-street/flamingo-night-street-vitri1.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"vị trí flamingo the legend villas\" src=\"/public/upload/images/flamingo-the-legend-villas/the-legend-villas-vitri.jpg\" /></p>', '<p>Kh&ocirc;ng đơn giản chỉ l&agrave; sống sang, cuộc sống tại <a href=\"https://maivietland.vn/du-an-bds/the-legend-villas.html\"><strong>The Legend Villas</strong></a> l&agrave; sống chất. Legned &ndash; Chất sống huyền thoại của mỗi chủ biệt thự khi tự k&yacute; t&ecirc;n của m&igrave;nh trong một cộng đồng đẳng cấp.&nbsp;</p>\r\n\r\n<p>Tại The&nbsp; Legend Villas, h&agrave;ng loạt tiện &iacute;ch hiện đại lu&ocirc;n sẵn s&agrave;ng đ&aacute;p ứng mọi ti&ecirc;u chuẩn của cuộc sống thượng lưu với những dịch vụ 5 sao: bể bơi ri&ecirc;ng ngo&agrave;i trời, khu vườn đi dạo hướng hồ v&agrave; hướng đồi Art in the Forest, tổ hợp nh&agrave; h&agrave;ng, ph&ograve;ng Gym, Spa, Seva Spa &amp; Beauty Destination, khu sinh hoạt cộng đồng&hellip;</p>\r\n\r\n<p>Với thẻ đặc quyền &ndash; Hộ chiếu nghỉ dưỡng All In Passport Diamond d&agrave;nh ri&ecirc;ng cho chủ nh&acirc;n biệt thự, mọi dịch vụ tại đ&acirc;y đều hướng tới gi&aacute; trị sống sang trọng giữa thi&ecirc;n nhi&ecirc;n, một cuộc sống nghỉ dưỡng thượng lưu đ&iacute;ch thực.</p>\r\n\r\n<p>Biệt thự tại The Legend Villas với kh&ocirc;ng gian sống xanh, những tiện &iacute;ch, dịch vụ đẳng cấp đ&uacute;ng chất LEGEND l&agrave; nơi an cư l&yacute; tưởng của những cư d&acirc;n c&oacute; xu hướng t&igrave;m đến lối sống tao nh&atilde;, t&aacute;ch biệt với những ồn &agrave;o, bận rộn thường nhật, để t&igrave;m về nơi c&oacute; kh&iacute; hậu trong l&agrave;nh.</p>', '<p><strong><a href=\"https://maivietland.vn/du-an-bds/the-legend-villas.html\">Legend Garden Villas</a> </strong>c&oacute; quy m&ocirc; 38 căn, tổng diện t&iacute;ch 5.775m2. 100% biệt thự sinh th&aacute;i hướng ra đồi nghệ thuật Art in the Forest &ndash; kh&ocirc;ng gian nghệ thuật v&agrave; văn h&oacute;a, tại đ&acirc;y các tác ph&acirc;̉m h&ocirc;̣i họa - đi&ecirc;u khắc b&ecirc;n h&ocirc;̀, tr&ecirc;n thảm cỏ, trong rừng th&ocirc;ng,&hellip; trải dài trong khu nghỉ dưỡng g&oacute;p phần t&ocirc;n vinh ngh&ecirc;̣ thu&acirc;̣t, đ&ocirc;̀ng thời th&ecirc;̉ hi&ecirc;̣n sự giao hòa giữa các y&ecirc;́u t&ocirc;́ cảnh quan tự nhi&ecirc;n và nét đẹp tạo n&ecirc;n từ bàn tay con người. C&aacute;c căn biệt thự của Legend Garden Villas dựa lưng v&agrave;o đồi th&ocirc;ng thơ mộng, tạo n&ecirc;n v&ugrave;ng cảnh quan ri&ecirc;ng tư, y&ecirc;n tĩnh cho chủ nh&acirc;n.Hai mẫu biệt thự Legend Garden Villas kh&aacute;c biệt về phong c&aacute;ch thiết kế, song đều ch&uacute; trọng đề cao khoảng kh&ocirc;ng v&agrave; &aacute;nh s&aacute;ng qua c&aacute;c cửa k&iacute;nh lớn v&agrave; ban c&ocirc;ng rộng mở, gi&uacute;p l&agrave;m nổi bật những chủ thể trang tr&iacute; một c&aacute;ch hiệu quả. Thi&ecirc;n nhi&ecirc;n được lồng gh&eacute;p kh&eacute;o l&eacute;o, gi&uacute;p chủ nh&acirc;n biệt thự c&oacute; những gi&acirc;y ph&uacute;t thư gi&atilde;n thoải m&aacute;i trong kh&ocirc;ng gian xanh. C&aacute;c căn biệt thự đều được trang bị hệ thống nội thất sang trọng theo &yacute; muốn của chủ nh&acirc;n, k&ecirc;́t hợp ánh sáng tự nhi&ecirc;n đ&ecirc;̉ tạo n&ecirc;n m&ocirc;̣t kh&ocirc;ng gian tho&aacute;ng đ&atilde;ng, nhẹ nhàng.</p>', '<p><a href=\"https://maivietland.vn/du-an-bds/the-legend-villas.html\"><strong>Luxury Legend Villas</strong></a> &ndash; Biệt thự si&ecirc;u sang bể bơi ch&acirc;n m&acirc;y giữa hồ Đại Lải. Với quy m&ocirc; 28 căn, tổng diện t&iacute;ch sản phẩm 7.785m2, Luxury Legend Villas c&oacute; 100% biệt thự sinh th&aacute;i hướng hồ, tầm nh&igrave;n rộng, được phủ xanh, bao bọc bởi h&agrave;ng ng&agrave;n hec-ta rừng th&ocirc;ng, thu trọn cảnh quan n&uacute;i rừng trong tầm mắt. Đặc biệt, c&aacute;c biệt thự Luxury Legend Villas sẽ thỏa m&atilde;n những vị kh&aacute;ch ưa th&iacute;ch sự l&atilde;ng mạn khi biệt thự c&oacute; view ngắm b&igrave;nh minh v&agrave; ho&agrave;ng h&ocirc;n đẹp nhất Flamingo Đại Lải Resort. Sự kh&aacute;c biệt của Luxury Legend Villas so với biệt thự th&ocirc;ng thường c&ograve;n nằm ở bể bơi v&ocirc; cực độc đ&aacute;o v&agrave; kiến tr&uacute;c minimalist hiện đại kết hợp với kiến tr&uacute;c xanh huyền thoại của Flamingo, mang thi&ecirc;n nhi&ecirc;n v&agrave;o cuộc sống với vườn v&agrave; cảnh quan xung quanh, đem lại cho chủ nh&acirc;n biệt thự phong c&aacute;ch cực đẳng cắp.</p>\r\n\r\n<p>Biệt thự Luxury Legend Villas độc đ&aacute;o với những mảng khối r&otilde; r&agrave;ng v&agrave; đường n&eacute;t khỏe khoắn, nhưng vẫn c&oacute; sự h&agrave;i h&ograve;a, uyển chuyển. Tất cả chi tiết được kết hợp để tạo n&ecirc;n một tổng thể ho&agrave;n hảo nhất.</p>', '<p><img alt=\"\" src=\"http://maivietland.vn/public/uploads/the-legend-villas/the-legend-villas3.jpg\" style=\"height:450px; width:800px\" />Quần thể <a href=\"https://maivietland.vn/du-an-bds/the-legend-villas.html\"><strong>The Legend Villas</strong></a> l&agrave; một trong những &ldquo;&aacute;t chủ b&agrave;i&rdquo; của tập đo&agrave;n Flamingo, l&agrave; sản phẩm bất động sản lu&ocirc;n tăng gi&aacute; v&agrave; tăng gi&aacute; cao theo thời gian, sinh lời bền vững. The Legend Villas c&oacute; cơ sở hạ tầng hiện đại, đẳng cấp, tọa lạc tại khu đất đắc địa n&ecirc;n cũng n&acirc;ng tầm gi&aacute; trị cho c&aacute;c căn biệt thự của dự &aacute;n. Mặt kh&aacute;c, The Legend Villas l&agrave; một sản phẩm đầu tư rủi ro rất thấp, tiệm cận 0 bởi Flamingo Đại Lải đ&atilde; đi v&agrave;o hoạt động 10 năm, chưa khi n&agrave;o c&oacute; dấu hiệu hạ nhiệt, tập đo&agrave;n Flamingo cũng l&agrave; đơn vị uy t&iacute;n, nhiều năm kinh nghiệm n&ecirc;n kh&aacute;ch h&agrave;ng ho&agrave;n to&agrave;n c&oacute; thể an t&acirc;m, tin tưởng khi lựa chọn đầu tư v&agrave;o The Legend Villas.&nbsp;</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, ph&aacute;p l&yacute; v&agrave; hạ tầng r&otilde; r&agrave;ng n&ecirc;n cũng giảm thiểu rủi ro rất lớn. Về bản chất th&igrave; đất sạch sẽ lu&ocirc;n tăng gi&aacute; theo thời gian. Một lợi &iacute;ch nữa khi kh&aacute;ch h&agrave;ng lựa chọn The Legend Villas l&agrave; được trở th&agrave;nh một trong những kh&aacute;ch h&agrave;ng VIP với 4 tấm thẻ All in Passport Diamond ưu đ&atilde;i mọi tiện &iacute;ch tại Flamingo Đại Lải - Top 10 Resort đẹp nhất thế giới. Cho n&ecirc;n h&atilde;y l&agrave; một nh&agrave; đầu tư th&ocirc;ng m&igrave;nh v&agrave; đầu tư v&agrave;o The Legend Villas.</p>', '<ul>\r\n	<li>&nbsp;</li>\r\n</ul>', NULL, '<ul>\r\n	<li>Tặng 4 thẻ All In Passport Diamond đặc quyền chủ biệt thự</li>\r\n	<li>Tặng 03 đ&ecirc;m nghỉ tại Biệt Thự 2 Ph&ograve;ng Ngủ cao cấp tại Flamingo Đại Lải Resort</li>\r\n	<li>Hỗ trợ l&atilde;i suất 0% l&ecirc;n đến 18&nbsp;th&aacute;ng l&ecirc;n tới 70% GTBT</li>\r\n	<li>Tặng g&oacute;i nội thất 1 tỷ đồng</li>\r\n	<li>Tặng g&oacute;i thi c&ocirc;ng s&acirc;n vườn VIP 500 triệu đồng</li>\r\n</ul>', 'The Legend Villas – Mảnh ghép hoàn hảo cho “bức họa xanh” Flamingo', 'Vị trí “tựa sơn hướng thủy”', 'Tiện ích Resort hàng đầu thế giới', 'Legend Garden Villas - Tôn vinh chất sống Huyền thoại', 'Luxury Legend Villas - Khu biệt thự đẳng cấp', 'The Legend Villas - Lựa chọn thông minh của giới đầu tư', NULL, NULL, 'Ưu đãi dành cho khách hàng đặt cọc sớm', 'The Legend Villas - dự án biệt thự  đầu tư thông minh nhất hiện nay', '05/19/2020', NULL, 'the-legend-villas', 'The Legend Villas có hai loại hình biệt thự là Luxury Legend Villas và Legend Garden Villas. dự án sẽ là một “siêu phẩm” làm nên huyền thoại cho Flamingo Resort', 'biệt thự the legend, biệt thự the legend villas, the legend villas, the legend villas flamingo, the legend villas đại lải', 'index, follow'),
(138, '69', 57, 'Admin', 'An Lạc Green Symphony', 'xã Vân Canh', 1010, 'Screenshot_1.png', NULL, NULL, NULL, 'true', '<ul>\r\n	<li style=\"text-align:justify\">T&ecirc;n dự &aacute;n: An Lạc Green Symphony</li>\r\n	<li style=\"text-align:justify\">Địa điểm: x&atilde; V&acirc;n Canh, huyện Ho&agrave;i Đức, TP. H&agrave; Nội</li>\r\n	<li style=\"text-align:justify\">Chủ đầu tư: C&ocirc;ng ty cổ phần đầu tư An Lạc</li>\r\n	<li style=\"text-align:justify\">Nh&agrave; thầu thi c&ocirc;ng: Tổng c&ocirc;ng ty XNK x&acirc;y dựng Việt Nam (VINACONEX)</li>\r\n	<li style=\"text-align:justify\">Tổng diện t&iacute;ch khu đất: 70ha</li>\r\n	<li style=\"text-align:justify\">Ph&acirc;n khu thấp tầng: 1200 căn biệt thự đơn lập, song lập v&agrave; liền kề</li>\r\n	<li style=\"text-align:justify\">Ph&acirc;n khu cao tầng: 20 t&ograve;a chung cư cao từ 21 đến 33 tầng, số lượng 11.000 căn (1 - 3PN)</li>\r\n	<li style=\"text-align:justify\">Khởi c&ocirc;ng ch&iacute;nh thức: Qu&yacute; IV/2018</li>\r\n	<li style=\"text-align:justify\">H&igrave;nh thức sở hữu: Sổ đỏ vĩnh viễn</li>\r\n</ul>', 'Tổng quan', 'Vị trí', 'Mặt bằng', 'Tiện ích', 'Đầu tư', NULL, NULL, NULL, 'Chính sách', '<p style=\"text-align:justify\">Thị trường bất động sản khu vực ph&iacute;a T&acirc;y đang n&oacute;ng l&ecirc;n từng ng&agrave;y với sự xuất hiện của h&agrave;ng loạt c&aacute;c dự &aacute;n lớn. Trong đ&oacute;, dự &aacute;n An Lạc Green Symphony được đ&aacute;nh gi&aacute; c&oacute; tiềm năng ph&aacute;t triển vượt trội nhất. Kh&ocirc;ng chỉ mang đến một kh&ocirc;ng gian nghỉ dưỡng l&yacute; tưởng, m&agrave; nơi đ&acirc;y c&ograve;n l&agrave; cơ hội đầu tư sinh lời bền vững.</p>\r\n\r\n<p style=\"text-align:justify\">An Lạc Green Symphony l&agrave; dự &aacute;n khu đ&ocirc; thị kiểu mẫu của chủ đầu tư C&ocirc;ng ty cổ phần Đầu tư An Lạc nằm ở x&atilde; V&acirc;n Canh Huyện Ho&agrave;i Đức được quy hoạch đồng bộ, hiện đại gồm c&aacute;c khu nh&agrave; ở cao tầng v&agrave; thấp tầng, c&aacute;c c&ocirc;ng tr&igrave;nh c&ocirc;ng cộng c&ocirc;ng vi&ecirc;n, trường học, hồ điều h&ograve;a, si&ecirc;u thị, bệnh viện phục vụ d&acirc;n cư đ&ocirc; thị. Trong đ&oacute; điểm nhấn cảnh quan l&agrave; khu Hồ điều h&ograve;a tạo n&ecirc;n một khu đ&ocirc; thị đ&aacute;ng sống, nơi con người h&ograve;a m&igrave;nh c&ugrave;ng thi&ecirc;n nhi&ecirc;n.</p>\r\n\r\n<p style=\"text-align:justify\"><strong>V&igrave; sao n&ecirc;n lựa chọn mua An Lạc Green Symphony</strong></p>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\">Vị tr&iacute; gần trung t&acirc;m - Thuận tiện kết nối</li>\r\n	<li style=\"text-align:justify\">Ph&aacute;p l&yacute; minh bạch, bảo đảm</li>\r\n	<li style=\"text-align:justify\">An ninh an to&agrave;n tuyệt đối</li>\r\n	<li style=\"text-align:justify\">Đa dạng loại h&igrave;nh sản phẩm</li>\r\n	<li style=\"text-align:justify\">Dịch vụ tiện &iacute;ch đồng bộ</li>\r\n	<li style=\"text-align:justify\">Tiềm năng tăng gi&aacute; cao</li>\r\n</ol>', '<p style=\"text-align:justify\">Khu đ&ocirc; thị V&acirc;n Canh An Lạc nằm ở vị tr&iacute; thuận lợi với 2 mặt tiếp gi&aacute;p của trục đường 70 Xu&acirc;n Phương v&agrave; đường v&agrave;nh đai 3.5, cửa ng&otilde; ph&iacute;a T&acirc;y thủ đ&ocirc; H&agrave; Nội. Đ&acirc;y l&agrave; một trong những khu vực c&oacute; tốc độ đ&ocirc; thị ho&aacute; v&agrave; ph&aacute;t triển nhanh nhất H&agrave; Nội hiện nay. Từ vị tr&iacute; dự &aacute;n chỉ c&aacute;ch trung t&acirc;m hội nghị Quốc gia 7km, bến xe Mỹ Đ&igrave;nh v&agrave; s&acirc;n vận động khoảng 6km.</p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>C&aacute;c hướng đường từ dự &aacute;n v&agrave;o trung t&acirc;m Th&agrave;nh phố:</strong></h3>\r\n\r\n<p style=\"text-align:justify\">Hướng 1: Từ hướng đường Xu&acirc;n Phương &ndash; tỉnh lộ 70 kết nối trục Đại Lộ Thăng Long thẳng đến Trung T&acirc;m Hội Nghị Quốc Gia đi v&agrave;o tới khu vực nội th&agrave;nh th&ocirc;ng qua Trần Duy Hưng &ndash; Nguyễn Ch&iacute; Thanh.</p>\r\n\r\n<p style=\"text-align:justify\">Hướng 2: Đi hết đường Trần Hữu Dực đến s&acirc;n vận động Quốc gia Mỹ Đ&igrave;nh kết nối với khu vực Mỹ Đ&igrave;nh, Cầu Giấy chỉ 10 ph&uacute;t l&aacute;i xe.</p>\r\n\r\n<p style=\"text-align:justify\">Hướng 3: Từ đường v&agrave;nh đai 3.5 thẳng l&ecirc;n quốc lộ 32 nối thẳng l&ecirc;n Hồ T&ugrave;ng Mậu &ndash; Xu&acirc;n Thuỷ v&agrave; Cầu Giấy.</p>\r\n\r\n<p style=\"text-align:justify\">Cơ sở hạ tầng kỹ thuật xung quanh dự &aacute;n Green Symphony&nbsp; đ&atilde; đồng bộ v&agrave; ho&agrave;n thiện, xung quanh dự &aacute;n l&agrave; c&aacute;c trường mầm non, trường cấp I, II, III; trường Quốc tế của quận Nam Từ Li&ecirc;m v&agrave; hệ thống bệnh viện: Bệnh viện Thể Thao, bệnh viện 198, trung t&acirc;m y tế quận Nam Từ Li&ecirc;m, bệnh viện Vinmec 5 sao,&hellip;</p>', '<p style=\"text-align:justify\">Dự &aacute;n Anlac Green Symphony được quy hoạch tr&ecirc;n khu đất rộng 70ha, tập trung ph&aacute;t triển với c&aacute;c d&ograve;ng sản phẩm ch&iacute;nh: biệt thự đơn lập &amp; song lập, liền kề v&agrave; nh&agrave; cao tầng đồng bộ hạ tầng kỹ thuật &ldquo;điện &ndash; đường &ndash; trường &ndash; trạm&rdquo; tạo dựng n&ecirc;n một khu đ&ocirc; thị hiện đại, văn minh.</p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>LIỀN KỀ&nbsp;</strong></h3>\r\n\r\n<p style=\"text-align:justify\">Số lượng: 372 căn</p>\r\n\r\n<p style=\"text-align:justify\">Diện t&iacute;ch trung b&igrave;nh: 85.6m2 &ndash; 115.16m2</p>\r\n\r\n<p style=\"text-align:justify\">Số tầng cao:3 tầng + 1 tum</p>\r\n\r\n<p style=\"text-align:justify\">Gi&aacute; b&aacute;n: 4 &ndash; 6 tỷ/căn</p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>BIỆT THỰ</strong></h3>\r\n\r\n<p style=\"text-align:justify\">Số lượng: 276 căn</p>\r\n\r\n<p style=\"text-align:justify\">Diện t&iacute;ch: 188m2 &ndash; 442m2</p>\r\n\r\n<p style=\"text-align:justify\">Số tầng cao: 2 Tầng v&agrave; 1 tum</p>\r\n\r\n<p style=\"text-align:justify\">Gi&aacute; b&aacute;n: 8 &ndash; 12 tỷ</p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>SHOPHOUSE&nbsp;</strong></h3>\r\n\r\n<p style=\"text-align:justify\">Số lượng: 175 căn</p>\r\n\r\n<p style=\"text-align:justify\">Diện t&iacute;ch trung b&igrave;nh: 90m2 &ndash; 126m2</p>\r\n\r\n<p style=\"text-align:justify\">Số tầng cao: 3 tầng + 1 tum</p>\r\n\r\n<p style=\"text-align:justify\">Gi&aacute; b&aacute;n: 6 &ndash; 8 tỷ/căn</p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>CHUNG CƯ&nbsp;</strong></h3>\r\n\r\n<p style=\"text-align:justify\">Số lượng: 20.000 căn</p>\r\n\r\n<p style=\"text-align:justify\">Số to&agrave; th&aacute;p: 20 to&agrave;</p>\r\n\r\n<p style=\"text-align:justify\">Chiều cao: 21 đến 33 tầng</p>\r\n\r\n<p style=\"text-align:justify\">Mức gi&aacute;: 800 &ndash; 1 tỷ/căn</p>', '<p style=\"text-align:justify\">Hướng đến một khu đ&ocirc; thị kiểu mẫu đẳng cấp ngay cửa ng&otilde; trung t&acirc;m tại ph&iacute;a T&acirc;y H&agrave; Nội, dự &aacute;n V&acirc;n Canh An Lạc Green Symphony được x&acirc;y dựng theo m&ocirc; h&igrave;nh với cảnh quan kiến tr&uacute;c độc đ&aacute;o d&agrave;nh được sự đầu tư t&acirc;m huyết của tập đo&agrave;n H&agrave; Đ&ocirc; khi trang bị chuỗi tiện &iacute;ch v&agrave; dịch vụ cao cấp đạt ti&ecirc;u chuẩn quốc tế đ&aacute;p ứng những nhu cầu sống khắt khắt khe như một khu nghỉ dưỡng cao cấp.</p>\r\n\r\n<p style=\"text-align:justify\">Bạn sẽ được hưởng những tiện &iacute;ch tuyệt vời v&agrave; đầy đủ nhất khi sinh sống tại đ&acirc;y. Với c&aacute;c khu si&ecirc;u thị, nh&agrave; h&agrave;ng, khu ẩm thực, qu&aacute;n c&agrave; ph&ecirc;&hellip; gi&uacute;p giải quyết vấn đề ăn uống v&agrave; sinh hoạt hằng ng&agrave;y của cư d&acirc;n một c&aacute;ch tốt nhất.</p>\r\n\r\n<p style=\"text-align:justify\">B&ecirc;n cạnh đ&oacute; l&agrave; c&aacute;c khu vui chơi giải tr&iacute;, bể bơi 4 m&ugrave;a, s&acirc;n golf, trung t&acirc;m thương mại, ph&ograve;ng thể h&igrave;nh, spa&hellip; Gi&uacute;p thỏa m&atilde;n nhu cầu mua sắm v&agrave; nghỉ dưỡng của mọi cư d&acirc;n.</p>\r\n\r\n<h3><strong>AN NINH AN TO&Agrave;N TUYỆT ĐỐI TẠI AN LẠC GREEN SYMPHONY</strong></h3>\r\n\r\n<p>Với hệ thống an ninh ti&ecirc;n tiến, đội ngũ bảo vệ chuy&ecirc;n nghiệp trang bị c&ugrave;ng c&aacute;c thiết bị hiện đại được lắp đặt bảo mật 3 lớp cho dự &aacute;n gồm: trạm g&aacute;c cổng ra v&agrave;o, bảo vệ trực g&aacute;c tại c&aacute;c ph&acirc;n khu, camera gi&aacute;m s&aacute;t 24/24h lắp đặt tr&ecirc;n c&aacute;c tuyến đường vị tr&iacute; trọng yếu của khu đ&ocirc; thị gi&uacute;p Qu&yacute; cư d&acirc;n c&oacute; thể ho&agrave;n to&agrave;n an t&acirc;m về an ninh, an to&agrave;n v&agrave; bảo mật khi sinh sống tại khu đ&ocirc; thị V&acirc;n Canh An Lạc Symphony.</p>\r\n\r\n<p style=\"text-align:justify\">Ngo&agrave;i ra, hệ thống điện đường, trạm bơm, cấp tho&aacute;t nước, vệ sinh m&ocirc;i trường về cơ bản đ&atilde; ho&agrave;n th&agrave;nh. Gi&uacute;p cho cư d&acirc;n được sinh sống trong m&ocirc;i trường trong sạch, kh&ocirc;ng &ocirc; nhiễm, kh&oacute;i bụi.</p>', '<p style=\"text-align:justify\">Dự &aacute;n đ&atilde; ho&agrave;n tất đầy đủ mọi thủ tục v&agrave; giấy tờ ph&aacute;p l&yacute; để ch&agrave;o b&aacute;n ra thị trường bao gồm: quy hoạch chi tiết x&acirc;y dựng 1/500, giấy ph&eacute;p x&acirc;y dựng, ho&agrave;n th&agrave;nh nghĩa vụ đ&oacute;ng thuế cho nh&agrave; nước, ph&ecirc; duyệt ph&ograve;ng ch&aacute;y chữa ch&aacute;y, văn bản chấp thuận đủ điều kiện ch&agrave;o b&aacute;n. Ngo&agrave;i ra, dự &aacute;n được ng&acirc;n h&agrave;ng BIDV trực tiếp bảo l&atilde;nh v&agrave; hỗ trợ vay đảm bảo tối đa quyền lợi người mua.</p>\r\n\r\n<p style=\"text-align:justify\">Khu đ&ocirc; thị An Lạc Symphony l&agrave; một trong những khu đất &ldquo;hiếm hoi&rdquo; c&ograve;n lại nằm ngay gần khu trung t&acirc;m của Thủ đ&ocirc;, hội tụ những lợi thế vượt trội so với c&aacute;c dự &aacute;n tr&ecirc;n thị trường hiện nay. Đặc biệt, mức gi&aacute; của An Lạc Green Symphony trong đợt đầu ch&agrave;o b&aacute;n kh&aacute; hấp dẫn trung b&igrave;nh chỉ từ 40 &ndash; 45 triệu đồng/m2, mang đến cơ hội đầu tư sinh lời hấp dẫn d&agrave;nh cho c&aacute;c nh&agrave; đầu tư th&ocirc;ng th&aacute;i.</p>', NULL, NULL, NULL, NULL, 'TỔNG QUAN DỰ ÁN AN LẠC GREEN SYMPHONY', 'VỊ TRÍ DỰ ÁN AN LẠC GREEN SYMPHONY', 'QUY HOẠCH TỔNG THỂ DỰ ÁN', 'DỊCH VỤ TIỆN ÍCH ĐỒNG BỘ, HIỆN ĐẠI', 'PHÁP LÝ MINH BẠCH - TIỀM NĂNG TĂNG GIÁ CAO', NULL, NULL, NULL, NULL, 'An Lạc Green Symphony - Bảng giá + Thông tin từ CĐT', '07/18/2020', NULL, 'an-lac-green-symphony', 'An Lạc Green Symphony là dự án khu đô thị kiểu mẫu của chủ đầu tư Công ty cổ phần Đầu tư An Lạc nằm ở xã Vân Canh Huyện Hoài Đức được quy hoạch đồng bộ, hiện đại', NULL, 'index, follow'),
(115, '69', 43, 'Admin', 'Flamingo Night Street', 'Cổng Nam của dự án Flamingo Đại Lải Resort', 2106, 'flamingo-night-street-1.png', '5 tỷ', 'false', NULL, 'true', '<ul>\r\n	<li><strong>Chủ Đầu Tư</strong>: Tập đo&agrave;n Flamingo Group</li>\r\n	<li><strong>Tổng diện t&iacute;ch</strong>: 123ha</li>\r\n	<li>100% biệt thự 2 mặt tiền</li>\r\n	<li><strong>Shophouse Gadend</strong>\r\n	<ul>\r\n		<li>Số lượng : 84 căn</li>\r\n		<li>Diện t&iacute;ch s&agrave;n : 365m2</li>\r\n		<li>Chiều cao: 3,5 tầng</li>\r\n	</ul>\r\n	</li>\r\n	<li><strong>Boutique Garden Villa</strong>\r\n	<ul>\r\n		<li>Số lượng : 56 căn</li>\r\n		<li>Diện t&iacute;ch s&agrave;n : 395m2</li>\r\n	</ul>\r\n	</li>\r\n</ul>', 'Tổng quan', 'Vị trí', 'Shophouse Garden', 'Boutique Garden Villa', 'Tiện ích', 'Đầu tư', NULL, NULL, 'Chính sách bán hàng', '<p>Khi lựa chọn Flamingo Đại Lải Resort l&agrave; nơi nghỉ dưỡng, chắc hẳn mỗi kh&aacute;ch h&agrave;ng đều c&oacute; mong muốn được tận hưởng cuộc sống thư th&aacute;i, y&ecirc;n b&igrave;nh, đắm ch&igrave;m v&agrave;o thi&ecirc;n nhi&ecirc;n, v&agrave;o những điều thuần khiết, trong l&agrave;nh nhất. Nhưng mọi điều trong cuộc sống đều diễn ra, biến đổi như một bản nhạc, c&oacute; nốt trầm, nốt bổng, c&oacute; l&uacute;c du dương, c&oacute; l&uacute;c lại rộn r&agrave;ng, mạnh mẽ. Những thanh &acirc;m đ&oacute; khiến cuộc sống c&oacute; th&ecirc;m nhiều sắc m&agrave;u, th&uacute; vị v&agrave; kh&ocirc;ng giậm ch&acirc;n ở những điều cũ kĩ, tẻ nhạt khiến ta buồn ch&aacute;n. Giữa một kh&ocirc;ng gian xanh tưởng chừng hoang sơ, vắng lặng, với những nốt nhạc trầm &ldquo;vỗ về&rdquo; t&acirc;m hồn du kh&aacute;ch, <a href=\"https://maivietland.vn/du-an-bds/flamingo-night-street.html\"><strong>Flamingo Night Street</strong></a> xuất hiện như một &ldquo;bản nhạc&rdquo; s&ocirc;i động t&ocirc; điểm, chấm ph&aacute; th&ecirc;m cho bản giao hưởng Flamingo Resort th&ecirc;m tuyệt mỹ.</p>\r\n\r\n<p>Lấy cảm hứng từ c&aacute;c con phố đi bộ nổi tiếng thế giới như thủ phủ shopping Chuo (Nhật Bản) hay Ximending s&ocirc;i động (Đ&agrave;i Loan), Biệt thự&nbsp;Flamingo&nbsp;tạo n&ecirc;n một kh&ocirc;ng gian sầm uất, s&ocirc;i động của phố thị rực rỡ giữa miền sinh th&aacute;i Flamingo Đại Lải Resort. Trong tương lai Flamingo Night Street sẽ l&agrave; khu phố hoa lệ, thu h&uacute;t kh&aacute;ch du lịch v&agrave; cư d&acirc;n Flamingo về đ&acirc;y vui chơi, giải tr&iacute;, mua sắm. Nơi đ&acirc;y sẽ l&agrave; nơi tổ chức c&aacute;c lễ hội phục vụ cư d&acirc;n v&agrave; du kh&aacute;ch như lễ hội hoa, lễ hội ẩm thực,... Khi ban ng&agrave;y du kh&aacute;ch được chi&ecirc;m ngưỡng, tận hưởng m&agrave;u xanh của thi&ecirc;n nhi&ecirc;n, những kiệt t&aacute;c nghệ thuật, kiến tr&uacute;c xanh đỉnh cao của c&aacute;c biệt thự cao cấp th&igrave; khi b&oacute;ng tối bao ch&ugrave;m, m&agrave;n đ&ecirc;m bu&ocirc;ng xuống, Biệt thự Flamingo&nbsp;sẽ l&ecirc;n đ&egrave;n rực rỡ, đưa qu&yacute; kh&aacute;ch v&agrave;o kh&ocirc;ng gian sống nhộn nhịp, tấp nấp.&nbsp;</p>\r\n\r\n<p><a href=\"https://maivietland.vn/du-an-bds/flamingo-night-street.html\"><strong>Flamingo Night Street</strong></a> bao gồm hai d&ograve;ng sản phẩm Shophouse Garden v&agrave; Boutique Garden Villa sang trọng đi k&egrave;m với hệ thống tiện &iacute;ch đồ sộ, độc đ&aacute;o. Đặc biệt hơn, 100% sản phẩm tại Flamingo Night Street được thiết kế với 02 mặt tiền vừa phục vụ cuộc sống sinh hoạt của cư d&acirc;n, vừa mang tới những lựa chọn giải tr&iacute; tuyệt hảo cho du kh&aacute;ch v&agrave; cơ hội đầu tư sinh lời bền vững cho nh&agrave; đầu tư.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Flamingo Night Street – “Bản nhạc” sôi động giữa miền xanh\" src=\"/public/upload/images/flamingo-night-street/so-do-phan-khu-flamingo-night-street1.jpg\" /></p>', '<p><strong>Flamingo Night Street</strong> nằm trong quần thể Flamingo Đại Lải Resort, chỉ c&aacute;ch trung t&acirc;m th&agrave;nh phố H&agrave; Nội 50km, c&aacute;ch s&acirc;n bay Nội B&agrave;i 25km, ri&ecirc;ng điều n&agrave;y đ&atilde; khiến cho <a href=\"https://maivietland.vn/du-an-bds/flamingo-night-street.html\"><strong>biệt thự Flamingo Đại Lải</strong></a>&nbsp;tạo được sức h&uacute;t tr&ecirc;n thị trường bất động sản nghỉ dưỡng.</p>\r\n\r\n<p>Flamingo Night Street được ưu &aacute;i đặt tại vị tr&iacute; đẹp nhất nh&igrave; quần thể Flamingo Đại Lải Resort, c&oacute; cổng đi ri&ecirc;ng. Nằm ngay lối v&agrave;o từ cổng Nam (South Gate) của Flamingo Đại Lải, tiếp gi&aacute;p liền kề s&acirc;n Golf Flamingo hiện đại; khu lưu tr&uacute;, giải tr&iacute;, ẩm thực, bể bơi ngo&agrave;i trời Forest đẳng cấp; c&ocirc;ng vi&ecirc;n cảnh quan; c&aacute;c d&atilde;y biệt thự v&agrave; c&aacute;c khu tiện &iacute;ch kh&aacute;c của Resort, biệt thự&nbsp;Flamingo Đại Lải&nbsp;sở hữu một vị tr&iacute; đắc địa hiếm c&oacute;, mang lại lợi thế đầu tư l&acirc;u d&agrave;i.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Flamingo Night Street - Khi vị trí làm nên giá trị\" src=\"/public/upload/images/flamingo-night-street/flamingo-night-street-vitri1.jpg\" style=\"height:507px; width:900px\" /></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Flamingo Night Street - Khi vị trí làm nên giá trị\" src=\"/public/upload/images/flamingo-night-street/vitri.jpg\" /></p>', '<p><a href=\"https://maivietland.vn/du-an-bds/flamingo-night-street.html\"><strong>Shophouse Garden</strong></a> c&oacute; số lượng 84 căn, diện t&iacute;ch s&agrave;n 365m2, cao 3,5 tầng, 100% shophouse 02 mặt tiền, l&agrave; loại h&igrave;nh nh&agrave; ở kết hợp với cửa h&agrave;ng bu&ocirc;n b&aacute;n kinh doanh. H&igrave;nh thức n&agrave;y đ&aacute;p ứng được đồng thời nhu cầu sinh sống, bu&ocirc;n b&aacute;n cũng như cho thu&ecirc;.&nbsp;</p>\r\n\r\n<p>C&oacute; sự xa hoa, tr&aacute;ng lệ nhưng shophouse cũng kh&ocirc;ng đi lạc &ldquo;tư duy xanh&rdquo; của quần thể nghỉ dưỡng tại Flamingo. Thi&ecirc;n nhi&ecirc;n lu&ocirc;n chiếm vị tr&iacute; quan trọng, chủ yếu trong mỗi thiết kế shophouse. Đặc trưng s&acirc;n vườn được thể hiện ở c&aacute;c tầng của Shophouse Garden. Chủ đầu tư Flamingo vẫn thể hiện được phong c&aacute;ch giao h&ograve;a với thi&ecirc;n nhi&ecirc;n đặc trưng trong thiết kế hiện đại của ph&acirc;n khu n&agrave;y. Shophouse đặc biệt th&iacute;ch hợp cho c&aacute;c hoạt động kinh doanh thời trang, nh&agrave; h&agrave;ng, spa, hay loại h&igrave;nh rooftop bar, rooftop caf&eacute; đang rất được ưa chuộng.</p>\r\n\r\n<p>Đặc biệt trong những năm gần đ&acirc;y, shophouse đang thu h&uacute;t sự quan t&acirc;m từ ph&iacute;a c&aacute;c nh&agrave; đầu tư, với những ưu điểm về khả năng sinh lời v&agrave; tinh thanh khoản cao. Thực tế cho thấy, shophouse lu&ocirc;n trong t&igrave;nh trạng &ldquo;cung kh&ocirc;ng đủ cầu&rdquo; do nhu cầu rất lớn từ thị trường. Theo thống k&ecirc; của Hiệp hội BĐS Việt Nam, năm 2018, ph&acirc;n kh&uacute;c shophouse c&oacute; doanh thu từ cho thu&ecirc; rất cao, khoảng 8-12%/năm. Chủ sở hữu đầu tư cho loại h&igrave;nh n&agrave;y sẽ thu lời cao hơn so với việc r&oacute;t tiền v&agrave;o căn hộ chung cư hay gửi ng&acirc;n h&agrave;ng, v&agrave; bền vững hơn so với k&ecirc;nh đầu tư v&agrave;ng hay chứng kho&aacute;n.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Shophouse Garden mặt bằng\" src=\"/public/upload/images/flamingo-night-street/shophouse-garden-mat-bang.jpg\" /></p>', '<p>Khu nh&agrave; phố <a href=\"https://maivietland.vn/du-an-bds/flamingo-night-street.html\"><strong>Boutique Garden Villa</strong></a> nằm tr&ecirc;n tuyến phố ch&iacute;nh của Flamingo Night Street, c&oacute; quy m&ocirc; 56 căn, diện t&iacute;ch s&agrave;n 395m2, l&agrave; sản phẩm đặc biệt v&agrave; cực hiếm tr&ecirc;n thị trường bất động sản, đ&aacute;p ứng nhu cầu kinh doanh v&agrave; nghỉ dưỡng của du kh&aacute;ch cũng như cư d&acirc;n. Tại đ&acirc;y, du kh&aacute;ch v&agrave; cư d&acirc;n sẽ được h&ograve;a m&igrave;nh trong những điệu nhảy cuồng nhiệt, lắng nghe những bản nhạc đầy ngẫu hứng đến từ c&aacute;c nghệ sĩ đường phố v&agrave; thưởng thức hương vị ẩm thực quyến rũ từ những qu&aacute;n ăn đặc sắc.</p>\r\n\r\n<p>Mỗi căn nh&agrave; phố Boutique Garden Villa đều mang phong c&aacute;ch kiến tr&uacute;c đặc trưng của Tập đo&agrave;n Flamingo tinh tế v&agrave; &ldquo;sống sang trọng c&ugrave;ng thi&ecirc;n nhi&ecirc;n&rdquo;. Với phong c&aacute;ch &ldquo;Boutique&rdquo; xuất ph&aacute;t từ nước Ph&aacute;p thơ mộng, ph&iacute;a trước của mỗi căn nh&agrave; sẽ l&agrave; những cửa h&agrave;ng thời trang, gian h&agrave;ng thủ c&ocirc;ng mỹ nghệ cao cấp. Phần &ldquo;Garden&rdquo; sẽ được thiết kế ở mặt sau ng&ocirc;i nh&agrave;, nơi chủ nh&acirc;n c&oacute; thể tận hưởng thời gian thư th&aacute;i với thi&ecirc;n nhi&ecirc;n s&acirc;n vườn. C&oacute; thể n&oacute;i đ&acirc;y l&agrave; sản phẩm đ&aacute;p ứng nhu cầu &quot;2 in&quot; vừa th&iacute;ch hợp kinh doanh vừa nghỉ ngơi y&ecirc;n tĩnh v&agrave; trang tr&iacute; khu vườn cũng như cảnh quan ng&ocirc;i nh&agrave; theo &yacute; muốn của chủ nh&acirc;n tương lai.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Boutique Garden Villa mặt bằng\" src=\"/public/upload/images/flamingo-night-street/Boutique-Garden-Villa-matbang.jpg\" /></p>', '<p>Ngo&agrave;i tiện &iacute;ch t&iacute;ch hợp giữa nghỉ dưỡng,&nbsp; kinh doanh trong m&ocirc;i trường xanh đẳng cấp v&agrave; tận hưởng kh&ocirc;ng kh&iacute; s&ocirc;i động, những hoạt động văn h&oacute;a, lễ hội đặc sắc của khu phố th&igrave; du kh&aacute;ch v&agrave; cư d&acirc;n của Flamingo Night Steet sẽ được sử dụng hệ sinh th&aacute;i tiện &iacute;ch chuẩn 5 sao quốc tế.&nbsp;</p>\r\n\r\n<p>Tại <a href=\"https://maivietland.vn/du-an-bds/flamingo-night-street.html\"><strong>Flamingo Night Street</strong></a> mọi nhu cầu nghỉ dưỡng v&agrave; tận hưởng cuộc sống thượng lưu của kh&aacute;ch h&agrave;ng sẽ được đ&aacute;p ứng bởi c&aacute;c dịch vụ: Si&ecirc;u tổ hợp Spa &amp; l&agrave;m đẹp lớn nhất ch&acirc;u &Aacute;; bể bơi ốc đảo bốn m&ugrave;a độc đ&aacute;o; Sky bar; Nh&agrave; h&agrave;ng &Yacute;; bar hầm rượu; ph&ograve;ng tập gym &amp; yoga; khu vui chơi trẻ em; Khu game hiện đại; khu karaoke; night club; sảnh đ&oacute;n tiếp v.v&hellip; Chắc chắn khi trải nghiệm hệ thống tiện &iacute;ch đỉnh cao của Flamingo Night Steet du kh&aacute;ch v&agrave; cư d&acirc;n sẽ c&oacute; những cảm x&uacute;c thăng hoa, thỏa m&atilde;n chưa từng c&oacute;.</p>', '<p>Dự &aacute;n <a href=\"https://maivietland.vn/du-an-bds/flamingo-night-street.html\"><strong>Flamingo Night Street</strong></a> c&oacute; sức h&uacute;t nổi bật từ kiến tr&uacute;c, từ cảnh quan du lịch hồ Đại Lải, vị tr&iacute; đắc địa v&agrave; tiềm năng tăng gi&aacute; cao của bất động sản Vĩnh Ph&uacute;c n&ecirc;n dự &aacute;n n&agrave;y c&oacute; gi&aacute; trị sinh lời bền vững, l&acirc;u d&agrave;i. Đặc biệt, Flamingo Night Street mở rộng cơ hội tham gia đầu tư sản phẩm v&agrave; kinh doanh sinh lời cho c&aacute;c nh&agrave; đầu tư. Tất cả sẽ trở th&agrave;nh những đại sứ thương hiệu, đại sứ du lịch trong ch&iacute;nh ph&acirc;n khu của m&igrave;nh c&ugrave;ng với nhiều ch&iacute;nh s&aacute;ch hấp dẫn kh&aacute;c, Flamingo Night Street sẽ l&agrave; lựa chọn đầu tư th&ocirc;ng th&aacute;i của c&aacute;c nh&agrave; đầu tư đ&oacute;n đầu xu hướng. Với uy t&iacute;n 20 năm tr&ecirc;n thị trường bất động sản của tập đo&agrave;n Flamingo, kh&aacute;ch h&agrave;ng ho&agrave;n to&agrave;n c&oacute; thể y&ecirc;n t&acirc;m khi đầu tư v&agrave;o c&aacute;c sản phẩm của tập đo&agrave;n bởi rủi ro thấp, chắc chắn sinh lời, sinh lời l&acirc;u d&agrave;i, bền vững.</p>', NULL, NULL, '<ul>\r\n	<li>Tặng 4&nbsp;thẻ All In Passport Diamond đặc quyền chủ biệt thự</li>\r\n	<li>Hỗ trợ vay 70% GTBT&nbsp; l&atilde;i suất 0% trong v&ograve;ng 18 th&aacute;ng</li>\r\n	<li>Tặng 03 đ&ecirc;m nghỉ miễn ph&iacute;&nbsp;tr&ecirc;n to&agrave;n hệ thống&nbsp;Flamingo</li>\r\n	<li>Chỉ đ&oacute;ng 30% trong v&ograve;ng 6 th&aacute;ng</li>\r\n	<li>Chiết khấu 9% khi kh&aacute;ch h&agrave;ng kh&ocirc;ng vay ng&acirc;n h&agrave;ng</li>\r\n	<li>Hưởng 80% lợi nhuận cho thu&ecirc; v&agrave; 30 đ&ecirc;m nghỉ free khi tham gia CT cho thu&ecirc; của CĐT</li>\r\n	<li>CĐT Cam kết lợi nhuận &iacute;t nhất 25tr/th&aacute;ng trong 2 năm đầu</li>\r\n</ul>\r\n\r\n<p><em><strong>Qu&agrave;n tặng đặc biệt:</strong></em></p>\r\n\r\n<ul>\r\n	<li>Tặng g&oacute;i nội thất <strong>200 triệu&nbsp;</strong>cho kh&aacute;ch h&agrave;ng mua Shophouse Garden</li>\r\n	<li>Tặng g&oacute;i nội thất <strong>300 triệu</strong>&nbsp;cho kh&aacute;ch h&agrave;ng mua Boutique Garden Villa</li>\r\n</ul>', 'Flamingo Night Street – “Bản nhạc” sôi động giữa miền xanh', 'Flamingo Night Street - Khi vị trí làm nên giá trị', 'Shophouse Garden – điểm nhấn ấn tượng của dự án', 'Boutique Garden Villa – “hàng hiếm 2n1”', 'Hệ thống tiện ích đỉnh cao tại Flamingo Night Street', 'Giá trị sinh lời bền vững', NULL, NULL, 'Quyền lợi khách hàng khi đầu tư shophouse', 'Flamingo Night Street quần thể shophouse duy nhất tại Flamingo', '05/23/2020', NULL, 'flamingo-night-street', 'Flamingo Night Street bao gồm hai dòng sản phẩm Shophouse Garden và Boutique Garden Villa sang trọng. nằm trong quần thể biệt thự Flamingo Đại Lải Resort', 'flamingo night street, biệt thự flamingo, biệt thự flamingo đại lải, shophouse flamingo, shophouse flamingo đại lải', 'index, follow');
INSERT INTO `tbl_product` (`id`, `cat_id`, `dis_id`, `user`, `name`, `address`, `hits`, `img`, `price`, `hot`, `tag`, `status`, `detail`, `tab1`, `tab2`, `tab3`, `tab4`, `tab5`, `tab6`, `tab7`, `tab8`, `tab9`, `content1`, `content2`, `content3`, `content4`, `content5`, `content6`, `content7`, `content8`, `content9`, `heading1`, `heading2`, `heading3`, `heading4`, `heading5`, `heading6`, `heading7`, `heading8`, `heading9`, `title`, `date`, `date_up`, `slug`, `description`, `keywords`, `robot`) VALUES
(117, '69', 44, 'Admin', 'Flamingo Cát Bà Beach Resort', 'Bãi tắm Cát Cò 1 và Cát Cò 2 thị trấn Cát Bà', 1189, 'flamingo-cat-ba2.jpg', NULL, 'false', NULL, 'true', '<ul>\r\n	<li>Quy m&ocirc;: 3 t&ograve;a căn hộ: Forest On The Sea, Forest On The Sand, Forest In The Sun</li>\r\n	<li>Loại h&igrave;nh sản phẩm:</li>\r\n	<li>Sky Villa/ Garden Villa: căn hộ 1PN, căn hộ 2PN, căn hộ 3PN</li>\r\n	<li>Căn hộ kh&aacute;ch sạn: Garden House, Sky House</li>\r\n</ul>', 'Tổng quan', 'Vị trí', 'Tiện ích', 'Forest Sky Villa', 'Forest On The Sand', 'Forest On The Sea', 'Forest In The Sun', 'Đầu tư', 'Chính sách', '<p>C&aacute;t B&agrave; được biết đến l&agrave; khu dự trữ sinh quyển thế giới nằm trong quần thể của Di sản văn h&oacute;a thi&ecirc;n nhi&ecirc;n thế giới Hạ Long. C&aacute;t B&agrave; l&agrave; đảo lớn nhất, với những d&atilde;y n&uacute;i đ&aacute; v&ocirc;i tr&ugrave;ng điệp, thảm thực vật phong ph&uacute; v&agrave; hệ sinh th&aacute;i đa dạng. B&ecirc;n cạnh đ&oacute;, từ H&agrave; Nội đến C&aacute;t B&agrave; chỉ mất 2 tiếng di chuyển, hạ tầng giao th&ocirc;ng thuận lợi, hiện đại n&ecirc;n C&aacute;t B&agrave; đang tr&ecirc;n đ&agrave; ph&aacute;t triển du lịch, thu h&uacute;t lượng du kh&aacute;ch lớn từ H&agrave; Nội v&agrave; c&aacute;c tỉnh lận cận đổ về nghỉ dưỡng, tham quan.</p>\r\n\r\n<p>Ch&iacute;nh bởi sự hấp dẫn của thi&ecirc;n nhi&ecirc;n v&agrave; tiềm năng đầu tư sinh lời lớn nơi đ&acirc;y, tập đo&agrave;n Flamingo &ndash; đơn vị đ&atilde; c&oacute; th&agrave;nh c&ocirc;ng vang dội với quần thể nghỉ dưỡng xanh tại Flamingo Đại Lải Resort, điển h&igrave;nh l&agrave; dự &aacute;n Forest in the Sky quyết định &ldquo;Mang rừng ra biển&rdquo; với dự &aacute;n Flamingo C&aacute;t B&agrave; Beach Resort.</p>', '<p>Flamingo C&aacute;t B&agrave; Beach Resort sở hữu vị tr&iacute; v&ocirc; c&ugrave;ng đắc địa, tọa lạc tại hai b&atilde;i biển đẹp nhất Đ&ocirc;ng Nam &Aacute;: C&aacute;t C&ograve; 1 v&agrave; C&aacute;t C&ograve; 2. Hai b&atilde;i biển được thi&ecirc;n nhi&ecirc;n ưu đ&atilde;i với l&agrave;n nước biển trong xanh như ngọc, b&atilde;i c&aacute;t d&agrave;i trắng mộng mơ v&agrave; được &ldquo;&ocirc;m ấp&rdquo; bởi n&uacute;i non h&ugrave;ng vĩ, c&acirc;y cối tốt tươi, phong cảnh hữu t&igrave;nh, c&oacute; thể l&agrave;m say đắm bất cứ vị kh&aacute;ch n&agrave;o khi đến với nơi đ&acirc;y. Ch&iacute;nh bởi sở hữu trọn vẹn cả 3 b&atilde;i tắm đẹp nhất C&aacute;t B&agrave; n&ecirc;n Flamingo C&aacute;t B&agrave; c&oacute; lợi thế rất lớn về tầm view v&agrave; cảnh quan. Tất cả những tuyệt sắc thi&ecirc;n nhi&ecirc;n nơi đ&acirc;y đều được Flamingo C&aacute;t B&agrave; &ocirc;m trọn v&agrave; t&ocirc; điểm th&ecirc;m rực rỡ.</p>\r\n\r\n<p>Ngo&agrave;i ra, vị tr&iacute; của Flamingo C&aacute;t B&agrave; nằm ở thế &ldquo;tựa sơn hướng hải&rdquo;, phong thủy &ldquo;C&aacute;t tường vượng lộc&rdquo;. Đ&acirc;y l&agrave; thế phong thủy cực k&igrave; hiếm c&oacute;, sau lưng tựa v&agrave;o n&uacute;i vững chắc, ph&iacute;a trước vươn m&igrave;nh ra biển lớn m&ecirc;nh m&ocirc;ng. C&aacute;c chuy&ecirc;n gia phong thủy đều đ&aacute;nh gi&aacute; đ&acirc;y l&agrave; v&ugrave;ng đất &ldquo;vượng địa&rdquo; sẽ đem đến ph&uacute; qu&yacute; l&acirc;u bền v&agrave; sức khỏe trường thọ cho gia chủ. Đ&acirc;y cũng c&oacute; thể l&agrave; dự b&aacute;o cho sự th&agrave;nh c&ocirc;ng của kh&aacute;ch h&agrave;ng khi lựa chọn đầu tư v&agrave;o Flamingo C&aacute;t B&agrave;, nơi c&oacute; nền tảng vững ch&atilde;i, chắc chắn, kh&oacute; x&ecirc; dịch, chỉ sinh lời, tiến về ph&iacute;a trước để đ&oacute;n nhận h&agrave;o quang, &aacute;nh s&aacute;ng của t&agrave;i vượng v&agrave; th&agrave;nh c&ocirc;ng.</p>', '<p>Thừa hưởng tinh hoa từ dự &aacute;n Forest in the Sky đ&atilde; &ldquo;tạo s&oacute;ng&rdquo; tại Đại Lải, dự &aacute;n Flamingo C&aacute;t B&agrave; cũng l&agrave; những t&ograve;a nh&agrave; c&oacute; kiến tr&uacute;c xanh huyền thoại, nơi thi&ecirc;n nhi&ecirc;n được len lỏi v&agrave;o từng căn hộ một c&aacute;ch tinh tế, nghệ thuật nhất. C&aacute;c t&ograve;a nh&agrave; được bao phủ bởi hệ thống c&acirc;y xanh lớn mang đến kh&ocirc;ng gian nghỉ dưỡng m&aacute;t mẻ, trong l&agrave;nh gi&uacute;p cho gia chủ v&agrave; du kh&aacute;ch cảm gi&aacute;c thư th&aacute;i khi được ngắm nh&igrave;n biển trời, non nước từ tr&ecirc;n c&aacute;c tầng cao.</p>\r\n\r\n<p>Nh&igrave;n từ xa, c&aacute;c t&ograve;a nh&agrave; của Flamingo C&aacute;t B&agrave; như những c&aacute;nh rừng rậm nhiệt đới nhiều sắc m&agrave;u được mẹ thi&ecirc;n nhi&ecirc;n ưu &aacute;i, chăm ch&uacute;t t&ocirc;n tạo n&ecirc;n. Những sẽ c&oacute; l&uacute;c nh&igrave;n những căn hộ như một vườn hoa rực rỡ, mang d&aacute;ng dấp của vườn treo Babylon hay vườn địa đ&agrave;ng nơi ti&ecirc;n cảnh như trong truyền thuyết của Adam v&agrave; Eva chứa đựng những điều huyền b&iacute;, l&atilde;ng mạn.</p>\r\n\r\n<p><strong>Hệ thống tiện &iacute;ch thượng lưu tại Flamingo C&aacute;t B&agrave;</strong></p>\r\n\r\n<p>Song h&agrave;nh với mục đ&iacute;ch mang đến một khu nghỉ dưỡng 5 sao đầu ti&ecirc;n tại C&aacute;t B&agrave;, Flamingo C&aacute;t B&agrave; được đầu tư h&agrave;ng loạt những tiện &iacute;ch nghỉ dưỡng cao cấp. Mỗi t&ograve;a nh&agrave; Forest On The Sea, Forest In The Sun v&agrave; Forest On The Sand đều c&oacute; những tiện &iacute;ch ri&ecirc;ng v&agrave; nổi bật như:&nbsp;</p>\r\n\r\n<p>Bể bơi ốc đảo ngo&agrave;i trời 1.200 m2 tại Forest On The Sand : Vừa ng&acirc;m m&igrave;nh trong l&agrave;n nước m&aacute;t lạnh, vừa c&oacute; thể ngắm nh&igrave;n trọn vẻ đẹp của C&aacute;t C&ograve; cũng như biển xanh m&ecirc;nh m&ocirc;ng từ tr&ecirc;n cao, đ&acirc;y chắc chắn sẽ l&agrave; trải nghiệm tuyệt vời v&agrave; kh&oacute; qu&ecirc;n cho mọi kh&aacute;ch h&agrave;ng.</p>\r\n\r\n<p>Sky Bar trong nh&agrave; v&agrave; ngo&agrave;i trời tại Forest In The Sun: Sky Bar ngo&agrave;i trời được đặt tr&ecirc;n tầng cao nhất đem đến cho thực kh&aacute;ch những trải nghiệm chưa từng c&oacute;. Giữa khung cảnh trời xanh đẹp ngỡ ng&agrave;ng của đất trời hay lung linh trong &aacute;nh đ&egrave;n&hellip; nơi đ&acirc;y l&agrave; bản h&ograve;a ca đất trời tuyệt vời d&agrave;nh cho những du kh&aacute;ch thượng lưu.</p>\r\n\r\n<p>Nh&agrave; h&agrave;ng đa năng phục vụ gần 300 kh&aacute;ch/lượt tại Forest On The Sea : Kh&ocirc;ng gian sang trọng với ẩm thực độc đ&aacute;o được chế biến bởi những đầu bếp h&agrave;ng đầu, sẽ l&agrave; nơi để cả gia đ&igrave;nh c&oacute; thể c&ugrave;ng nhau thưởng thức v&agrave; tận hưởng.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute; l&agrave; c&aacute;c bể bơi v&ocirc; cực, bể bơi 4 m&agrave;u, bể bơi ốc đảo với thiết kế hiện đại, cao cấp với tầm nh&igrave;n trực diện ra biển, đ&acirc;y l&agrave; nơi du kh&aacute;ch c&oacute; thể vừa ng&acirc;m m&igrave;nh, phơi nắng vừa ngắm trọn cảnh biển. C&aacute;c trung t&acirc;m mua sắm, trung t&acirc;m hội nghị, hổi thảo cũng được đầu tư hiện đại phục vụ mọi nhu cầu của du kh&aacute;ch v&agrave; cư d&acirc;n.</p>\r\n\r\n<p>Với si&ecirc;u tổ hợp dịch vụ kh&eacute;p k&iacute;n 5 sao với quy m&ocirc; ho&agrave;nh tr&aacute;ng xứng tầm thế giới, Flamingo C&aacute;t B&agrave; Beach Resort sẽ l&agrave; điểm đến cho những chuyến phưu lưu kh&aacute;m ph&aacute; tuyệt vời, một thi&ecirc;n đường nghỉ dưỡng b&ecirc;n bờ biển đầy m&agrave;u sắc.</p>', '<p>Dự &aacute;n Flamingo C&aacute;t B&agrave; c&oacute; ba t&ograve;a căn hộ, được đặt t&ecirc;n lần lượt l&agrave; Forest On The Sea, Forest In The Sun v&agrave; Forest On The Sand. Mỗi t&ograve;a nh&agrave; đều c&oacute; n&eacute;t kiến tr&uacute;c độc đ&aacute;o v&agrave; th&ocirc;ng điệp &yacute; nghĩa m&agrave; chủ đầu tư Flamingo Group muốn gửi gắm.</p>', '<p><strong>&ldquo;</strong>Đẳng cấp&rdquo; ch&iacute;nh l&agrave; từ đầu ti&ecirc;n m&agrave; mọi kh&aacute;ch h&agrave;ng đều nghĩ đến khi nhắc về c&aacute;c dự &aacute;n biệt thự của tập đo&agrave;n Flamingo. Đ&oacute; cũng ch&iacute;nh l&agrave; mục ti&ecirc;u, gi&aacute; trị m&agrave; tập đo&agrave;n lu&ocirc;n hướng tới. Hướng đến ph&acirc;n kh&uacute;c kh&aacute;ch h&agrave;ng thuộc giới thượng lưu, tập đo&agrave;n Flamingo lu&ocirc;n chăm ch&uacute;t, đầu tư đến từng chi tiết nhỏ nhất của mỗi dự &aacute;n để khi ho&agrave;n thiện đều l&agrave; những c&ocirc;ng tr&igrave;nh đẳng cấp, hiện đại bậc nhất. Forest On The Sand l&agrave; một t&ograve;a nh&agrave; đẳng cấp đ&uacute;ng nghĩa.&nbsp;</p>\r\n\r\n<p>Forest On The Sand c&oacute; số tầng cao 17 tầng, tổng diện t&iacute;ch s&agrave;n 41.650 m2, tổng 340 ph&ograve;ng. Nằm ẩn m&igrave;nh trong b&atilde;i C&aacute;t C&ograve; &ndash; b&atilde;i đẹp v&agrave; nổi tiếng nhất tr&ecirc;n đảo, Forest On The Sand được bao bọc bởi kh&ocirc;ng gian xanh của những rặng n&uacute;i bao quanh, ph&iacute;a trước l&agrave; biển cả. Kh&ocirc;ng gian sống nơi đ&acirc;y gợi li&ecirc;n tưởng về một kh&aacute;ch sạn trong rừng, nơi c&oacute; thể lắng nghe tiếng gi&oacute; biển dội v&agrave;o v&aacute;ch đ&aacute;, h&ograve;a với tiếng chim mu&ocirc;ng v&agrave; s&oacute;ng vỗ r&igrave; r&agrave;o.</p>\r\n\r\n<p>Đẳng cấp của Forest On The Sand kh&ocirc;ng chỉ ở kiến tr&uacute;c xanh huyền thoại, sự hiện đại, thời thượng trong thiết kế, nội thất của c&aacute;c căn hộ m&agrave; c&ograve;n ở t&agrave;i hoa xuất ch&uacute;ng của c&aacute;c kiến tr&uacute;c sư, của chủ đầu tư khi kết hợp ho&agrave;n hảo giữa nghệ thuật v&agrave; kiến tr&uacute;c. T&ograve;a nh&agrave; như một t&aacute;c phẩm nghệ thuật đặc sắc của thế kỉ, nơi vẻ đẹp, tiện &iacute;ch, gi&aacute; trị đạt tới mức ho&agrave;n hảo, tuyệt đ&iacute;ch.</p>', '<p>Nếu như một sự vật được coi l&agrave; rực rỡ khi n&oacute; c&oacute; m&agrave;u sắc đẹp đẽ v&agrave; nổi bật th&igrave; Forest On The Sea đ&atilde; vượt qua quy chuẩn của rực rỡ. Bởi t&ograve;a nh&agrave; c&oacute; n&eacute;t cuốn h&uacute;t, ho&agrave;n mỹ, c&oacute; thể thu h&uacute;t mọi &aacute;nh nh&igrave;n của du kh&aacute;ch với vẻ tr&aacute;ng lệ, đẳng cấp của m&igrave;nh.&nbsp;</p>\r\n\r\n<p>Với tổng diện t&iacute;ch s&agrave;n 58.670 m2 , 17 tầng v&agrave; 142 ph&ograve;ng,&nbsp; Forest On The Sea tọa lạc tr&ecirc;n b&atilde;i tắm C&aacute;t C&ograve; 1. Đ&acirc;y ch&iacute;nh l&agrave; b&atilde;i tắm đẹp nhất v&agrave; rộng nhất, nằm v&ograve;ng theo n&uacute;i, quanh năm s&oacute;ng vỗ v&agrave; kh&ocirc;ng kh&iacute; trong l&agrave;nh. Đặc biệt l&agrave; l&agrave;n nước trong xanh như ngọc b&iacute;ch, được nhiều du kh&aacute;ch lựa chọn để tắm v&agrave; nghỉ ngơi. Nằm trải d&agrave;i tr&ecirc;n b&atilde;i biển tuyệt đẹp n&agrave;y, Forest On The Sea thừa hưởng trọn một miền cảnh quan tươi đẹp.</p>\r\n\r\n<p>To&agrave;n bộ t&ograve;a nh&agrave; được bao bọc bởi những mảng xanh thẳng đứng. C&aacute;c căn hộ được thiết kế rộng r&atilde;i, th&ocirc;ng tho&aacute;ng nhưng vẫn đảm bảo sự ri&ecirc;ng tư cho chủ sở hữu. Những căn Sky Villa tr&ecirc;n cao c&oacute; tầm nh&igrave;n ngoạn mục ra vịnh Lan Hạ, thu trọn những cảnh biển tuyệt đẹp, mang đến những trải nghiệm chưa từng c&oacute;. N&eacute;t rực rỡ của Forest On The Sea kh&ocirc;ng phải ở những m&agrave;u sắc qu&aacute; l&ograve;e loẹt, sến s&uacute;a m&agrave; ở m&agrave;u xanh tươi m&aacute;t của thi&ecirc;n nhi&ecirc;n, c&acirc;y cỏ, một m&agrave;u xanh c&oacute; thể n&oacute;i l&agrave; đẹp nhất h&agrave;nh tinh. Bởi ở đ&acirc;u c&oacute; m&agrave;u xanh của c&acirc;y, của l&aacute;, cả rừng, ở đ&oacute; c&oacute; sự trong l&agrave;nh, sức khỏe v&agrave; thịnh vượng trường tồn.</p>', '<p>L&agrave; một trong ba t&ograve;a nh&agrave; đẳng cấp của dự &aacute;n Flamingo C&aacute;t B&agrave;, Forest In The Sun c&oacute; tổng diện t&iacute;ch s&agrave;n 19.071 m2, cao 16 tầng v&agrave; 208 ph&ograve;ng, tọa lạc tr&ecirc;n b&atilde;i C&aacute;t C&ograve; 2A, &ocirc;m trọn v&ugrave;ng biển trời tuyệt đẹp, thanh b&igrave;nh lại pha ch&uacute;t huyền b&iacute; của đảo C&aacute;t B&agrave;.&nbsp;</p>\r\n\r\n<p>T&ograve;a Forest l&agrave; t&ograve;a nh&agrave; xanh rực rỡ được v&iacute; như &ldquo;vi&ecirc;n ngọc qu&yacute;&rdquo; tỏa s&aacute;ng, lấp l&aacute;nh tuyệt đẹp dưới &aacute;nh mặt trời. Đ&acirc;y l&agrave; t&ograve;a nh&agrave; c&oacute; diện t&iacute;ch lớn với những căn biệt thự tr&ecirc;n cao độc đ&aacute;o. Nơi đ&acirc;y l&agrave; điểm đến l&yacute; tưởng cho những kh&aacute;ch h&agrave;ng thượng lưu.</p>\r\n\r\n<p>Với kiến tr&uacute;c xanh đặc trưng, to&agrave;n bộ t&ograve;a nh&agrave; được bao bọc bởi hệ thống c&acirc;y xanh tầng tầng lớp lớp từ ch&acirc;n t&ograve;a đến từng căn biệt thự. Đến đ&acirc;y chủ nh&acirc;n vừa được trải nghiệm một kh&ocirc;ng gian tr&agrave;n ngập m&agrave;u xanh, chan h&ograve;a với thi&ecirc;n nhi&ecirc;n vừa được nghỉ dưỡng trong biệt thự sang trọng, hiện đại với hệ thống nội thất v&agrave; tiện &iacute;ch dịch vụ đỉnh cao.</p>', '<p>Kh&ocirc;ng chỉ l&agrave; quần thể nghỉ dưỡng đẳng cấp 5 sao đầu ti&ecirc;n tại C&aacute;t B&agrave;, Flamingo C&aacute;t B&agrave; c&ograve;n mang đến cơ hội đầu tư hấp dẫn chưa từng c&oacute; cho c&aacute;c nh&agrave; đầu tư bất động sản nghỉ dưỡng.&nbsp;</p>\r\n\r\n<p>Với vị tr&iacute; thuận lợi c&ugrave;ng cảnh sắc thi&ecirc;n nhi&ecirc;n hữu t&igrave;nh, C&aacute;t B&agrave; đang trở th&agrave;nh một trong những địa điểm du lịch biển hot, mỗi năm thu h&uacute;t h&agrave;ng triệu lượt kh&aacute;ch trong nước v&agrave; ngo&agrave;i nước đổ về. Năm 2018, tổng số kh&aacute;ch du lịch đến C&aacute;t B&agrave; đạt tr&ecirc;n 2,5 triệu lượt, tăng 18% so với năm 2017. Trong đ&oacute;, kh&aacute;ch quốc tế đạt 608 ngh&igrave;n lượt, tăng 27%, kh&aacute;ch nội địa đạt 1,9 triệu lượt. Ch&iacute;nh v&igrave; vậy, Flamingo C&aacute;t B&agrave; Beach Resort sẽ l&agrave; dự &aacute;n tăng gi&aacute; tốt nhất, bền vững theo thời gian. Mặt kh&aacute;c Flamingo C&aacute;t B&agrave; được đặt ở vị tr&iacute; độc t&ocirc;n ngay tại b&atilde;i tắm đ&ocirc;ng v&agrave; nổi tiếng nhất tr&ecirc;n đảo n&ecirc;n sở hữu ưu thế m&agrave; kh&ocirc;ng dự &aacute;n n&agrave;o kh&aacute;c c&oacute; được. Điều n&agrave;y cũng đảm bảo gi&aacute; trị của dự &aacute;n trong tương lai. Trong tương lai 10 năm tới, Flamingo C&aacute;t B&agrave; sẽ kh&ocirc;ng c&oacute; đối thủ cạnh tranh.</p>\r\n\r\n<p>Những nh&agrave; đầu tư bất động sản th&ocirc;ng th&aacute;i đều c&oacute; thể nhận thấy rằng c&aacute;c căn hộ nghỉ dưỡng/biệt thự nghỉ dưỡng hiện đang l&agrave; một trong c&aacute;c k&ecirc;nh đầu tư đặc biệt được ưu chuộng bởi t&iacute;nh an to&agrave;n, ổn định, sinh lời vững v&agrave;ng. Dựa tr&ecirc;n số lượng kh&aacute;ch du lịch đến với C&aacute;t B&agrave; ng&agrave;y c&agrave;ng tăng cộng với những vượt trội về tiện &iacute;ch, dịch vụ, c&aacute;c nh&agrave; đầu tư ho&agrave;n to&agrave;n c&oacute; thể tin tưởng v&agrave;o gi&aacute; trị lợi nhuận m&agrave; c&aacute;c căn hộ Flamingo C&aacute;t B&agrave; mang lại.&nbsp;</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, tập đo&agrave;n Flamingo l&agrave; chủ đầu tư uy t&iacute;n, đ&atilde; c&oacute; bề d&agrave;y kinh nghiệm c&ugrave;ng với nhiều th&igrave;nh t&iacute;ch vượt trội trong lĩnh vực bất động sản nghỉ dưỡng. Vậy n&ecirc;n, c&aacute;c nh&agrave; đầu tư h&atilde;y tin rằng, khi đầu tư v&agrave;o c&aacute;c sản phẩm của Flamingo đều c&oacute; sự an to&agrave;n cao, gi&aacute; trị lớn v&agrave; được hưởng nhiều ch&iacute;nh s&aacute;ch ưu đ&atilde;i, hỗ trợ hấp dẫn.</p>', NULL, 'Tổng quan dự án Flamingo Cát Bà Beach Resort', 'Vị trí tuyệt đẹp, làm say lòng người', 'Flamingo Cát Bà nơi chốn giao thoa của rừng, trời và biển', 'Forest Sky Villa – Tinh hoa biệt thự trên cao giữa biển xanh', 'Forest On The Sand - Biệt thự đẳng cấp trên bờ cát trắng.', 'Forest On The Sea - Tòa nhà rực rỡ giữa biển xanh', 'Forest In The Sun - Kiến trúc xanh độc đáo dưới ánh mặt trời', 'Flamingo Cát Bà mang đến cơ hội đầu tư chưa từng có', NULL, NULL, '10/24/2019', NULL, 'flamingo-cat-ba-beach-resort', NULL, NULL, 'index, follow'),
(118, '69', 43, 'Admin', 'Flamingo SkyLake', 'Ngọc Thanh', 2327, 'flamingo-skylake.JPG', '19 tỷ', 'false', NULL, 'true', '<ul>\r\n	<li>Chủ đầu tư: Tập đo&agrave;n Flamingo</li>\r\n	<li>Loại h&igrave;nh: Biệt thự</li>\r\n	<li>H&igrave;nh thức: Ch&igrave;a kh&oacute;a trao tay</li>\r\n</ul>', 'Tổng quan', 'Vị trí', 'Tiện ích', 'Đầu tư', NULL, NULL, NULL, NULL, 'Chính sách', '<p>Nằm lẩn khuất sau những c&aacute;nh rừng th&ocirc;ng thơ mộng, Luxury Skylake Villa mang phong c&aacute;ch kiến tr&uacute;c Minimalist coi trọng sự tối giản hiện đại, khoảng kh&ocirc;ng v&agrave; &aacute;nh s&aacute;ng được đề cao gi&uacute;p l&agrave;m nổi bật những chủ thể trang tr&iacute; một c&aacute;ch hiệu quả nhất. Với tổng diện t&iacute;ch sử dụng l&ecirc;n đến gần 1000m2 v&agrave; hơn 200m2 s&agrave;n tầng 1, Luxury Skylake Villa c&oacute; thiết kế 3 tầng với 4 ph&ograve;ng ngủ rộng, c&aacute;c ph&ograve;ng ngủ đều li&ecirc;n kết với s&acirc;n vườn hoặc giếng trời bằng những cửa k&iacute;nh lớn để lấy &aacute;nh s&aacute;ng mặt trời v&agrave; gần gũi với thi&ecirc;n nhi&ecirc;n. Vẫn theo lối kiến tr&uacute;c vườn xanh độc đ&aacute;o trong c&aacute;c dự &aacute;n nghỉ dưỡng của tập đo&agrave;n Flamingo, Luxury Skylake Villa h&ograve;a m&igrave;nh trong kh&ocirc;ng gian s&acirc;n vườn được mở rộng tối đa l&ecirc;n tới gần 600m2. C&aacute;c căn biệt thự được bao quanh bởi khu rừng nhiệt đới tr&agrave;n ngập m&agrave;u xanh của những h&agrave;ng th&ocirc;ng, b&agrave;ng Nhật hay c&acirc;y leo&hellip; Thi&ecirc;n nhi&ecirc;n len lỏi chạm v&agrave;o mọi ng&otilde; ng&aacute;ch của cuộc sống, mang đến sự thư th&aacute;i v&agrave; những gi&acirc;y ph&uacute;t thăng hoa giữa chốn thi&ecirc;n đường.&nbsp;</p>\r\n\r\n<p>Điều l&agrave;m n&ecirc;n sự kh&aacute;c biệt, tạo n&ecirc;n thương hiệu &ldquo;biệt thự hoa hồng&rdquo; của Luxury Skylake Villa ch&iacute;nh l&agrave; c&aacute;c biệt thự được bao quanh bởi khu vườn hoa hồng qu&yacute; hiếm tr&agrave;n ngập hương sắc. H&agrave;ng triệu c&acirc;y hoa hồng từ hơn 500 lo&agrave;i hoa hồng cổ qu&yacute; gi&aacute; v&agrave; độc đ&aacute;o đến từ khắp nơi tr&ecirc;n thế giới đ&atilde; được quy tụ về vườn địa đ&agrave;ng Flamingo. Đ&oacute; l&agrave; Juliet triệu đ&ocirc; ki&ecirc;u sa v&agrave; đ&agrave;i c&aacute;c, Mary Rose xinh đẹp ngọt ng&agrave;o, Bạch hồng tinh tế, thanh khiết hay hồng cổ đỏ thắm như nhung&hellip; Mỗi giống hoa hồng lại c&oacute; những đặc trưng kh&aacute;c nhau, mang đến những cảm x&uacute;c kh&aacute;c nhau cho gia chủ.&nbsp;</p>\r\n\r\n<p>Hoa hồng lu&ocirc;n l&agrave; biểu tượng của t&igrave;nh y&ecirc;u, hạnh ph&uacute;c, sự ki&ecirc;u sa, thanh lịch. V&igrave; vậy, biệt thự hoa hồng Luxury Skylake Villa gửi gắm th&ocirc;ng điệp về phong c&aacute;ch sống sang trọng, cuộc sống thượng lưu, hạnh ph&uacute;c, hướng tới gi&aacute; trị ch&acirc;n - thiện - mỹ đ&iacute;ch thực.</p>', '<p>Người xưa c&oacute; c&acirc;u &ldquo;Nhất vị nhị hướng&rdquo; để khẳng định vị tr&iacute; l&agrave; yếu tố quan trọng nhất khi chọn đất l&agrave;m nh&agrave;. Vị tr&iacute; c&oacute; tốt, phong thủy c&oacute; c&aacute;t tường, an l&agrave;nh th&igrave; gia chủ mới đ&oacute;n được t&agrave;i lộc, sức khỏe dồi d&agrave;o, gia đ&igrave;nh y&ecirc;n ấm. Với sứ mệnh mang đến cuộc sống ho&agrave;n mỹ, hạnh ph&uacute;c v&agrave; thịnh vượng cho những chủ nh&acirc;n tương lai của Luxury Skylake Villa, chủ đầu tư Flamingo Group đ&atilde; d&agrave;nh trọn t&acirc;m huyết để kiến tạo n&ecirc;n những biệt thự đẳng cấp c&oacute; vị tr&iacute; đắc địa, xứng tầm thượng lưu.</p>\r\n\r\n<p>Luxury Skylake Villa sở hữu địa thế phong thủy độc đ&aacute;o v&agrave; hiếm c&oacute; tại khu nghỉ dưỡng cao cấp 5 sao Flamingo Đại Lải Resort. Theo phong thủy phương Đ&ocirc;ng, nước l&agrave; nơi tụ kh&iacute; tốt, tượng trưng cho sự thịnh vượng v&agrave; gi&agrave;u sang. Luxury Skylake Villa tọa lạc tr&ecirc;n một b&aacute;n đảo ri&ecirc;ng biệt, c&oacute; đến ba mặt gi&aacute;p hồ, tận hưởng trọn vẻ đẹp thanh khiết của hơn 500 ha mặt hồ Đại Lải thơ mộng, một mặt gi&aacute;p s&acirc;n golf độc đ&aacute;o, xa xa l&agrave; rặng n&uacute;i mờ ảo ph&iacute;a ch&acirc;n trời. Nơi đ&acirc;y c&oacute; sự giao h&ograve;a giữa nước v&agrave; trời sẽ đem đến nguồn sinh kh&iacute; c&aacute;t tường, ph&uacute; qu&yacute; cho chủ nh&acirc;n.&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"vị trí flamingo skylake villas\" src=\"/public/upload/images/flamingo-night-street/flamingo-night-street-vitri1.jpg\" /></p>\r\n\r\n<p>Đẳng cấp ở vị tr&iacute;, độc t&ocirc;n về tầm nh&igrave;n. Luxury Skylake Villa sở hữu tầm nh&igrave;n v&agrave; hướng tuyệt đẹp. Kh&ocirc;ng chỉ hội tụ mọi yếu tố của một hướng nh&igrave;n đẹp trong phong thủy, c&aacute;c căn biệt thự c&ograve;n thỏa m&atilde;n gia chủ về ti&ecirc;u ch&iacute; chan h&ograve;a với thi&ecirc;n nhi&ecirc;n, tho&aacute;ng đ&atilde;ng v&agrave; lu&ocirc;n m&aacute;t mẻ. Mỗi căn biệt thự đều được thiết kế theo hướng đ&oacute;n &aacute;nh s&aacute;ng tự nhi&ecirc;n tốt nhất, đem lại cảm gi&aacute;c ch&acirc;n thực cho cư d&acirc;n. Hồ nước rộng lớn bao trọn 3 mặt b&aacute;n đảo, tạo n&ecirc;n v&ugrave;ng tiểu kh&iacute; hậu m&aacute;t mẻ khiến Luxury Skylake Villa tự c&oacute; khả năng lưu th&ocirc;ng kh&ocirc;ng kh&iacute; để đ&ocirc;ng ấm - hạ m&aacute;t.</p>\r\n\r\n<p>Cảnh sắc thi&ecirc;n nhi&ecirc;n v&agrave; phong thủy giao h&ograve;a tạo n&ecirc;n một vị tr&iacute; &ldquo;v&agrave;ng&rdquo; để an cư, lạc nghiệp v&agrave; tận hưởng cuộc sống vi&ecirc;n m&atilde;n. Xa rời kh&oacute;i bụi chốn đ&ocirc; th&agrave;nh, sự v&ocirc; hồn, bức bối của những t&ograve;a nh&agrave; cao tầng, Luxury Skylake Villa sẽ tiếp th&ecirc;m nguồn năng lượng tươi mới, một sức khỏe dồi d&agrave;o, một cuộc sống trong mơ cho bạn v&agrave; gia đ&igrave;nh bạn.</p>', '<p>D&ugrave; rời xa chốn thị th&agrave;nh tấp nập để về nghỉ dưỡng giữa chốn thi&ecirc;n nhi&ecirc;n thanh b&igrave;nh, t&aacute;ch biệt với những ồn &agrave;o, n&aacute;o nhiệt nhưng kh&aacute;ch h&agrave;ng vẫn c&oacute; thể chạm tay v&agrave;o những tiện &iacute;ch 5 sao chuẩn quốc tế ngay khi c&oacute; nhu cầu. Nằm tr&ecirc;n b&aacute;n đảo Golf xinh đẹp, Luxury Skylake Villa được thụ hưởng hệ sinh th&aacute;i c&oacute; sẵn tr&ecirc;n đảo như Flamingo Golf &amp; Spa lớn nhất Đ&ocirc;ng Nam &Aacute;, s&acirc;n golf 9 lỗ v&agrave; s&acirc;n gạt golf 18 lỗ đạt ti&ecirc;u chuẩn quốc tế. Ngo&agrave;i ra, chủ nh&acirc;n biệt thự sẽ được trải nghiệm những hoạt động dưới nước độc đ&aacute;o tại Flamingo Beach Club như c&acirc;u lạc bộ du thuyền, c&acirc;u lạc bộ b&atilde;i biển, ca n&ocirc;, b&oacute;ng nước&hellip;</p>\r\n\r\n<p>Hệ thống nh&agrave; h&agrave;ng 5 sao với những đầu bếp đạt ti&ecirc;u chuẩn quốc tế sẽ phục vụ kh&aacute;ch h&agrave;ng những m&oacute;n ăn đẳng cấp trong kh&ocirc;ng gian h&ograve;a với thi&ecirc;n nhi&ecirc;n, c&oacute; kiến tr&uacute;c ph&aacute; c&aacute;ch đặc trưng &iacute;t nơi n&agrave;o c&oacute; được. Cư d&acirc;n Flamingo c&ograve;n c&oacute; thể thoải m&aacute;i thư gi&atilde;n, chăm s&oacute;c sức khỏe c&ugrave;ng sắc đẹp tại hệ thống Spa lớn ti&ecirc;u chuẩn với c&aacute;c trang thiết bị hiện đại v&agrave; đội ngũ nh&acirc;n vi&ecirc;n chuy&ecirc;n nghiệp được đ&agrave;o tạo b&agrave;i bản. C&ograve;n rất nhiều những tiện &iacute;ch sang trọng kh&aacute;c, m&agrave; chỉ cần trở th&agrave;nh chủ nh&acirc;n biệt thự Luxury Skylake Villa, qu&yacute; kh&aacute;ch sẽ được tận hưởng kh&ocirc;ng giới hạn trong tương lai. Lựa chọn sở hữu biệt thự Luxury Skylake Villa l&agrave; qu&yacute; kh&aacute;ch đ&atilde; chọn một cuộc sống thực sự đ&aacute;ng sống, sự khỏe mạnh v&agrave; niềm vui đến với cả gia đ&igrave;nh.</p>', '<p>V&igrave; mang đầy đủ những tiện &iacute;ch của một dự &aacute;n nghỉ dưỡng thượng lưu, đẳng cấp cộng với kiến tr&uacute;c hiện đại, phong cảnh hữu t&igrave;nh, Luxury Skylake Villa đem lại tiềm năng kinh doanh du lịch hiệu quả cho c&aacute;c chủ biệt thự. Tại đ&acirc;y, lợi &iacute;ch của kh&aacute;ch h&agrave;ng lu&ocirc;n được đặt l&ecirc;n h&agrave;ng đầu khi kết hợp nghỉ dưỡng v&agrave; đầu tư. Khi sở hữu biệt thự cao cấp Luxury Skylake Villa, kh&aacute;ch h&agrave;ng sẽ được tặng hộ chiếu nghỉ dưỡng All In Passport Diamond sử dụng kh&ocirc;ng giới hạn 156 dịch vụ tiện &iacute;ch tr&ecirc;n to&agrave;n hệ thống Flamingo. Ngo&agrave;i ra, Flamingo hỗ trợ vay vốn ng&acirc;n h&agrave;ng 60% gi&aacute; trị hợp đồng với l&atilde;i suất 0% trong thời hạn 12 th&aacute;ng đầu ti&ecirc;n kể từ ng&agrave;y giải ng&acirc;n. C&aacute;c chủ biệt thự sẽ được hưởng trọn 30 đ&ecirc;m nghỉ dưỡng mỗi năm, sử dụng linh hoạt tr&ecirc;n to&agrave;n hệ thống Flamingo. Đặc biệt từ ng&agrave;y 1/8/2019, kh&aacute;ch h&agrave;ng giao dịch th&agrave;nh c&ocirc;ng sản phẩm biệt thự Luxury Skylake Villa sẽ được tặng <strong>10 c&acirc;y v&agrave;ng 9999</strong> cho mỗi giao dịch.</p>\r\n\r\n<p>Kh&aacute;ch h&agrave;ng ho&agrave;n to&agrave;n c&oacute; thể đặt trọn niềm tin khi hợp t&aacute;c với chủ đầu tư Flamingo Group bởi Flamingo l&agrave; c&aacute;i t&ecirc;n h&agrave;ng đầu tr&ecirc;n thị trường bất động sản nghỉ dưỡng, uy t&iacute;n 20 năm kinh nghiệm với v&ocirc; v&agrave;n giải thưởng trong nước v&agrave; quốc tế. Flamingo Group đảm bảo sẽ mang đến lợi &iacute;ch tốt nhất, lợi nhuận l&acirc;u d&agrave;i v&agrave; hỗ trợ kh&aacute;ch h&agrave;ng hết m&igrave;nh tr&ecirc;n h&agrave;nh tr&igrave;nh đầu tư bền vững.</p>\r\n\r\n<p>Dự &aacute;n n&ecirc;n xem: <a href=\"https://maivietland.vn/du-an-bds/the-matrix-one-me-tri.html\">the matrix one</a></p>', NULL, NULL, NULL, NULL, '<ul>\r\n	<li>&nbsp;Vay 70% GTHĐ với LS 0% trong 25 th&aacute;ng</li>\r\n	<li>Thanh to&aacute;n sớm 95% - chiết khấu 7%/tổng gi&aacute;</li>\r\n	<li>CK 9% nếu kh&ocirc;ng tham gia CT ưu đ&atilde;i LS</li>\r\n	<li>Chia sẻ lợi nhuận 80/20 với CĐT khi CĐT vận h&agrave;nh</li>\r\n	<li>Tặng g&oacute;i qu&agrave; tặng l&ecirc;n tới 1 TỶ ĐỒNG</li>\r\n</ul>', '“Biệt thự hoa hồng” kiêu sa cho cuộc sống thăng hoa', 'Đẳng cấp trong vị trí, độc tôn về tầm nhìn', 'Thỏa mãn với tiện ích 5 sao vượt trội', 'Tiềm năng đầu tư sinh lời lớn', NULL, NULL, NULL, NULL, 'Chính sách dành cho khách hàng', 'Flamingo Luxury SkyLake Villas - Đầu Tư Sinh Lời 2,5 tỷ/năm', '05/27/2020', NULL, 'flamingo-skylake', 'biệt thự Luxury SkyLake sở hữu địa thế phong thủy độc đáo và hiếm có tại khu nghỉ dưỡng cao cấp 5 sao Flamingo Đại Lải Resort. Đẳng cấp ở vị trí, độc tôn về tầm nhìn', NULL, 'index, follow'),
(139, '69', 58, 'Admin', 'Hòa Bình New City', 'Trần Hưng Đạo', 2501, 'ba030fbf925b6e05374a.jpg', NULL, 'true', NULL, 'true', '<ul>\r\n	<li style=\"text-align:justify\">T&ecirc;n dự &aacute;n: Khu đ&ocirc; thị H&ograve;a B&igrave;nh New City</li>\r\n	<li style=\"text-align:justify\">Chủ đầu tư: C&ocirc;ng ty CP Sudico H&ograve;a B&igrave;nh</li>\r\n	<li style=\"text-align:justify\">Vị tr&iacute;: Nằm trong Khu đ&ocirc; thị Bắc Trần Hưng Đạo rộng gần 30.000 ha</li>\r\n	<li style=\"text-align:justify\">Tổng diện t&iacute;ch: 25,48 ha</li>\r\n	<li style=\"text-align:justify\">Quy m&ocirc;: 300 l&ocirc; đất liền kề v&agrave; biệt thự, c&ocirc;ng vi&ecirc;n, trường học, hồ điều h&ograve;a</li>\r\n	<li style=\"text-align:justify\">H&igrave;nh thức sở hữu: Sổ đỏ l&acirc;u d&agrave;i</li>\r\n</ul>', 'Tổng quan', 'Vị trí', 'Đầu tư', 'Thiết kế', 'Tiện ích', NULL, NULL, NULL, 'Chính sách', '<p style=\"text-align:justify\">Thị trường bất động sản th&agrave;nh phố H&ograve;a B&igrave;nh đầu năm 2020 đang c&oacute; nguồn cung đất nền tương đối dồi d&agrave;o với h&agrave;ng loạt c&aacute;c dự &aacute;n tại cả hai khu vực bờ tr&aacute;i v&agrave; bờ phải th&agrave;nh phố. Nổi bật trong c&aacute;c dự &aacute;n l&agrave; H&ograve;a B&igrave;nh New City với vị tr&iacute; đắt gi&aacute; c&oacute; một kh&ocirc;ng hai.</p>\r\n\r\n<p style=\"text-align:justify\"><a href=\"https://maivietland.vn/du-an-bds/hoa-binh-new-city.html\"><strong>H&ograve;a B&igrave;nh New City</strong></a> c&oacute; quy m&ocirc; 25,48 ha, bao gồm liền kề, đất biệt thự v&agrave; đầy đủ hạ tầng x&atilde; hội: Trường học, khu c&acirc;y xanh, hồ điều h&ograve;a. Dự &aacute;n sở hữu vị tr&iacute; đắc địa, thuận tiện kết nối với nhiều tiện &iacute;ch ngoại khu đa dạng như: Vincom H&ograve;a B&igrave;nh, Bệnh viện tỉnh, Bến xe th&agrave;nh phố&hellip;&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Với lợi thế đ&atilde; cơ bản ho&agrave;n th&agrave;nh cơ sở hạ tầng, thủ tục ph&aacute;p l&yacute; vững chắc, sổ đỏ ch&iacute;nh chủ sở hữu l&acirc;u d&agrave;i, dự &aacute;n đang thu h&uacute;t được rất nhiều sự quan t&acirc;m, tạo niềm tin tuyệt đối cho kh&aacute;ch h&agrave;ng v&agrave; c&aacute;c nh&agrave; đầu tư.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"tổng quan hòa bình newcity\" src=\"/public/upload/files/hoa-binh-new-city/tong-quan.jpg\" /></p>', '<p style=\"text-align:justify\">Khu đ&ocirc; thị <strong>H&ograve;a B&igrave;nh New City</strong> tọa lạc tr&ecirc;n đường Trần Hưng Đạo, phường Đồng Tiến, th&agrave;nh phố H&ograve;a B&igrave;nh.</p>\r\n\r\n<p style=\"text-align:justify\">Sở hữu vị tr&iacute; đẳng cấp &ldquo;qu&yacute; tộc&rdquo; v&agrave; độc nhất khi liền kề Quảng trường H&ograve;a B&igrave;nh v&agrave; trung t&acirc;m h&agrave;nh ch&iacute;nh tỉnh H&ograve;a B&igrave;nh.</p>\r\n\r\n<p style=\"text-align:justify\">Dự &aacute;n được đ&aacute;nh gi&aacute; c&oacute; ưu thế về thương mại bậc nhất, nằm giữa trung t&acirc;m TP H&ograve;a B&igrave;nh, tiếp gi&aacute;p v&agrave; gần c&aacute;c khu vui chơi như: chợ Phương L&acirc;m, Vincom H&ograve;a B&igrave;nh, bến xe trung t&acirc;m TP H&ograve;a B&igrave;nh, Bệnh viện đa khoa tỉnh H&ograve;a B&igrave;nh, Quảng trường th&agrave;nh phố&hellip; v&agrave; c&aacute;c trục đường trung t&acirc;m như C&ugrave; Ch&iacute;nh Lan, Chi Lăng, Trần Hưng Đạo&hellip;</p>\r\n\r\n<table border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:100%\">\r\n	<tbody>\r\n		<tr>\r\n			<td>1 km: Bệnh viện Đa Khoa tỉnh</td>\r\n			<td>1 km : TTTM Vincom H&ograve;a B&igrave;nh</td>\r\n		</tr>\r\n		<tr>\r\n			<td>800m : Chợ Phương L&acirc;m</td>\r\n			<td>600m : SVĐ tỉnh H&ograve;a B&igrave;nh</td>\r\n		</tr>\r\n		<tr>\r\n			<td>400m : Bến xe trung t&acirc;m TP. H&ograve;a B&igrave;nh</td>\r\n			<td>200m : C&ocirc;ng vi&ecirc;n H&ograve;a B&igrave;nh</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Liền kề Quảng Trường H&ograve;a B&igrave;nh</td>\r\n			<td>300m : Hệ thống trường học c&aacute;c cấp</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style=\"text-align:center\"><img alt=\"vị trí dự án\" src=\"/public/upload/files/hoa-binh-new-city/vitr.jpg\" /></p>', '<ul>\r\n	<li style=\"text-align:justify\">Địa thế ho&agrave;n hảo, phong thủy hanh th&ocirc;ng, tiện &iacute;ch dự &aacute;n&nbsp;đa dạng, m&ocirc;i trường sống xanh, trong l&agrave;nh.</li>\r\n	<li style=\"text-align:justify\">Diện t&iacute;ch l&ocirc; đất hợp l&yacute;, thời gian b&agrave;n giao nhanh ch&oacute;ng, cơ sở hạ tầng đ&atilde; ho&agrave;n thiện 100%.</li>\r\n	<li style=\"text-align:justify\"><a href=\"https://maivietland.vn/du-an-bds/hoa-binh-new-city.html\"><strong>H&ograve;a B&igrave;nh New City</strong></a> nằm trong quần thể đ&ocirc; thị mới hiện đại, đẳng cấp bậc nhất TP H&ograve;a B&igrave;nh.</li>\r\n	<li style=\"text-align:justify\">Ph&aacute;p l&yacute; ho&agrave;n thiện, an ninh đảm bảo, cộng đồng cư d&acirc;n văn minh, th&agrave;nh đạt, d&acirc;n tr&iacute; cao.</li>\r\n	<li style=\"text-align:justify\">Vị tr&iacute; độc t&ocirc;n, Tiện &iacute;ch vượt trội kết hợp với một mức gi&aacute; phải chăng, tiếp cận được với đa số kh&aacute;ch h&agrave;ng từ đầu tư đến nhu cầu ở thực tế.</li>\r\n	<li style=\"text-align:justify\">Dự &aacute;n&nbsp;được đầu tư v&agrave; triển khai bởi Chủ đầu tư uy t&iacute;n, tiềm lực kinh tế mạnh v&agrave; nhiều kinh nghiệm ph&aacute;t triển BĐS tại TP H&ograve;a B&igrave;nh.</li>\r\n</ul>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"tổng quan\" src=\"/public/upload/files/hoa-binh-new-city/tongquan.jpg\" /></p>', '<p style=\"text-align:justify\"><strong>Dự &aacute;n H&ograve;a B&igrave;nh New City</strong> sẽ tạo ra một quần thể đ&ocirc; thị mới vừa mang t&iacute;nh hiện đại vừa chứa đựng những n&eacute;t truyền thống của d&acirc;n tộc. Người d&acirc;n trong khu vực sẽ được hưởng m&ocirc;i trường sống c&oacute; hạ tầng kỹ thuật đồng bộ, hệ thống hạ tầng x&atilde; hội ho&agrave;n chỉnh, đảm bảo n&acirc;ng cao điều kiện sống cho người d&acirc;n trong khu d&acirc;n cư.</p>\r\n\r\n<p style=\"text-align:justify\">Sản phẩm đang được nhiều nh&agrave; đầu tư quan t&acirc;m nhất ở dự &aacute;n l&agrave; đất nền &ndash; nh&agrave; phố. Tại c&aacute;c trục đường lớn rộng đến 36m v&agrave; c&oacute; mật độ d&acirc;n cư qua lại ng&agrave;y c&agrave;ng gia tăng th&igrave; l&ocirc; đất nh&agrave; phố c&ocirc;ng năng 2 trong 1 vừa ở vừa kinh doanh được kh&aacute;ch h&agrave;ng quan t&acirc;m nhất.</p>\r\n\r\n<p style=\"text-align:justify\">Dự &aacute;n c&oacute; khu đất ph&acirc;n l&ocirc; liền kề với đa dạng diện t&iacute;ch từ 108m2 đến 136m2. Khu biệt thự liền kề c&oacute; diện t&iacute;ch 217,2; 240m2 v&agrave; 269,5m2 c&oacute; thiết kế sang trọng, hiện đại, sau khi ho&agrave;n thiện sẽ g&oacute;p phần t&ocirc; điểm th&ecirc;m cho nhịp sống hiện đại, s&ocirc;i động v&agrave; sự ph&aacute;t triển kh&ocirc;ng ngừng của th&agrave;nh phố H&ograve;a B&igrave;nh.</p>', '<p><a href=\"https://maivietland.vn/du-an-bds/hoa-binh-new-city.html\"><strong>Khu đ&ocirc; thị H&ograve;a B&igrave;nh New City</strong></a> đ&atilde; ho&agrave;n thiện hạ tầng cấp tho&aacute;t nước, điện chiếu s&aacute;ng đ&ocirc; thị. Dự &aacute;n ưu ti&ecirc;n những mảng xanh lớn kết hợp hồ nước đem đến m&ocirc;i trường trong l&agrave;nh y&ecirc;n tĩnh. C&aacute;c d&atilde;y nh&agrave; phố kinh doanh được d&agrave;nh ri&ecirc;ng vỉa h&egrave; rộng với đường nội bộ 2 l&agrave;n xe. 100% sản phẩm sẽ đảm bảo &ocirc; t&ocirc; v&agrave;o tận nh&agrave;. C&aacute;c hướng t&acirc;y tứ trạch thi&ecirc;n về kinh doanh được ưu ti&ecirc;n.</p>\r\n\r\n<p>- Thừa hưởng to&agrave;n bộ tiện &iacute;ch ngoại khu của to&agrave;n TP H&ograve;a B&igrave;nh: Cạnh quảng trường H&ograve;a B&igrave;nh, Trung t&acirc;m h&agrave;nh ch&iacute;nh mới của Th&agrave;nh phố, C&ocirc;ng vi&ecirc;n Tuổi Trẻ lớn nhất TP, TTTM Vincom &hellip;<br />\r\n- Tiện &iacute;ch nội khu hiện đại đẳng cấp: Với hồ điều h&ograve;a, c&ocirc;ng vi&ecirc;n c&acirc;y xanh, trường học &hellip;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"TIỆN ÍCH NỘI NGOẠI KHU ĐA DẠNG HÒA BÌNH NEW CITY\" src=\"/public/upload/files/hoa-binh-new-city/tienich.jpg\" /></p>', NULL, NULL, NULL, '<p>Hỗ Trợ Vay 70% GTHĐ</p>', 'TỔNG QUAN DỰ ÁN HÒA BÌNH NEW CITY', 'VỊ TRÍ TRUNG TÂM - ĐẲNG CẤP QUÝ TỘC', '6 LÝ DO NÊN ĐẦU TƯ KHU ĐÔ THỊ HÒA BÌNH NEW CITY', 'THIẾT KẾ SANG TRỌNG - MẶT BẰNG PHÂN LÔ HÒA BÌNH NEW CITY', 'TIỆN ÍCH NỘI NGOẠI KHU ĐA DẠNG HÒA BÌNH NEW CITY', NULL, NULL, NULL, 'CHÍNH SÁCH HỖ TRỢ TÀI CHÍNH CỰC TỐT', 'Hòa Bình New City - Ra Mắt Chính Thức Quỹ Lô Đẹp Nhất', '08/14/2020', NULL, 'hoa-binh-new-city', 'Hòa Bình New City có quy mô 25,48 ha, bao gồm liền kề, đất biệt thự và đầy đủ hạ tầng xã hội: Trường học, khu cây xanh, hồ điều hòa', NULL, 'index, follow');
INSERT INTO `tbl_product` (`id`, `cat_id`, `dis_id`, `user`, `name`, `address`, `hits`, `img`, `price`, `hot`, `tag`, `status`, `detail`, `tab1`, `tab2`, `tab3`, `tab4`, `tab5`, `tab6`, `tab7`, `tab8`, `tab9`, `content1`, `content2`, `content3`, `content4`, `content5`, `content6`, `content7`, `content8`, `content9`, `heading1`, `heading2`, `heading3`, `heading4`, `heading5`, `heading6`, `heading7`, `heading8`, `heading9`, `title`, `date`, `date_up`, `slug`, `description`, `keywords`, `robot`) VALUES
(119, '69', 45, 'Admin', 'The Matrix One Mễ Trì', 'Mặt đường Lê Quang Đạo', 3976, 'The-Matrix-One-eyeview.jpg', 'liên hệ', 'false', NULL, 'true', '<ul>\r\n	<li style=\"text-align:justify\">T&ecirc;n dự &aacute;n: The Matrix One</li>\r\n	<li style=\"text-align:justify\">Chủ đầu tư: C&ocirc;ng ty cổ phần Đầu tư Mai Linh</li>\r\n	<li style=\"text-align:justify\">Vị tr&iacute;: Ng&atilde; tư L&ecirc; Quang Đạo &ndash; Mễ Tr&igrave;, quận Nam Từ Li&ecirc;m, H&agrave; Nội</li>\r\n	<li style=\"text-align:justify\">Đơn vị ph&aacute;t triển: MIK Group</li>\r\n	<li style=\"text-align:justify\">Tổng thầu x&acirc;y dựng: Coteccons</li>\r\n	<li style=\"text-align:justify\">Tổng diện t&iacute;ch đất quy hoạch: 39.8ha bao gồm:</li>\r\n	<li style=\"text-align:justify\">C&ocirc;ng vi&ecirc;n hồ điều h&ograve;a (14ha)</li>\r\n	<li style=\"text-align:justify\">Trường học quốc tế v&agrave; tổ hợp nh&agrave; ở, thương mại,<br />\r\n	dịch vụ, kh&aacute;ch sạn 5*</li>\r\n	<li style=\"text-align:justify\">Cơ cấu sản phẩm</li>\r\n	<li style=\"text-align:justify\">&nbsp;Shop Apartment ch&acirc;n khối đế: 20 căn</li>\r\n	<li style=\"text-align:justify\">&nbsp;Căn hộ chung cư: 740 căn</li>\r\n	<li style=\"text-align:justify\">&nbsp;Penthouse: 2 căn</li>\r\n</ul>', 'Tổng quan', 'Vị trí', 'Thiết kế', 'Tiện ích', 'Tiến độ thanh toán', 'Đầu tư', NULL, NULL, 'Chính sách', '<p style=\"text-align:justify\"><a href=\"https://maivietland.vn/du-an-bds/the-matrix-one-me-tri.html\"><strong>The Matrix One Mễ Tr&igrave;</strong></a>&nbsp; l&agrave; dự &aacute;n chung cư cao cấp c&oacute; thiết kế hiện đại, độc đ&aacute;o,&nbsp;c&ugrave;ng với đ&oacute; l&agrave; hệ thống tiện &iacute;ch đa dạng hứa hẹn sẽ l&agrave; dự &aacute;n bất động sản hot nhất năm 2020.</p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>Sở hữu những ưu thế cạnh tranh kh&ocirc;ng dự &aacute;n n&agrave;o c&oacute; được:</strong></h3>\r\n\r\n<ul>\r\n	<li>Vị tr&iacute; CBD - Trung t&acirc;m h&agrave;nh ch&iacute;nh thương mại đắt gi&aacute; bậc nhất &amp; hệ thống giao th&ocirc;ng đồng bộ hiện đại</li>\r\n	<li>MIKGroup - Đơn vị ph&aacute;t triển dự &aacute;n v&agrave; c&aacute;c đơn vị tư vấn, thiết kế uy t&iacute;n</li>\r\n	<li>Dự &aacute;n liền kề c&ocirc;ng vi&ecirc;n v&agrave; hồ điều h&ograve;a 14 ha trong l&agrave;nh</li>\r\n	<li>Dự &aacute;n duy nhất sở hữu tầm nh&igrave;n panorama to&agrave;n cảnh trường đua tốc độ F1</li>\r\n	<li>39+ tiện &iacute;ch đẳng cấp v&agrave; tối ưu d&agrave;nh cho cư d&acirc;n sinh sống, giao lưu, l&agrave;m việc, ph&aacute;t triển bản th&acirc;n</li>\r\n	<li>Kh&ocirc;ng kh&iacute; tươi &ndash; ngu&ocirc;n nước sạch với hệ thống lọc nước trung t&acirc;m, m&aacute;y lọc kh&ocirc;ng kh&iacute; cho ph&ograve;ng kh&aacute;ch &amp; m&aacute;y lọc nước tinh khiết tại ph&ograve;ng bếp</li>\r\n	<li>Hệ thống Smart Building t&acirc;n tiến, hệ thống quản l&yacute; to&agrave; nh&agrave; BMS chặt chẽ, khoa học.</li>\r\n	<li>Căn hộ b&agrave;n giao nội thất cao cấp đến từ những thương hiệu h&agrave;ng đầu thế giới</li>\r\n	<li>Cộng đồng cư d&acirc;n tinh hoa, ưu t&uacute;, th&agrave;nh đạt, lu&ocirc;n c&acirc;n bằng cuộc sống c&ocirc;ng việc v&agrave; hưởng thụ</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">Mặt kh&aacute;c, dự &aacute;n được chăm ch&uacute;t bởi chủ đầu tư MIK Group - đơn vị danh tiếng, uy t&iacute;n với nhiều dự &aacute;n chung cư cao cấp n&ecirc;n qu&yacute; kh&aacute;ch h&agrave;ng ho&agrave;n to&agrave;n c&oacute; thể y&ecirc;n t&acirc;m về chất lượng cũng như tiến độ thi c&ocirc;ng của dự &aacute;n. C&aacute;c nh&agrave; đầu tư cũng c&oacute; th&ecirc;m một sản phẩm đầu tư hấp dẫn, khả năng sinh lời lớn.</p>', '<p style=\"text-align:justify\"><a href=\"https://maivietland.vn/du-an-bds/the-matrix-one-me-tri.html\"><strong>Dự &aacute;n The Matrix One</strong></a> tọa lạc ngay vị tr&iacute; giao điểm của hai tuyến đường trọng điểm ph&iacute;a T&acirc;y l&agrave; L&ecirc; Quang Đạo v&agrave; Mễ Tr&igrave;, sở hữu đến 2 mặt tiền rộng lớn. Từ dự &aacute;n, dễ d&agrave;ng kết nối với nhiều tuyến đường huyết mạch đi v&agrave;o trung t&acirc;m th&agrave;nh phố hay c&aacute;c tỉnh l&acirc;n cận như: Phạm H&ugrave;ng, Xu&acirc;n Thủy, Đại lộ Thăng Long, L&ecirc; Đức Thọ, Hồ T&ugrave;ng Mậu&hellip; Ngo&agrave;i ra, xung quanh dự &aacute;n c&ograve;n c&oacute; nhiều tuyến đường sắt đ&ocirc; thị&nbsp;đ&atilde; v&agrave; đang đi v&agrave;o hoạt động n&ecirc;n việc di chuyển của dư d&acirc;n c&agrave;ng th&ecirc;m thuận tiện.</p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>&nbsp;C&aacute;c tiện &iacute;ch dịch vụ phong ph&uacute;, hấp dẫn bao quanh dự &aacute;n</strong></h3>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Gần hệ thống c&aacute;c trường gi&aacute;o dục h&agrave;ng đầu &amp; danh tiếng như trường li&ecirc;n cấp Amsterdam, Marie Curie, Lomonoxop, ĐH Quốc gia H&agrave; Nội&hellip;</li>\r\n	<li style=\"text-align:justify\">Hệ thống trung t&acirc;m thương mại đẳng cấp như TTTM The Garden, Vincom Center &hellip;</li>\r\n	<li style=\"text-align:justify\">Tiệm cận c&aacute;c bệnh viện hiện đại đầu ng&agrave;nh của cả nước như bệnh viện 19-8, Bệnh vi&ecirc;n huyết học TW, Bệnh viện An Sinh (Hồng Ngọc)&hellip;</li>\r\n</ul>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Vị trí bao trọn cung đường đua F1 danh giá của The Matrix One Mễ Trì\" src=\"/public/upload/files/the-matrix-one/vitri.jpg\" /></p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>Tầm nh&igrave;n Đường đua F1 danh gi&aacute;</strong></h3>\r\n\r\n<p style=\"text-align:justify\">Điểm s&aacute;ng trong vị tr&iacute; của chung cư&nbsp;l&agrave; nằm ngay cạnh trục đường đua F1 đang được x&acirc;y dựng v&agrave; sẽ ch&iacute;nh thức hoạt động v&agrave;o năm 2020. C&aacute;ch đ&oacute; kh&ocirc;ng xa l&agrave; S&acirc;n vận động quốc gia Mỹ Đ&igrave;nh. Với tầm view rộng, tho&aacute;ng tới hai địa điểm nổi tiếng n&agrave;y, cư d&acirc;n c&oacute; thể h&ograve;a m&igrave;nh với những trận cầu nảy lửa hay nghẹt thở, hồi hộp với những v&ograve;ng đua F1 rất ch&acirc;n thực ngay tại căn hộ của m&igrave;nh m&agrave; kh&ocirc;ng cần chen lấn, x&ocirc; đẩy nơi kh&aacute;n đ&agrave;i đ&ocirc;ng đ&uacute;c.&nbsp;The Matrix One hứa hẹn trở th&agrave;nh phi&ecirc;n bản &ldquo;Monaco xa hoa trong l&ograve;ng H&agrave; Nội&rdquo;.</p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>Tầm nh&igrave;n &ocirc;m trọn &quot;đảo xanh&quot; 14ha</strong></h3>\r\n\r\n<p style=\"text-align:justify\">Tầm nh&igrave;n panorama &ocirc;m trọn khu c&ocirc;ng vi&ecirc;n v&agrave; hồ điều h&ograve;a 14ha mang lại cho cư d&acirc;n The Matrix One trải nghiệm v&ocirc; gi&aacute;. C&ocirc;ng vi&ecirc;n hồ điều h&ograve;a c&oacute; quy hoạch phần nổi: c&ocirc;ng vi&ecirc;n c&acirc;y xanh, khu vui chơi giải tr&iacute;, khu thể dục thể thao... hứa hẹn sẽ l&agrave; kh&ocirc;ng gian vui chơi, giải tr&iacute;, tận hưởng nguồn kh&iacute; trong l&agrave;nh, tốt cho sức khỏe cư d&acirc;n.&nbsp;</p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>Vị tr&iacute; trung t&acirc;m Mỹ Đ&igrave;nh đắt gi&aacute; mang lại tiềm năng lớn</strong></h3>\r\n\r\n<p style=\"text-align:justify\">Khu vực Mỹ Đ&igrave;nh lu&ocirc;n l&agrave; mảnh đất &ldquo;v&agrave;ng&rdquo; của c&aacute;c nh&agrave; đầu tư v&igrave; gi&aacute; trị bất động sản lu&ocirc;n c&oacute; xu hướng tăng mạnh, mang lại lợi nhuận rất lớn. The Matrix Mễ Tr&igrave; lại sở hữu vị tr&iacute; đắc địa n&ecirc;n kh&ocirc;ng kh&oacute; để dự đo&aacute;n về tương lai s&aacute;ng lạn khi đầu tư v&agrave;o dự &aacute;n. Vậy n&ecirc;n đ&acirc;y l&agrave; cơ hội đầu tư hấp dẫn, hiếm c&oacute; cho c&aacute;c nh&agrave; đầu tư, những người đang t&igrave;m kiếm một dự &aacute;n mang lại lợi nhuận vượt trội.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Vị trí bao trọn cung đường đua F1 danh giá của The Matrix One Mễ Trì\" src=\"/public/upload/files/the-matrix-one/lien-ket-vung.jpg\" /></p>', '<h3 style=\"text-align:justify\"><strong>Đơn vị tư vấn thiết kế&nbsp;BAUMSCHIAGER EBERLE ARCHITEKTEN (BEA)</strong></h3>\r\n\r\n<ul>\r\n	<li>Đơn vị c&oacute; kiến thức chuy&ecirc;n ng&agrave;nh bậc nhất về lĩnh vực thiết kế, đặc biệt l&agrave; nghỉ dưỡng</li>\r\n	<li>Thiết kế nhiều c&ocirc;ng tr&igrave;nh kh&aacute;ch sạn nổi tiếng tr&ecirc;n thế giới: Shangri-La (Vienna, London), Cube Savognin (Thụy Sỹ), Loipersdorf &ndash; Martinspark (&Aacute;o)...</li>\r\n	<li>Thiết kế c&aacute;c kh&aacute;ch sạn nổi tiếng do c&aacute;c tập đo&agrave;n quản l&yacute; KS h&agrave;ng đầu thế giới tại Việt Nam (Accor, M&ouml;venpick, Absolute, Pan Pacific...)</li>\r\n	<li>Thiết kế c&aacute;c dự &aacute;n nghỉ dưỡng đ&igrave;nh đ&aacute;m: The Coastal Hill - Dự &aacute;n kh&aacute;ch sạn lớn nhất Việt Nam...</li>\r\n</ul>\r\n\r\n<h3 style=\"text-align:justify\"><strong>Cơ cấu sản phẩm</strong></h3>\r\n\r\n<p style=\"text-align:justify\">Dự &aacute;n được thiết kế gồm c&aacute;c hạng mục:&nbsp;Khu căn hộ cao tầng (2 t&ograve;a, mỗi t&ograve;a 44 tầng), Shophouse,&nbsp;Trung t&acirc;m thương mại, C&ocirc;ng vi&ecirc;n - Nh&agrave; h&aacute;t quy lớn v&agrave; c&aacute;c tiện &iacute;ch kh&aacute;c.&nbsp;Được tạo n&ecirc;n dưới b&agrave;n tay t&agrave;i hoa của c&aacute;c kiến tr&uacute;c sư h&agrave;ng đầu H&agrave;n Quốc, c&aacute;c t&ograve;a nh&agrave;, căn hộ đều được thiết kế ấn tượng, hiện đại, tinh tế, tạo n&ecirc;n kh&ocirc;ng gian sống tiện nghi, thoải m&aacute;i cho cư d&acirc;n.</p>\r\n\r\n<p style=\"text-align:justify\"><a href=\"https://maivietland.vn/du-an-bds/the-matrix-one-me-tri.html\"><strong>Chung cư The Matrix One</strong></a> gồm 2 t&ograve;a căn hộ cao cấp, mỗi t&ograve;a 44 tầng với c&aacute;c căn hộ diện t&iacute;ch từ 86 - 114m2, 2-3 ph&ograve;ng ngủ. Đặc biệt, căn hộ Penthouse c&oacute; diện t&iacute;ch 783m2 với 5 ph&ograve;ng ngủ thể hiện sự đẳng cấp tối thượng của chủ sở hữu. Nội thất trong mỗi căn hộ được lựa chọn kỹ lưỡng từ c&aacute;c thương hiệu nổi tiếng theo phong c&aacute;ch trang ho&agrave;ng t&acirc;n cổ điển để căn hộ đơn giản, hiện đại m&agrave; vẫn to&aacute;t l&ecirc;n vẻ thanh lịch, sang trọng. Đặc biệt, mặt ngo&agrave;i của c&aacute;c t&ograve;a chung cư đều được lắp cửa sổ k&iacute;nh lớn, mang đến hiệu ứng bắt s&aacute;ng cho dự &aacute;n, khiến cho vẻ ngo&agrave;i của t&ograve;a nh&agrave; trở n&ecirc;n nổi bật hơn.</p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>Kh&ocirc;ng gian xanh được ch&uacute; trọng tại The Matrix One</strong></h3>\r\n\r\n<p style=\"text-align:justify\">The Matrix One được bao bọc bởi hệ thống c&acirc;y xanh d&agrave;y đặc.&nbsp;C&acirc;y xanh được trồng theo nhiều tầng lớp, đảm bảo độ phủ xanh tối đa tại dự &aacute;n.&nbsp;S&acirc;n vườn tầng 1, tầng m&aacute;i, từng g&oacute;c nhỏ gi&uacute;p giảm tiếng ồn, kh&oacute;i bụi, điều h&ograve;a kh&ocirc;ng kh&iacute; v&agrave; giảm &ocirc; nhiễm m&ocirc;i trường. C&aacute;c phương &aacute;n c&acirc;y xanh được đề xuất:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">C&acirc;y tầng thấp &amp; tầng trung &ndash; C&acirc;y bụi &amp; c&acirc;y thảm</li>\r\n	<li style=\"text-align:justify\">Tầng cao &ndash; c&acirc;y ăn quả, c&acirc;y th&acirc;n gỗ nhiệt đới</li>\r\n	<li style=\"text-align:justify\">C&aacute;c c&acirc;y &aacute;nh s&aacute;ng, &aacute;nh s&aacute;ng &acirc;m s&agrave;n được bố tr&iacute; đan xen, ph&ugrave; hợp với cảnh quan v&agrave; từng lo&agrave;i c&acirc;y cỏ, tạo n&ecirc;n một tổng thể lung linh huyền ảo về đ&ecirc;m.</li>\r\n	<li style=\"text-align:justify\">Vườn Nhật được bố tr&iacute; tại tầng 43 của dự &aacute;n: C&acirc;y xanh, đ&aacute;, nước, &aacute;nh s&aacute;ng... được sắp xếp một c&aacute;ch tinh tế, mang lại cảm gi&aacute;c thư gi&atilde;n c&ugrave;ng thi&ecirc;n nhi&ecirc;n, tĩnh t&acirc;m giữa vườn Nhật.</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">Trong bất kỳ dự &aacute;n, c&ocirc;ng tr&igrave;nh n&agrave;o, chủ đầu tư MIK Group cũng mong muốn đạt tới sự ho&agrave;n mỹ trong tất cả c&aacute;c phương diện, Dự &aacute;n&nbsp;thực sự đ&atilde; đạt tới sự ho&agrave;n mỹ tuyệt đối, mỗi chi tiết thiết kế, kh&ocirc;ng gian sống, sinh hoạt&nbsp;đều được chăm ch&uacute;t tỉ mỉ, đem lại sự đẳng cấp v&agrave; sự tự h&agrave;o khi sở hữu cho kh&aacute;ch h&agrave;ng.</p>', '<p style=\"text-align:justify\"><a href=\"https://maivietland.vn/du-an-bds/the-matrix-one-me-tri.html\"><strong>The Matrix One</strong></a> ngay từ khi l&agrave; bản vẽ tr&ecirc;n giấy, chủ đầu tư đ&atilde; c&oacute; mong muốn x&acirc;y dựng cuộc sống văn minh, đẳng cấp d&agrave;nh cho cư d&acirc;n, ngo&agrave;i những tiện &iacute;ch ngoại khu l&acirc;n cận quanh dự &aacute;n, chủ đầu tư MIK Group c&ograve;n x&acirc;y dựng cho dự &aacute;n&nbsp;một chuỗi c&aacute;c tiện &iacute;ch đa năng kh&eacute;p k&iacute;n, đa dạng như:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Bể bơi 4 m&ugrave;a được bố tr&iacute; ở tầng 4, tại cầu nối giữa 2 t&ograve;a th&aacute;p với tầm nh&igrave;n tuyệt mỹ ra c&ocirc;ng vi&ecirc;n hồ điều h&ograve;a</li>\r\n	<li style=\"text-align:justify\">Trường học quốc tế</li>\r\n	<li style=\"text-align:justify\">Shop apartment hiện đại</li>\r\n	<li style=\"text-align:justify\">Quảng trường trung t&acirc;m</li>\r\n	<li style=\"text-align:justify\">Khu vui chơi trẻ em</li>\r\n	<li style=\"text-align:justify\">Khu tập thiền</li>\r\n	<li style=\"text-align:justify\">Ch&ograve;i nghỉ</li>\r\n	<li style=\"text-align:justify\">Business Lounge, thư vi&ecirc;n &amp; co-working space, Ph&ograve;ng sinh hoạt cộng đồng (t&ograve;a A)</li>\r\n	<li style=\"text-align:justify\">Khu vui chơi trẻ em trong nh&agrave; (t&ograve;a B)</li>\r\n	<li style=\"text-align:justify\">Ph&ograve;ng tập Gym</li>\r\n</ul>\r\n\r\n<h3 style=\"text-align:justify\"><strong>Tiện &iacute;ch đặc biệt:</strong></h3>\r\n\r\n<p style=\"text-align:justify\">- PH&Ograve;NG L&Aacute;NH LẠN AN TO&Agrave;N&nbsp;được bố tr&iacute; tại tầng 4, 23, 43,&nbsp;Bao gồm 6 ph&ograve;ng l&aacute;nh nạn với tổng diện t&iacute;ch 900m2</p>\r\n\r\n<p style=\"text-align:justify\">- 3 HẦM ĐỖ XE RỘNG R&Atilde;I VỚI CAM KẾT VƯỢT TRỘI:&nbsp;tự động đọc biển số v&agrave; thẻ từ mở cửa barrier, x&aacute;c định được số lượng xe v&agrave;o ra/tầng hầm...Đảm bảo c&ocirc;ng suất 01 suất xe m&aacute;y/01 căn hộ.</p>\r\n\r\n<p style=\"text-align:justify\">- HỆ THỐNG SMART BUILDING:&nbsp;</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Hệ thống Carparking tự nhận diện biển số v&agrave; tem từ để mở cửa cho xe gửi theo v&eacute; th&aacute;ng</li>\r\n	<li style=\"text-align:justify\">Hệ thống Video phone cho ph&eacute;p chủ căn hộ mở cửa sảnh cho kh&aacute;ch sau khi x&aacute;c định được khu&ocirc;n mặt th&ocirc;ng qua bảng gọi c&oacute; gắn Camera đặt tại tầng hầm v&agrave; sảnh tầng 1</li>\r\n	<li style=\"text-align:justify\">Phần mềm th&ocirc;ng b&aacute;o từ BQL đến từng căn hộ, nhận phản hồi th&ocirc;ng tin, cung cấp cẩm nangcư d&acirc;n, phản &aacute;nh sự cố của c&aacute;c hệ thống hoặc thiết bị trong nh&agrave;, booking c&aacute;c dịch vụ trong t&ograve;a nh&agrave;, li&ecirc;n kết t&agrave;i khoản ng&acirc;n h&agrave;ng/ V&iacute; điện tử/ Khảo s&aacute;t lấy &yacute; kiến d&acirc;n cư.</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">- M&Aacute;Y LỌC KH&Ocirc;NG KH&Iacute;, M&Aacute;Y LỌC NƯỚC cục bộ tại mỗi căn hộ.</p>\r\n\r\n<p style=\"text-align:justify\"><strong>An ninh đảm bảo tuyệt đối</strong></p>\r\n\r\n<p style=\"text-align:justify\">Sự an to&agrave;n cũng được đảm bảo tuyệt đối v&igrave; dự &aacute;n c&oacute; hệ thống an ninh nghi&ecirc;m ngặt với: đội ngũ bảo an chuy&ecirc;n nghiệp thường xuy&ecirc;n tuần tra, gi&aacute;m s&aacute;t; hệ thống thang m&aacute;y vận h&agrave;nh ri&ecirc;ng phục vụ cư d&acirc;n t&ograve;a nh&agrave;; kh&oacute;a cửa Intercom được lắp đặt ở cửa ch&iacute;nh căn hộ cho ph&eacute;p hiển thị h&igrave;nh ảnh kh&aacute;ch gh&eacute; thăm v&agrave; kết nối trực tiếp với hệ thống an ninh; n&uacute;t b&aacute;o động khẩn cấp trong mỗi căn hộ, khi c&oacute; việc nguy cấp chỉ cần nhấn chu&ocirc;ng, chưa đầy 3 ph&uacute;t đội ngũ bảo vệ sẽ xuất hiện&hellip; Cho n&ecirc;n, lựa chọn sống tại chung cư l&agrave; qu&yacute; kh&aacute;ch h&agrave;ng đ&atilde; lựa chọn một cuộc sống tiện nghi, an to&agrave;n v&agrave; b&igrave;nh an trong từng hơi thở.</p>', NULL, '<h3 style=\"text-align:justify\"><strong>Chủ đầu tư MIK GROUP uy t&iacute;n bậc nhất thị trường</strong></h3>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">C&ocirc;ng ty Cổ Phần Tập đo&agrave;n MIKGroup Việt Nam (viết tắt l&agrave; MIKGroup) hoạt động chuy&ecirc;n s&acirc;u trong lĩnh vực Đầu tư, Kinh doanh v&agrave; Ph&aacute;t triển Bất động sản tại Việt Nam</li>\r\n	<li style=\"text-align:justify\">L&agrave; 1 trong những đơn vị ph&aacute;t triển dự &aacute;n uy t&iacute;n bậc nhất tr&ecirc;n thị trường BĐS với đội ngũ quản l&yacute;, chuy&ecirc;n gia gi&agrave;u kinh nghiệm.</li>\r\n	<li style=\"text-align:justify\">X&acirc;y dựng &amp; ph&aacute;t triển h&agrave;ng loạt c&aacute;c dự &aacute;n về căn hộ cao cấp, Villa &amp; Nh&agrave; phố, BĐS Nghỉ dưỡng, kh&aacute;ch sạn &amp; căn hộ dịch vụ.</li>\r\n</ul>\r\n\r\n<h3 style=\"text-align:justify\"><strong>Ng&acirc;n h&agrave;ng t&agrave;i trợ - VPBANK&nbsp;</strong></h3>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">T&agrave;i trợ dự &aacute;n</li>\r\n	<li style=\"text-align:justify\">Bảo l&atilde;nh tiến độ</li>\r\n	<li style=\"text-align:justify\">Bảo l&atilde;nh kh&aacute;ch h&agrave;ng</li>\r\n	<li style=\"text-align:justify\">Cho vay kh&aacute;ch h&agrave;ng c&aacute; nh&acirc;n l&ecirc;n tới 75% gi&aacute; trị hợp đồng.</li>\r\n	<li style=\"text-align:justify\">Ch&iacute;nh s&aacute;ch vay hấp dẫn, cạnh tranh. Thủ tục vay linh hoạt.</li>\r\n	<li style=\"text-align:justify\">Kh&aacute;ch h&agrave;ng thế chấp bằng hợp đồng mua b&aacute;n.</li>\r\n</ul>\r\n\r\n<h3 style=\"text-align:justify\"><strong>Sản phẩm đẳng cấp hiếm c&oacute; tr&ecirc;n thị trường</strong></h3>\r\n\r\n<p style=\"text-align:justify\"><a href=\"https://maivietland.vn/du-an-bds/the-matrix-one-me-tri.html\"><strong>The Matrix One</strong></a> l&agrave; dự &aacute;n vừa đẳng cấp, hiện đại lại vừa c&oacute; kh&ocirc;ng gian xanh rộng r&atilde;i, chiếm gần 70% diện t&iacute;ch dự &aacute;n, một sản phẩm hiếm c&oacute; trong thị trường bất động sản nội đ&ocirc; H&agrave; Nội. Mặt kh&aacute;c dự &aacute;n c&ograve;n nằm ở vị tr&iacute; kim cương trong khu đất v&agrave;ng Mỹ Đ&igrave;nh - một trong những khu vực c&oacute; gi&aacute; đất đắt đỏ nhất th&agrave;nh phố th&igrave; kh&ocirc;ng c&oacute; l&yacute; do g&igrave; gi&aacute; trị bất động sản của dự &aacute;n lại thấp.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Ngo&agrave;i ra, Dự &Aacute;n&nbsp;c&ograve;n c&oacute; hệ thống tiện &iacute;ch hiện đại, kiến tr&uacute;c ấn tượng, hội tụ mọi yếu tố của một bất động sản đẳng cấp, một nơi an cư mơ ước của nhiều gia đ&igrave;nh Việt Nam. Nh&agrave; đầu tư c&oacute; thể mua căn hộ để ở hoặc cho thu&ecirc;, b&aacute;n lại đều thu được nguồn lợi nhuận khủng m&agrave; kh&ocirc;ng phải dự &aacute;n n&agrave;o cũng đạt được. Vậy n&ecirc;n đ&acirc;y l&agrave; một dự &aacute;n tiềm năng, một &ldquo;con mồi b&eacute;o bở&rdquo; m&agrave; nh&agrave; đầu tư kh&ocirc;ng n&ecirc;n bỏ lỡ.</p>', NULL, NULL, '<p style=\"text-align:justify\">- Thanh to&aacute;n sớm 70% được hưởng chiết khấu 5.5% Gi&aacute; b&aacute;n căn hộ ( chưa bao gồm VAT v&agrave; chưa bao gồm kinh ph&iacute; bảo tr&igrave;)</p>\r\n\r\n<p style=\"text-align:justify\">- Thanh to&aacute;n sớm 95%, Kh&aacute;ch h&agrave;ng được lựa chọn 1 trong 2 ưu đ&atilde;i dưới đ&acirc;y:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Chiết khấu 9% gi&aacute; b&aacute;n căn hộ (chưa bao gồm VAT v&agrave; chưa bao gồm kinh ph&iacute; bảo tr&igrave;)</li>\r\n	<li style=\"text-align:justify\">Hỗ trợ l&atilde;i suất 0% trong 18 th&aacute;ng tối đa 75% Gi&aacute; b&aacute;n căn hộ</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">- Miễn ph&iacute; 03 năm Ph&iacute; Quản L&yacute;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>', 'Tổng quan dự án The Matrix One Mễ Trì', 'Vị trí bao trọn cung đường đua F1 danh giá của The Matrix One Mễ Trì', 'Kiến tạo Biểu tượng đẳng cấp mới của Thủ đô', '39++ Tiện ích vượt trội cho cuộc sống vượt chuẩn', 'Tiến độ thanh toán dự án The Matrix One', 'Cơ hội đầu tư hiếm có, thu lợi nhuận vượt trội', NULL, NULL, 'Chính sách bán hàng', 'The Matrix One Mễ Trì - Thông Tin Đặc Biệt Được Public từ CĐT', '03/25/2020', NULL, 'the-matrix-one-me-tri', 'Dự án The Matrix One Mễ trì thiết kế độc đáo, hệ thống tiện ích và dịch vụ cao cấp. Dự án tọa lạc ngay vị trí giao điểm của Lê Quang Đạo và Mễ Trì', 'The Matrix One, Dự án The Matrix One Mễ trì, The Matrix One Mễ trì, chung cư The Matrix One', 'index, follow'),
(120, '69', 46, 'Admin', 'Five Star West Lake', '162 Hoàng Hoa Thám', 1603, 'five-star-westlake-2.jpg', '10 tỷ', 'false', NULL, 'true', '<ul>\r\n	<li style=\"text-align:justify\">T&ecirc;n dự &aacute;n: Five Star West Lake</li>\r\n	<li style=\"text-align:justify\">Vị tr&iacute; dự &aacute;n: Số 162 Ho&agrave;ng Hoa Th&aacute;m &amp; 167 Thụy Khu&ecirc;, T&acirc;y Hồ, H&agrave; Nội</li>\r\n	<li style=\"text-align:justify\">Chủ đầu tư: Tập đo&agrave;n GFS</li>\r\n	<li style=\"text-align:justify\">Nh&agrave; thầu thi c&ocirc;ng: Coteccons</li>\r\n	<li style=\"text-align:justify\">Tổng diện t&iacute;ch đất: 2.665m2</li>\r\n	<li style=\"text-align:justify\">Diện t&iacute;ch x&acirc;y dựng: 764m2</li>\r\n	<li style=\"text-align:justify\">Quy m&ocirc;: Gồm 14 tầng nổi v&agrave; 3 tầng hầm trong đ&oacute;:</li>\r\n	<li style=\"text-align:justify\">Tổng số căn hộ để b&aacute;n: 32 căn với diện t&iacute;ch từ 136m2 - 400m2</li>\r\n	<li style=\"text-align:justify\">Tổng số căn hộ dịch vụ: 38 căn với diện t&iacute;ch từ 30m2 - 50m2</li>\r\n	<li style=\"text-align:justify\">Chỗ để xe &ocirc; t&ocirc;: 79 chỗ</li>\r\n	<li style=\"text-align:justify\">Khởi c&ocirc;ng x&acirc;y dựng: Qu&yacute; III/2018</li>\r\n	<li style=\"text-align:justify\">Dự kiến ho&agrave;n th&agrave;nh: Qu&yacute; III/2020</li>\r\n	<li style=\"text-align:justify\">H&igrave;nh thức sở hữu: Sổ hồng vĩnh viễn</li>\r\n</ul>', 'Tổng quan', 'Vị trí', 'Kiến trúc', 'Thiết kế', 'Tiện ích', 'Đầu tư', NULL, NULL, 'Chính sách', '<p>Chung cư Five Star West Lake l&agrave; dự &aacute;n cao cấp của tập đo&agrave;n GFS, mang đến cho kh&aacute;ch h&agrave;ng cơ hội sở hữu căn hộ view Hồ T&acirc;y sang trọng, cảm nhận trọn vẹn vẻ đẹp s&ocirc;ng nước hữu t&igrave;nh.</p>\r\n\r\n<p>Với diện t&iacute;ch x&acirc;y dựng chiếm chưa đến 30% tr&ecirc;n tổng diện t&iacute;ch đất 2.665m2, dự &aacute;n kiến tạo một kh&ocirc;ng gian sống tho&aacute;ng đ&atilde;ng v&agrave; biệt lập với phố thị sầm uất. Five Star West Lake được định vị l&agrave; d&ograve;ng bất động sản hạng sang phi&ecirc;n bản giới hạn - boutique luxury. To&agrave;n bộ dự &aacute;n cao 14 tầng chỉ c&oacute; 32 căn hộ để b&aacute;n. Kh&ocirc;ng cầu kỳ v&agrave; ph&ocirc; trương, mỗi căn hộ ẩn giấu sự sang trọng tinh tế v&agrave; gi&aacute; trị ri&ecirc;ng biệt của chủ nh&acirc;n.</p>\r\n\r\n<p>Tọa lạc tại vị tr&iacute; đắc địa đắt gi&aacute;, c&oacute; hệ thống tiện &iacute;ch dịch vụ &ldquo;đặc quyền&rdquo;, cộng đồng d&acirc;n cư văn minh, tri thức v&agrave; cảnh sắc tuyệt đẹp của Hồ T&acirc;y sẽ kiến tạo cuộc sống ho&agrave;n mỹ, sang trọng, kh&ocirc;ng dễ kiếm t&igrave;m cho chủ nh&acirc;n căn hộ.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Tổng quan dự án Five Star West Lake\" src=\"/public/upload/images/five-star-westlake/five-star-westlake-3.jpg\" /></p>', '<p style=\"text-align:justify\">Được v&iacute; như n&agrave;ng T&acirc;y Thi trong giai thoại với cảnh sắc mỹ lệ, Hồ T&acirc;y đ&atilde; l&agrave;m say l&ograve;ng biết bao thế hệ người H&agrave; Nội v&agrave; du kh&aacute;ch thập phương. Chốn &ldquo;danh thắng đẹp nhất kinh kỳ&rdquo; kh&ocirc;ng chỉ c&oacute; c&oacute; vẻ đẹp thơ mộng của hồ nước trong xanh rộng h&agrave;ng trăm hecta, ven bờ liễu phủ m&agrave; c&ograve;n c&oacute; n&eacute;t huyền b&iacute;, những giai thoại cổ xưa được th&ecirc;u dệt l&ecirc;n xoay quanh hồ nước h&agrave;ng ngh&igrave;n năm tuổi.</p>\r\n\r\n<p style=\"text-align:justify\">Vị tr&iacute; độc đắc về phong thủy, nơi hội tụ tinh hoa truyền thống v&agrave; t&iacute;n ngưỡng d&acirc;n tộc, nơi quy tụ truyền thuyết v&agrave; thần thoại h&agrave;ng ngh&igrave;n năm&hellip; tất cả đ&atilde; khiến cho Hồ T&acirc;y trở th&agrave;nh rốn rồng của d&acirc;n tộc, nơi hội tụ vượng kh&iacute; của đất trời H&agrave; Nội.</p>\r\n\r\n<p style=\"text-align:justify\">Dự &aacute;n Five Star West Lake tọa lạc tại miền đất sinh kh&iacute; linh thi&ecirc;ng Hồ T&acirc;y, được thổi hồn từ c&aacute;i n&ocirc;i của tinh hoa v&agrave; văn h&oacute;a H&agrave; Th&agrave;nh, vang vọng &acirc;m hưởng của những dấu t&iacute;ch cổ xưa. B&ecirc;n cạnh đ&oacute;, phong thủy Hồ T&acirc;y l&agrave; nơi đ&oacute;n nhận v&agrave; t&iacute;ch tụ vượng kh&iacute;. Vậy n&ecirc;n, d&acirc;n cư sống quanh đ&acirc;y sẽ nhận được nguồn sinh kh&iacute; c&aacute;t l&agrave;nh, t&agrave;i vượng m&agrave; bất cứ ai cũng đều mơ ước.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"vị tí\" src=\"/public/upload/images/five-star-westlake/five-star-westlake-vitri.jpg\" /></p>\r\n\r\n<p style=\"text-align:justify\">Sở hữu tầm nh&igrave;n l&yacute; tưởng hướng ra mặt Hồ T&acirc;y rộng lớn, dự &aacute;n l&agrave; một kiệt t&aacute;c văn minh, l&agrave; biểu tượng giao thoa giữa ngh&igrave;n năm văn vật v&agrave; sức sống căng tr&agrave;n đang vươn m&igrave;nh mạnh mẽ.</p>\r\n\r\n<p style=\"text-align:justify\">Tọa lạc tại vị tr&iacute; đắc địa 162 Ho&agrave;ng Hoa Th&aacute;m (167 Thụy Khu&ecirc;), Five Star West Lake c&oacute; lợi thế thu trọn to&agrave;n cảnh đẹp nhất của Hồ T&acirc;y v&agrave; to&agrave;n th&agrave;nh phố trong tầm mắt. Từ đ&acirc;y chỉ mất v&agrave;i ph&uacute;t để di chuyển đến c&aacute;c tuyến đường huyết mạch v&agrave; khu vực trọng yếu của thủ đ&ocirc;.</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Phủ T&acirc;y Hồ</li>\r\n	<li style=\"text-align:justify\">C&ocirc;ng vi&ecirc;n nước Hồ T&acirc;y</li>\r\n	<li style=\"text-align:justify\">Cầu Nhật T&acirc;n</li>\r\n	<li style=\"text-align:justify\">Lotte Center Hanoi</li>\r\n	<li style=\"text-align:justify\">Ch&ugrave;a Trấn Quốc</li>\r\n	<li style=\"text-align:justify\">Quảng trường Ba Đ&igrave;nh</li>\r\n</ul>', '<p style=\"text-align:justify\">Hồ T&acirc;y từ bao đời nay vẫn mang n&eacute;t đẹp tinh tế, sự long lanh của s&oacute;ng nước, c&aacute;i l&atilde;ng đ&atilde;ng của sương mờ. Lấy cảm hứng từ những con s&oacute;ng m&ecirc;nh mang tr&ecirc;n mặt Hồ T&acirc;y, chung cư Five Star West Lake mang những đường cong mượt m&agrave;, tu&ocirc;n chảy như đang h&ograve;a quyện, đắm ch&igrave;m v&agrave;o phong cảnh thi&ecirc;n nhi&ecirc;n hữu t&igrave;nh. C&ocirc;ng tr&igrave;nh được thiết kế bởi c&aacute;c kiến tr&uacute;c sư của Humphrey &amp; Partner, đ&acirc;y l&agrave; c&ocirc;ng ty thiết kế kiến tr&uacute;c lớn tại Mỹ, đ&atilde; c&oacute; 25 năm hoạt động trong lĩnh vực cung cấp dịch vụ thiết kế v&agrave; quy hoạch tại hơn 40 quốc gia tr&ecirc;n thế giới, trong đ&oacute; c&oacute; Việt Nam.</p>\r\n\r\n<p style=\"text-align:justify\">Điểm nhấn của dự &aacute;n nằm ở sự kết hợp h&agrave;i h&ograve;a giữa thi&ecirc;n nhi&ecirc;n v&agrave; kiến tr&uacute;c ch&acirc;u &Acirc;u hiện đại, giữa văn h&oacute;a &Aacute; Đ&ocirc;ng v&agrave; đường n&eacute;t kiến tr&uacute;c phương T&acirc;y. B&ecirc;n cạnh kh&ocirc;ng gian văn h&oacute;a bản địa, thiết kế c&ograve;n hấp thụ địa h&igrave;nh khi h&ograve;a hợp tối đa với cảnh quan xung quanh giữa kh&ocirc;ng gian đ&ocirc; thị hiện đại.</p>\r\n\r\n<p style=\"text-align:justify\">Sự kh&eacute;o l&eacute;o, tinh tế của c&aacute;c kiến tr&uacute;c sư t&agrave;i hoa đ&atilde; khiến cho Five Star West Lake kh&ocirc;ng chỉ c&oacute; kiến tr&uacute;c ấn tượng, t&ocirc;n vinh đẳng cấp v&agrave; vị thế của chủ nh&acirc;n m&agrave; c&ograve;n mang dấu ấn văn h&oacute;a đặc trưng của mảnh đất H&agrave; Nội ngh&igrave;n năm văn hiến. Một dự &aacute;n cao tầng đẳng cấp, hiện đại nhưng kh&ocirc;ng hề th&ocirc; cứng m&agrave; mềm mại, quyến rũ b&ecirc;n Hồ T&acirc;y tuyệt sắc.</p>', '<p style=\"text-align:justify\">Dự &aacute;n Five Star West Lake bao gồm c&aacute;c thiết kế:</p>\r\n\r\n<p style=\"text-align:justify\">Căn hộ dịch vụ - Tầng 2,3,4: Diện t&iacute;ch nhỏ gọn 35-50m2 nhưng c&oacute; sự b&agrave;i tr&iacute; khoa học tinh tế n&ecirc;n căn hộ trở n&ecirc;n rộng r&atilde;i, tho&aacute;ng đ&atilde;ng.</p>\r\n\r\n<p style=\"text-align:justify\">Căn hộ VIP d&agrave;nh cho c&aacute;c chủ nh&acirc;n xứng tầm - Tầng 5,6,7,8: Diện t&iacute;ch lớn, thiết kế sang trọng, đẳng cấp, t&ocirc;n vinh chất sống thượng lưu của chủ nh&acirc;n căn hộ.</p>\r\n\r\n<p style=\"text-align:justify\">Duplex - Tầng 9,10,11,12: Độc đ&aacute;o với kiến tr&uacute;c th&ocirc;ng tầng, căn hộ Duplex mang đến cho gia chủ cảm gi&aacute;c thoải m&aacute;i vượt trội so với căn hộ th&ocirc;ng thường. Gia chủ c&oacute; thể thoải m&aacute;i l&ecirc;n &yacute; tưởng, thiết kế nội thất, quầy bar&hellip; m&agrave; kh&ocirc;ng bị giới hạn về diện t&iacute;ch. Đặc biệt, sở hữu căn hộ Duplex l&agrave; chủ nh&acirc;n đang khẳng định đẳng cấp của m&igrave;nh trong cộng đồng tinh hoa, thượng lưu.</p>\r\n\r\n<p style=\"text-align:justify\">Mỗi căn hộ trong dự &aacute;n đều được chăm ch&uacute;t kỹ lưỡng trong từng chi tiết thiết kế, c&aacute;ch b&agrave;i tr&iacute; khoa học với tầm nh&igrave;n rộng tho&aacute;ng nhờ những khung cửa k&iacute;nh lớn, tận dụng tối đa c&aacute;c khoảng xanh, &aacute;nh s&aacute;ng tự nhi&ecirc;n, nắng, gi&oacute; v&agrave; kh&ocirc;ng kh&iacute; m&aacute;t l&agrave;nh của Hồ T&acirc;y. Vật liệu nội, ngoại thất sử dụng đ&aacute; tự nhi&ecirc;n, chất liệu gỗ cao cấp nhập khẩu gi&uacute;p căn hộ th&ecirc;m sang trọng, tinh tế.</p>', '<p style=\"text-align:justify\">Tiện &iacute;ch 5 sao phục vụ mọi nhu cầu vui chơi, giải tr&iacute;, chăm s&oacute;c sức khỏe cho cư d&acirc;n:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Sảnh lounge sang trọng</li>\r\n	<li style=\"text-align:justify\">Nh&agrave; h&agrave;ng v&agrave; qu&aacute;n c&agrave; ph&ecirc;</li>\r\n	<li style=\"text-align:justify\">Sky bar tầm view tho&aacute;ng &ocirc;m trọn Hồ T&acirc;y</li>\r\n	<li style=\"text-align:justify\">Si&ecirc;u thị mini</li>\r\n	<li style=\"text-align:justify\">Bể bơi bốn m&ugrave;a độc đ&aacute;o</li>\r\n	<li style=\"text-align:justify\">Gym, yoga spa</li>\r\n	<li style=\"text-align:justify\">Ph&ograve;ng sinh hoạt cộng đồng</li>\r\n	<li style=\"text-align:justify\">Đường dạo bộ</li>\r\n	<li style=\"text-align:justify\">C&acirc;u lạc bộ tri thức trẻ (Thư viện)</li>\r\n	<li style=\"text-align:justify\">Khu vui chơi trẻ em</li>\r\n	<li style=\"text-align:justify\">Vườn treo</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">Hệ thống tiện &iacute;ch mang đẳng cấp Five Star tinh tế v&agrave; kh&aacute;c biệt - l&agrave; đặc quyền của cư d&acirc;n</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Hệ thống quản l&yacute; t&ograve;a nh&agrave; cao cấp</li>\r\n	<li style=\"text-align:justify\">Hệ thống PCCC an to&agrave;n v&agrave; hiện đại</li>\r\n	<li style=\"text-align:justify\">Hệ thống an ninh 3 lớp bảo vệ</li>\r\n	<li style=\"text-align:justify\">Hệ thống điện dự ph&ograve;ng cấp 100%</li>\r\n	<li style=\"text-align:justify\">Hệ thống quản l&yacute; hầm gửi xe th&ocirc;ng minh</li>\r\n	<li style=\"text-align:justify\">Business Center</li>\r\n	<li style=\"text-align:justify\">Dịch vụ ẩm thực</li>\r\n	<li style=\"text-align:justify\">Dịch vụ cung cấp thực phẩm hữu cơ GFS</li>\r\n	<li style=\"text-align:justify\">Dịch vụ vệ sinh</li>\r\n	<li style=\"text-align:justify\">Dịch vụ trồng v&agrave; chăm s&oacute;c c&acirc;y xanh</li>\r\n	<li style=\"text-align:justify\">Dịch vụ giặt, l&agrave;</li>\r\n	<li style=\"text-align:justify\">Dịch vụ truyền h&igrave;nh c&aacute;p, internet</li>\r\n	<li style=\"text-align:justify\">Dịch vụ quản l&yacute; khu căn hộ</li>\r\n	<li style=\"text-align:justify\">Dịch vụ thi c&ocirc;ng, sửa chữa căn hộ</li>\r\n	<li style=\"text-align:justify\">Dịch vụ sửa chữa, bảo tr&igrave; kỹ thuật</li>\r\n	<li style=\"text-align:justify\">Dịch vụ tr&ocirc;ng giữ xe</li>\r\n	<li style=\"text-align:justify\">Dịch vụ đặt v&eacute; m&aacute;y bay</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">Kh&ocirc;ng gian sống &ldquo;biệt lập&rdquo; ho&agrave;n hảo - N&eacute;t đặc trưng kh&ocirc;ng thể nhầm lẫn, nơi thiết lập chuẩn mực sống văn minh, xứng tầm với gia chủ.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Bỏ lại sau lưng những ồn &agrave;o, căng thẳng của nhịp sống vội v&atilde;, bước qua ngưỡng cửa Five Star West Lake, qu&yacute; gia chủ c&oacute; thể cảm nhận được r&otilde; n&eacute;t sự thư th&aacute;i, an nhi&ecirc;n trong từng hơi thở.</p>', '<p style=\"text-align:justify\">Với t&igrave;nh h&igrave;nh quỹ đất khu vực Hồ T&acirc;y ng&agrave;y c&agrave;ng khan hiếm, những dự &aacute;n thực sự đẳng cấp, kh&aacute;c biệt chưa c&oacute; nhiều th&igrave; Five Star West Lake như một l&agrave;n&nbsp;gi&oacute; mới thổi v&agrave;o thị trường bất động sản Hồ T&acirc;y. Vị tr&iacute; độc nhất, c&aacute;c căn hộ độc - lạ - hiếm số lượng hạn chế t&ocirc;n vinh chuẩn mực sống mới, Five Star West Lake như một &quot;phi&ecirc;n bản giới hạn của h&agrave;ng hiệu&quot; c&oacute; tiềm năng đầu tư lớn, sinh lời bền vững m&agrave; nh&agrave; đầu tư kh&ocirc;ng n&ecirc;n bỏ lỡ.</p>\r\n\r\n<p style=\"text-align:justify\">B&ecirc;n cạnh đ&oacute;, chủ đầu tư GFS cũng l&agrave; một trong những tập đo&agrave;n h&agrave;ng đầu Việt Nam trong x&acirc;y dựng, đầu tư bất động sản, c&oacute; uy t&iacute;n l&acirc;u năm, bền vững. Vậy n&ecirc;n nh&agrave; đầu tư ho&agrave;n to&agrave;n c&oacute; thể y&ecirc;n t&acirc;m khi đầu tư v&agrave;o dự &aacute;n. Chủ đầu tư đảm bảo sẽ mang lại những lợi &iacute;ch tốt nhất cho kh&aacute;ch h&agrave;ng.</p>', NULL, NULL, '<p style=\"text-align:justify\">Hỗ trợ vay vốn ng&acirc;n h&agrave;ng với l&atilde;i suất 0% trong 18 th&aacute;ng với tỷ lệ hỗ trợ l&ecirc;n tới 65% gi&aacute; trị căn hộ</p>\r\n\r\n<p style=\"text-align:justify\">Ch&iacute;nh s&aacute;ch thanh to&aacute;n sớm: Kh&aacute;ch h&agrave;ng thanh to&aacute;n sớm 70% được hưởng chiết khấu tới 05%</p>\r\n\r\n<p style=\"text-align:justify\">Ch&iacute;nh s&aacute;ch thanh to&aacute;n sớm: Kh&aacute;ch h&agrave;ng thanh to&aacute;n sớm 95% được hưởng chiết khấu tới 07%</p>\r\n\r\n<p style=\"text-align:justify\">Miễn ph&iacute; dịch vụ quản l&yacute; trong 05 năm đầu: Kh&aacute;ch h&agrave;ng được hưởng những tiện &iacute;ch cao cấp của t&ograve;a nh&agrave; bao gồm Gym &amp; bể bơi ti&ecirc;u chuẩn hạng A+</p>\r\n\r\n<p style=\"text-align:justify\">Tặng g&oacute;i thiết kế hạng sang: Kh&aacute;ch h&agrave;ng được lựa chọn một trong c&aacute;c g&oacute;i thiết kế nội thất do chủ đầu tư thiết kế sẵn cho c&aacute;c loại h&igrave;nh căn hộ.</p>', 'Tổng quan dự án Five Star West Lake', 'Vị trí hội tụ vượng khí đất trời của Five Star West Lake', 'Kiến trúc tinh tế của Five Star West Lake', 'Thiết kế căn hộ giao hòa với thiên nhiên', 'Tiện ích đặc quyền của Five Star West Lake', 'Giá trị sinh lời đầu tư của Five Star West Lake', NULL, NULL, 'Chính sách bán hàng “siêu hot”', 'Five Star West Lake - Siêu Dự Án Có Vị Trí Đẹp Nhất Hồ Tây', '10/24/2019', NULL, 'five-star-west-lake', 'Dự án tọa lạc tại vị trí đắc địa đắt giá, có hệ thống tiện ích dịch vụ “đặc quyền”, cộng đồng dân cư văn minh, tri thức và cảnh sắc Hồ Tây tuyệt đẹp', NULL, 'index, follow'),
(121, '69', 47, 'Admin', 'Para Grus Cam Ranh', 'Liền kề sân bay quốc tế Cam Ranh', 975, 'nha-pho-para-grus.jpg', NULL, 'false', NULL, 'true', '<ul>\r\n	<li>T&ecirc;n dự &aacute;n: Para Grus Cam Ranh</li>\r\n	<li>Chủ đầu tư: C&ocirc;ng ty Cổ phần đầu tư v&agrave; Kinh doanh Golf Long Th&agrave;nh</li>\r\n	<li>Vị tr&iacute; dự &aacute;n: B&atilde;i D&agrave;i, TP Cam Ranh, Tỉnh Kh&aacute;nh H&ograve;a</li>\r\n	<li>Quy m&ocirc; dự &aacute;n: 39ha</li>\r\n	<li>Mẫu x&acirc;y dựng: 1 trệt + 2 lầu</li>\r\n	<li>Chiều ngang: 6 - 8 -9.4m</li>\r\n	<li>Chiều d&agrave;i: 20m</li>\r\n	<li>Lộ giới đường trước nh&agrave;: 16, 20, 28, 20m+</li>\r\n</ul>', 'Tổng quan', 'Vị trí', 'Thiết kế', 'Tiện ích', 'Đầu tư', NULL, NULL, NULL, 'Chính sách', '<p style=\"text-align:justify\"><a href=\"https://maivietland.vn/du-an-bds/para-grus-cam-ranh.html\"><strong>Para Grus</strong></a>&nbsp;Cam Ranh l&agrave; dự &aacute;n đất nền, nh&agrave; phố nằm trong&nbsp;Khu đ&ocirc; thị nghỉ dưỡng KN Paradise thu h&uacute;t kh&ocirc;ng &iacute;t sự quan t&acirc;m từ giới đầu tư trong v&agrave; ngo&agrave;i nước với quy m&ocirc; l&ecirc;n đến 800ha dọc theo bờ biển B&atilde;i D&agrave;i của Th&agrave;nh phố Cam Ranh, Kh&aacute;nh H&ograve;a. Tổ hợp nh&agrave; phố biển&nbsp;được chủ đầu tư x&acirc;y dựng v&agrave; quy hoạch rất đồng bộ, quy m&ocirc; v&agrave; đẳng cấp, c&ugrave;ng với tiện &iacute;ch 5 sao vượt trội như c&ocirc;ng vi&ecirc;n c&acirc;y xanh, trường học, tiện &iacute;ch c&ocirc;ng cộng&hellip; Dự &aacute;n hứa hẹn sẽ l&agrave; &ldquo;thi&ecirc;n đường nghỉ dưỡng&rdquo;, khu kinh doanh sầm uất, nhộn nhịp, mang đến cơ hội đầu tư, trải nghiệm nghỉ dưỡng độc đ&aacute;o cho kh&aacute;ch h&agrave;ng.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"TỔNG QUAN DỰ ÁN PARA GRUS\" src=\"/public/upload/images/para-grus/khu-villa.jpg\" /></p>', '<p style=\"text-align:justify\">Nh&agrave; phố biển<strong>&nbsp;<a href=\"https://maivietland.vn/du-an-bds/para-grus-cam-ranh.html\">Para Grus</a></strong> sở hữu vị tr&iacute; kh&ocirc;ng thể đắc địa hơn khi nằm ở mặt biển B&agrave;i D&agrave;i, c&oacute;&nbsp;5km đường biển c&aacute;t trắng, mịn.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Vị tr&iacute; n&agrave;y kết nối trực tiếp đến c&aacute;c điểm du lịch v&ocirc; c&ugrave;ng hấp dẫn m&agrave; kh&aacute;ch h&agrave;ng kh&ocirc;ng thể bỏ qua khi đến với Kh&aacute;nh H&ograve;a như c&aacute;c đảo: B&igrave;nh Ba, B&igrave;nh Hưng, B&igrave;nh Lập hay th&agrave;nh phố Nha Trang. Điều n&agrave;y c&oacute; nghĩa dự &aacute;n sẽ thừa hưởng lượng kh&aacute;ch du lịch lớn trong nước v&agrave; ngo&agrave;i nước, tăng tiềm năng ph&aacute;t triển kinh doanh.</p>\r\n\r\n<p style=\"text-align:justify\">Từ dự &aacute;n, dễ d&agrave;ng kết nối đến tuyến đường, s&acirc;n bay, cảng biển trung t&acirc;m như:&nbsp;</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">5 ph&uacute;t đến S&acirc;n bay quốc tế Cam Ranh&nbsp;</li>\r\n	<li style=\"text-align:justify\">10 ph&uacute;t đến Chợ Mỹ Ca</li>\r\n	<li style=\"text-align:justify\">10 ph&uacute;t đến Th&agrave;nh phố Cam Ranh</li>\r\n	<li style=\"text-align:justify\">10 ph&uacute;t đến Quốc lộ 1 A</li>\r\n	<li style=\"text-align:justify\">10 ph&uacute;t đến Cảng biển Quốc tế Cam Ranh</li>\r\n	<li style=\"text-align:justify\">30 ph&uacute;t đến Ga Nha Trang</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">B&ecirc;n cạnh đ&oacute;, dự &aacute;n c&ograve;n tọa lạc ngay cạnh KN Golf Links - Một trong những s&acirc;n Golf Đẹp Nhất Ch&acirc;u &Aacute;. Para Grus Cam Ranh được kỳ vọng sẽ trở th&agrave;nh một &ldquo;th&agrave;nh phố thu nhỏ&rdquo; về du lịch, nghỉ dưỡng l&agrave;m thay đổi diện mạo Kh&aacute;nh H&ograve;a.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"VỊ TRÍ DỰ ÁN\" src=\"/public/upload/images/para-grus/vitri.png\" /></p>', '<p style=\"text-align:justify\">Tổng quan dự &aacute;n&nbsp;được quy hoạch, thiết kế dựa tr&ecirc;n &yacute; tưởng kết hợp văn h&oacute;a Đ&ocirc;ng - T&acirc;y, h&ograve;a quyện c&aacute;c yếu tố truyền thống gắn chặt với phong thủy, ngũ h&agrave;nh của phương Đ&ocirc;ng c&ugrave;ng phong c&aacute;ch hiện đại v&agrave; sang trọng của phương T&acirc;y, để tạo n&ecirc;n một th&agrave;nh phố thu nhỏ vừa đẳng cấp, lịch l&atilde;m nhưng kh&ocirc;ng thiếu n&eacute;t tinh tế, đậm chất &Aacute; Đ&ocirc;ng.</p>\r\n\r\n<p style=\"text-align:justify\">Nh&agrave; phố KN Paradise&nbsp;quy hoạch tr&ecirc;n địa h&igrave;nh đồi cao, với độ dốc thoai thoải v&agrave; c&aacute;c khu phố theo kiến tr&uacute;c kh&ocirc;ng gian mở, xen kẽ giữa hệ thống c&ocirc;ng vi&ecirc;n c&acirc;y xanh. B&ecirc;n cạnh đ&oacute; c&ograve;n c&oacute; loạt c&ocirc;ng tr&igrave;nh tiện &iacute;ch c&ocirc;ng cộng cho ri&ecirc;ng từng ph&acirc;n khu, lộ giới quy hoạch rộng, tho&aacute;ng, đồng bộ, t&iacute;ch hợp đầy đủ gi&aacute; trị của một đ&ocirc; thị văn minh, hiện đại.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"THIẾT KẾ DỰ ÁN\" src=\"/public/upload/images/para-grus/cum-khach-san-4-sao.jpg\" /></p>', '<p style=\"text-align:justify\">Với &yacute; tưởng ph&aacute;t triển th&agrave;nh một quần thể du lịch nghỉ dưỡng cao cấp, đa năng, dự &aacute;n <a href=\"https://maivietland.vn/du-an-bds/para-grus-cam-ranh.html\"><strong>Para Grus</strong></a> được chủ đầu tư x&acirc;y dựng tổ hợp tiện &iacute;ch vượt trội ti&ecirc;u chuẩn quốc tế 5 sao, k&egrave;m theo l&agrave; nguy&ecirc;n một hệ thống dịch vụ ho&agrave;n hảo cho qu&yacute; kh&aacute;ch h&agrave;ng:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">B&atilde;i biển d&agrave;i 5 km</li>\r\n	<li style=\"text-align:justify\">S&acirc;n Golf 27 lỗ đạt chuẩn Quốc Tế</li>\r\n	<li style=\"text-align:justify\">Vườn thượng uyển</li>\r\n	<li style=\"text-align:justify\">Kh&aacute;ch sạn 5 sao</li>\r\n	<li style=\"text-align:justify\">Trung t&acirc;m thể dục thể thao</li>\r\n	<li style=\"text-align:justify\">Trường học</li>\r\n	<li style=\"text-align:justify\">C&acirc;u lạc bộ vui chơi giải tr&iacute; biển</li>\r\n	<li style=\"text-align:justify\">Khu Spa Resort</li>\r\n	<li style=\"text-align:justify\">C&ocirc;ng vi&ecirc;n nước</li>\r\n	<li style=\"text-align:justify\">Quảng trường trung t&acirc;m rộng 5ha</li>\r\n	<li style=\"text-align:justify\">Hồ bơi nước mặn</li>\r\n	<li style=\"text-align:justify\">Hồ cảnh quan</li>\r\n</ul>\r\n\r\n<p style=\"text-align:center\"><img alt=\"tiện ích dự án para grus\" src=\"/public/upload/images/para-grus/khu-pho-thuong-mai.png\" /></p>', '<p style=\"text-align:justify\"><em><strong>Mua sau để ở</strong></em></p>\r\n\r\n<p style=\"text-align:justify\">Sự lựa chọn nh&agrave; phố Para Grus để về sau&nbsp;ở l&agrave; v&ocirc; c&ugrave;ng hợp l&yacute;. Với đặc điểm kh&iacute; hậu tốt, b&atilde;i biển đẹp thơ mộng, v&agrave; quy hoạch khu đ&ocirc; thị đồng bộ đi k&egrave;m c&aacute;c tiện &iacute;ch nghỉ dưỡng, vui chơi, giải tr&iacute; chắc chắn đ&acirc;y&nbsp;sẽ l&agrave; nơi an cư l&yacute; tưởng cho c&aacute;c cư d&acirc;n nơi đ&acirc;y.</p>\r\n\r\n<p style=\"text-align:justify\"><em><strong>Đầu tư l&acirc;u d&agrave;i sinh lời</strong></em></p>\r\n\r\n<p style=\"text-align:justify\">Nh&agrave; phố biển, đất nền&nbsp;Para Grus ch&iacute;nh l&agrave; sản phẩm đầu tư &ldquo;đ&aacute;ng đồng tiền&rdquo; nhất m&agrave; c&aacute;c nh&agrave; đầu tư th&ocirc;ng th&aacute;i n&ecirc;n lựa chọn. Với lợi thế nằm trong Khu đ&ocirc; thị sinh th&aacute;i KN Paradise&nbsp;đẳng cấp c&oacute; lượng kh&aacute;ch du lịch lớn, cộng với vị tr&iacute;, giao th&ocirc;ng thuận lợi, cảnh sắc biển tuyệt đẹp, dự &aacute;n shophouse hứa hẹn mang đến một nguồn lợi nhuận khủng, bền vững mỗi năm khi kh&aacute;ch h&agrave;ng lựa chọn đầu tư v&agrave;o đ&acirc;y.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"CƠ HỘI ĐẦU TƯ DỰ ÁN\" src=\"/public/upload/images/para-grus/san-khau-nhac-nuoc.png\" /></p>', NULL, NULL, NULL, '<p>UPDATING!!!</p>', 'TỔNG QUAN DỰ ÁN PARA GRUS', 'VỊ TRÍ DỰ ÁN', 'THIẾT KẾ DỰ ÁN', 'TIỆN ÍCH CỦA DỰ ÁN PARA GRUS', 'CƠ HỘI ĐẦU TƯ DỰ ÁN', NULL, NULL, NULL, 'CHÍNH SÁCH BÁN HÀNG', 'Para Grus Cam Ranh - Nhà Phố Biển KN Paradise', '11/22/2019', NULL, 'para-grus-cam-ranh', 'Nhà phố biển Para Grus thuộc dự án Khu đô thị nghỉ dưỡng KN Paradise. Vị trí dự án kết nối trực tiếp đến các điểm du lịch vô cùng hấp dẫn tại Khánh Hòa', NULL, 'index, follow');
INSERT INTO `tbl_product` (`id`, `cat_id`, `dis_id`, `user`, `name`, `address`, `hits`, `img`, `price`, `hot`, `tag`, `status`, `detail`, `tab1`, `tab2`, `tab3`, `tab4`, `tab5`, `tab6`, `tab7`, `tab8`, `tab9`, `content1`, `content2`, `content3`, `content4`, `content5`, `content6`, `content7`, `content8`, `content9`, `heading1`, `heading2`, `heading3`, `heading4`, `heading5`, `heading6`, `heading7`, `heading8`, `heading9`, `title`, `date`, `date_up`, `slug`, `description`, `keywords`, `robot`) VALUES
(122, '69', 48, 'Admin', 'One World Đà Nẵng', 'Khu đô thị mới Điện Nam, P. Điện Ngọc', 789, 'one-world-view.jpg', NULL, 'false', NULL, 'true', '<ul>\r\n	<li>T&ecirc;n thương mại: One World Regency</li>\r\n	<li>Chủ đầu tư: C&ocirc;ng ty cổ phần Đất xanh Miền Trung</li>\r\n	<li>Vị tr&iacute;: Khu đ&ocirc; thị mới Điện Nam, P. Điện Ngọc, TX. Điện B&agrave;n, T. Quảng Nam</li>\r\n	<li>Tổng diện t&iacute;ch: 20ha</li>\r\n	<li>D&ograve;ng sản phẩm: Biệt thự, Đất nền ph&acirc;n l&ocirc;</li>\r\n	<li>Ph&aacute;p l&yacute;: Sổ đỏ sở hữu vĩnh viễn</li>\r\n	<li>Quy m&ocirc;:\r\n	<ul>\r\n		<li>98 l&ocirc; biệt thự, diện t&iacute;ch 232m2, 262m2 v&agrave; 272m2</li>\r\n		<li>158 l&ocirc; liền kề, diện t&iacute;ch 79m2, 89m2, 99m2 v&agrave; 109m2</li>\r\n		<li>176 l&ocirc; shophouse, diện t&iacute;ch 100 - 300m2</li>\r\n	</ul>\r\n	</li>\r\n</ul>', 'Tổng quan', 'Vị trí', 'Thiết kế', 'Tiện ích', 'Thanh toán', NULL, NULL, NULL, 'Chính sách', '<p>Miền Trung - mảnh đất m&agrave;u mỡ với nhiều danh lam thắng cảnh, những điểm du lịch nổi tiếng đang c&oacute; tiềm năng lớn để ph&aacute;t triển bất động sản. Nhiều chủ đầu tư lớn đ&atilde; lựa chọn nơi đ&acirc;y để &ldquo;gửi gắm&rdquo; nhiều dự &aacute;n bất động sản đẳng cấp, c&oacute; gi&aacute; trị đầu tư lớn. One World Regency ch&iacute;nh l&agrave; một trong số đ&oacute;.</p>\r\n\r\n<p>Dự &aacute;n c&oacute; những ưu thế vượt bậc về vị tr&iacute; v&agrave; khả năng sinh lời vượt trội, thu h&uacute;t lượng lớn giới đầu tư quan t&acirc;m săn đ&oacute;n. One World Đ&agrave; Nẵng được x&acirc;y dựng theo m&ocirc; h&igrave;nh khu đ&ocirc; thị ven biển - ven s&ocirc;ng Cổ C&ograve; hiện đại với lối kiến tr&uacute;c tinh tế, thời thượng song vẫn lưu giữ n&eacute;t đẹp trong l&agrave;nh của thi&ecirc;n nhi&ecirc;n. Dự &aacute;n hướng đến việc tạo lập một kh&ocirc;ng gian đẳng cấp, một cuộc sống hiện đại nhưng vẫn đảm bảo sự ri&ecirc;ng tư v&agrave; dấu ấn r&otilde; n&eacute;t của chủ nh&acirc;n, đ&aacute;p ứng gi&aacute; trị sống lẫn đầu tư ti&ecirc;u chuẩn thời đại mới.</p>', '<p>Đắc địa, &ldquo;kim cương&rdquo;, ho&agrave;n hảo l&agrave; những từ để mi&ecirc;u tả vị tr&iacute; của One Word. Dự &aacute;n nằm kế b&ecirc;n tuyến đường du lịch 5 sao Trường Sa - Ho&agrave;ng Sa, ph&iacute;a Nam chạy thẳng phố cổ Hội An, ph&iacute;a Bắc nối liền với biển Non Nước, Sơn Thủy, Mỹ Kh&ecirc;. Đ&acirc;y l&agrave; khu vực &ldquo;v&agrave;ng&rdquo; được định hướng trở th&agrave;nh trục đường ph&aacute;t triển chiến lược của Th&agrave;nh phố Đ&agrave; Nẵng v&agrave; Hội An.&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Ph&iacute;a Bắc: gi&aacute;p s&ocirc;ng Cổ C&ograve;, KĐT FPT v&agrave; Trung t&acirc;m th&agrave;nh phố Đ&agrave; Nẵng</li>\r\n	<li>Ph&iacute;a Đ&ocirc;ng: gi&aacute;p biển, tổ hợp giải tr&iacute; Cocobay v&agrave; 2 s&acirc;n Golf lớn nhất Đ&agrave; Nẵng</li>\r\n	<li>Ph&iacute;a T&acirc;y: gi&aacute;p khu d&acirc;n cư số 9, khu phố chợ Điện Ngọc</li>\r\n	<li>Ph&iacute;a Nam: gi&aacute;p tuyến đường trục ch&iacute;nh đường Dũng Sĩ Điện Ngọc, khu đ&ocirc; thị mới 7B Điện Nam.</li>\r\n</ul>\r\n\r\n<p>Dự &aacute;n c&oacute; li&ecirc;n kết v&ugrave;ng thuận lợi, tiếp cận với nhiều tiện &iacute;ch ngoại khu đa dạng:</p>\r\n\r\n<ul>\r\n	<li>B&aacute;n k&iacute;nh 300m: s&ocirc;ng Cổ C&ograve;, biển T&acirc;n Tr&agrave;, đường Trường Sa chạy dọc về biển Cửa Đại Hội An, S&acirc;n Golf Montgomerie Links&hellip;</li>\r\n	<li>B&aacute;n k&iacute;nh 1,5km: biển Non Nước, s&acirc;n Golf The Point Da Nang Golf Club Sandy Beach, Resort - kh&aacute;ch sạn 5 sao, FPT city....</li>\r\n	<li>B&aacute;n k&iacute;nh 2 - 3km: biển Sơn Thủy, Cocobay, Ocean Villa, Danh thắng Ngũ H&agrave;nh Sơn, L&agrave;ng Đại học, Trung t&acirc;m y tế quận Ngũ H&agrave;nh Sơn&hellip;</li>\r\n	<li>B&aacute;n k&iacute;nh 5 - 8km: biển Mỹ Kh&ecirc;, ch&ugrave;a Linh Ứng, C&ocirc;ng vi&ecirc;n Ch&acirc;u &Aacute;, S&acirc;n vận động Ti&ecirc;n Sơn, Helio Center&hellip;</li>\r\n	<li>B&aacute;n k&iacute;nh 10 - 15km: s&acirc;n bay, trung t&acirc;m th&agrave;nh phố, Bệnh viện Phụ sản Nhi, Phố cổ Hội An, Vinpearl Land Nam Hội An, C&ocirc;ng vi&ecirc;n văn h&oacute;a Hội An...</li>\r\n</ul>\r\n\r\n<p>Tọa lạc tại vị tr&iacute; gi&agrave;u tiềm năng, kết nối với nhiều tuyến đường huyết mạch, địa điểm du lịch nổi tiếng, dự &aacute;n mang đến cho nh&agrave; đầu tư cơ hội đầu tư an to&agrave;n, sinh lời bền vững kh&ocirc;ng n&ecirc;n bỏ lỡ.</p>', '<p>Dự &aacute;n bao gồm 2 sản phẩm ch&iacute;nh l&agrave; đất nền v&agrave; biệt thự x&acirc;y th&ocirc; hoặc ho&agrave;n thiện nội thất liền tường. Được x&acirc;y dựng tr&ecirc;n &yacute; tưởng kết hợp tinh hoa của 5 nền văn h&oacute;a: Anh, Ph&aacute;p, Italia, Mỹ v&agrave; Việt Nam, One World Regency l&agrave; một quần thể đ&ocirc; thị độc bản, kiến tạo kh&ocirc;ng gian sống sang trọng, đẳng cấp, tiện nghi cho những cư d&acirc;n lựa chọn sống tại đ&acirc;y.</p>\r\n\r\n<p>Đất nền nh&agrave; phố: sẽ c&oacute; 2 định hướng về kiến tr&uacute;c cho mẫu nh&agrave; phố, một l&agrave; kiến tr&uacute;c t&acirc;n cổ điển, hai l&agrave; kiến tr&uacute;c Hội An (thuần Việt).</p>\r\n\r\n<p>Biệt thự: 3 ph&acirc;n khu villas của dự &aacute;n được thiết kế độc đ&aacute;o: Villas Anh, Villas Italia, Villas Mỹ. Mỗi d&ograve;ng villas mang những dấu ấn kh&aacute;c biệt, g&oacute;i trọn tinh hoa từng nền văn h&oacute;a c&aacute;c nước tương ứng tạo n&ecirc;n một tổng thể h&agrave;i h&ograve;a v&agrave; bắt mắt.</p>\r\n\r\n<ul>\r\n	<li>Ph&acirc;n khu Villas Anh: được thiết kế ấn tượng dựa tr&ecirc;n &yacute; tưởng về sự kết hợp n&eacute;t đặc trưng của c&aacute;c c&ocirc;ng tr&igrave;nh cổ điển thời nữ ho&agrave;ng Victoria c&ugrave;ng với sự độc đ&aacute;o trong phong c&aacute;ch tối giản, mang đậm &ldquo;chủ nghĩa th&ocirc; mộc&rdquo; của Mỹ. Đ&acirc;y l&agrave; hai trường ph&aacute;i kiến tr&uacute;c được đ&aacute;nh gi&aacute; cao về t&iacute;nh thẩm mỹ cũng như độ ổn định trường tồn l&acirc;u d&agrave;i.</li>\r\n	<li>Ph&acirc;n khu Villas Italia nổi bật với phong c&aacute;ch thiết kế Địa Trung Hải mang hơi thở của thi&ecirc;n nhi&ecirc;n nhưng được chấm ph&aacute; th&ecirc;m n&eacute;t quyền qu&yacute;, cao sang. Villas Italia mang vẻ đẹp rực rỡ, tr&agrave;n đầy sức sống, tạo x&uacute;c cảm mạnh mẽ cho người chi&ecirc;m ngưỡng.</li>\r\n	<li>Biệt thự Mỹ g&acirc;y ấn tượng bởi sự ph&oacute;ng kho&aacute;ng về kiến tr&uacute;c trong từng đường n&eacute;t. Với mặt trước l&agrave; một d&atilde;y phố sầm uất hiện đại, mặt sau đen xen những chi tiết xanh ở s&acirc;n vườn, lối đi ph&ograve;ng kh&aacute;ch, b&agrave;n l&agrave;m việc, Villas Mỹ cho gia chủ cảm nhận như đang nghỉ dưỡng tại Resort 5 sao.</li>\r\n</ul>', '<p>Dự &aacute;n sở hữu nhiều tiện &iacute;ch nội khu đa dạng bao gồm:</p>\r\n\r\n<ul>\r\n	<li>Hệ thống c&acirc;y xanh, hệ thống mặt nước hồ nội khu, s&ocirc;ng Cổ C&ograve; v&agrave; biển tạo n&ecirc;n kh&ocirc;ng gian thơ mộng.</li>\r\n	<li>Quảng trường</li>\r\n	<li>C&ocirc;ng vi&ecirc;n trung t&acirc;m</li>\r\n	<li>C&ocirc;ng vi&ecirc;n thể dục thể thao</li>\r\n	<li>S&acirc;n Tennis</li>\r\n	<li>Khu Spa Club</li>\r\n	<li>Trung t&acirc;m thương mại One World Regency Shopping Mall hơn 4000m2, hội tụ đầy đủ c&aacute;c dịch vụ như: Trung t&acirc;m mua sắm, si&ecirc;u thị, qu&aacute;n cafe, nh&agrave; h&agrave;ng, khu vui chơi giải tr&iacute; cao cấp, rạp chiếu phim&hellip;</li>\r\n	<li>Club House phục vụ cư d&acirc;n</li>\r\n</ul>\r\n\r\n<p>Ngo&agrave;i ra tiện &iacute;ch ngoại khu liền kề bao gồm: 2 s&acirc;n Golf lớn nhất Đ&agrave; Nẵng, Cocobay, b&atilde;i tắm T&acirc;n Tr&agrave;, tổ hợp nhiều resort ven biển, trường học, bệnh viện&hellip;</p>', '<p><em>Sản phẩm đất nền</em></p>\r\n\r\n<p><strong>Thanh to&aacute;n trong v&ograve;ng 12 th&aacute;ng</strong></p>\r\n\r\n<ul>\r\n	<li>Đợt 1: Đảm bảo y&ecirc;u cầu tư vấn, tại thời điểm k&yacute; phiếu y&ecirc;u cầu tư vấn thanh to&aacute;n 50,000,000 đồng/sản phẩm</li>\r\n	<li>Đợt 2: Trong v&ograve;ng 24h kể từ khi x&aacute;c nhận tư vấn, kh&aacute;ch h&agrave;ng bổ sung đủ 300,000,000 đồng/sản phẩm (đ&atilde; bao gồm đợt 1)</li>\r\n	<li>Đợt 3: Trong v&ograve;ng 07 ng&agrave;y kể từ đợt 1, kh&aacute;ch h&agrave;ng thanh to&aacute;n 50% tổng gi&aacute; trị thỏa thuận tư vấn, tiến h&agrave;nh k&yacute; thỏa thuận tư vấn (Đ&atilde; bao gồm tiền đợt 1 v&agrave; đợt 2)</li>\r\n	<li>Đợt 4: Trong v&ograve;ng 06 th&aacute;ng kể từ đợt 1, kh&aacute;ch h&agrave;ng thanh to&aacute;n tiếp 10% tổng gi&aacute; trị thỏa thuận tư vấn</li>\r\n	<li>Đợt 6: Trong v&ograve;ng 09 th&aacute;ng kể từ đợt 1, kh&aacute;ch h&agrave;ng thanh to&aacute;n tiếp 10% tổng gi&aacute; trị thỏa thuận tư vấn</li>\r\n	<li>Đợt 13: Trong v&ograve;ng 12 th&aacute;ng kể từ đợt 1, kh&aacute;ch h&agrave;ng thanh to&aacute;n tiếp 20% c&ograve;n lại, tiến h&agrave;nh thủ tục c&ocirc;ng chứng chuyển nhượng QSDĐ</li>\r\n</ul>', NULL, NULL, NULL, NULL, 'Tổng quan dự án One World', 'Vị trí giàu tiềm năng đầu tư', 'Thiết kế hội tụ tinh hoa của 5 nền văn hóa', 'Tiện ích nội khu và ngoại khu đa dạng cao cấp', 'Phương thức thanh toán', NULL, NULL, NULL, NULL, NULL, '12/02/2019', NULL, 'one-world-da-nang', NULL, NULL, 'index, follow'),
(123, '69', 49, 'Admin', 'Aqua City HaLong', 'Bán đảo 1 Khu đô thị Marina Hạ Long', 985, 'Aqua-City-2.jpg', NULL, 'false', NULL, 'true', '<ul>\r\n	<li>Chủ đầu tư: C&ocirc;ng Ty TNHH Đầu tư Ph&aacute;t triển Sản xuất Hạ Long</li>\r\n	<li>Vị tr&iacute;: Ph&iacute;a đ&ocirc;ng b&aacute;n đảo 1 Khu đ&ocirc; thị Marina Hạ Long</li>\r\n	<li>Quy m&ocirc; dự &aacute;n: 9,15 ha</li>\r\n	<li>Mật độ x&acirc;y dựng: 27%</li>\r\n	<li>Diện t&iacute;ch d&agrave;nh cho c&acirc;y xanh, giao th&ocirc;ng: 60%</li>\r\n	<li>Hồ cảnh quan: 14ha</li>\r\n	<li>Loại h&igrave;nh sản phẩm: 307 căn shoptel cao 6 tầng, mặt tiền rộng 6.5 - 9m</li>\r\n	<li>Đơn vị thiết kế quy hoạch tổng thể: Belt Collins</li>\r\n	<li>Đơn vị thiết kế cảnh quan: Belt Collins</li>\r\n	<li>Đơn vị thiết kế kiến tr&uacute;c: Belt Collins</li>\r\n	<li style=\"text-align:justify\">H&igrave;nh thức sở hữu: Sổ đỏ l&acirc;u d&agrave;i</li>\r\n</ul>', 'Tổng quan', 'Vị trí', 'Mặt bằng phân lô', 'Tiện ích', 'Đầu tư', 'Tiến độ thanh toán', NULL, NULL, 'Chính sách', '<p style=\"text-align:justify\">Hạ Long giờ đ&acirc;y đ&atilde; trở th&agrave;nh &ldquo;điểm phải đến trong đời&rdquo; - &ldquo;weekend get away&rdquo; cho kh&aacute;ch du lịch tại ph&iacute;a Bắc khi hệ thống hạ tầng - giao th&ocirc;ng đ&atilde; ho&agrave;n thiện. B&ecirc;n cạnh đ&oacute; l&agrave; những dự &aacute;n nghỉ dưỡng 5 sao được x&acirc;y dựng g&oacute;p phần l&agrave;m thay đổi diện mạo của th&agrave;nh phố di sản.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Halong Marina - Khu đ&ocirc; thị du lịch được v&iacute; như th&agrave;nh phố nghỉ dưỡng ven biển di sản được quy hoạch đồng bộ với đầy đủ hệ sinh th&aacute;i nghỉ dưỡng - giải tr&iacute; v&agrave; an cư. Nằm trong quy hoạch của khu đ&ocirc; thị, Aqua City Hạ Long sở hữu nhiều ưu thế vượt trội m&agrave; nh&agrave; đầu tư kh&ocirc;ng n&ecirc;n bỏ lỡ.</p>\r\n\r\n<p style=\"text-align:justify\">Được định hướng ph&aacute;t triển trở th&agrave;nh phố kh&aacute;ch sạn thu nhỏ với những căn shoptel đa chức năng, <a href=\"https://maivietland.vn/du-an-bds/aqua-city-halong.html\"><strong>Aqua City Hạ Long</strong></a> đang l&agrave; dự &aacute;n bất động sản thu h&uacute;t giới đầu tư trong thời gian gần đ&acirc;y. Dự &aacute;n c&oacute; kiến tr&uacute;c thiết kế mang phong c&aacute;ch đặc trưng của v&ugrave;ng biển Địa Trung Hải, với tầm view hướng ra vịnh di sản, nơi du kh&aacute;ch tận hưởng trọn vẹn hương vị của Hạ Long.</p>\r\n\r\n<p style=\"text-align:justify\">Dự &aacute;n&nbsp;ra mắt sẽ l&agrave; một m&ocirc; h&igrave;nh kinh doanh tối ưu, lần đầu ti&ecirc;n c&oacute; mặt tại <em><strong>Hạ Long</strong></em> với nhiều điểm lợi thế độc đ&aacute;o, chắc chắn c&oacute; thể mang lại lợi nhuận bạc tỷ h&agrave;ng năm cho c&aacute;c kh&aacute;ch h&agrave;ng. Đồng thời, dự &aacute;n cũng đ&aacute;p ứng được nhu cầu nghỉ dưỡng, tận hưởng của du kh&aacute;ch với Hệ sinh th&aacute;i vui chơi - giải tr&iacute; c&ugrave;ng những tiện &iacute;ch h&agrave;ng đầu đẳng cấp.</p>', '<p style=\"text-align:justify\"><em><a href=\"https://maivietland.vn/du-an-bds/aqua-city-halong.html\"><strong>Shoptel Aqua City Hạ Long</strong></a></em> sở hữu vị tr&iacute; đắc địa, tọa lạc tại b&aacute;n đảo 1 c&oacute; quy hoạch đẹp nhất Khu đ&ocirc; thị <em><strong>Halong Marina</strong></em>, l&agrave; nơi giao h&ograve;a giữa biển, trời v&agrave; đất, hội tụ những n&eacute;t tinh t&uacute;y nhất của thi&ecirc;n nhi&ecirc;n. Với 3 tầm view hướng ra vịnh di sản v&agrave; hồ cảnh quan, dự &aacute;n &ldquo;&ocirc;m trọn&rdquo; to&agrave;n bộ khung cảnh tuyệt đẹp của Vịnh Hạ Long - di sản thi&ecirc;n nhi&ecirc;n thế giới, mang đến cho kh&aacute;ch h&agrave;ng những trải nghiệm th&uacute; vị, m&atilde;n nh&atilde;n v&agrave; bất ngờ.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"vị trí aqua city\" src=\"/public/upload/images/aqua-city-halong/vitri-qua-city-halong.png\" /></p>\r\n\r\n<p style=\"text-align:justify\">Ngo&agrave;i ra, vị tr&iacute; của dự &aacute;n c&ograve;n l&agrave; nơi c&oacute; hạ tầng giao th&ocirc;ng đồng bộ, hiện đại v&agrave; thuận tiện. Từ <em><strong>Aqua City</strong></em> dễ d&agrave;ng kết nối với c&aacute;c địa điểm h&agrave;nh ch&iacute;nh, gi&aacute;o dục, y tế, văn h&oacute;a, du lịch&hellip; h&agrave;ng đầu của th&agrave;nh phố Hạ Long.</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">3km - B&atilde;i biển nh&acirc;n tạo B&atilde;i Ch&aacute;y</li>\r\n	<li style=\"text-align:justify\">4km - Bến xe B&atilde;i Ch&aacute;y</li>\r\n	<li style=\"text-align:justify\">4km - Khu vui chơi giải tr&iacute; quốc tế Tuần Ch&acirc;u</li>\r\n	<li style=\"text-align:justify\">5km - C&ocirc;ng vi&ecirc;n Hạ Long</li>\r\n	<li style=\"text-align:justify\">100m - Tổ hợp thương mại v&agrave; giải tr&iacute; Marine Plaza</li>\r\n	<li style=\"text-align:justify\">100m - Rạp chiếu phim CGV</li>\r\n	<li style=\"text-align:justify\">200m - Khu ẩm thực</li>\r\n	<li style=\"text-align:justify\">300m - Chợ đ&ecirc;m Hạ Long</li>\r\n	<li style=\"text-align:justify\">1km - B&atilde;i biển Marina Bay</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">B&ecirc;n cạnh đ&oacute;, c&aacute;c hạ tầng cao tốc li&ecirc;n kết đến <em><strong>Hạ Long</strong></em> đ&atilde; ho&agrave;n th&agrave;nh như: cao tốc H&agrave; Nội - Hải Ph&ograve;ng - Hạ Long, Cao tốc Nội B&agrave;i - V&acirc;n Đồn. Đặc biệt, s&acirc;n bay Quốc tế V&acirc;n Đồn đ&atilde; ho&agrave;n th&agrave;nh v&agrave; đi v&agrave;o hoạt động sẽ mang đến những lượng kh&aacute;ch lớn từ c&aacute;c tỉnh Việt Nam v&agrave; Quốc tế.</p>', '<p style=\"text-align:justify\">Với tổng diện t&iacute;ch dự &aacute;n 9,15ha, dự &aacute;n&nbsp;<em><strong>Aqua City Halong</strong></em>&nbsp;với những căn Shoptel v&agrave; Villa Suite 3 trong 1 độc đ&aacute;o. Với thiết kế 6 tầng, mặt tiền rộng 6.5 - 9m, chủ sở hữu c&oacute; thể vừa kinh doanh căn hộ lưu tr&uacute;, vừa c&oacute; thể ph&aacute;t triển theo hướng kh&aacute;ch sạn nghỉ dưỡng hoặc kinh doanh cửa h&agrave;ng mang lại nguồn lợi nhuận hấp dẫn.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"mặt bằng phân lô qua hạ long\" src=\"/public/upload/images/aqua-city-halong/mb-phanlo-aqua-halong.jpg\" /></p>\r\n\r\n<p style=\"text-align:justify\">Tổng thể dự &aacute;n chia l&agrave;m 5 tiểu khu ch&iacute;nh:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><em><strong>Tiểu khu&nbsp;Aqua Zen Garden</strong></em>: Định hướng tiểu khu kinh doanh h&igrave;nh thức du lịch sức khoẻ để phục vụ th&oacute;i quen đi du lịch quanh năm nhưng duy tr&igrave; thời gian biểu sinh hoạt khoa học của d&ograve;ng kh&aacute;ch cao cấp, trung t&acirc;m spa, massage, gym, fitness. Đ&acirc;y cũng l&agrave; những h&igrave;nh thức kỳ vọng h&aacute;i ra tiền tại đ&acirc;y</li>\r\n	<li style=\"text-align:justify\"><em><strong>Tiểu khu Aqua Capella</strong></em>: hứa hẹn trở th&agrave;nh điểm đến mơ ước của c&aacute;c t&iacute;n đồ thời trang tr&ecirc;n to&agrave;n thế giới với những con phố thời trang thời thượng, nơi hội tụ những xu hướng mới nhất.\\</li>\r\n	<li style=\"text-align:justify\"><em><strong>Tiểu khu Aqua&nbsp; Havana</strong></em>: Thưởng thức ẩm thực năm ch&acirc;u tại những d&atilde;y phố ẩm thực độc đ&aacute;o, sầm uất &ndash; L&agrave; điểm h&uacute;t lượng kh&aacute;ch khổng lồ</li>\r\n	<li style=\"text-align:justify\"><em><strong>Tiểu khu Aqua Laguna</strong></em>: Tiểu khu Laguna xinh đẹp nằm kề b&ecirc;n mặt hồ nước tự nhi&ecirc;n hưởng trọn kh&ocirc;ng kh&iacute; trong l&agrave;nh, l&agrave; nơi tập trung nhiều qu&aacute;n cafe thư gi&atilde;n, những cửa h&agrave;ng, qu&aacute;n ăn &Aacute; &ndash; &Acirc;u thanh lịch, lịch l&atilde;m...</li>\r\n	<li style=\"text-align:justify\"><em><strong>Tiểu khu Aqua Monarch</strong></em>: Chủ sở hữu những căn <em><strong>Shoptel </strong></em>tại Tiểu khu Aqua Monarch c&oacute; thể hướng đến nhiều m&ocirc; h&igrave;nh kinh doanh đa dạng. Trong đ&oacute; thuận lợi vận h&agrave;nh, hiệu quả v&agrave; ph&ugrave; hợp l&agrave; c&aacute;c dịch vụ phụ trợ cho nh&oacute;m kh&aacute;ch cao cấp tại khu Resort kề b&ecirc;n hay phục vụ ch&iacute;nh những kh&aacute;ch h&agrave;ng tới nghỉ dưỡng tại Th&agrave;nh phố kh&aacute;ch sạn như c&aacute;c shop thời trang m&ugrave;a h&egrave;, đồ đi biển, nh&agrave; h&agrave;ng, si&ecirc;u thị mini.</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">Đặc biệt, dự &aacute;n c&oacute; thiết kế hội tụ tinh hoa khắp năm ch&acirc;u, những căn <em><strong>shoptel </strong></em>c&oacute; thiết kế nội thất của 5 ch&acirc;u với catalogue nội thất theo phong c&aacute;ch Ph&aacute;p, &Yacute;, Hy Lạp, Mỹ&hellip; độc đ&aacute;o, đa dạng, hứa hẹn sẽ mang lại những trải nghiệm mới lạ, kh&aacute;c biệt cho du kh&aacute;ch mỗi lần gh&eacute; thăm.</p>', '<p style=\"text-align:justify\">Tại <a href=\"https://maivietland.vn/du-an-bds/aqua-city-halong.html\"><em><strong>Aqua City Halong</strong></em></a>, mỗi ng&agrave;y sẽ tr&agrave;n ngập những điều mới mẻ, những cảm x&uacute;c thăng hoa, trọn vẹn. Một cuộc sống thư th&aacute;i mơ ước với buổi s&aacute;ng b&igrave;nh minh tr&ecirc;n biển, ngắm nh&igrave;n kỳ quan vịnh di sản nước xanh như ngọc; thưởng thức những trải nghiệm mua sắm v&agrave; nghỉ dưỡng ban ng&agrave;y, v&agrave; thổn thức với những vũ điệu &acirc;m nhạc v&agrave; bầu kh&ocirc;ng kh&iacute; n&aacute;o nhiệt v&agrave;o ban đ&ecirc;m. Một điểm dừng ch&acirc;n &ldquo;all-in-one&rdquo; đậm c&aacute; t&iacute;nh khi đến với Hạ Long.</p>\r\n\r\n<p style=\"text-align:justify\">Hệ thống tiện &iacute;ch tại <em><strong>shoptel Hạ Long</strong></em> đẳng cấp, sang trọng với:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">C&ocirc;ng vi&ecirc;n cảnh quan</li>\r\n	<li style=\"text-align:justify\">Khu phố cảng s&ocirc;i động</li>\r\n	<li style=\"text-align:justify\">Phố đi bộ ven hồ cảnh quan rộng 14ha</li>\r\n	<li style=\"text-align:justify\">Đ&agrave;i phun nước v&agrave; quảng trường</li>\r\n	<li style=\"text-align:justify\">Bến du thuyền sầm uất</li>\r\n	<li style=\"text-align:justify\">B&atilde;i biển</li>\r\n	<li style=\"text-align:justify\">Chuối qu&aacute;n bar Sailing Club v&agrave; nh&agrave; h&agrave;ng đẳng cấp.</li>\r\n	<li style=\"text-align:justify\">Khu phức hợp gi&aacute;o dục quốc tế Singapore International School</li>\r\n</ul>', '<p><em><strong>BIM Land</strong></em> - chủ đầu tư dự &aacute;n l&agrave; một trong những c&ocirc;ng ty ph&aacute;t triển hạ tầng du lịch v&agrave; đầu tư bất động sản h&agrave;ng đầu Việt Nam. Với 25 năm kinh nghiệm kiến tạo n&ecirc;n những dự &aacute;n nghỉ dưỡng 5 sao thế giới, chắc chắn chủ đầu tư sẽ tạo n&ecirc;n <em><strong>Aqua City HaLong</strong></em> ho&agrave;n mỹ hơn cả những g&igrave; được diễn tả tr&ecirc;n giấy.</p>\r\n\r\n<p>Mặt kh&aacute;c,<strong> Quảng Ninh</strong> l&agrave; tỉnh th&agrave;nh đ&aacute;ng đầu tư bậc nhất miền Bắc. Tỉnh c&oacute; hệ thống giao th&ocirc;ng hạ tầng được đầu tư b&agrave;i bản: hệ thống cao tốc d&agrave;i nhất H&agrave; Nội - Hải Ph&ograve;ng - Hạ Long - V&acirc;n Đồn - M&oacute;ng C&aacute;i, S&acirc;n bay quốc tế, Bến du thuyền... c&ugrave;ng tốc độ tăng trưởng kinh tế vượt trội nhờ sở hữu nguồn kho&aacute;ng sản gi&agrave;u c&oacute;, c&aacute;c cửa khẩu, bến t&agrave;u v&agrave; địa danh du lịch nổi tiếng như Vịnh Hạ Long - Kỳ quan thi&ecirc;n nhi&ecirc;n Thế giới.&nbsp;</p>\r\n\r\n<p>Vậy n&ecirc;n, Aqua City HaLong l&agrave; dự &aacute;n đ&aacute;ng đầu tư năm 2019 khi được c&aacute;c chuy&ecirc;n gia đ&aacute;nh gi&aacute; cao về khả năng kinh doanh dịch vụ thương mại, t&iacute;nh thanh khoản cao, l&agrave; t&agrave;i sản t&iacute;ch lũy l&acirc;u d&agrave;i v&agrave; gia tăng gi&aacute; trị trong thời gian ngắn cho c&aacute;c nh&agrave; đầu tư nhanh nhạy với thời cuộc.</p>', NULL, NULL, NULL, '<p style=\"text-align:justify\"><strong>+ Hỗ trợ l&atilde;i suất 0% trong 24 th&aacute;ng</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>+ Miễn 02 năm ph&iacute; quản l&yacute;</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>+ Bốc thăm cho 200 căn. Tổng trị gi&aacute;: khoảng 3</strong> <strong>tỷ</strong></p>\r\n\r\n<p style=\"text-align:justify\">1 đặc biệt: Xe Audi A3 ~ 1500tr</p>\r\n\r\n<p style=\"text-align:justify\">1 giải nhất: Kim cương ~ 300tr</p>\r\n\r\n<p style=\"text-align:justify\">1 giải nh&igrave;: Đồng hồ Rolex ~ 200tr</p>\r\n\r\n<p style=\"text-align:justify\">10 giải 3: Chuyến du lịch Địa Trung Hải ~ 80tr</p>\r\n\r\n<p style=\"text-align:justify\"><strong>+ Nhận ngay 2 lượng v&agrave;ng khi đặt mua trong th&aacute;ng 12</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>+ CK thanh to&aacute;n sớm 95% TGT: 9-10%</strong></p>', 'TỔNG QUAN DỰ ÁN AQUA CITY HALONG', 'DỰ ÁN AQUA CITY SỞ HỮU VỊ TRÍ HIẾM CÓ', 'MẶT BẰNG PHÂN LÔ DỰ ÁN', 'TIỆN ÍCH DỰ ÁN ĐẲNG CẤP HẠ LONG', 'CƠ HỘI ĐẦU TƯ SHOPTEL HẠ LONG KHÔNG NÊN BỎ LỠ', 'TIẾN ĐỘ THANH TOÁN DỰ ÁN SHOPTEL HẠ LONG', NULL, NULL, 'CHÍNH SÁCH BÁN HÀNG', 'Shoptel Aqua City Hạ Long - Giá Gốc Trực Tiếp Chủ Đầu Tư‎', '12/10/2019', NULL, 'aqua-city-halong', 'Aqua City Hạ Long có kiến trúc thiết kế mang phong cách đặc trưng của vùng biển Địa Trung Hải, với tầm view hướng ra vịnh di sản. cao tốc đến Hạ Long đã hoàn thành', NULL, 'index, follow'),
(124, '69', 49, 'Admin', 'Grand Bay Hạ Long', 'Bán đảo 3 Khu đô thị Hạ Long Marina', 1609, 'grandbay-halong4.jpg', NULL, 'false', NULL, 'true', '<ul>\r\n	<li>T&ecirc;n dự &aacute;n: Grand Bay Hạ Long</li>\r\n	<li>Vị tr&iacute;: B&aacute;n đảo 3 Khu đ&ocirc; thị Hạ Long Marina, P. H&ugrave;ng Thắng, B&atilde;i Ch&aacute;y, Hạ Long</li>\r\n	<li>Tổng diện t&iacute;ch dự &aacute;n: 19,3 ha</li>\r\n	<li>Loại h&igrave;nh:</li>\r\n	<li>Khu biệt thự nghỉ dưỡng (Grand Bay Villas) 65 căn: 6.6 ha</li>\r\n	<li>Khu nh&agrave; liền kề (Grand Bay Townhouse) 106 căn: 2.5 ha</li>\r\n	<li>Khu Hotel Resort: 10.4 ha</li>\r\n	<li>Clubhouse: 2.000 m2</li>\r\n	<li>Mật độ x&acirc;y dựng: 35%</li>\r\n	<li>Chủ đầu tư : BIM Group</li>\r\n	<li>Đơn vị tư vấn thiết kế: WATG</li>\r\n</ul>', 'Tổng quan', 'Vị trí', 'Tiện ích', 'Mặt bằng', 'Tiến độ', NULL, NULL, NULL, 'Chính sách', '<p style=\"text-align: justify;\"><a href=\"https://maivietland.vn/du-an-bds/grand-bay-ha-long.html\"><strong>Grand Bay Halong</strong></a> l&agrave; dự &aacute;n thuộc khu đ&ocirc; thị Hạ Long Marina của Tập đo&agrave;n BIM Group. Nằm giữa B&atilde;i Ch&aacute;y v&agrave; Tuần Ch&acirc;u - nơi được coi l&agrave; điểm giao h&ograve;a giữa Đất, Trời v&agrave; Biển, Hạ Long Marina hiện l&ecirc;n như một kiệt t&aacute;c kiến tr&uacute;c, ti&ecirc;n phong cho phong c&aacute;ch sống thượng lưu, cao cấp. Khu đ&ocirc; thị được x&acirc;y dựng v&agrave; ph&aacute;t triển tr&ecirc;n tổng quy m&ocirc; 248ha, trải d&agrave;i tr&ecirc;n 3,8km đường biển hứa hẹn mang đến cho cư d&acirc;n chất lượng kh&ocirc;ng gian sống trong l&agrave;nh v&agrave; nhiều trải nghiệm đ&aacute;ng nhớ.</p>\r\n\r\n<p style=\"text-align: justify;\">Được x&acirc;y dựng th&agrave;nh c&aacute;c ph&acirc;n khu Biệt thự nghỉ dưỡng, Nh&agrave; liền kề, Hotel Resort, Clubhouse; <strong>Grand Bay Halong</strong> l&agrave; tổ hợp NGHỈ DƯỠNG - GIẢI TR&Iacute; - AN CƯ 5 sao lần đầu ti&ecirc;n xuất hiện tại Quảng Ninh. Thừa hưởng vị tr&iacute; đẹp b&ecirc;n vịnh di sản Hạ Long nổi tiếng, hạ tầng quy hoạch đồng bộ c&ugrave;ng hệ thống tiện &iacute;ch nội khu chung của Hạ Long Marina, dự &aacute;n hứa hẹn sẽ l&agrave; nơi lưu tr&uacute; của lượng lớn kh&aacute;ch du lịch v&agrave; nơi an cư, nghỉ dưỡng l&yacute; tưởng của nhiều kh&aacute;ch h&agrave;ng c&oacute; nhu cầu sở hữu Second Home - Ng&ocirc;i nh&agrave; thứ 2.&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\"><strong>&ldquo;Sống phong c&aacute;ch Home Resort - Sống c&ugrave;ng di sản mỗi ng&agrave;y&rdquo;</strong> ch&iacute;nh l&agrave; th&ocirc;ng điệp cũng như gi&aacute; trị sống m&agrave; dự &aacute;n hướng đến. Kh&aacute;ch h&agrave;ng sẽ được hưởng cuộc sống hạnh ph&uacute;c, an nhi&ecirc;n, tiện nghi, đẳng cấp khi lựa chọn nơi đ&acirc;y để &ldquo;gửi gắm&rdquo; những gi&acirc;y ph&uacute;t nghỉ ngơi trong ng&agrave;y.</p>', '<p>Tổ hợp 5 sao Grand Bay tọa lạc tại vị tr&iacute; đẹp nhất b&aacute;n đảo 3 khu đ&ocirc; thị Halong Marina, s&aacute;t bờ vịnh di sản với đường bờ biển trải d&agrave;i l&ecirc;n tới 1km. C&aacute;c căn Villas, Townhouse đều sở hữu tầm nh&igrave;n kho&aacute;ng đạt hướng biển, &ocirc;m trọn khung cảnh di sản Vịnh Hạ Long. Khung cảnh h&ugrave;ng vĩ, tr&aacute;ng lệ nhưng kh&ocirc;ng k&eacute;m phần l&atilde;ng mạn của Vịnh di sản lu&ocirc;n l&agrave; điểm ước đến của rất nhiều du kh&aacute;ch. Sở hữu một Second Home ven biển Hạ Long, nơi &ldquo;s&oacute;ng biển l&agrave; thềm nh&agrave;&rdquo; l&agrave; điều hiếm c&oacute;, kh&oacute; t&igrave;m. Nhưng dự &aacute;n n&agrave;y của BIM Group sẽ hiện thực h&oacute;a giấc mơ đ&oacute;.</p>\r\n\r\n<p>B&ecirc;n cạnh điểm s&aacute;ng ở vị tr&iacute;, dự &aacute;n c&ograve;n dễ d&agrave;ng kết nối, di chuyển đến c&aacute;c tiện &iacute;ch nội khu như Clubhouse, Trung t&acirc;m thương mại Hạ Long Marina, Khu phố đi bộ, B&atilde;i tắm Hạ Long&hellip; L&agrave; một trong số &iacute;t c&aacute;c dự &aacute;n tận hưởng lợi &iacute;ch từ việc gi&aacute;p cả hồ cảnh quan, bờ biển Vịnh Hạ Long v&agrave; tuyến đi bộ trung t&acirc;m, Grand Bay Halong tạo n&ecirc;n một kh&ocirc;ng gian giải tr&iacute; v&agrave; sinh sống l&yacute; tưởng ngay b&ecirc;n bờ Vịnh.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"vị trí\" src=\"/public/upload/images/grandbay-halong/Vi-tri-du-an-Grand-Bay-Halong-03.jpg\" /></p>', '<p>Sống hạnh ph&uacute;c, sống đẳng cấp l&agrave; điều <a href=\"https://maivietland.vn/du-an-bds/grand-bay-ha-long.html\"><strong>Grand Bay Halong</strong></a> muốn mang đến cho kh&aacute;ch h&agrave;ng. Dự &aacute;n tạo dựng một kh&ocirc;ng gian sống đầy tiện nghi khi thừa hưởng hệ thống tiện &iacute;ch sống 5 sao của khu đ&ocirc; thị Hạ Long Marina như: Rạp chiếu phim CGV, tổ hợp thương mại v&agrave; giải tr&iacute; Halong Marine Plaza, Trường quốc tế Singapore SIS, tuyến phố đi bộ trung t&acirc;m Marina Square, Bến du thuyền, B&atilde;i biển Marina Bay, quảng trường biển&hellip;&nbsp;</p>\r\n\r\n<p>Điểm nhấn ấn tượng trong hệ thống tiện &iacute;ch của dự &aacute;n l&agrave; Grand Halong Clubhouse. Chủ đầu tư đ&atilde; đầu tư để x&acirc;y dựng l&ecirc;n C&acirc;u lạc bộ giải tr&iacute; đẳng cấp phục vụ ri&ecirc;ng cho cư d&acirc;n sống tại tổ hợp. Clubhouse c&oacute; diện t&iacute;ch s&agrave;n l&ecirc;n đến hơn 2000m2, bao gồm quầy bar v&agrave; lounge sang trọng, c&acirc;u lạc bộ vui chơi cho trẻ em với những hoạt động phong ph&uacute;, hệ thống ph&ograve;ng gym v&agrave; spa với c&aacute;c thiết bị ti&ecirc;u chuẩn quốc tế, bể bơi trong nh&agrave; v&agrave; ngo&agrave;i trời ph&ugrave; hợp d&agrave;nh cho cả gia đ&igrave;nh. Kh&aacute;ch h&agrave;ng sẽ kh&ocirc;ng phải đi qu&aacute; xa để tiếp cận với c&aacute;c tiện &iacute;ch bởi tất cả đều được kh&eacute;p k&iacute;n, đặt ngay ngưỡng cửa.</p>', '<p>&ldquo;Sống theo phong c&aacute;ch nghỉ dưỡng quốc tế&rdquo; l&agrave; những g&igrave; BIM Group gửi gắm v&agrave;o từng mẫu thiết kế Villas, Townhouse. Dự &aacute;n được thiết kế bởi WATG - một trong những nh&agrave; tư vấn thiết kế h&agrave;ng đầu thế giới&nbsp; đến từ nước Mỹ với hơn 70 năm kinh nghiệm đ&atilde; hợp t&aacute;c với nhiều thương hiệu kh&aacute;ch sạn nổi tiếng.&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"MẶT BẰNG THIẾT KẾ DỰ ÁN\" src=\"/public/upload/images/grandbay-halong/grandbay-halong-matbang.jpg\" /></p>\r\n\r\n<p>Dự &aacute;n c&oacute; quy m&ocirc; 19,3 ha với c&aacute;c ph&acirc;n khu ch&iacute;nh:</p>\r\n\r\n<p><em><strong>Khu nh&agrave; liền kề (Grand Bay Townhouse)</strong></em></p>\r\n\r\n<ul>\r\n	<li>Tổng diện t&iacute;ch: 2.5 ha</li>\r\n	<li>Số lượng căn: 106</li>\r\n	<li>Số tầng cao: 4</li>\r\n	<li>Diện t&iacute;ch đất: 104 - 214m2</li>\r\n	<li>Tổng diện t&iacute;ch s&agrave;n x&acirc;y dựng: 324 - 372m2</li>\r\n	<li>Mặt tiền trung b&igrave;nh: 6.5m</li>\r\n</ul>\r\n\r\n<p>Biệt thự liền kề Grand Bay Townhouse được lấy cảm hứng từ những d&atilde;y biệt thự biển miền Nam California, nơi nắng v&agrave;ng, biển xanh ấm &aacute;p, phảng phất b&oacute;ng d&aacute;ng phong c&aacute;ch thiết kế Art Deco thanh lịch, ấn tượng v&agrave; sang trọng, tạo n&ecirc;n kh&ocirc;ng gian sống cao cấp cho c&aacute;c chủ nh&acirc;n thượng lưu.</p>\r\n\r\n<p><em><strong>Khu biệt thự nghỉ dưỡng (Grand Bay Villas)</strong></em></p>\r\n\r\n<ul>\r\n	<li>Tổng diện t&iacute;ch: 6.6ha</li>\r\n	<li>Số lượng căn: 65 căn</li>\r\n	<li>Chiều cao: 3 tầng</li>\r\n</ul>\r\n\r\n<p>Loại biệt thự:</p>\r\n\r\n<ul>\r\n	<li>Biệt thự Entry: 22 căn, diện t&iacute;ch 300m2/căn</li>\r\n	<li>Biệt thự ti&ecirc;u chuẩn: 40 căn, diện t&iacute;ch 500m2/căn</li>\r\n	<li>Biệt thự hạng sang: 3 căn, diện t&iacute;ch 875m2/căn</li>\r\n</ul>\r\n\r\n<p>C&aacute;c căn Villas đều c&oacute; thiết kế thanh lịch, sang trọng, pha trộn n&eacute;t cổ điển ch&acirc;u &Acirc;u. C&aacute;c cửa sổ của Villas đều được thiết kế rộng hưởng trọn phong cảnh tuyệt đẹp, nguồn kh&ocirc;ng kh&iacute; trong sạch của Vịnh Hạ Long. Nhờ vậy, cư d&acirc;n sẽ c&oacute; những gi&acirc;y ph&uacute;t thư th&aacute;i tuyệt vời, những trải nghiệm nghỉ dưỡng trọn vẹn trong kh&ocirc;ng gian sống l&yacute; tưởng.</p>', '<p style=\"text-align:center\"><img alt=\"tiến độ đống tiền grand bay\" src=\"/public/upload/images/grandbay-halong/tien-do.png\" /></p>', NULL, NULL, NULL, '<p>Tặng g&oacute;i nội thất 200 Triệu Đồng v&agrave; được trừ trực tiếp v&agrave;o gi&aacute; b&aacute;n</p>', 'GRAND BAY HẠ LONG - KIỆT TÁC SỐNG BÊN VỊNH DI SẢN', 'VỊ TRÍ ÔM TRỌN VỊNH DI SẢN CỦA DỰ ÁN', 'DỰ ÁN CÓ TIỆN ÍCH CAO CẤP THƯỢNG LƯU', 'MẶT BẰNG THIẾT KẾ DỰ ÁN', 'TIẾN ĐỘ THANH TOÁN', NULL, NULL, NULL, 'CHÍNH SÁCH BÁN HÀNG', 'Dự Án Grand Bay Hạ Long - Thông Tin Chính Thức Từ CĐT', '12/17/2019', NULL, 'grand-bay-ha-long', 'Grand Bay Halong là dự án thuộc khu đô thị Hạ Long Marina của Tập đoàn BIM Group, Grand Bay Halong là tổ hợp NGHỈ DƯỠNG - GIẢI TRÍ - AN CƯ 5 sao lần đầu tiên tại QN', NULL, 'index, follow'),
(125, '69', 49, 'Admin', 'FLC Tropical City Hạ Long', 'Khu đô thị Cao Xanh, P. Hà Khánh', 1191, 'flc-tropical-city-ha-long1.jpg', NULL, 'false', NULL, 'true', '<ul>\r\n	<li style=\"text-align:justify\">Quy m&ocirc;: 87.4ha\r\n	<ul>\r\n		<li>Giai đoạn 1: 40.4 ha</li>\r\n		<li>Giai đoạn 2: 47.45ha</li>\r\n	</ul>\r\n	</li>\r\n	<li style=\"text-align:justify\">Tổng mức đầu tư: 8.000 tỷ VNĐ</li>\r\n	<li style=\"text-align:justify\">Sản phẩm:\r\n	<ul>\r\n		<li>Shophouse/LK: 2609 căn</li>\r\n		<li>Căn hộ chung cư: 826 căn DT từ 45m2 đến 57m2</li>\r\n	</ul>\r\n	</li>\r\n	<li style=\"text-align:justify\">Tiện &iacute;ch: Trường học, Bệnh viện, Khu thể thao, Quảng trường đi bộ, C&ocirc;ng vi&ecirc;n c&acirc;y xanh&hellip;</li>\r\n	<li style=\"text-align:justify\">Tư vấn thiết kế: Humphreys &amp; Partners Architects</li>\r\n	<li style=\"text-align:justify\">Tổng thầu: Faros</li>\r\n</ul>', 'Tổng quan', 'Vị trí', 'Tiện ích', 'Mặt bằng', 'Tiến độ', NULL, NULL, NULL, 'Chính sách', '<p style=\"text-align:justify\">Với sự ph&aacute;t triển mạnh về hạ tầng giao th&ocirc;ng, hạ tầng du lịch, sự bảo tồn, ph&aacute;t huy đ&uacute;ng c&aacute;ch c&aacute;c di sản, di t&iacute;ch lịch sử v&agrave; kỳ quan thi&ecirc;n nhi&ecirc;n, Hạ Long đang vươn m&igrave;nh ph&aacute;t triển mạnh mẽ. Minh chứng cho điều đ&oacute; l&agrave; h&agrave;ng loạt con số ấn tượng: GDP 2019 cao gấp 2 lần trung b&igrave;nh cả nước; GDP b&igrave;nh qu&acirc;n 5.110 USD/người;&nbsp; Tr&ecirc;n 12 triệu lượt kh&aacute;ch du lịch đến Hạ Long 2019, tăng 20% so với năm 2018...&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Đặc biệt l&agrave; từ khi c&aacute;c tuyến đường giao th&ocirc;ng trọng điểm được ho&agrave;n thiện như Đường cao tốc H&agrave; Nội - Hải Ph&ograve;ng - Hạ Long - M&oacute;ng C&aacute;i d&agrave;i 320km, Dự &aacute;n cầu Cửa Lục 1 v&agrave; 3 kết nối Hạ Long với Ho&agrave;nh Bồ, Cảng t&agrave;u kh&aacute;ch quốc tế Hạ Long, Cảng h&agrave;ng kh&ocirc;ng quốc tế V&acirc;n Đồn&hellip; đ&atilde; tạo thuận lợi cho du kh&aacute;ch đến với Hạ Long, từ đ&oacute; ng&agrave;nh du lịch ph&aacute;t triển vượt bậc.</p>\r\n\r\n<p style=\"text-align:justify\">H&agrave;ng loạt dự &aacute;n mở rộng th&agrave;nh phố, mở rộng giao th&ocirc;ng - kinh tế, th&agrave;nh lập khu vui chơi - du lịch quốc tế đ&atilde; v&agrave; đang được thực hiện. Hạ Long cũng l&agrave; mảnh đất m&agrave;u mỡ để ph&aacute;t triển c&aacute;c dự &aacute;n bất động sản nghỉ dưỡng đẳng cấp. Nhiều chủ đầu tư lớn đ&atilde; chọn Hạ Long l&agrave; điểm dừng ch&acirc;n, trong đ&oacute; c&oacute; Tập đo&agrave;n FLC.</p>\r\n\r\n<p style=\"text-align:justify\"><a href=\"https://maivietland.vn/du-an-bds/flc-tropical-city-ha-long.html\"><em><strong>Tropical City Hạ Long</strong></em></a> ch&iacute;nh l&agrave; dự &aacute;n trọng điểm m&agrave; FLC đang đầu tư tại &ldquo;v&ugrave;ng đất rồng&rdquo; Hạ Long. T&iacute;ch hợp tr&ecirc;n 50 tiện &iacute;ch hiện đại, đồng bộ như kh&aacute;ch sạn, spa, khu lễ hội v&agrave; bể bơi ngo&agrave;i trời, clubhouse, hệ thống trường li&ecirc;n cấp chất lượng cao&hellip; FLC City&nbsp;dự kiến l&agrave; một đ&ocirc; thị trẻ, năng động, được kỳ vọng trở th&agrave;nh đ&ocirc; thị kiểu mẫu đ&aacute;ng sống tại Hạ Long.</p>', '<p style=\"text-align:justify\">Dự &aacute;n <a href=\"https://maivietland.vn/du-an-bds/flc-tropical-city-ha-long.html\"><strong>FLC Tropical City</strong></a>&nbsp;trải rộng tr&ecirc;n diện t&iacute;ch 87.4ha c&oacute; vị tr&iacute; hiếm c&oacute; khi ph&iacute;a T&acirc;y Nam, Đ&ocirc;ng Nam hướng n&uacute;i, c&ograve;n ph&iacute;a T&acirc;y Bắc tiếp gi&aacute;p s&ocirc;ng Diễn Vọng với tầm nh&igrave;n thẳng tuyệt đẹp ra Vịnh Cửa Lục.</p>\r\n\r\n<p style=\"text-align:justify\">Đ&acirc;y cũng l&agrave; vị tr&iacute; trung t&acirc;m văn h&oacute;a, kinh tế, ch&iacute;nh trị lớn của Hạ Long với nhiều yếu tố g&oacute;p phần t&ocirc;n vinh những c&aacute;i nhất của dự &aacute;n đ&atilde; khiến nơi đ&acirc;y trở th&agrave;nh t&acirc;m điểm đầu tư tại th&agrave;nh phố n&agrave;y.</p>\r\n\r\n<p style=\"text-align:justify\">Dự &aacute;n chỉ c&aacute;ch trung t&acirc;m th&agrave;nh phố 6km, cư d&acirc;n c&oacute; thể dễ d&agrave;ng di chuyển nhanh đến c&aacute;c địa điểm trọng yếu của th&agrave;nh phố Hạ Long như:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Chợ Hạ Long: 7 ph&uacute;t</li>\r\n	<li style=\"text-align:justify\">Big C Hạ Long: 16 ph&uacute;t</li>\r\n	<li style=\"text-align:justify\">Cột đồng hồ: 9 ph&uacute;t</li>\r\n	<li style=\"text-align:justify\">Bệnh viện đa khoa Quảng Ninh: 11 ph&uacute;t</li>\r\n	<li style=\"text-align:justify\">Bảo t&agrave;ng Quảng Ninh: 12 ph&uacute;t</li>\r\n	<li style=\"text-align:justify\">Cầu B&atilde;i Ch&aacute;y: 9 ph&uacute;t</li>\r\n	<li style=\"text-align:justify\">BV Vinmec Hạ Long: 8 ph&uacute;t</li>\r\n	<li style=\"text-align:justify\">Sunworld Hạ Long Complex: 16 ph&uacute;t</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">Ngo&agrave;i ra, FLC Tropical c&ograve;n tiếp gi&aacute;p c&aacute;c trục đường huyết mạch như: Quốc lộ 1B, Quốc lộ 18, Đường v&agrave;nh đai ph&iacute;a Bắc, đường Trần Ph&uacute;, gần đường rẽ cao tốc H&agrave; Nội - Hải Ph&ograve;ng - Hạ Long (từ Ho&agrave;nh Bồ), gần trung t&acirc;m n&uacute;t thắt giao th&ocirc;ng của Hạ Long (ng&atilde; tư Loong To&ograve;ng). Vậy n&ecirc;n rất thuận tiện cho việc di chuyển của cư d&acirc;n cũng như du kh&aacute;ch. Mặt kh&aacute;c dự &aacute;n cũng được n&acirc;ng tầm gi&aacute; trị đầu tư cũng như tiềm năng tăng gi&aacute; cho c&aacute;c nh&agrave; đầu tư khi dự &aacute;n sở hữu vị tr&iacute; rất đắc địa v&agrave; hiếm c&oacute;.</p>', '<p style=\"text-align:justify\">Bất cứ sản phẩm n&agrave;o của tập đo&agrave;n FLC đều được đầu tư, chăm ch&uacute;t trong từng chi tiết, lu&ocirc;n mang đến cho kh&aacute;ch h&agrave;ng những sản phẩm đẳng cấp, vượt trội. Đối với&nbsp;<a href=\"https://maivietland.vn/du-an-bds/flc-tropical-city-ha-long.html\"><strong>Tropical City</strong></a>, được định vị l&agrave; khu đ&ocirc; thị kiểu mẫu đ&aacute;ng sống nhất Hạ Long, dự &aacute;n c&oacute; hơn 100 tiện &iacute;ch nội khu đồng bộ, hiện đại. C&aacute;c điểm nhấn tiện &iacute;ch đặc biệt như chuỗi quảng trường chủ đề, những cung đường rợp b&oacute;ng c&acirc;y xanh dọc&nbsp;hai b&ecirc;n đường, c&ocirc;ng vi&ecirc;n Palm Park với hệ thảm thực vật đặc trưng, Hồ điều h&ograve;a thanh lọc kh&ocirc;ng kh&iacute; cho h&agrave;ng ng&agrave;n cư d&acirc;n khu đ&ocirc; thị v&agrave; c&aacute;c khu vực l&acirc;n cận. Kh&ocirc;ng gian xanh được bao phủ xung quanh để nu&ocirc;i dưỡng năng lượng sống căng tr&agrave;n của mỗi tổ ấm y&ecirc;u thương. Để mỗi khi trở về nh&agrave;, mọi bộn bề, lo toan của cư d&acirc;n sẽ lắng xuống, nhường chỗ cho những ph&uacute;t gi&acirc;y thảnh thơi, thư gi&atilde;n.</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">B&atilde;i đỗ xe th&ocirc;ng tho&aacute;ng</li>\r\n	<li style=\"text-align:justify\">Quảng trường nhạc nước</li>\r\n	<li style=\"text-align:justify\">C&ocirc;ng vi&ecirc;n c&acirc;y xanh</li>\r\n	<li style=\"text-align:justify\">Clubhouse</li>\r\n	<li style=\"text-align:justify\">Trung t&acirc;m thương mại</li>\r\n	<li style=\"text-align:justify\">Nh&agrave; h&agrave;ng, qu&aacute;n Cafe</li>\r\n	<li style=\"text-align:justify\">Rooftop Bar</li>\r\n	<li style=\"text-align:justify\">Trường học</li>\r\n	<li style=\"text-align:justify\">Khu thể thao phức hợp</li>\r\n	<li style=\"text-align:justify\">Phố đi bộ</li>\r\n	<li style=\"text-align:justify\">Hồ điều h&ograve;a</li>\r\n	<li style=\"text-align:justify\">Public Art</li>\r\n	<li style=\"text-align:justify\">Ch&ograve;i nghỉ thư gi&atilde;n</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">C&aacute;c tiện &iacute;ch hiện đại quốc tế được FLC x&acirc;y dựng c&oacute; thể l&agrave;m h&agrave;i l&ograve;ng mọi cư d&acirc;n, mang đến kh&ocirc;ng gian sống chất lượng, một cuộc sống hạnh ph&uacute;c, tận hưởng vi&ecirc;n m&atilde;n, trọn vẹn cảm x&uacute;c.</p>', '<p style=\"text-align:justify\"><strong><a href=\"https://maivietland.vn/du-an-bds/flc-tropical-city-ha-long.html\">FLC&nbsp;City Hạ Long</a></strong> được ph&aacute;t triển theo m&ocirc; h&igrave;nh khu đ&ocirc; thị ven s&ocirc;ng đẳng cấp với c&aacute;c khu nh&agrave; thấp tầng, cao ốc, c&ocirc;ng tr&igrave;nh c&ocirc;ng cộng, mặt nước c&acirc;y xanh v&agrave; c&aacute;c c&ocirc;ng tr&igrave;nh hạ tầng kỹ thuật.</p>\r\n\r\n<p style=\"text-align:justify\">Dự &aacute;n gồm 4 ph&acirc;n khu ch&iacute;nh v&agrave; chia th&agrave;nh 2 giai đoạn triển khai</p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>Giai đoạn 1</strong></h3>\r\n\r\n<p style=\"text-align:justify\"><em><strong>Ph&acirc;n khu Bali Forest</strong></em></p>\r\n\r\n<p style=\"text-align:justify\">Ph&acirc;n khu Bali Forest bao gồm shophouse v&agrave; chung cư phong c&aacute;ch FLC Tropical Hạ Long lu&ocirc;n gắn liền với biểu tượng của c&acirc;y chuối cảnh, chất liệu v&agrave; tơ lụa, lũy tre, kh&oacute;m tr&uacute;c nhỏ. 4 t&ograve;a chung cư Tropical City sẽ gắn liền với những biểu tượng đ&oacute;.</p>\r\n\r\n<p style=\"text-align:justify\"><em><strong>Ph&acirc;n khu Botanic Garden&nbsp;</strong></em></p>\r\n\r\n<p style=\"text-align:justify\">Ph&acirc;n khu Botanic Garden&nbsp;Tropical City tập hợp những d&atilde;y nh&agrave; liền kề, được &ocirc;m trọn bởi một trong những bến cảng lớn nhất của Hạ Long. Botanic Garden sẽ l&agrave; địa điểm l&yacute; tưởng của những người muốn tận hưởng kh&ocirc;ng gian sống y&ecirc;n b&igrave;nh, bỏ sau những nặng nề của cuộc sống.</p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>Giai đoạn 2 (đang triển khai v&agrave; b&aacute;n)</strong></h3>\r\n\r\n<p style=\"text-align:justify\"><em><strong>Ph&acirc;n khu Palm Village</strong></em></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Quy m&ocirc;: 18.45ha</li>\r\n	<li style=\"text-align:justify\">Concept thiết kế: T&acirc;n cổ điển</li>\r\n	<li style=\"text-align:justify\">Sản phẩm:\r\n	<ul>\r\n		<li>Shophouse: 76</li>\r\n		<li>Liền kề: 581</li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">Ph&acirc;n khu Palm Village mang m&agrave;u sắc, kh&ocirc;ng kh&iacute; của phong c&aacute;ch nhiệt đới với biểu tượng c&acirc;y cọ. Những h&agrave;ng cọ rợp b&oacute;ng m&aacute;t tr&ecirc;n đường dạo bộ, quanh kh&ocirc;ng gian sống mang lại cảm gi&aacute;c mới lạ cho cư d&acirc;n.</p>\r\n\r\n<p style=\"text-align:justify\">Ph&acirc;n khu c&oacute; c&aacute;c tiện &iacute;ch như đường dạo bộ, cảnh quan v&agrave; hệ thống trường học tại dự &aacute;n Tropical City Hạ Long.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><em><strong>Ph&acirc;n khu Hawaii Seaside</strong></em></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Quy m&ocirc;: 29ha</li>\r\n	<li style=\"text-align:justify\">Concept thiết kế: Hiện đại</li>\r\n	<li style=\"text-align:justify\">Sản phẩm:&nbsp;\r\n	<ul>\r\n		<li>Shophouse: 378</li>\r\n		<li>Liền kề: 367</li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">Ph&acirc;n khu Hawaii Seaside l&agrave; ph&acirc;n khu sang trọng v&agrave; hiện đại nhất của dự &aacute;n. Trải d&agrave;i tr&ecirc;n b&atilde;i biển, dưới b&oacute;ng c&acirc;y m&aacute;t rượi, h&ograve;a m&igrave;nh v&agrave;o thi&ecirc;n nhi&ecirc;n của đất trời, khơi nguồn những n&eacute;t đậm chất Đ&ocirc;ng &Aacute;.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Ph&acirc;n khu với quy hoạch v&agrave; thiết kế x&acirc;y dựng đỉnh cao nhưng vẫn giữ trọn n&eacute;t đẹp thi&ecirc;n nhi&ecirc;n. Ph&acirc;n khu hội tụ đầy đủ mọi tiện &iacute;ch hiện đại v&agrave; đẳng cấp, nhằm cung cấp cho kh&aacute;ch h&agrave;ng một cuộc sống thượng lưu, vẹn tr&ograve;n hạnh ph&uacute;c.</p>', NULL, NULL, NULL, NULL, '<p>UPDATING!!!</p>', 'Tổng quan dự án FLC Tropical City Hạ Long', 'Vị trí dự án', '100+ tiện ích KĐT kiểu mẫu phục vụ đời sống cư dân', 'Mặt bằng tổng thể dự án', 'Tiến độ thanh toán FLC Tropical City', NULL, NULL, NULL, NULL, 'FLC Tropical City Hạ Long - Cập Nhật Mới Nhất Từ CĐT', '12/17/2019', NULL, 'flc-tropical-city-ha-long', 'FLC Tropical City là một đô thị trẻ, năng động, được kỳ vọng trở thành đô thị kiểu mẫu đáng sống tại Hạ Long.', NULL, 'index, follow');
INSERT INTO `tbl_product` (`id`, `cat_id`, `dis_id`, `user`, `name`, `address`, `hits`, `img`, `price`, `hot`, `tag`, `status`, `detail`, `tab1`, `tab2`, `tab3`, `tab4`, `tab5`, `tab6`, `tab7`, `tab8`, `tab9`, `content1`, `content2`, `content3`, `content4`, `content5`, `content6`, `content7`, `content8`, `content9`, `heading1`, `heading2`, `heading3`, `heading4`, `heading5`, `heading6`, `heading7`, `heading8`, `heading9`, `title`, `date`, `date_up`, `slug`, `description`, `keywords`, `robot`) VALUES
(126, '69', 50, 'Admin', 'Ecopark Grand - The Island', 'Khu đô thị Ecopark, Xuân Quan', 2107, 'view.jpg', '21 tỷ', 'false', NULL, 'true', '<ul>\r\n	<li>T&ecirc;n dự &aacute;n: Ecopark Grand - The Island</li>\r\n	<li>Loại h&igrave;nh: Biệt thự đảo&nbsp;</li>\r\n	<li>Chủ đầu tư: C&ocirc;ng ty Cổ phần Tập đo&agrave;n Ecopark</li>\r\n	<li>Diện t&iacute;ch to&agrave;n khu: 60,4 ha</li>\r\n	<li>Mật độ x&acirc;y dựng: 18%</li>\r\n	<li>Diện t&iacute;ch mặt nước &amp; c&acirc;y xanh: 37,5 ha</li>\r\n	<li>Diện t&iacute;ch giao th&ocirc;ng: 4,8 ha</li>\r\n	<li>Đơn vị quy hoạch: WATG</li>\r\n	<li>Thời điểm b&agrave;n giao:&nbsp;Đối với song lập: 30/6/2020</li>\r\n	<li>Thời điểm b&agrave;n giao:&nbsp;Đối với đơn lập: 30/9/2020</li>\r\n</ul>', 'Tổng quan', 'Vị trí', 'Thiết kế độc bản', 'Tiện ích', 'Nội thất', NULL, NULL, NULL, 'Chính sách', '<p>Với tốc độ đ&ocirc; thị h&oacute;a nhanh ch&oacute;ng mặt, H&agrave; Nội đang phải đối mặt với nhiều vấn đề nan giải như &ocirc; nhiễm kh&ocirc;ng kh&iacute;, nguồn nước. B&ecirc;n cạnh đ&oacute;, số lượng d&acirc;n số tăng nhanh khiến hạ tầng ng&agrave;y c&agrave;ng qu&aacute; tải n&ecirc;n thường xuy&ecirc;n tắc đường, ngập nước sau mưa. V&igrave; vậy, nhiều người d&acirc;n H&agrave; Nội dần thay đổi nhu cầu về nh&agrave; ở, hướng tới những khu đ&ocirc; thị xanh, đầy đủ tiện &iacute;ch ở ngoại &ocirc;. Đặc biệt l&agrave; giới nh&agrave; gi&agrave;u, họ kh&ocirc;ng c&ograve;n th&iacute;ch th&uacute; sở hữu những căn chung cư cao cấp, đầy đủ tiện &iacute;ch, tầm nh&igrave;n trọn th&agrave;nh phố m&agrave; ngược lại họ t&igrave;m kiếm những căn biệt thự ngoại &ocirc; c&oacute; s&acirc;n vườn, mặt nước. <a href=\"https://maivietland.vn/du-an-bds/ecopark-grand-the-island.html\"><strong>Khu đ&ocirc; thị ECOPARK</strong></a> ch&iacute;nh l&agrave; kh&ocirc;ng gian sống l&yacute; tưởng h&agrave;ng đầu hiện nay.</p>\r\n\r\n<p>Khu đ&ocirc; thị c&oacute; quy m&ocirc; 499,9 với diện t&iacute;ch c&acirc;y xanh chiếm tỷ lệ lớn. Sau hơn 10 năm x&acirc;y dựng v&agrave; ph&aacute;t triển, Ecopark đ&atilde; trở th&agrave;nh khu đ&ocirc; thị xanh kiểu mẫu của Việt Nam v&agrave; c&aacute;c nước trong khu vực. Tiếp nối những th&agrave;nh c&ocirc;ng v&agrave; triết l&yacute; về một cuộc sống xanh mang đến trải nghiệm tuyệt vời, chủ đầu tư tiếp tục kiến tạo n&ecirc;n ECOPARK GRAND - THE ISLAND mang những gi&aacute; trị tinh hoa vượt bậc kh&ocirc;ng thể t&igrave;m thấy ở một dự &aacute;n n&agrave;o kh&aacute;c.</p>\r\n\r\n<p><a href=\"https://maivietland.vn/du-an-bds/ecopark-grand-the-island.html\"><strong>Biệt thự The Island</strong></a> l&agrave; tuyệt phẩm đẳng cấp nhất khu đ&ocirc; thị Ecopark, mang trong m&igrave;nh sứ mệnh đại diện c&oacute; những gi&aacute; trị sống vượt trội, đẳng cấp nhất, một chuẩn mực sống tinh hoa xứng tầm. Quần thể biệt thự đảo gồm những căn biệt thự được x&acirc;y dựng tr&ecirc;n những nh&aacute;nh đảo vươn ra vịnh giữa hồ, nơi cuộc sống l&agrave; một kỳ nghỉ dưỡng bất tận giữa thi&ecirc;n nhi&ecirc;n.</p>\r\n\r\n<p>Dự &aacute;n c&oacute; mật độ x&acirc;y dựng thấp, chỉ xấp xỉ 40%, phần c&ograve;n lại d&agrave;nh cho kh&ocirc;ng gian xanh. C&aacute;c biệt thự được thiết kế với kh&ocirc;ng gian sinh hoạt 3 tầng rộng tho&aacute;ng, nh&igrave;n thẳng ra mặt hồ xanh ngọc b&iacute;ch, 4 ph&ograve;ng ngủ rộng r&atilde;i c&ugrave;ng s&acirc;n vườn xanh m&aacute;t chắc chắn sẽ khiến gia chủ thỏa m&atilde;n mỗi khi trở về nh&agrave;. B&ecirc;n cạnh đ&oacute; l&agrave; chuỗi tiện &iacute;ch cao cấp, xứng tầm thượng lưu d&agrave;nh ri&ecirc;ng cho cư d&acirc;n như xe đưa đ&oacute;n Maybach cao cấp, du thuyền sang trọng, Clubhouse hơn 7.000 m2&hellip; sẽ mang tới cuộc sống tận hưởng đ&iacute;ch thực, nơi mọi nhu cầu được thỏa m&atilde;n, mọi cảm x&uacute;c được thăng hoa.</p>', '<p><a href=\"https://maivietland.vn/du-an-bds/ecopark-grand-the-island.html\"><strong>Ecopark Grand - The Island</strong></a> tọa lạc ngay vị tr&iacute; trung t&acirc;m, đắt gi&aacute; bậc nhất giữa l&ograve;ng Ecopark. Ph&iacute;a Bắc tiếp gi&aacute;p khu Wetland xanh mướt v&agrave; Park River. Ph&iacute;a Đ&ocirc;ng gi&aacute;p với khu thương mại Đảo Rồng v&agrave; khu vực trung t&acirc;m CBD của Ecopark, dự kiến sẽ l&agrave; địa điểm thu h&uacute;t c&aacute;c hoạt động văn h&oacute;a thương mại sầm uất. Nơi đ&acirc;y được mệnh danh l&agrave; tr&aacute;i tim của Ecopark, nơi m&agrave;u xanh v&ocirc; gi&aacute; vươn đến tận ch&acirc;n m&acirc;y, s&oacute;ng nước n&ecirc;n thơ ngay b&ecirc;n dưới thềm nh&agrave;.&nbsp;</p>\r\n\r\n<p>Mang thi&ecirc;n hướng Đảo Cọ Palm Island kỳ vĩ tại Dubai, quần thể biệt thự Ecopark Grand gồm những căn biệt thự được x&acirc;y tr&ecirc;n những rẻo đất nh&ocirc; ra giữa vịnh hồ xanh m&aacute;t. Đ&acirc;y l&agrave; dự &aacute;n duy nhất tr&ecirc;n thị trường c&oacute; 100% c&aacute;c biệt thự đều tiếp x&uacute;c mặt nước tuần ho&agrave;n. Trong một kh&ocirc;ng gian rộng lớn 60,4ha, diện t&iacute;ch mặt nước đ&atilde; chiếm tới 30ha, những căn biệt thự lộng lẫy soi b&oacute;ng xuống mặt nước lấp l&aacute;nh. Kh&ocirc;ng chỉ l&agrave; dấu ấn tuyệt t&aacute;c giữa trọn n&eacute;t thi&ecirc;n nhi&ecirc;n, The Island c&ograve;n l&agrave; một di sản c&aacute; nh&acirc;n mang đến những trải nghiệm phong c&aacute;ch nghỉ dưỡng thượng lưu vượt tầm đẳng cấp.Vị tr&iacute; đắc địa của dự &aacute;n kh&ocirc;ng chỉ thuận tiện m&agrave; c&ograve;n hứa hẹn mang tới gi&aacute; trị gia tăng rất lớn cho gia chủ.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Vị trí đắt giá của biệt thự đảo đẳng cấp nhất Ecopark\" src=\"/public/upload/images/ecopark-grand-the-island/map-ecopark.jpg\" /></p>', '<p>Ở Ecopark Grand, mỗi căn biệt thự đều l&agrave; &ldquo;độc bản&rdquo; bởi c&aacute;c kiến tr&uacute;c sư danh tiếng sẽ ho&agrave;n thiện bản vẽ chi tiết của từng căn theo từng đơn &ldquo;đặt h&agrave;ng&rdquo; đặc biệt từ mỗi một chủ nh&acirc;n. To&agrave;n bộ c&aacute;c biệt thự đều được thiết kế tr&ecirc;n trục T&acirc;y Bắc - Đ&ocirc;ng Nam, th&ocirc;ng tho&aacute;ng c&aacute;c m&ugrave;a nhờ sự đối lưu kh&iacute; tự nhi&ecirc;n, khắc chế tối đa ảnh hưởng bởi thời tiết mưa ẩm miền bắc.&nbsp;</p>\r\n\r\n<p>Mỗi căn biệt thự đều được thiết kế với &iacute;t nhất 4 ph&ograve;ng ngủ, garage đủ rộng đều vừa hai xe sang v&agrave; sở hữu s&acirc;n vườn c&ugrave;ng lầu vọng thủy vươn ra s&aacute;t mặt nước.Tất cả&nbsp; c&aacute;c biệt thự đều mang phong c&aacute;ch resort cao cấp với tầm nh&igrave;n tuyệt đẹp &ocirc;m trọn mặt nước lấp l&aacute;nh. Kh&ocirc;ng gian b&ecirc;n trong căn nh&agrave; lu&ocirc;n căng tr&agrave;n &aacute;nh s&aacute;ng v&agrave; gi&oacute; tự nhi&ecirc;n, nơi gia chủ được tận hưởng trọn vẹn vẻ đẹp của cuộc sống.</p>\r\n\r\n<p>Kh&ocirc;ng gian sống xanh m&aacute;t bởi c&oacute; mặt hồ nước bao quanh c&ugrave;ng c&acirc;y xanh v&agrave; những thảm thực vật nhiều tầng t&aacute;n. Biệt thự Island ch&iacute;nh l&agrave; tuyệt t&aacute;c giữa thi&ecirc;n nhi&ecirc;n, l&agrave; nơi cảnh quan đẹp như tranh h&ograve;a quyện với kh&ocirc;ng gian kiến tr&uacute;c c&oacute; một kh&ocirc;ng hai. Những kiến tr&uacute;c sư nổi tiếng h&agrave;ng đầu thế giới sẽ gi&uacute;p chủ nh&acirc;n biệt thự kiến tạo n&ecirc;n kiệt t&aacute;c c&oacute; một kh&ocirc;ng hai, mang dấu ấn, gi&aacute; trị sống thượng lưu ri&ecirc;ng c&oacute; từng gia chủ, thổi hồn v&agrave;o từng đường n&eacute;t thiết kế tất cả những g&igrave; tinh t&uacute;y nhất để mang đến kh&ocirc;ng gian sống ho&agrave;n hảo, tuyệt mỹ. Mỗi căn biệt thự sẽ trở th&agrave;nh &ldquo;Di sản c&aacute; nh&acirc;n&rdquo;, l&agrave; tuy&ecirc;n ng&ocirc;n về phong c&aacute;ch sống v&agrave; thẩm mỹ ri&ecirc;ng của gia chủ.</p>', '<p>Kh&ocirc;ng chỉ sở hữu kh&ocirc;ng gian sống &ldquo;độc bản giữa thi&ecirc;n nhi&ecirc;n&rdquo;, Ecopark Grand c&ograve;n thiết lập một cộng đồng cư d&acirc;n thượng lưu, th&agrave;nh đạt với những ti&ecirc;u chuẩn sống xứng tầm vị thế. Dự &aacute;n tạo dựng một hệ thống tiện &iacute;ch đặc quyền cho cuộc sống thượng lưu của cư d&acirc;n. C&aacute;c chủ nh&acirc;n <a href=\"https://maivietland.vn/du-an-bds/ecopark-grand-the-island.html\"><strong>biệt thự đảo The Island</strong></a> sẽ được nhận c&aacute;c dịch vụ v&agrave; tiện &iacute;ch với ti&ecirc;u chuẩn 5 sao cao nhất của Ecopark.</p>\r\n\r\n<p><em><strong>Đặc quyền đưa đ&oacute;n bằng du thuyền v&agrave; xe sang Maybach</strong></em></p>\r\n\r\n<p>Ecopark Grand trang bị sẵn du thuyền sang trọng v&agrave; si&ecirc;u xe Maybach chỉ để phục vụ nhu cầu đưa đ&oacute;n, di chuyển của c&aacute;c chủ nh&acirc;n biệt thự đảo. Đội ngũ nh&acirc;n vi&ecirc;n phục vụ chuy&ecirc;n nghiệp, được đ&agrave;o tạo khắt khe sẽ l&agrave;m h&agrave;i l&ograve;ng những chủ nh&acirc;n tinh tế nhất.</p>\r\n\r\n<p><em><strong>Hệ thống an ninh nhiều lớp</strong></em></p>\r\n\r\n<p>Khu biệt thự đảo The Island được đảm bảo an ninh với việc &aacute;p dụng đồng thời nhiều giải ph&aacute;p, nhiều lớp với c&ocirc;ng nghệ v&agrave; mật độ thiết bị cao. Lớp kiểm so&aacute;t an ninh v&ograve;ng ngo&agrave;i gồm: Hệ thống k&ecirc;nh cảnh quan với mặt hồ rộng từ 40 - 50m cho c&aacute;c đảo nhỏ v&agrave; 80 - 120m cho c&aacute;c đảo lớn; Chốt an ninh 2 đầu khu được trang bị hệ thống kiểm so&aacute;t ra v&agrave;o sử dụng 2 c&ocirc;ng nghệ song song; H&agrave;ng r&agrave;o an ninh ảo. Lớp thứ hai kiểm so&aacute;t an ninh nội khu bằng Hệ thống chu&ocirc;ng h&igrave;nh v&agrave; hệ thống camera nội bộ. Lớp thứ ba kiểm so&aacute;t an ninh b&ecirc;n trong biệt thự gồm c&oacute; c&aacute;c thiết bị: Hệ thống cảnh b&aacute;o đột nhập, N&uacute;t ấn khẩn cấp, Hệ thống b&aacute;o ch&aacute;y; Hệ thống cảnh b&aacute;o r&ograve; ga&hellip; Với 3 lớp an ninh nghi&ecirc;m ngặt, cư d&acirc;n biệt thự ho&agrave;n to&agrave;n c&oacute; thể an t&acirc;m tận hưởng cuộc sống an to&agrave;n, y&ecirc;n b&igrave;nh.</p>\r\n\r\n<p><em><strong>Dịch vụ Concierge cao cấp cho chủ nh&acirc;n với dịch vụ tận t&igrave;nh</strong></em></p>\r\n\r\n<p>Đội ngũ concierge chuy&ecirc;n nghiệp, tận t&acirc;m lu&ocirc;n phục vụ 24/7 để đ&aacute;p ứng mọi nhu cầu của những chủ nh&acirc;n t&ocirc;n qu&yacute;. Từ việc t&igrave;m kiếm một dịch vụ di chuyển ph&ugrave; hợp đến chiếc v&eacute; VIP cho những sự kiện đỉnh cao&hellip; đội ngũ concierge lu&ocirc;n sẵn s&agrave;ng mang đến cho kh&aacute;ch h&agrave;ng trải nghiệm tuyệt vời nhất.</p>\r\n\r\n<p><em><strong>Tổ hợp Clubhouse ti&ecirc;u chuẩn resort</strong></em></p>\r\n\r\n<p>Clubhouse Ecopark Grand với những tiện &iacute;ch đặc biệt d&agrave;nh ri&ecirc;ng cho cư d&acirc;n như ph&ograve;ng họp ri&ecirc;ng, thư viện với nhiều đầu s&aacute;ch gi&aacute; trị, trung t&acirc;m tổ chức sự kiện ho&agrave;nh tr&aacute;ng, lầu vọng thủy với tầm nh&igrave;n tuyệt đẹp v&agrave; Cigar&amp;Wine lounge với những bộ sưu tập rượu v&agrave; cigar thượng hạng.</p>\r\n\r\n<p><em><strong>Hệ thống gi&aacute;o dục, y tế vượt trội</strong></em></p>\r\n\r\n<p>L&agrave; 1 trong những dịch vụ tiện &iacute;ch nổi bật tại Th&agrave;nh phố Xanh Ecopark, Ph&ograve;ng kh&aacute;m An Việt Eco l&agrave; điểm đến tin cậy của cư d&acirc;n to&agrave;n Th&agrave;nh phố với nhiệm vụ chăm lo, bảo vệ sức khỏe nhằm mang lại nguồn sống Xanh cho tất cả kh&aacute;ch h&agrave;ng.</p>\r\n\r\n<p>Chủ đầu tư lu&ocirc;n ch&uacute; trọng hợp t&aacute;c c&ugrave;ng c&aacute;c t&ecirc;n tuổi lớn trong gi&aacute;o dục như trường PT Đo&agrave;n Thị Điểm, Ecopark c&ograve;n k&yacute; kết thỏa thuận ph&aacute;t triển gi&aacute;o dục với hệ thống gi&aacute;o dục CIEM Hoa Kỳ, hệ thống mầm non Fuji, Mầm non quốc tế Twinkle Town, Mầm non cao cấp Sunrise Kidz&hellip;</p>\r\n\r\n<p>Ngo&agrave;i ra, cư d&acirc;n sẽ được thỏa sức tận hưởng những trải nghiệm mua sắm bất tận tại trung t&acirc;m thương mại hiện đại, c&aacute;c hệ thống cửa h&agrave;ng tiện &iacute;ch, si&ecirc;u thị mini, gian h&agrave;ng thủ c&ocirc;ng mỹ nghệ hiện đại. Đặc biệt l&agrave; hệ thống rạp chiếu phim CGV lớn nhất cả nước, cư d&acirc;n sẽ kh&ocirc;ng bỏ lỡ bất kỳ một bộ phim &ldquo;bom tấn&rdquo; n&agrave;o.</p>', '<p style=\"text-align:justify\">Khi vừa cho ra mắt biệt thự mẫu Tiffany Topaz, kh&aacute;ch h&agrave;ng tham quan kh&ocirc;ng chỉ cho&aacute;ng ngợp bởi thiết kế sắc sảo, hiện đại, đẳng cấp của biệt thự m&agrave; c&ograve;n m&atilde;n nh&atilde;n với nội thất tinh tế, đ&aacute;ng gi&aacute; triệu đ&ocirc; của biệt thự.</p>\r\n\r\n<p style=\"text-align:justify\">To&agrave;n bộ nội thất của biệt thự đảo đều l&agrave; h&agrave;ng xa xỉ với tổng gi&aacute; trị l&ecirc;n tới h&agrave;ng triệu đ&ocirc; la từ những thương hiệu trứ danh được giới thượng lưu thế giới tin d&ugrave;ng.</p>\r\n\r\n<p style=\"text-align:justify\">Một trong những điểm nhấn đắt gi&aacute; l&agrave; bộ đ&egrave;n mang thương hiệu Lasvit được trưng b&agrave;y ngay tại ph&ograve;ng kh&aacute;ch của căn biệt thự. Đ&acirc;y l&agrave; bộ đ&egrave;n được c&aacute;c chuy&ecirc;n gia thiết kế ri&ecirc;ng, sau đ&oacute; được c&aacute;c nghệ nh&acirc;n thổi thủy tinh h&agrave;ng đầu của Tiệp Khắc chế t&aacute;c ho&agrave;n to&agrave;n thủ c&ocirc;ng, m&ocirc; phỏng theo h&igrave;nh d&aacute;ng v&agrave; m&agrave;u sắc đa dạng của gần 120 vi&ecirc;n ngọc trai thi&ecirc;n nhi&ecirc;n. C&oacute; thể n&oacute;i đ&acirc;y l&agrave; bộ đ&egrave;n c&oacute; một kh&ocirc;ng hai tại Việt Nam, l&agrave; một t&aacute;c phẩm nghệ thuật &ldquo;độc bản&rdquo; đắt gi&aacute; với gi&aacute; trị l&ecirc;n tới hơn 5 tỷ đồng.</p>\r\n\r\n<p style=\"text-align:justify\">Bộ sofa đặt tại trung t&acirc;m ph&ograve;ng kh&aacute;ch cũng c&oacute; gi&aacute; l&ecirc;n tới hơn 1 tỷ đồng, đến từ thương hiệu nội thất xa xỉ h&agrave;ng đầu thế giới Minotti (Italia). Với thiết kế cong độc đ&aacute;o, c&oacute; thể ngồi được từ hai hướng c&ugrave;ng m&agrave;u xanh thời thượng nhất hiện nay, bộ sofa Minotti vừa nổi bật m&agrave; vẫn h&agrave;i h&ograve;a với t&ocirc;ng n&acirc;u - ghi trầm của sắc đ&aacute; cẩm thạch trong căn biệt thự</p>\r\n\r\n<p style=\"text-align:justify\">Kh&ocirc;ng gian nh&agrave; bếp được chăm ch&uacute;t với đảo bếp l&agrave;m từ đ&aacute; cẩm thạch nguy&ecirc;n khối, được thiết kế v&agrave; nhập nguy&ecirc;n kiện từ thương hiệu Arcline, &Yacute;. B&agrave;n ăn thương hiệu Calligaris với ch&acirc;n k&iacute;nh v&agrave; mặt b&agrave;n gỗ nguy&ecirc;n tấm gi&uacute;p kh&ocirc;ng gian ph&ograve;ng ăn trở n&ecirc;n thanh tho&aacute;t lạ kỳ, kết hợp với tầm nh&igrave;n hướng ra mặt hồ lộng gi&oacute;, kiến tạo n&ecirc;n trải nghiệm sống thực sự đẳng cấp.</p>\r\n\r\n<p style=\"text-align:justify\">Kh&ocirc;ng gian thư gi&atilde;n như c&aacute;c ph&ograve;ng ngủ v&agrave; sinh hoạt chung sử dụng nội thất v&agrave; c&aacute;c chi tiết trang tr&iacute; m&agrave;u gỗ ấm &aacute;p, được c&acirc;n bằng bởi mức &aacute;nh s&aacute;ng tăng cường v&agrave; tầm nh&igrave;n cao đ&aacute;ng kể hướng ra mặt hồ v&agrave; khu s&acirc;n vườn mơn mởn sắc xanh.</p>\r\n\r\n<p style=\"text-align:justify\">C&aacute;c thiết bị vệ sinh cũng được lựa chọn từ những thương hiệu h&agrave;ng đầu của &Yacute; với chất liệu l&agrave;m từ đ&aacute; thi&ecirc;n nhi&ecirc;n nguy&ecirc;n khối, sứ cao cấp v&agrave; gỗ tự nhi&ecirc;n c&oacute; khả năng chịu nước.</p>\r\n\r\n<p style=\"text-align:justify\">Ecopark Grand - The Island đ&iacute;ch thực l&agrave; một c&ocirc;ng tr&igrave;nh ho&agrave;n hảo đến từng chi tiết, xa xỉ, đẳng cấp cho c&aacute;c chủ nh&acirc;n xứng tầm.&nbsp;</p>', NULL, NULL, NULL, NULL, 'Tổng quan biệt thự đảo Ecopark', 'Vị trí đắt giá của biệt thự đảo đẳng cấp nhất Ecopark', 'Mỗi căn biệt thự đều là 1 kiệt tác độc bản', 'Đặc quyền tận hưởng cuộc sống thượng lưu', 'Nội thất sang trọng triệu đô độc nhất Việt Nam', NULL, NULL, NULL, NULL, 'Dự Án biệt thự đảo Ecopark Grand - The Island', '12/27/2019', NULL, 'ecopark-grand-the-island', 'Biệt thự The Island là tuyệt phẩm đẳng cấp nhất khu đô thị Ecopark, mang trong mình sứ mệnh đại diện có những giá trị sống vượt trội, đẳng cấp nhất', NULL, 'index, follow'),
(128, '69', 49, 'Admin', 'Ruby City Hạ Long', 'Đường Trần Phú', 2685, 'ruby-city-ha-long1.jpg', NULL, 'false', NULL, 'true', '<ul>\r\n	<li style=\"text-align:justify\">T&ecirc;n dự &aacute;n: Ruby City Hạ Long</li>\r\n	<li style=\"text-align:justify\">Chủ đầu tư: C&ocirc;ng ty Cổ phần Đầu Tư X&acirc;y dựng v&agrave; Thương Mại T&acirc;n Th&agrave;nh</li>\r\n	<li style=\"text-align:justify\">Quy m&ocirc;: 9,8ha</li>\r\n	<li style=\"text-align:justify\">Mật độ x&acirc;y dựng: 26,5%</li>\r\n	<li style=\"text-align:justify\">Diện t&iacute;ch c&acirc;y xanh + Giao th&ocirc;ng: 62%</li>\r\n	<li style=\"text-align:justify\">Loại h&igrave;nh sản phẩm: Shophouse + Đất nền</li>\r\n	<li style=\"text-align:justify\">Số lượng sản phẩm: 268</li>\r\n	<li style=\"text-align:justify\">Diện t&iacute;ch l&ocirc; đất&rdquo; 58,5m2 - 75m2 - 93m2 - 99m2 - 120m2 - 165m2</li>\r\n	<li style=\"text-align:justify\">Chung cư: 2 t&ograve;a 5 tầng, 68 căn hộ</li>\r\n	<li style=\"text-align:justify\">C&aacute;c tuyến đường: 17,5m - 19m - 20,5m - 52,5m</li>\r\n	<li style=\"text-align:justify\">H&igrave;nh thức sở hữu: Sổ hồng l&acirc;u d&agrave;i</li>\r\n</ul>', 'Tổng quan', 'Vị trí', 'Quy hoạch', 'Tiện ích', 'Chủ đầu tư', 'Pháp lý', 'Tiến độ thanh toán', NULL, 'Chính sách', '<p style=\"text-align:justify\">Ruby City Hạ Long - Vi&ecirc;n hồng ngọc giữa l&ograve;ng th&agrave;nh phố Hạ Long xinh đẹp sắp được ra mắt, sẵn s&agrave;ng l&agrave;m n&ecirc;n một cơn sốt cho tất cả những ai đang t&igrave;m kiếm gi&aacute; trị sống đ&iacute;ch thực, một cuộc sống đẳng cấp v&agrave; một cơ hội đầu tư tiềm năng.</p>\r\n\r\n<p style=\"text-align:justify\">Nằm tại vị tr&iacute; đắc địa, trung t&acirc;m th&agrave;nh phố Hạ Long, Ruby City kiến tạo một m&ocirc;i trường sống đẳng cấp thực thụ, sang trọng, tiện nghi, đ&aacute;p ứng đầy đủ nhu cầu của cư d&acirc;n. Ch&iacute;nh v&igrave; vậy, dự &aacute;n được nhiều nh&agrave; đầu tư cũng như chuy&ecirc;n gia bất động sản săn đ&oacute;n bởi hội tụ đầy đủ c&aacute;c gi&aacute; trị của một bất động sản đầu tư sinh lời cao, l&acirc;u d&agrave;i. Mặt kh&aacute;c, dự &aacute;n nằm tr&ecirc;n <strong>khu đất nền c&ograve;n lại cuối c&ugrave;ng tại Hạ Long</strong>, sở hữu view biển tuyệt đẹp nhưng <strong>gi&aacute; lại rất hợp l&yacute;</strong>, <strong>thanh khoản tốt</strong> n&ecirc;n đ&acirc;y l&agrave; dự &aacute;n kh&ocirc;ng thể bỏ lỡ.</p>\r\n\r\n<p style=\"text-align:justify\">Với tốc độ ph&aacute;t triển hạ tầng v&agrave; kinh tế của Quảng Ninh n&oacute;i chung v&agrave; Hạ Long n&oacute;i ri&ecirc;ng, Ruby City Hạ Long được dự đo&aacute;n sẽ trở th&agrave;nh c&aacute;i t&ecirc;n cực hot tr&ecirc;n thị trường v&agrave; tạo n&ecirc;n l&agrave;n s&oacute;ng đầu tư mạnh mẽ trong năm 2020.</p>', '<p style=\"text-align:justify\">Dự &aacute;n tọa lạc tại mặt đường Trần Ph&uacute;, đ&acirc;y được coi l&agrave; vị tr&iacute; trung t&acirc;m ph&aacute;t triển được th&agrave;nh phố Hạ Long ch&uacute; trọng quy hoạch đầu tư, rất thuận lợi cho giao th&ocirc;ng v&agrave; thương mại.&nbsp;</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Ph&iacute;a Đ&ocirc;ng gi&aacute;p vườn treo dự &aacute;n</li>\r\n	<li style=\"text-align:justify\">Ph&iacute;a Bắc gi&aacute;p khu d&acirc;n cư hiện hữu</li>\r\n	<li style=\"text-align:justify\">Ph&iacute;a T&acirc;y gi&aacute;p dự &aacute;n FLC Tropical Hạ Long</li>\r\n	<li style=\"text-align:justify\">Ph&iacute;a Nam gi&aacute;p Khu d&acirc;n cư hiện hữu</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">Ngo&agrave;i ra, vị tr&iacute; của Ruby Hạ Long c&ograve;n rất vượng kh&iacute; khi lưng tựa n&uacute;i, mặt hướng vịnh Cửa Lục, l&agrave; một trong những yếu tố phong thủy tốt nhất, hội tụ ưu điểm vượt trội để trở th&agrave;nh v&ugrave;ng đất địa linh nh&acirc;n kiệt.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Trong quy hoạch ph&aacute;t triển sắp tới của th&agrave;nh phố Hạ Long, khi hai c&acirc;y cầu Cửa Lục 1 v&agrave; Cửa Lục 3 được ho&agrave;n th&agrave;nh, dự &aacute;n sẽ l&agrave; nơi l&yacute; tưởng nằm ngay điểm kết nối H&ograve;n Gai, B&atilde;i Ch&aacute;y qua Ho&agrave;nh Bồ, đ&acirc;y l&agrave; l&uacute;c m&agrave; gi&aacute; trị bất động sản tại đ&acirc;y sẽ tăng cao đột biển. Vậy n&ecirc;n, việc chọn đầu tư v&agrave;o dự &aacute;n ngay l&uacute;c n&agrave;y, khi gi&aacute; vẫn c&ograve;n hợp l&yacute;, kh&ocirc;ng qu&aacute; cao l&agrave; một quyết định s&aacute;ng suốt.</p>\r\n\r\n<p style=\"text-align:justify\">Hơn nữa, dự &aacute;n lại l&agrave; <strong>quỹ đất cuối c&ugrave;ng tại phường H&agrave; Kh&aacute;nh</strong> c&oacute; đầy đủ t&iacute;nh chất ph&aacute;p l&yacute; n&ecirc;n hứa hẹn sẽ l&agrave; khu đ&ocirc; thị ven biển thượng lưu, c&oacute; tiềm năng ph&aacute;t triển l&acirc;u d&agrave;i.</p>\r\n\r\n<p style=\"text-align:justify\">Một trong những điều l&agrave;m n&ecirc;n sự hấp dẫn của dự &aacute;n l&agrave; việc dễ d&agrave;ng kết nối với những địa điểm du lịch - dịch vụ nổi tiếng của th&agrave;nh phố Hạ Long:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">FLC Tropical City: 500m</li>\r\n	<li style=\"text-align:justify\">Sun World Hạ Long: 7km</li>\r\n	<li style=\"text-align:justify\">FLC Hạ Long: 5km</li>\r\n	<li style=\"text-align:justify\">Cầu B&atilde;i Ch&aacute;y: 6km</li>\r\n	<li style=\"text-align:justify\">Hệ thống trường mầm non: 1,5km</li>\r\n	<li style=\"text-align:justify\">Cao tốc H&agrave; Nội - Hạ Long: 5,5km</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"vị trí ruby city hạ long\" src=\"/public/upload/images/ruby-city-halong/vitri.jpg\" /></p>', '<p style=\"text-align:justify\">Với tổng diện t&iacute;ch 9,8ha, Ruby City c&oacute; tất cả 268 l&ocirc; đất nền, Shophouse sẽ b&agrave;n giao th&ocirc;, 2 t&ograve;a chung cư 5 tầng v&agrave; c&aacute;c tiện &iacute;ch đầy đủ phục vụ cho cư d&acirc;n như: phố đi bộ, khu c&ocirc;ng vi&ecirc;n, khu vui chơi giải tr&iacute;, club house&hellip; với mục đ&iacute;ch tạo một kh&ocirc;ng gian sống thoải m&aacute;i, một nơi an cư ho&agrave;n hảo cho mọi cư d&acirc;n sinh sống tại đ&acirc;y.</p>\r\n\r\n<ul>\r\n	<li>Diện t&iacute;ch đất chung cư: 4,781.2m2</li>\r\n	<li>Đất nền nh&agrave; liền kề: 28,431.9m2</li>\r\n	<li>Đất c&ocirc;ng tr&igrave;nh nh&agrave; văn h&oacute;a: 985.2m2</li>\r\n	<li>Đất c&ocirc;ng tr&igrave;nh nh&agrave; trẻ: 3,064.9m2</li>\r\n	<li>Đất x&acirc;y dựng giao th&ocirc;ng, vỉa h&egrave; v&agrave; hộp kỹ thuật: 60,832.2m2<img alt=\"mặt bàng\" src=\"/public/upload/images/ruby-city-halong/matbang.jpg\" /></li>\r\n</ul>', '<p style=\"text-align:justify\">Với mục đ&iacute;ch tạo lập một cuộc sống đẳng cấp, tiện nghi cho cư d&acirc;n, dự &aacute;n c&oacute; đầy đủ c&aacute;c tiện &iacute;ch nội - ngoại khu được đồng bộ một c&aacute;ch ho&agrave;n hảo, gi&uacute;p cư d&acirc;n tận hưởng cuộc sống thượng lưu, văn m&igrave;nh, hiện đại.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Sống tại Ruby Hạ Long, cư d&acirc;n sẽ được tận hưởng kh&ocirc;ng gian sống trong l&agrave;nh với vườn treo babylon, đường dạo bộ xanh m&aacute;t, vườn hoa bốn m&ugrave;a lu&ocirc;n khoe sắc, l&agrave; nơi l&yacute; tưởng để cư d&acirc;n nghỉ ngơi, thư gi&atilde;n, tận hưởng nguồn kh&iacute; trời sau những giờ l&agrave;m việc căng thẳng. Phố quảng trường trung t&acirc;m sầm uất sẽ l&agrave; nơi tập trung đ&ocirc;ng đ&uacute;c v&agrave; diễn ra nhiều sự kiện, lễ hội nội khu. V&agrave; đặc biệt một phần kh&ocirc;ng thể thiếu l&agrave; trung t&acirc;m mua sắm, với những shophouse cao cấp c&oacute; thể đ&aacute;p ứng được tất cả nhu cầu của cư d&acirc;n, c&ugrave;ng Clubhouse quy m&ocirc;, ho&agrave;nh tr&aacute;ng, gi&uacute;p cho cư d&acirc;n c&oacute; th&ecirc;m lựa chọn giải tr&iacute; thư gi&atilde;n cuối tuần.</p>\r\n\r\n<p style=\"text-align:justify\">B&ecirc;n cạnh đ&oacute;, hệ thống c&aacute;c c&ocirc;ng tr&igrave;nh trường học, khu thể thao ngo&agrave;i trời, b&atilde;i đỗ xe, khu vui chơi d&agrave;nh cho trẻ em, khu ẩm thực, hồ bơi, ph&ograve;ng gym, spa&hellip; sẽ đ&aacute;p ứng mọi nhu cầu tận hưởng của cư d&acirc;n.</p>\r\n\r\n<p style=\"text-align:justify\">Với tất cả những tiện &iacute;ch đ&oacute;, Ruby City Hạ Long ch&iacute;nh l&agrave; một th&agrave;nh phố thu nhỏ mang đến cho cư d&acirc;n tương lai những trải nghiệm trọn vẹn nhất về một cuộc sống đầy đủ v&agrave; mang lại gi&aacute; trị sống thượng lưu đ&iacute;ch thực.</p>', '<p><strong>C&ocirc;ng ty Cổ phần Đầu tư, X&acirc;y dựng v&agrave; Thương Mại T&acirc;n Th&agrave;nh </strong>l&agrave; một trong những c&ocirc;ng ty c&oacute; uy t&iacute;n tr&ecirc;n cả nước, được th&agrave;nh lập bởi c&aacute;c đại cổ đ&ocirc;ng c&oacute; tiềm lực t&agrave;i ch&iacute;nh vững mạnh v&agrave; d&agrave;y dạn kinh nghiệm, c&oacute; th&acirc;m ni&ecirc;n trong đầu tư bất động sản v&agrave; ph&aacute;t triển cơ sở hạ tầng c&aacute;c khu đ&ocirc; thị mới, T&acirc;n Th&agrave;nh kh&ocirc;ng c&ograve;n l&agrave; c&aacute;i t&ecirc;n xa lạ trong giới bất động sản.</p>\r\n\r\n<p style=\"text-align:justify\">Sau th&agrave;nh c&ocirc;ng vang dội của dự &aacute;n New City U&ocirc;ng B&iacute; v&agrave; dự &aacute;n khu nh&agrave; ở Y&ecirc;n Thanh, Ruby City Hạ Long sẽ l&agrave; c&aacute;i t&ecirc;n tiếp theo sẽ c&oacute; được tiềm năng ph&aacute;t triển vượt bậc, đảm bảo t&iacute;nh hiện đại, đồng bộ dưới sự tự vấn thiết kế v&agrave; thi c&ocirc;ng của c&aacute;c đơn vị tư vấn, nh&agrave; thầu c&oacute; uy t&iacute;n trong v&agrave; ngo&agrave;i nước.</p>', '<p style=\"text-align:justify\">Trong l&agrave;n s&oacute;ng c&aacute;c dự &aacute;n tr&ecirc;n thị trường ph&aacute;p l&yacute; lủng củng, kh&ocirc;ng ho&agrave;n thiện, kh&ocirc;ng ra được dự &aacute;n th&igrave; dự &aacute;n Ruby City Hạ Long đ&atilde; c&oacute; quyết định chủ trương đầu tư 1/500, chấp thuận chủ trương đầu tư v&agrave; quyết định b&agrave;n giao đất, đ&oacute;ng thuế đất đầy đủ.</p>\r\n\r\n<p style=\"text-align:justify\">Theo kế hoạch, cuối th&aacute;ng <strong>3/2020</strong>, dự &aacute;n sẽ ra sổ đỏ cho <strong>148 l&ocirc;</strong>.</p>\r\n\r\n<p style=\"text-align:justify\">T&iacute;nh ph&aacute;p l&yacute; an to&agrave;n, minh bạch l&agrave; cơ sở để dự &aacute;n c&oacute; sức hấp lực lớn đối với kh&aacute;ch h&agrave;ng, nh&agrave; đầu tư v&agrave; cũng l&agrave; bảo chứng cho sự ph&aacute;t triển l&acirc;u bền, sinh lời lớn của dự &aacute;n.</p>', '<p style=\"text-align:center\"><img alt=\"Tiến độ thanh toán Ruby City Hạ Long\" src=\"/public/upload/images/ruby-city-halong/thanh-toan.JPG\" /></p>', NULL, '<p>ĐANG CẬP NHẬT!!!</p>', 'TỔNG QUAN DỰ ÁN RUBY CITY HẠ LONG', 'VỊ TRÍ TRUNG TÂM - VÔ CÙNG TIỀM NĂNG CỦA RUBY CITY', 'QUY HOẠCH DỰ ÁN RUBY HẠ LONG', 'TIỆN ÍCH ĐA DẠNG, ĐẲNG CẤP THƯỢNG LƯU CỦA RUBY CITY', 'CHỦ ĐẦU TƯ UY TÍN CỦA RUBY CITY HẠ LONG', 'PHÁP LÝ HOÀN THIỆN - AN TÂM ĐẦU TƯ RUBY CITY', 'TIẾN ĐỘ THANH TOÁN RUBY HẠ LONG LINH HOẠT', NULL, NULL, 'Ruby City Hạ Long - Suất Mua Ngoại Giao Uy Tín Từ CĐT', '02/07/2020', NULL, 'ruby-city-ha-long', 'Ruby City Hạ Long - Viên hồng ngọc giữa lòng thành phố Hạ Long xinh đẹp sắp được ra mắt. Nằm tại vị trí đắc địa, trung tâm thành phố Hạ Long, Ruby City kiến tạo môi trờng', NULL, 'index, follow'),
(129, '69', 49, 'Admin', 'Citadines Marina Hạ Long', 'Bán đảo 3 Khu đô thị Hạ Long Marina', 1802, 'citadines-marina-halong2.jpg', NULL, 'false', NULL, 'true', '<ul>\r\n	<li>T&ecirc;n dự &aacute;n: Citadines Marina Hạ Long</li>\r\n	<li>Vị tr&iacute;: B&aacute;n đảo 3, Hạ Long Marina, H&ugrave;ng Thắng, B&atilde;i Ch&aacute;y, Quảng Ninh</li>\r\n	<li>Tổng thể dự &aacute;n bao gồm: 2 th&aacute;p A, th&aacute;p B v&agrave; 2 tầng hầm</li>\r\n	<li>Tổng diện t&iacute;ch: 5.287m2</li>\r\n	<li>Tổng số căn hộ: 762 căn75\r\n	<ul>\r\n		<li>345 căn Studio</li>\r\n		<li>292 căn 1 ph&ograve;ng ngủ</li>\r\n		<li>75 căn 2 ph&ograve;ng ngủ (căn hộ chung cư)</li>\r\n		<li>50 căn 3 ph&ograve;ng ngủ (căn hộ chung cư)</li>\r\n	</ul>\r\n	</li>\r\n	<li>Tổng diện t&iacute;ch s&agrave;n x&acirc;y dựng: 75,452m2</li>\r\n	<li>H&igrave;nh thức sở hữu: Sổ hồng l&acirc;u d&agrave;i</li>\r\n</ul>', 'Đầu Tư', 'Tổng Quan', 'Vị Trí', 'Quy Mô', 'Tiện Ích', 'Mặt bằng', 'Tiến độ thanh toán', NULL, 'Chính sách', '<ol>\r\n	<li style=\"text-align:justify\">100% căn hộ hướng trọn view vịnh Hạ Long</li>\r\n	<li style=\"text-align:justify\">Dự &aacute;n Condotel thương hiệu quốc tế đầu ti&ecirc;n tại Hạ Long</li>\r\n	<li style=\"text-align:justify\">Quản l&yacute; bởi The Ascott - thương hiệu quản l&yacute; vận h&agrave;nh kh&aacute;ch sạn h&agrave;ng đầu</li>\r\n	<li style=\"text-align:justify\">Dự &aacute;n condotel ti&ecirc;n phong tại Miền Bắc</li>\r\n	<li style=\"text-align:justify\">Nằm trong tổ hợp du lịch đẳng cấp Hạ Long Marina</li>\r\n	<li style=\"text-align:justify\">Chủ đầu tư uy t&iacute;n, tiềm lực mạnh</li>\r\n	<li style=\"text-align:justify\">Tiềm năng du lịch hấp dẫn, tăng trưởng nhanh</li>\r\n</ol>\r\n\r\n<p style=\"text-align:justify\"><strong>TIỀM NĂNG TĂNG GI&Aacute; CONDOTEL CITADINES HẠ LONG</strong></p>\r\n\r\n<p style=\"text-align:justify\">Quảng Ninh đang ng&agrave;y c&agrave;ng ph&aacute;t triển, loại h&igrave;nh lưu tr&uacute; cao cấp c&ograve;n thiếu hụt, c&oacute; thể n&oacute;i chương tr&igrave;nh &ldquo;ủy th&aacute;c cho thu&ecirc;&rdquo; được &aacute;p dụng cho c&aacute;c căn Condotel Citadines mở ra một k&ecirc;nh đầu tư ổn định v&agrave; bền vững. Đặc biệt dự &aacute;n được thừa hưởng uy t&iacute;n v&agrave; nguồn kh&aacute;ch dồi d&agrave;o sẵn c&oacute; từ thương hiệu Citadines - The Ascott Limited n&ecirc;n tỷ lệ lấp đầy của dự &aacute;n khi đi v&agrave;o hoạt động chắc chắn sẽ rất khả quan.</p>\r\n\r\n<p style=\"text-align:justify\">B&ecirc;n cạnh đ&oacute;, chủ đầu tư BIM Group sở hữu nguồn lực mạnh về nh&acirc;n lực, năng lực t&agrave;i ch&iacute;nh cũng như kinh nghiệm s&acirc;u sắc trong lĩnh vực nghi&ecirc;n cứu, đầu tư v&agrave; x&acirc;y dựng bất động sản nghỉ dưỡng. Cho n&ecirc;n đ&oacute; l&agrave; &ldquo;chỗ dựa&rdquo; vững chắc cho nh&agrave; đầu tư về t&iacute;nh an to&agrave;n v&agrave; tiềm năng của dự &aacute;n.</p>', '<p style=\"text-align:justify\">Nằm trong khu đ&ocirc; thị Hạ Long Marina, Citadines l&agrave; dự &aacute;n chung cư v&agrave; condotel cao cấp của tập đo&agrave;n BIM Group. Dự &aacute;n c&oacute; vị tr&iacute; đắc địa bao trọn to&agrave;n cảnh vịnh Hạ Long, lại nằm tại trung t&acirc;m khu du lịch, giải tr&iacute;, nghỉ dưỡng quy m&ocirc; lớn với h&agrave;ng loạt c&ocirc;ng tr&igrave;nh dịch vụ tiện &iacute;ch trong b&aacute;n k&iacute;nh 2km.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Được thiết kế bởi Archetype - đơn vị thiết kế nổi tiếng đến từ Ph&aacute;p, c&aacute;c căn hộ của Citadines mang n&eacute;t kiến tr&uacute;c hiện đại, gu thẩm mỹ tinh tế, sang trọng c&ugrave;ng thiết kế nội thất đẳng cấp, dự &aacute;n hy vọng sẽ c&oacute; sức h&uacute;t lớn với c&aacute;c nh&agrave; đầu tư, kh&aacute;ch du lịch Việt Nam cũng như nước ngo&agrave;i.</p>\r\n\r\n<p style=\"text-align:justify\">Đứng sau kiến tạo n&ecirc;n Citadines l&agrave; chủ đầu tư BIM Group - một trong những tập đo&agrave;n đa ng&agrave;nh h&agrave;ng đầu Việt Nam v&agrave; cũng l&agrave; một &ldquo;&ocirc;ng lớn&rdquo; tr&ecirc;n thị trường bất động sản. Tập đo&agrave;n đ&atilde; th&agrave;nh c&ocirc;ng v&agrave; tạo tiếng vang với rất nhiều c&aacute;c dự &aacute;n trong quần thể Hạ Long Marina như Aqua City Hạ Long, Grand Bay Hạ Long&hellip; Với tiềm lực t&agrave;i ch&iacute;nh mạnh c&ugrave;ng với tầm nh&igrave;n chiến lược vững chắc, BIM Group chắc chắn sẽ tạo n&ecirc;n một Citadines Hạ Long ho&agrave;n mỹ, đẳng cấp v&agrave; tiềm năng, trở th&agrave;nh lựa chọn đầu tư s&aacute;ng suốt, th&ocirc;ng minh của mọi nh&agrave; đầu tư.</p>', '<p style=\"text-align:justify\">Vị tr&iacute; l&agrave; một trong những ưu thế lớn nhất tạo n&ecirc;n sức h&uacute;t của Citadines Marina. Dự &aacute;n nằm ở b&aacute;n đảo 3 của khu đ&ocirc; thị nghỉ dưỡng quy m&ocirc; đẳng cấp nhất Quảng Ninh - Hạ Long Marina, l&agrave; t&acirc;m điểm du lịch mới của th&agrave;nh phố đ&oacute;n đầu xu thế chuyển dịch từ khu H&ograve;n Gai sang B&atilde;i Ch&aacute;y.</p>\r\n\r\n<p style=\"text-align:justify\">Nằm tại vị tr&iacute; đắc địa, c&aacute;ch biển chưa đến 100m, hưởng trọn kh&ocirc;ng gian trong l&agrave;nh v&agrave; tầm nh&igrave;n bao trọn cảnh biển trong xanh của vịnh Hạ Long - tuyệt t&aacute;c thi&ecirc;n nhi&ecirc;n của thế giới. L&agrave; nơi l&yacute; tưởng để cư d&acirc;n tận hưởng nguồn kh&iacute; trong l&agrave;nh, m&aacute;t lạnh v&agrave;o mỗi buổi s&aacute;ng v&agrave; sự b&igrave;nh y&ecirc;n, l&atilde;ng mạn dưới &aacute;nh ho&agrave;ng h&ocirc;n tr&ecirc;n biển buổi chiều. Tất cả hội tụ l&agrave;m n&ecirc;n những khoảnh khắc kh&oacute; qu&ecirc;n, những trải nghiệm nghỉ dưỡng tuyệt vời.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Xung quanh dự &aacute;n l&agrave; chuỗi c&aacute;c dự &aacute;n giải tr&iacute;, nghỉ dưỡng cao cấp đa dạng. To&agrave;n bộ quần thể Hạ Long Marina sẽ l&agrave; trung t&acirc;m du lịch mới của th&agrave;nh phố, hội tụ những thương hiệu h&agrave;ng đầu Việt Nam v&agrave; thế giới, trang bị cơ sở đồng bộ cho kỳ nghỉ đ&aacute;ng nhớ của cả gia đ&igrave;nh:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Trung t&acirc;m thương mại Marine Plaza</li>\r\n	<li style=\"text-align:justify\">Khu vui chơi TiniWorld</li>\r\n	<li style=\"text-align:justify\">Chợ đ&ecirc;m</li>\r\n	<li style=\"text-align:justify\">Khu kh&aacute;ch sạn 4-5 sao</li>\r\n	<li style=\"text-align:justify\">C&ocirc;ng vi&ecirc;n Đại Dương</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">Ch&iacute;nh bởi vị tr&iacute; độc t&ocirc;n, đắc địa m&agrave; dự &aacute;n đ&atilde; nhận được sự quan t&acirc;m của nhiều nh&agrave; đầu tư cũng như kh&aacute;ch h&agrave;ng, l&agrave; sản phẩm đ&aacute;ng mong đợi trong năm 2020.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"vị trí citadines Hạ Long\" src=\"/public/upload/images/citadines-marina-halong/vitri-citadines-halong.jpg\" /></p>', '<p style=\"text-align:justify\">Dự &aacute;n c&oacute; tổng diện t&iacute;ch 5.287m2, quy m&ocirc; gồm 2 t&ograve;a th&aacute;p cao 30 tầng nổi v&agrave; 1 tầng hầm. Mật độ x&acirc;y dựng khối đế l&agrave; 65%, khối th&aacute;p l&agrave; 45%. Tổng diện t&iacute;ch s&agrave;n x&acirc;y dựng l&agrave; 78.123m2.</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Tầng 1 l&agrave; khu thương mại, nh&agrave; h&agrave;ng, sảnh lễ t&acirc;n, ph&ograve;ng họp</li>\r\n	<li style=\"text-align:justify\">Tầng 2 - 3 l&agrave; khu vực để xe</li>\r\n	<li style=\"text-align:justify\">Tầng 4 l&agrave; bể bơi ngo&agrave;i trời, ph&ograve;ng gym, spa v&agrave; khu sinh hoạt cộng đồng</li>\r\n	<li style=\"text-align:justify\">Tầng 5 trở l&ecirc;n l&agrave; c&aacute;c căn hộ (cao cấp v&agrave; dịch vụ kh&aacute;ch sạn)</li>\r\n	<li style=\"text-align:justify\">Tầng 30 l&agrave; tầng thượng, bao gồm rooftop skybar v&agrave; hồ bơi trong nh&agrave;</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">Citadines Marina Hạ Long c&oacute; 292 căn hộ cao cấp (condominium) với diện t&iacute;ch từ 30,47 - 194,3m2 gồm c&aacute;c căn hộ từ 1 - 3 ph&ograve;ng ngủ v&agrave; c&aacute;c căn studio c&ugrave;ng 580 căn hộ dịch vụ kh&aacute;ch sạn c&oacute; diện t&iacute;ch từ 30,47 - 115,44m2 bao gồm c&aacute;c căn studio v&agrave; căn 1 ph&ograve;ng ngủ. Ngo&agrave;i ra, dự &aacute;n c&ograve;n t&iacute;ch hợp c&aacute;c căn hộ dual key.</p>\r\n\r\n<p style=\"text-align:justify\">C&aacute;c căn hộ ch&igrave;a kh&oacute;a đ&ocirc;i (dual key) l&agrave; sự kết hợp giữa căn hộ th&ocirc;ng thường (căn hộ c&oacute; nhiều ph&ograve;ng ngủ, ph&ograve;ng kh&aacute;ch v&agrave; bếp) v&agrave; căn hộ một ph&ograve;ng ngủ. C&aacute;c căn hộ n&agrave;y c&oacute; chung cửa ra v&agrave;o ch&iacute;nh, sau đ&oacute; được chia th&agrave;nh 2 lối ri&ecirc;ng biệt - một dẫn tới ph&ograve;ng ch&iacute;nh, một dẫn tới ph&ograve;ng phụ nhỏ hơn.</p>\r\n\r\n<p style=\"text-align:justify\">C&aacute;c căn hộ studio rộng từ 30,47 - 43m2, c&ograve;n c&aacute;c căn hộ 1 ph&ograve;ng ngủ rộng từ 56,5 - 95,6m2.</p>\r\n\r\n<p style=\"text-align:justify\">Loại h&igrave;nh căn hộ cao cấp gồm căn hộ 1 ph&ograve;ng ngủ rộng từ 66,1 - 68,4m2, căn hộ 2 ph&ograve;ng ngủ rộng từ 73,8 - 114m2, căn hộ 3 ph&ograve;ng ngủ rộng từ 150 - 157m2 v&agrave; căn hộ studio rộng từ 35,6 - 42,6m2.</p>', '<p style=\"text-align:justify\">Với mục đ&iacute;ch mang đến cuộc sống nghỉ dưỡng đẳng cấp cho cư d&acirc;n, dự &aacute;n được trang bị c&aacute;c tiện &iacute;ch nội khu đa dạng, tiện nghi.</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Tiện &iacute;ch khu&ocirc;n vi&ecirc;n - Cảnh quan mặt đất: Thảm thực vật xanh, đường đ dạo ven 2 t&ograve;a th&aacute;p, mật độ cảnh quan 37%</li>\r\n	<li style=\"text-align:justify\">Tầng&nbsp; 1 t&ograve;a th&aacute;p: tổ hợp tiện &iacute;ch dịch vụ thương mại: TTT b&aacute;n lẻ, nh&agrave; h&agrave;ng, ph&ograve;ng họp, khu vực bếp, lounge&hellip;</li>\r\n	<li style=\"text-align:justify\">Tầng 2 v&agrave; 3: b&atilde;i để xe &ocirc; t&ocirc;</li>\r\n	<li style=\"text-align:justify\">Tầng 4: Bể bơi c&aacute;ch điệu ngo&agrave;i trời, view ra vịnh Hạ Long; Ph&ograve;ng Gym, Spa, Khu sinh hoạt cộng đồng, dịch vụ giao vận nội khu; Nh&agrave; h&agrave;ng view biển sang trọng cao cấp.</li>\r\n	<li style=\"text-align:justify\">Tầng 30:&nbsp;</li>\r\n	<li style=\"text-align:justify\">Rooftop Skybar: Tổ hợp bar club thời thượng tr&ecirc;n độ cao gần 100m, m&atilde;n nh&atilde;n với cảnh quan to&agrave;n diện view ra vịnh biển Hạ Long, nơi l&yacute; tưởng để tổ chức những buổi dạ tiệc sang trọng.</li>\r\n	<li style=\"text-align:justify\">Rooftop Swimming Pool: Bể bơi tr&ecirc;n m&acirc;y kế b&ecirc;n Sky bar, tạo cảm gi&aacute;c mới lạ v&agrave; đẳng cấp.</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">Ngo&agrave;i ra, Citadines nằm trong quần thể khu đ&ocirc; thị Hạ Long Marina n&ecirc;n được thừa hưởng to&agrave;n bộ hệ thống tiện &iacute;ch chung như tổ hợp thương mại v&agrave; giải tr&iacute; Marine Plaza, Rạp chiếu phim CGV v&agrave; Khu vui chơi trẻ em, Quảng trường biển v&agrave; b&atilde;i biển Marina Bay, c&ocirc;ng vi&ecirc;n Sun World.... Tất cả đều mang đến những trải nghiệm nghỉ dưỡng trọn vẹn cho cư d&acirc;n.</p>', NULL, '<p style=\"text-align:center\"><img alt=\"Tiến độ thanh toán citadines\" src=\"/public/upload/images/citadines-marina-halong/citadine-thanhtoan-vaynh.JPG\" style=\"height:425px; width:705px\" /></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Tiến độ thanh toán Citadines\" src=\"/public/upload/images/citadines-marina-halong/citadine-thanhtoan-kovay.JPG\" style=\"height:330px; width:702px\" /></p>', NULL, '<p style=\"text-align:justify\">Hỗ trợ l&atilde;i suất 0% trong 24 th&aacute;ng l&ecirc;n tới 70% gi&aacute; trị căn hộ</p>\r\n\r\n<p style=\"text-align:justify\">Chia sẻ lợi nhuận 85% - 15% từ th&aacute;ng thứ 19 (sau khi nhận nh&agrave;)</p>\r\n\r\n<p style=\"text-align:justify\">Qu&agrave; tặng kh&aacute;ch h&agrave;ng:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">C&aacute;c căn hộ 1 v&agrave; 2 ph&ograve;ng ngủ: 50.000.000 VNĐ</li>\r\n	<li style=\"text-align:justify\">C&aacute;c căn hộ Studio: 20.000.000 VNĐ</li>\r\n	<li style=\"text-align:justify\">Bốc thăm tr&uacute;ng thưởng cho kh&aacute;ch h&agrave;ng: V&agrave;ng 9999, Xe &ocirc; t&ocirc; Mazda CX5, Xe m&aacute;y SH, tổng gi&aacute; trị giải thưởng l&ecirc;n tới 4 tỷ VNĐ.</li>\r\n</ul>', 'VÌ SAO NÊN ĐẦU TƯ CITADINES HẠ LONG', 'TỔNG QUAN DỰ ÁN CITADINES MARINA HẠ LONG', 'CITADINES  HẠ LONG CÓ LỢI THẾ LỚN VỀ VỊ TRÍ', 'QUY MÔ CĂN HỘ CITADINES MARINA HẠ LONG', 'TIỆN ÍCH 5 SAO THỜI THƯỢNG CỦA CITADINES MARINA', 'Mặt bằng dự án Citadines Marina Hạ Long', 'TIẾN ĐỘ THANH TOÁN LINH HOẠT', NULL, 'CHÍNH SÁCH BÁN HÀNG', 'Citadines Marina Hạ Long - Thông Tin Chính Thức CĐT', '02/13/2020', NULL, 'citadines-marina-ha-long', 'Citadines là dự án chung cư và condotel cao cấp của tập đoàn BIM Group. Nằm trong khu đô thị Hạ Long Marina. Được thiết kế bởi Archetype - đơn vị thiết kế đến từ Pháp', NULL, 'index, follow');
INSERT INTO `tbl_product` (`id`, `cat_id`, `dis_id`, `user`, `name`, `address`, `hits`, `img`, `price`, `hot`, `tag`, `status`, `detail`, `tab1`, `tab2`, `tab3`, `tab4`, `tab5`, `tab6`, `tab7`, `tab8`, `tab9`, `content1`, `content2`, `content3`, `content4`, `content5`, `content6`, `content7`, `content8`, `content9`, `heading1`, `heading2`, `heading3`, `heading4`, `heading5`, `heading6`, `heading7`, `heading8`, `heading9`, `title`, `date`, `date_up`, `slug`, `description`, `keywords`, `robot`) VALUES
(134, '69', 45, 'Admin', 'TNR Goldmark City', '136 Hồ Tùng Mậu', 987, 'gzkg_tnr-goldmark-city-ho-tung-mau.jpg', NULL, NULL, NULL, 'true', '<ul>\r\n	<li style=\"text-align:justify\">T&ecirc;n dự &aacute;n: TNR GOLDMARK CITY</li>\r\n	<li style=\"text-align:justify\">Chủ Đầu Tư: C&ocirc;ng ty TNHH TM - QC - XD - Địa ốc Việt H&acirc;n</li>\r\n	<li style=\"text-align:justify\">Đơn Vị Điều H&agrave;nh: C&ocirc;ng Ty CP BĐS TNR Holdings Việt Nam</li>\r\n	<li style=\"text-align:justify\">Tổng diện t&iacute;ch: 113.909m2</li>\r\n	<li style=\"text-align:justify\">Diện t&iacute;ch x&acirc;y dựng: 26.597m2</li>\r\n	<li style=\"text-align:justify\">Mật độ x&acirc;y dựng to&agrave;n khu: 23,3%</li>\r\n	<li style=\"text-align:justify\">Diện t&iacute;ch cho tiện &iacute;ch chung: 48.104m2</li>\r\n	<li style=\"text-align:justify\">Tổng số t&ograve;a nh&agrave;: 9 to&agrave;</li>\r\n	<li style=\"text-align:justify\">Chiều cao 40 tầng/ to&agrave;</li>\r\n	<li style=\"text-align:justify\">Tổng số căn hộ: ~ 5000 căn</li>\r\n	<li style=\"text-align:justify\">Số căn hộ/s&agrave;n : 11~17 Căn</li>\r\n	<li style=\"text-align:justify\">06 thang m&aacute;y tốc độ cao/ t&ograve;a</li>\r\n	<li style=\"text-align:justify\">Diện t&iacute;ch căn hộ : từ 74m2 đến 174m2</li>\r\n</ul>', 'Tổng quan', 'Tiêu chuẩn 5s', 'Vị trí', 'Tiện ích', 'Thiết kế', 'Thanh toán', NULL, NULL, 'Chính sách', '<p style=\"text-align:justify\">Cuộc sống ng&agrave;y c&agrave;ng t&acirc;n tiến, hiện đại n&ecirc;n ch&uacute;ng ta c&oacute; nhu cầu hưởng thụ cao về vật chất cũng như tinh thần. Bởi vậy, quan niệm chọn nh&agrave; ở cũng c&oacute; nhiều thay đổi. Kh&ocirc;ng chỉ đơn thuần l&agrave; nơi ở h&agrave;ng ng&agrave;y, nhiều người mong muốn nơi an cư của gia đ&igrave;nh phải ở một vị tr&iacute; thuận lợi, một kh&ocirc;ng gian sống tiện nghi, đặc biệt l&agrave; m&ocirc;i trường sống xanh đang được nhiều gia đ&igrave;nh đặt l&ecirc;n h&agrave;ng đầu. Nhưng rất &iacute;t dự &aacute;n nội đ&ocirc; H&agrave; Nội thỏa m&atilde;n được những y&ecirc;u cầu tr&ecirc;n. Ch&iacute;nh v&igrave; vậy, sự xuất hiện của Goldmark City đ&atilde; tạo n&ecirc;n cơn sốt tr&ecirc;n thị trường nh&agrave; ở.</p>\r\n\r\n<p style=\"text-align:justify\">Chung cư <a href=\"https://maivietland.vn/du-an-bds/tnr-goldmark-city.html\"><strong>TNR Goldmark City</strong></a> l&agrave; tổ hợp nh&agrave; ở, văn ph&ograve;ng v&agrave; dịch vụ cao cấp nằm tại cửa ng&otilde; ph&iacute;a T&acirc;y H&agrave; Nội với quy m&ocirc; l&ecirc;n tới 113.909m2. Dự &aacute;n kiến tạo n&ecirc;n m&ocirc;i trường sống <strong>XANH - KHỎE - ĐẲNG CẤP</strong> với những thế mạnh:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Vị tr&iacute; đắc địa, kết nối giao th&ocirc;ng ho&agrave;n hảo</li>\r\n	<li style=\"text-align:justify\">Thiết kế căn hộ th&ocirc;ng minh, đ&oacute;n tối đa gi&oacute; v&agrave; &aacute;nh s&aacute;ng tự nhi&ecirc;n&nbsp;</li>\r\n	<li style=\"text-align:justify\">Kh&ocirc;ng gian xanh với C&ocirc;ng vi&ecirc;n hồ 25ha v&agrave; hệ thống c&acirc;y xanh d&agrave;y đặc&nbsp;</li>\r\n	<li style=\"text-align:justify\">Tiện &iacute;ch nội khu đa dạng, chuẩn quốc tế</li>\r\n	<li style=\"text-align:justify\">Nơi hội tụ của cộng đồng tinh hoa trong v&agrave; ngo&agrave;i nước</li>\r\n</ul>', '<p>&ldquo;Sapphire - Định vị phong c&aacute;ch sống to&agrave;n cầu&rdquo; ch&iacute;nh l&agrave; l&yacute; tưởng m&agrave; chủ đầu tư muốn hướng tới khi x&acirc;y dựng dự &aacute;n. C&aacute;c t&ograve;a chung cư Sapphire kh&ocirc;ng chỉ l&agrave; chốn đi về, m&agrave; c&ograve;n l&agrave; nơi nghỉ dưỡng, nơi mang đến ti&ecirc;u chuẩn sống với những dịch vụ to&agrave;n cầu v&agrave; thể hiện được đẳng cấp của chủ nh&acirc;n.</p>\r\n\r\n<p><strong>Ti&ecirc;u chuẩn Service: Tận hưởng sống - Dịch vụ v&agrave;ng</strong></p>\r\n\r\n<p>+ Đơn vị quản l&yacute; cư d&acirc;n số 1 Việt Nam PMC , ti&ecirc;u chuẩn an to&agrave;n. Mọi dịch vụ cung cấp mang chất lượng ti&ecirc;u chuẩn quốc tế.</p>\r\n\r\n<p>+ C&acirc;u lạc bộ tiện &iacute;ch chuy&ecirc;n nghiệp, cung cấp chất lượng sống đẳng cấp cho cư d&acirc;n : C&acirc;u lạc bộ dưỡng sinh, Bơi, B&oacute;ng đ&aacute;, &Acirc;m nhạc, Hội họa&hellip;d&agrave;nh cho tất cả cư d&acirc;n GMC</p>\r\n\r\n<p><strong>Ti&ecirc;u chuẩn Safe: Sống an to&agrave;n - Chuẩn hạng sang</strong></p>\r\n\r\n<p>+ Số 1 ti&ecirc;u chuẩn an to&agrave;n từ căn hộ đến kh&ocirc;ng gian sống.Chốt chặn bảo vệ kiểm so&aacute;t chuy&ecirc;n nghiệp,an to&agrave;n tối đa cho cư d&acirc;n</p>\r\n\r\n<p>+ Hệ thống gi&aacute;m s&aacute;t an ninh tự động kiểm so&aacute;t 24/24. Hệ thống cảnh b&aacute;o ch&aacute;t, cảnh b&aacute;o sự cố, gi&aacute;m s&aacute;t an to&agrave;n đến từng tầng căn hộ 24/24</p>\r\n\r\n<p><strong>Ti&ecirc;u chuẩn Smart: Tiện &iacute;ch th&ocirc;ng minh đến từng căn hộ</strong></p>\r\n\r\n<p>+ Thiết kế th&ocirc;ng minh, c&aacute;c căn hộ ngập tr&agrave;n &aacute;nh s&aacute;ng v&agrave; kh&ocirc;ng kh&iacute; tự nhi&ecirc;n, trong l&agrave;nh</p>\r\n\r\n<p>+ Hệ thống cửa kinh Eurowindow chống nắng, ngăn chặn tia UV, tiết kiệm năng lượng đến mức tối đa</p>\r\n\r\n<p>+ Hệ thống nước sạch lọc trung t&acirc;m tự động, an to&agrave;n đến từng căn hộ</p>\r\n\r\n<p><strong>Ti&ecirc;u chuẩn Style: Phong c&aacute;ch sống to&agrave;n cầu</strong></p>\r\n\r\n<p>+ Tầm nh&igrave;n view Panorama to&agrave;n th&agrave;nh phố H&agrave; Nội</p>\r\n\r\n<p>+ Phong c&aacute;ch sống đa quốc gia với 15- 20% cư d&acirc;n l&agrave; người nước ngo&agrave;i (H&agrave;n Quốc , Nhật B&aacute;n &hellip;</p>\r\n\r\n<p><strong>Ti&ecirc;u chuẩn Saving: Sống bền vững - Thu&ecirc; sinh lời</strong></p>\r\n\r\n<p>+ Gi&aacute; trị sinh lời đầu tư cho thu&ecirc; v&agrave; dịch vị gi&aacute; trị gia tăng</p>\r\n\r\n<p>+ Đầu tư căn hộ dịch vụ cho thu&ecirc; chuy&ecirc;n gia sinh lời 12%/năm chỉ với căn hộ hơn 2 tỷ đồng</p>', '<p style=\"text-align:justify\"><strong><a href=\"https://maivietland.vn/du-an-bds/tnr-goldmark-city.html\">TNR Goldmark City</a></strong> tọa lạc tại 136 Hồ T&ugrave;ng Mậu - một trong những vị tr&iacute; đắc địa của thủ đ&ocirc; H&agrave; Nội, khu vực trung t&acirc;m h&agrave;nh ch&iacute;nh v&agrave; kinh tế năng động sầm uất nhất của th&agrave;nh phố trong tương lai. Từ vị tr&iacute; dự &aacute;n, chỉ mất v&agrave;i ph&uacute;t, cư d&acirc;n dễ d&agrave;ng tiếp cận c&aacute;c địa điểm tiện &iacute;ch:&nbsp;</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">&nbsp;Đại học Thương Mại - 0,3km</li>\r\n	<li style=\"text-align:justify\">&nbsp;Bệnh viện 198 - 0,9km</li>\r\n	<li style=\"text-align:justify\">&nbsp;Trường Đo&agrave;n Thị Điểm - 1,2km</li>\r\n	<li style=\"text-align:justify\">&nbsp;S&acirc;n vận động Mỹ Đ&igrave;nh - 1,5km</li>\r\n	<li style=\"text-align:justify\">&nbsp;Si&ecirc;u thị Fivimart - 1,5km</li>\r\n	<li style=\"text-align:justify\">&nbsp;Si&ecirc;u thị Metro - 2km</li>\r\n	<li style=\"text-align:justify\">&nbsp;Pico Plaza - 2km</li>\r\n	<li style=\"text-align:justify\">&nbsp;Đại học Quốc gia - 3.5km</li>\r\n	<li style=\"text-align:justify\">&nbsp;Đại học S&acirc;n khấu điện ảnh - 4km</li>\r\n	<li style=\"text-align:justify\">&nbsp;Hồ T&acirc;y - 4.5km</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">B&ecirc;n cạnh đ&oacute;, chung cư Goldmark c&ograve;n nằm ngay tr&ecirc;n mặt đường Nguyễn Cơ Thạch k&eacute;o d&agrave;i, đối diện khu c&ocirc;ng vi&ecirc;n v&agrave; hồ điều h&ograve;a gần 2ha đ&atilde; được quy hoạch. Một vị tr&iacute; đắc địa, một điểm cộng vượt trội cho dự &aacute;n.</p>', '<p style=\"text-align:justify\">Chung cư <a href=\"https://maivietland.vn/du-an-bds/tnr-goldmark-city.html\"><strong>Goldmark City</strong></a> mang đến cho cư d&acirc;n một cuộc sống hưởng thụ thực sự với hệ tiện &iacute;ch nội khu đa dạng, đ&aacute;p ứng mọi nhu cầu của cư d&acirc;n</p>\r\n\r\n<p style=\"text-align:justify\">MẬT ĐỘ X&Acirc;Y DỰNG THẤP - chỉ 23,3% mang đến cuộc sống xanh, an l&agrave;nh cho mọi cư d&acirc;n, tr&aacute;nh xa sự ồn &agrave;o, &ocirc; nhiễm</p>\r\n\r\n<p style=\"text-align:justify\">C&Ocirc;NG VI&Ecirc;N 25ha trước dự &aacute;n v&agrave; 06 HỒ CẢNH QUAN xung quanh dự &aacute;n gi&uacute;p điều h&ograve;a kh&ocirc;ng kh&iacute;, giảm tối đa c&aacute;c t&aacute;c động của &ocirc; nhiễm m&ocirc;i trường cho cư d&acirc;n</p>\r\n\r\n<p style=\"text-align:justify\">4 QUẢNG TRƯỜNG NỘI KHU RỘNG GẦN 2ha l&agrave; kh&ocirc;ng gian rộng r&atilde;i để cư d&acirc;n c&oacute; thể dạo bộ, trẻ em vui chơi gắn kết ngay trong khu&ocirc;n vi&ecirc;n nh&agrave; m&igrave;nh</p>\r\n\r\n<p style=\"text-align:justify\">BỂ BƠI TRONG NH&Agrave; V&Agrave; NGO&Agrave;I TRỜI - gi&uacute;p cư d&acirc;n thư gi&atilde;n, giải tỏa căng thẳng sau những giờ l&agrave;m việc</p>\r\n\r\n<p style=\"text-align:justify\">SKY GARDEN tr&ecirc;n tầng 41 l&agrave; nơi thư gi&atilde;n, thưởng ngoạn cho cư d&acirc;n được thả m&igrave;nh v&agrave;o thi&ecirc;n nhi&ecirc;n, đất trời.</p>\r\n\r\n<p style=\"text-align:justify\">HỆ THỐNG AN NINH kh&eacute;p k&iacute;n nghi&ecirc;m ngặt gi&uacute;p gia đ&igrave;nh bạn ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m. Hệ thống quản l&yacute; t&ograve;a nh&agrave; th&ocirc;ng minh, hệ thống ph&ograve;ng ch&aacute;y chữa ch&aacute;y tự động.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">TRUNG T&Acirc;M THƯƠNG MẠI dưới khối đế c&aacute;c t&ograve;a nh&agrave; sẽ cung cấp đầy đủ v&agrave; phong ph&uacute; c&aacute;c dịch vụ tiện &iacute;ch phục vụ cho nhu cầu đa dạng của cư d&acirc;n.</p>\r\n\r\n<p style=\"text-align:justify\">KHU VUI CHƠI TRẺ EM nơi c&aacute;c b&eacute; c&ugrave;ng bố mẹ thỏa sức vui chơi, l&agrave; nơi gắn kết t&igrave;nh cảm giữa c&aacute;c th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh.</p>\r\n\r\n<p style=\"text-align:justify\">KHU NH&Agrave; SINH HOẠT CỘNG ĐỒNG l&agrave; nơi để cư d&acirc;n gặp gỡ, giao lưu tăng t&iacute;nh đo&agrave;n kết, gắn b&oacute; cộng đồng.</p>', '<p style=\"text-align:justify\">Tất cả căn hộ Sapphire 2 đều được thiết kế th&ocirc;ng minh, đ&oacute;n tối đa gi&oacute; v&agrave; &aacute;nh s&aacute;ng tự nhi&ecirc;n mang đến những ph&uacute;t gi&acirc;y thư th&aacute;i, đảm bảo sức khỏe tốt nhất cho cư d&acirc;n.</p>\r\n\r\n<p style=\"text-align:justify\">Sở hữu vị tr&iacute; tuyệt đẹp với 2 hướng.&nbsp; Nếu như hướng ngoại khu đem đến cho t&ograve;a Sapphire 2 tầm nh&igrave;n rộng mở của s&ocirc;ng nước v&agrave; m&acirc;y trời th&igrave; hướng nội khu lại đem đến những tiện &iacute;ch y&ecirc;n b&igrave;nh như: bể bơi, khu quảng trường rộng lớn với những t&aacute;n c&acirc;y xanh mướt c&ugrave;ng kh&ocirc;ng gian trong l&agrave;nh, thư gi&atilde;n.</p>\r\n\r\n<p style=\"text-align:justify\">+ 100% to&agrave; nh&agrave; khu Sapphire đều sử dụng hệ cửa sổ Eurowindow với c&ocirc;ng nghệ mới nhất kh&ocirc;ng chỉ giảm thiểu tối đa tiếng ồn m&agrave; c&ograve;n chống n&oacute;ng tốt gi&uacute;p tiết kiệm điện năng cho chủ nh&agrave;.</p>\r\n\r\n<p style=\"text-align:justify\">+ Hệ thống điện ba chấu được thiết kế th&ocirc;ng minh với c&aacute;c thiết bị điện (đường d&acirc;y, ổ cắm, c&ocirc;ng tắc&hellip;) c&oacute; thương hiệu uy t&iacute;n như: Cadivi; Panasonic&hellip;</p>\r\n\r\n<p style=\"text-align:justify\">+ Hệ thống s&agrave;n gỗ chịu ẩm Newsky&hellip; hạn chế tối đa sự sự cong, v&ecirc;nh do thời tiết tại miền Bắc Việt Nam.</p>', '<p style=\"text-align:center\"><img alt=\"Tiến độ thanh toán TNR Goldmark city\" src=\"/public/upload/images/tnr-goldmark-city/t%C4%91tt%20goldmark.JPG\" style=\"height:393px; width:780px\" /></p>', NULL, NULL, '<ul>\r\n	<li style=\"text-align:justify\">Chiết khấu tới 17.99% GTCH&nbsp;</li>\r\n	<li style=\"text-align:justify\">Thanh to&aacute;n 50% GTCH nhận nh&agrave; ở ngay</li>\r\n	<li style=\"text-align:justify\">Hỗ trợ 50% GTCH &ndash; l&atilde;i suất 0% - 36 th&aacute;ng</li>\r\n	<li style=\"text-align:justify\">Hỗ trợ 70% GTCH &ndash; l&atilde;i suất 0% - 24 th&aacute;ng</li>\r\n	<li style=\"text-align:justify\">Chương tr&igrave;nh bốc thăm tr&uacute;ng thưởng : 01 xe Mercedes C200 ( 1,4 tỷ )</li>\r\n</ul>', 'TỔNG QUAN DỰ ÁN GOLDMARK CITY', 'TIÊU CHUẨN 5S ĐẦU TIÊN TẠI VIỆT NAM CHỈ DUY NHẤT TẠI GOLDMARK CITY', 'VỊ TRÍ ĐỘC TÔN - KẾT NỐI THỊNH VƯỢNG CỦA GOLDMARK CITY', 'TIỆN ÍCH HOÀN HẢO - CHUẨN SỐNG HOÀNG GIA CỦA GOLDMARK CITY', 'THIẾT KẾ & MẶT BẰNG CĂN HỘ TÒA SAPPHIRE 2 GOLDMARK', 'TIẾN ĐỘ THANH TOÁN SAPPHIRE 2 GOLDMARK CITY', NULL, NULL, 'CHÍNH SÁCH BÁN HÀNG', 'Sapphire 2 Goldmark City - Quỹ Căn Đẹp + Thông Tin Mới Nhất Từ CĐT', '04/24/2020', NULL, 'tnr-goldmark-city', 'Chung cư Goldmark City là tổ hợp nhà ở, văn phòng và dịch vụ cao cấp nằm tại cửa ngõ phía Tây Hà Nội với quy mô lên tới 113.909m2', NULL, 'index, follow'),
(131, '69', 53, 'Admin', 'Green Dragon City Cẩm Phả', NULL, 1046, 'green-dragon-city5.jpg', NULL, NULL, NULL, 'true', '<ul>\r\n	<li style=\"text-align:justify\">T&ecirc;n dự &aacute;n: Green Dragon City</li>\r\n	<li style=\"text-align:justify\">Vị tr&iacute;: phường Cẩm Trung, Cẩm Th&agrave;nh &amp; Cẩm B&igrave;nh, th&agrave;nh phố Cẩm Phả, Quảng Ninh.</li>\r\n	<li style=\"text-align:justify\">Tổng diện t&iacute;ch 99,2ha</li>\r\n	<li style=\"text-align:justify\">Mật độ x&acirc;y dựng: 69,1%</li>\r\n	<li style=\"text-align:justify\">Loại h&igrave;nh: Biệt thự đơn lập, shophouse, liền kề</li>\r\n	<li style=\"text-align:justify\">H&igrave;nh thức sở hữu: Sổ đỏ l&acirc;u d&agrave;i</li>\r\n	<li style=\"text-align:justify\">Chủ đầu tư: C&ocirc;ng ty Cổ phần đầu tư ph&aacute;t triển c&ocirc;ng nghiệp TTP</li>\r\n</ul>', 'Lý do sở hữu', 'Tổng quan', 'Vị trí', 'Quy hoạch', 'Tiện ích', 'Tiến độ xây dựng', NULL, NULL, 'Chính sách', '<ol>\r\n	<li style=\"text-align:justify\">Vị tr&iacute; kim cương đẹp nhất Cẩm Phả</li>\r\n	<li style=\"text-align:justify\">Phong thủy bậc nhất: Tựa sơn tiếp hải (tựa n&uacute;i, gi&aacute;p vịnh B&aacute;i Tử Long), thế đất &ldquo;Địa linh sinh vượng kh&iacute;&rdquo; c&aacute;t l&agrave;nh.</li>\r\n	<li style=\"text-align:justify\">Hạ tầng đồng bộ, ho&agrave;n chỉnh</li>\r\n	<li style=\"text-align:justify\">Kh&ocirc;ng gian sống CHUẨN XANH với 11,6 ha diện t&iacute;ch cho c&acirc;y xanh v&agrave; mặt nước</li>\r\n	<li style=\"text-align:justify\">Tiện &iacute;ch V&Agrave;NG cho cuộc sống CHUẨN SANG: hơn 50 tiện &iacute;ch nội khu v&agrave; v&ocirc; v&agrave;n tiện &iacute;ch ngoại khu l&acirc;n cận.</li>\r\n	<li style=\"text-align:justify\">Nghỉ dưỡng 5 sao trong ch&iacute;nh ng&ocirc;i nh&agrave; của bạn</li>\r\n	<li style=\"text-align:justify\">Đầu tư th&ocirc;ng minh - Sinh lời tối ưu</li>\r\n	<li style=\"text-align:justify\">Ph&aacute;p l&yacute; vững v&agrave;ng</li>\r\n	<li style=\"text-align:justify\">Ch&iacute;nh s&aacute;ch ưu đ&atilde;i hấp dẫn</li>\r\n	<li style=\"text-align:justify\">Chủ đầu tư uy t&iacute;n</li>\r\n</ol>', '<p style=\"text-align:justify\">Sở hữu vẻ đẹp hoang sơ, hấp dẫn, những b&atilde;i biển nguy&ecirc;n sơ, tuyệt đẹp, Cẩm Phả hiện đang l&agrave; địa điểm thu h&uacute;t kh&aacute;ch du lịch trong v&agrave; ngo&agrave;i nước, kh&ocirc;ng hề k&eacute;m cạnh so với kỳ quan Vịnh Hạ Long. Tuy nhi&ecirc;n, v&ugrave;ng &ldquo;đất Mỏ&rdquo; lấp l&aacute;nh &ldquo;v&agrave;ng đen&rdquo; vẫn c&ograve;n nhiều tiềm năng chưa được khai th&aacute;c, ẩn s&acirc;u trong từng mảng địa chất, từng h&ograve;n đảo, từng nhịp s&oacute;ng vỗ dập d&igrave;u h&uacute;t mắt. Người ta v&iacute; vẻ đẹp của Cẩm Phả như &ldquo;N&agrave;ng Ti&ecirc;n chưa được đ&aacute;nh thức&rdquo;, c&agrave;ng cảm nhận s&acirc;u c&agrave;ng kh&aacute;m ph&aacute; ra nhiều c&aacute;i hay, c&aacute;i đẹp của miền đất hứa gi&agrave;u tiềm năng n&agrave;y.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Giữa th&agrave;nh phố Cẩm Phả đang &ldquo;thay da đổi thịt&rdquo; từng ng&agrave;y, bao quanh l&agrave; vịnh biển trong xanh như ngọc, nơi dấu ấn rồng thi&ecirc;ng c&ograve;n lưu lại, một <strong>Đ&Ocirc; THỊ XANH ĐẲNG CẤP, Đ&Aacute;NG SỐNG BẬC NHẤT CẨM PHẢ</strong> sắp xuất hiện, nằm trải d&agrave;i tr&ecirc;n 3km ven vịnh B&aacute;i Tử Long &ldquo;nức tiếng&rdquo; Quảng Ninh. Đ&oacute; ch&iacute;nh l&agrave; <strong>GREEN DRAGON CITY.</strong></p>\r\n\r\n<p style=\"text-align:justify\">Green Dragon City kiến tạo n&ecirc;n một t&acirc;m điểm sống mới với kh&aacute;i niệm <strong>&ldquo;SỐNG CHUẨN XANH&rdquo;</strong>. Tại đ&acirc;y sẽ mở ra ra kh&ocirc;ng gian sống ho&agrave;n hảo, dẫn lối giao thương v&agrave; thắp lửa thịnh vượng cho cộng đồng cư d&acirc;n tinh hoa.</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Nơi phố phường s&ocirc;i động l&agrave; cận kề Vịnh ngọc</li>\r\n	<li style=\"text-align:justify\">Nơi liền kề với nh&agrave; l&agrave; hoa l&aacute; reo ca, m&acirc;y nước giao h&ograve;a</li>\r\n	<li style=\"text-align:justify\">Nơi chỉ một bước ch&acirc;n l&agrave; chạm tay tới đại dương xanh m&aacute;t</li>\r\n	<li style=\"text-align:justify\">Nơi mỗi ng&agrave;y l&agrave; an nhi&ecirc;n vui sống giữa th&agrave;nh phố Green Dragon</li>\r\n</ul>', '<p style=\"text-align:justify\">Dự &aacute;n trải dọc 3km bờ biển Vịnh B&aacute;i Tử Long thuộc địa phận 3 phường Cẩm Trung, Cẩm Th&agrave;nh, Cẩm B&igrave;nh, TP. Cẩm Phả, tỉnh Quảng Ninh. Đ&acirc;y l&agrave; vị tr&iacute; &ldquo;Đầu Rồng&rdquo; chiến lược ph&aacute;t triển dọc bờ biển Quảng Ninh, nơi khai th&aacute;c tiềm năng c&oacute; một kh&ocirc;ng hai, v&ocirc; c&ugrave;ng đắc địa của th&agrave;nh phố Cẩm Phả.</p>\r\n\r\n<p style=\"text-align:justify\">Đặc biệt dự &aacute;n nằm tr&ecirc;n thế đất &ldquo;tựa sơn nghinh hải&rdquo;, mặt tiền hướng biển, lưng tựa n&uacute;i vững chắc, đ&acirc;y l&agrave; thế phong thủy bậc nhất, sinh vượng kh&iacute;, c&aacute;t l&agrave;nh cho những ai sinh sống tại đ&acirc;y. C&oacute; thể n&oacute;i đ&acirc;y l&agrave; v&ugrave;ng đất địa linh, l&agrave; nơi an cư b&igrave;nh y&ecirc;n, thịnh vượng m&agrave; bất cứ ai cũng mơ ước.</p>\r\n\r\n<p>Tại Green Dragon City cư d&acirc;n sinh sống tận hưởng hạ tầng dịch vụ đồng bộ, ho&agrave;n chỉnh m&agrave; kh&ocirc;ng mất ph&iacute; dịch vụ:</p>\r\n\r\n<ul>\r\n	<li>Nhất cận thị (chợ bến Thủy)</li>\r\n	<li>Nhị cận hải (biển Đ&ocirc;ng &ndash; vịnh B&aacute;i Tử Long)</li>\r\n	<li>Tam cận lộ (QL18 v&agrave; bao biển Hạ Long &ndash; Cẩm Phả)</li>\r\n	<li>Tứ hội ngộ cận vi&ecirc;n (gi&aacute;p c&ocirc;ng vi&ecirc;n Bến Do)</li>\r\n	<li>Ngũ li&ecirc;n th&ocirc;ng cận gi&aacute;o (trường quốc tế li&ecirc;n cấp trong dự &aacute;n)</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">B&ecirc;n cạnh đ&oacute;, dự &aacute;n c&ograve;n sở hữu li&ecirc;n kết v&ugrave;ng ho&agrave;n hảo. Đ&acirc;y l&agrave; trung t&acirc;m mới của th&agrave;nh phố Cẩm Phả năng động. Tọa độ v&agrave;ng kh&ocirc;ng chỉ dễ d&agrave;ng kết nối tới c&aacute;c địa điểm quan trọng trong th&agrave;nh phố như: khu trung t&acirc;m h&agrave;nh ch&iacute;nh, bệnh viện, trường học, bến cảng, c&aacute;c địa danh du lịch&hellip; m&agrave; c&ograve;n rất thuận tiện khi giao thương với c&aacute;c khu vực sầm uất l&acirc;n cận như: V&acirc;n Đồn, Đ&ocirc;ng Triều, Hạ Long, U&ocirc;ng B&iacute;&hellip; Đặc biệt quốc lộ 18 kết nối chuỗi th&agrave;nh phố trọng yếu của tỉnh Quảng Ninh l&agrave; Hạ Long - Cẩm Phả - V&acirc;n Đồn - M&oacute;ng C&aacute;i.</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">S&acirc;n vận động Cẩm Phả: 800m</li>\r\n	<li style=\"text-align:justify\">Bệnh viện Đa Khoa Cẩm Phả: 1,5km</li>\r\n	<li style=\"text-align:justify\">Vịnh B&aacute;i Tử Long: Trực Diện</li>\r\n	<li style=\"text-align:justify\">Quốc Lộ 18: 100m</li>\r\n	<li style=\"text-align:justify\">Đường bao biển: 1km</li>\r\n	<li style=\"text-align:justify\">Vincom Cẩm Phả: Liền kề</li>\r\n</ul>\r\n\r\n<p>Đặc biệt hơn nữa, vị tr&iacute; tọa lạc của Green Dragon City tiếp gi&aacute;p c&aacute;c trục giao th&ocirc;ng huyết mạch, được coi l&agrave; 4 động lực kinh tế lớn của tỉnh Quảng Ninh l&agrave;:</p>\r\n\r\n<ul>\r\n	<li>Đường bao biển Hạ Long &ndash; V&acirc;n Đồn</li>\r\n	<li>Cảng h&agrave;ng kh&ocirc;ng Quốc tế V&acirc;n Đồn</li>\r\n	<li>Cao tốc Hạ Long &ndash; V&acirc;n Đồn</li>\r\n	<li>Đường cao tốc Nội B&agrave;i &ndash; Hạ Long &ndash; M&oacute;ng C&aacute;i</li>\r\n</ul>', '<p style=\"text-align:justify\">Dự &aacute;n c&oacute; thiết kế mặt bằng ưu ti&ecirc;n quỹ đất lớn d&agrave;nh cho giao th&ocirc;ng v&agrave; kh&ocirc;ng gian c&ocirc;ng cộng.</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Đất ở: 39,4ha (39,7%)</li>\r\n	<li style=\"text-align:justify\">Đất c&ocirc;ng tr&igrave;nh c&ocirc;ng cộng: 1,034ha (0,1%)</li>\r\n	<li style=\"text-align:justify\">Đất trường học: 1,01ha (2,2%)</li>\r\n	<li style=\"text-align:justify\">Đất c&ocirc;ng tr&igrave;nh TM- DV: 8,5ha (8,7%)</li>\r\n	<li style=\"text-align:justify\">Đất c&acirc;y xanh, quảng trường, mặt nước: 11,6ha (11,6%)</li>\r\n	<li style=\"text-align:justify\">Đất giao th&ocirc;ng: 37ha (37,4%)</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><strong>Ph&acirc;n khu 1: Green Harbour City - T&acirc;n cảng phồn vinh, khởi sinh thịnh vượng</strong></p>\r\n\r\n<p style=\"text-align:justify\">Đ&acirc;y l&agrave; giai đoạn 1 của dự &aacute;n gồm: 610 l&ocirc; shophouse, 122 liền kề, 141 biệt thự ven biển.</p>\r\n\r\n<p style=\"text-align:justify\">Green Harbour City được xem l&agrave; <strong>T&Acirc;N CẢNG PHỒN VINH</strong>, nơi Rồng phun ch&acirc;u nhả ngọc tạo n&ecirc;n v&ugrave;ng đất tr&ugrave; ph&uacute;, kết tinh ng&agrave;n đời để khởi sinh một cuộc sống thịnh vượng cho cư d&acirc;n. Kh&ocirc;ng chỉ l&agrave; một <strong>THƯƠNG CẢNG GIAO TH&Ocirc;NG SẦM UẤT</strong> m&agrave; c&ograve;n l&agrave; <strong>TRUNG T&Acirc;M PHỐ THỊ VUI CHƠI GIẢI TR&Iacute;</strong> thời thượng bậc nhất của th&agrave;nh phố Cẩm Phả. Đằng sau một Green Harbour city xa hoa, lộng lẫy l&agrave; cả một cuộc sống xanh tươi, trong l&agrave;nh v&agrave; an y&ecirc;n đang vẫy gọi mỗi ng&agrave;y.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Ph&acirc;n khu 2: Green Central Park&nbsp;</strong></p>\r\n\r\n<p style=\"text-align:justify\">Lấy cảm hứng về một miền thi&ecirc;n nhi&ecirc;n xanh m&aacute;t ngay giữa l&ograve;ng đ&ocirc; thị, khi sở hữu 17,7ha diện t&iacute;ch c&acirc;y xanh mặt nước. Nằm ngay cạnh hồ điều h&ograve;a rộng lớn của to&agrave;n dự &aacute;n, ph&acirc;n khu Green Central Park được xem l&agrave; th&agrave;nh phố c&ocirc;ng vi&ecirc;n, mang đến một cuộc sống xanh tươi, trọn vẹn v&agrave; đủ đầy cho cư d&acirc;n. Đan c&agrave;i giữa phố phường s&ocirc;i động l&agrave; thi&ecirc;n đường xanh của nước, của cỏ c&acirc;y hoa l&aacute; giao m&ugrave;a.</p>\r\n\r\n<p style=\"text-align:justify\">Tại đ&acirc;y, cư d&acirc;n sẽ được tận hưởng kh&ocirc;ng gian sống trong l&agrave;nh, kho&aacute;ng đạt v&agrave; thịnh vượng mỗi ng&agrave;y, nơi vườn trong phố, phố giữa c&ocirc;ng vi&ecirc;n.</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Ph&acirc;n khu 3: Green Bayview Resort - Tuyệt t&aacute;c nghỉ dưỡng giữa l&ograve;ng kỳ quan</strong></p>\r\n\r\n<p style=\"text-align:justify\">Đ&acirc;y sẽ l&agrave; khu nghỉ dưỡng đẳng cấp, hứa hẹn trở th&agrave;nh một thi&ecirc;n đường du lịch mới của th&agrave;nh phố Cẩm Phả. Nơi mỗi ng&agrave;y tr&ocirc;i qua l&agrave; nguồn cảm hứng bất tận, thụ hưởng những gi&acirc;y ph&uacute;t an y&ecirc;n c&ugrave;ng dịch vụ thượng lưu b&ecirc;n bờ vịnh xanh.</p>', '<p style=\"text-align:justify\">V&igrave; sở hữu vị tr&iacute; trung t&acirc;m, thuận tiện kết nối n&ecirc;n cư d&acirc;n sống tại Green Dragon City được thừa hưởng trọn vẹn hệ thống tiện &iacute;ch kề b&ecirc;n: B&atilde;i biển vịnh B&aacute;i Tử Long, trung t&acirc;m thương mại, dịch vụ Vincom Plaza, khu c&ocirc;ng vi&ecirc;n c&acirc;y xanh, chuỗi cửa h&agrave;ng thời trang, mua sắm, khu ẩm thực, rạp chiếu phim, si&ecirc;u thị điện m&aacute;y VinPro, si&ecirc;u thị Vinmart&hellip;</p>\r\n\r\n<p style=\"text-align:justify\">B&ecirc;n cạnh đ&oacute;, nội khu dự &aacute;n cũng được t&iacute;ch hợp đầy đủ mọi tiện &iacute;ch hiện đại, kh&eacute;p k&iacute;n với ti&ecirc;u chuẩn 5* đẳng cấp.</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">C&ocirc;ng vi&ecirc;n c&acirc;y xanh</li>\r\n	<li style=\"text-align:justify\">Đường dạo bộ</li>\r\n	<li style=\"text-align:justify\">Phố đi bộ hoa ban</li>\r\n	<li style=\"text-align:justify\">Khu chiếu phim ngo&agrave;i trời</li>\r\n	<li style=\"text-align:justify\">Khu vui chơi trẻ em</li>\r\n	<li style=\"text-align:justify\">Khu thể thao ngo&agrave;i trời</li>\r\n	<li style=\"text-align:justify\">Quảng trường nhạc nước</li>\r\n	<li style=\"text-align:justify\">Club house</li>\r\n	<li style=\"text-align:justify\">Bể bơi 4 m&ugrave;a</li>\r\n	<li style=\"text-align:justify\">Khu tắm kho&aacute;ng n&oacute;ng</li>\r\n	<li style=\"text-align:justify\">C&ocirc;ng vi&ecirc;n nghệ thuật đi&ecirc;u khắc than đ&aacute;</li>\r\n	<li style=\"text-align:justify\">B&atilde;i tắm nh&acirc;n tạo d&agrave;i 1km</li>\r\n	<li style=\"text-align:justify\">Bến du thuyền</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">Ngo&agrave;i ra, dự &aacute;n c&oacute; hệ thống an ninh nghi&ecirc;m ngặt bảo vệ 24/24 tuyệt đối, đảm bảo kh&ocirc;ng gian ri&ecirc;ng tư, an to&agrave;n cho qu&yacute; cư d&acirc;n. V&igrave; vậy cư d&acirc;n c&oacute; thể an t&acirc;m tận hưởng cuộc sống, trải nghiệm những tiện &iacute;ch V&Agrave;NG m&agrave; dự &aacute;n đem lại.</p>', '<ul>\r\n	<li>Th&aacute;ng 11/2019: bắt đầu x&acirc;y nh&agrave; mẫu</li>\r\n	<li>Th&aacute;ng 12/2019: ho&agrave;n thiện 80% hạ tầng đợt 1</li>\r\n	<li>Th&aacute;ng 05/2020: ho&agrave;n thiện to&agrave;n bộ giai đoạn 1</li>\r\n	<li>Thời gian cam kết x&acirc;y dựng (đối với nh&agrave; th&ocirc;): 2 năm</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>', NULL, NULL, NULL, '10 LÝ DO NÊN SỞ HỮU GREEN DRAGON CITY', 'TỔNG QUAN DỰ ÁN GREEN DRAGON CITY', 'VỊ TRÍ “ĐỊA LINH SINH VƯỢNG KHÍ” CỦA GREEN DRAGON CITY', 'QUY HOẠCH, PHÂN KHU DỰ ÁN GREEN DRAGON CITY', 'HỆ TIỆN ÍCH ĐẲNG CẤP CHO CUỘC SỐNG CHUẨN SANG Ở GREEN DRAGON CITY', 'TIẾN ĐỘ XÂY DỰNG GREEN DRAGON CITY', NULL, NULL, NULL, 'Green Dragon City Cẩm Phả - Thông Tin Mới Nhất Vừa Cập Nhật', '02/19/2020', NULL, 'green-dragon-city-cam-pha', 'Green Dragon City mở ra ra không gian sống hoàn hảo, dẫn lối giao thương và thắp lửa thịnh vượng cho cộng đồng cư dân tinh hoa.', NULL, 'index, follow'),
(132, '69', 49, 'Admin', 'Beverly Hills Hạ Long', 'Đồi Hải Quân, phường Bãi Cháy', 818, 'beverly-hills-ha-long.jpg', NULL, NULL, NULL, 'true', '<ul>\r\n	<li style=\"text-align:justify\">T&ecirc;n dự &aacute;n: Beverly Hills Hạ Long</li>\r\n	<li style=\"text-align:justify\">Vị tr&iacute;: Đồi Hải Qu&acirc;n, phường B&atilde;i Ch&aacute;y, TP. Hạ Long</li>\r\n	<li style=\"text-align:justify\">Chủ đầu tư: C&ocirc;ng ty TNHH Đức Dương</li>\r\n	<li style=\"text-align:justify\">Quy m&ocirc;, loại h&igrave;nh: 10.8 ha</li>\r\n	<li style=\"text-align:justify\">Biệt thự: 138 căn biệt thự cao cấp, với diện t&iacute;ch từ 240m2 - 400m2</li>\r\n	<li style=\"text-align:justify\">Căn hộ kh&aacute;ch sạn: 160 căn, diện t&iacute;ch 35m2 - 62m2</li>\r\n	<li style=\"text-align:justify\">Chung cư cao cấp: 92 căn, diện t&iacute;ch từ 62m2 - 128m2</li>\r\n	<li style=\"text-align:justify\">T&ograve;a nh&agrave; CT1 v&agrave; CT2 - Condotel: Gồm 19 tầng nổi trong đ&oacute; c&oacute; 2 tầng khối đế d&agrave;nh cho c&aacute;c tiện &iacute;ch cao cấp Thương mại, Spa, si&ecirc;u thị, bể bơi v&ocirc; cực&hellip; v&agrave; 17 tầng căn hộ</li>\r\n	<li style=\"text-align:justify\">Mật độ x&acirc;y dựng: 40%</li>\r\n	<li style=\"text-align:justify\">Ho&agrave;n thiện căn hộ: Full nội thất cao cấp</li>\r\n	<li style=\"text-align:justify\">H&igrave;nh thức sở hữu: Sổ đỏ l&acirc;u d&agrave;i</li>\r\n</ul>', 'Tại sao nên mua', 'Tổng quan', 'Vị Trí', 'Mặt bằng', 'Tiện ích', 'Tiến độ thanh toán', NULL, NULL, 'Chính sách', '<ol>\r\n	<li style=\"text-align:justify\">Chủ đầu tư c&oacute; tiềm lực t&agrave;i ch&iacute;nh mạnh, uy t&iacute;n, tận t&acirc;m</li>\r\n	<li style=\"text-align:justify\">Vị tr&iacute; đắt gi&aacute;, tầm nh&igrave;n rộng mở hướng vịnh Hạ Long - Tiềm năng cho thu&ecirc; cao</li>\r\n	<li style=\"text-align:justify\">Căn hộ đ&atilde; ho&agrave;n thiện c&oacute; thể nhận b&agrave;n giao v&agrave; sử dụng ngay</li>\r\n	<li style=\"text-align:justify\">Đầy đủ tiện &iacute;ch đẳng cấp: Skybar, bể bơi v&ocirc; cực, casino&hellip;</li>\r\n	<li style=\"text-align:justify\">Thiết kế hợp l&yacute;, diện t&iacute;ch linh hoạt kh&ocirc;ng gian tho&aacute;ng rộng</li>\r\n	<li style=\"text-align:justify\">Căn hộ kh&aacute;ch sạn chủ đầu tư sẽ vận h&agrave;nh v&agrave; cam kết lợi nhuận 12%/năm trong 5 năm đầu</li>\r\n	<li style=\"text-align:justify\">Ph&aacute;p l&yacute; đầy đủ, minh bạch - Sở hữu vĩnh viễn</li>\r\n	<li style=\"text-align:justify\">Gi&aacute; b&aacute;n cạnh tranh, ch&iacute;nh s&aacute;ch hấp dẫn</li>\r\n	<li style=\"text-align:justify\">Tiềm năng tăng gi&aacute; cao</li>\r\n</ol>', '<p style=\"text-align:justify\">Đến với Quảng Ninh, kh&aacute;ch du lịch thường đến những địa danh nổi tiếng như Vịnh Hạ Long, Đảo Tuần Ch&acirc;u, N&uacute;i Y&ecirc;n Tử&hellip; Nhưng cũng đừng bỏ qua việc gh&eacute; thăm Đồi Hải Qu&acirc;n thơ mộng. V&agrave; tọa lạc tr&ecirc;n ngọn đồi thơ mộng ấy, Beverly Hilla nổi l&ecirc;n như một ng&ocirc;i sao s&aacute;ng, với tầm nh&igrave;n cao v&agrave; tho&aacute;ng rộng, 2 t&ograve;a nh&agrave; cao 19 tầng trở n&ecirc;n nổi trội giữa v&ugrave;ng trời Hạ Long, thể hiện kh&aacute;t khao v&agrave; tầm v&oacute;c của những người muốn sở hữu n&oacute;.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Dự &aacute;n được lấy cảm hứng từ ngọn đồi Beverly Hills tại California, Hoa Kỳ với những căn biệt thự triệu đ&ocirc;, những bữa tiệc xa hoa v&agrave; những buổi lễ trao giải nghệ thuật danh gi&aacute;. Dự &aacute;n sở hữu tầm nh&igrave;n rộng mở, 4 mặt đều c&oacute; thể ngắm Vịnh Hạ Long - kỳ quan thi&ecirc;n nhi&ecirc;n thế giới đầy mộng mơ. Với những cơn gi&oacute; m&aacute;t l&agrave;nh từ Vịnh Hạ Long v&agrave; những h&agrave;ng c&acirc;y xanh mướt, Beverly Hills Hạ Long hứa hẹn sẽ mang đến cho qu&yacute; cư d&acirc;n một cuộc sống trong l&agrave;nh, y&ecirc;n b&igrave;nh v&agrave; thịnh vượng.</p>', '<p style=\"text-align:justify\">Tọa lạc tại đồi Hải Qu&acirc;n, ph&iacute;a T&acirc;y cầu B&atilde;i Ch&aacute;y, Hạ Long, Beverly Hills l&agrave; một trong 3 đỉnh tam gi&aacute;c k&eacute;o d&agrave;i bao tr&ugrave;m khu vực H&ugrave;ng Thắng, qua phường B&atilde;i Ch&aacute;y sang phường Hồng Hải với nhiều kỷ lục nhất Hạ Long. Với vị tr&iacute; tr&ecirc;n cao đắc địa, dự &aacute;n c&oacute; tầm nh&igrave;n tho&aacute;ng rộng, ri&ecirc;ng một v&ugrave;ng trời m&agrave; kh&ocirc;ng một dự &aacute;n n&agrave;o tại Hạ Long c&oacute; được. Cả 4 ph&iacute;a của Beverly Hills đều c&oacute; view tho&aacute;ng đ&atilde;ng, ph&oacute;ng tầm mắt bao trọn một v&ugrave;ng thi&ecirc;n nhi&ecirc;n biển h&ugrave;ng vĩ, hữu t&igrave;nh.</p>\r\n\r\n<p style=\"text-align:justify\">Với vị tr&iacute; &ldquo;Ngự đỉnh kỳ quan&rdquo; độc t&ocirc;n, duy nhất, khi dự &aacute;n ho&agrave;n th&agrave;nh sẽ l&agrave;:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Nơi tập trung nhiều Biệt thự triệu đ&ocirc; nhất</li>\r\n	<li style=\"text-align:justify\">Nơi diễn ra nhiều bữa tiệc v&agrave; festival ho&agrave;nh tr&aacute;ng nhất</li>\r\n	<li style=\"text-align:justify\">C&aacute;c con phố ẩm thực s&ocirc;i động, thu h&uacute;t kh&aacute;ch du lịch nhất</li>\r\n	<li style=\"text-align:justify\">Nơi gi&aacute; bất động sản lu&ocirc;n giữ bền vững</li>\r\n	<li style=\"text-align:justify\">Nơi tựa lưng v&agrave;o n&uacute;i v&agrave; nh&igrave;n ra Vịnh Hạ Long kỳ quan thế giới</li>\r\n	<li style=\"text-align:justify\">Nơi hạ tầng giao th&ocirc;ng v&agrave; li&ecirc;n kết v&ugrave;ng ho&agrave;n hảo nhất.&nbsp;</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">Ngo&agrave;i ra dự &aacute;n cũng c&oacute; li&ecirc;n kết v&ugrave;ng thuận tiện, dễ d&agrave;ng kết nối với nhiều tuyến đường giao th&ocirc;ng huyết mạch của th&agrave;nh phố: ph&iacute;a T&acirc;y gi&aacute;p đường dẫn l&ecirc;n cầu B&atilde;i Ch&aacute;y; ph&iacute;a Nam gi&aacute;p đường 8 cũ; ph&iacute;a Đ&ocirc;ng gi&aacute;p khu C đo&agrave;n điều dưỡng 22 Hải Qu&acirc;n; ph&iacute;a Nam gi&aacute;p khu d&acirc;n cư hiện c&oacute;. Từ dự &aacute;n cũng c&oacute; thể đi tới nhiều địa điểm quan trọng như:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">TTTM B&atilde;i Ch&aacute;y (300m)</li>\r\n	<li style=\"text-align:justify\">&nbsp;Sunworld Hạ Long Park (1 km)</li>\r\n	<li style=\"text-align:justify\">Cầu B&atilde;i Ch&aacute;y (1,5 km)</li>\r\n	<li style=\"text-align:justify\">Bệnh viện B&atilde;i Ch&aacute;y (3 km)</li>\r\n	<li style=\"text-align:justify\">Bệnh viện Vinmec (6 km)</li>\r\n	<li style=\"text-align:justify\">Bảo t&agrave;ng QN (7 km)</li>\r\n	<li style=\"text-align:justify\">Khu vui chơi Typhoon Water Park Hạ Long (2 km)</li>\r\n	<li style=\"text-align:justify\">Vịnh Cửa Lục (3,6 km)</li>\r\n	<li style=\"text-align:justify\">V&ograve;ng Quay Mặt Trời (Sun Wheel) &ndash; 4,5 km</li>\r\n	<li style=\"text-align:justify\">Hồ C&ocirc; Ti&ecirc;n (6,5 km)</li>\r\n	<li style=\"text-align:justify\">Ga Hạ Long (6,5 km)</li>\r\n	<li style=\"text-align:justify\">Đảo Tuần Ch&acirc;u (14 km)</li>\r\n</ul>', '<p style=\"text-align:justify\">Dự &aacute;n được thiết kế theo phong c&aacute;ch m&ugrave;a xu&acirc;n Địa Trung Hải. Đ&oacute; l&agrave; sự kết hợp h&agrave;i h&ograve;a giữa kh&ocirc;ng gian v&agrave; thi&ecirc;n nhi&ecirc;n để tạo n&ecirc;n một kh&ocirc;ng gian ngập tr&agrave;n sức sống như những bức tranh pha trộn đầy m&agrave;u sắc đan xen sự nhịp nh&agrave;ng của c&aacute;c chi tiết. Với kh&ocirc;ng gian n&agrave;y, chủ nh&acirc;n sẽ được tận hưởng cảm gi&aacute;c b&igrave;nh y&ecirc;n, giản dị nhưng vẫn đầy chất hiện đại. C&aacute;c căn hộ c&oacute; thiết kế với diện t&iacute;ch từ 35,2m2 đến 62m2 đ&aacute;p ứng tối đa nhu cầu sử dụng của kh&aacute;ch h&agrave;ng.</p>', '<p style=\"text-align:justify\">Nằm trong quần thể đ&ocirc; thị kh&eacute;p k&iacute;n, kh&aacute;ch h&agrave;ng được thừa hưởng c&aacute;c dịch vụ tiện &iacute;ch hiện đại, đẳng cấp:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Sky Bar tr&ecirc;n tầng 19, cao nhất khu vực B&atilde;i Ch&aacute;y</li>\r\n	<li style=\"text-align:justify\">Đ&agrave;i thi&ecirc;n văn duy nhất tại Hạ Long</li>\r\n	<li style=\"text-align:justify\">C&aacute;ch Casino, kh&aacute;ch sạn 5 sao 50m</li>\r\n	<li style=\"text-align:justify\">Cổng ch&agrave;o ch&iacute;nh khu đ&ocirc; thị</li>\r\n	<li style=\"text-align:justify\">B&atilde;i gửi xe ngay dưới t&ograve;a nh&agrave;</li>\r\n	<li style=\"text-align:justify\">Club house, Cigar Lounge kh&aacute;ch sạn 5 sao</li>\r\n	<li style=\"text-align:justify\">Khu vui chơi thể thao</li>\r\n	<li style=\"text-align:justify\">Ph&ograve;ng tập đa năng</li>\r\n	<li style=\"text-align:justify\">Beauty Spa</li>\r\n	<li style=\"text-align:justify\">Ph&ograve;ng tập ngo&agrave;i trời</li>\r\n	<li style=\"text-align:justify\">Nh&agrave; trẻ</li>\r\n	<li style=\"text-align:justify\">Trung t&acirc;m thương mại</li>\r\n	<li style=\"text-align:justify\">Khu vườn b&iacute; mật</li>\r\n	<li style=\"text-align:justify\">Khu phố T&acirc;y</li>\r\n	<li style=\"text-align:justify\">S&acirc;n Tennis</li>\r\n</ul>', '<p><strong>1. Thanh to&aacute;n nhanh:</strong></p>\r\n\r\n<p>- Chung cư CT1:</p>\r\n\r\n<p>Kh&aacute;ch h&agrave;ng thanh to&aacute;n nhanh 95% được chiết khấu 5%/tổng gi&aacute; trị căn hộ.</p>\r\n\r\n<p>- Hometel CT2:</p>\r\n\r\n<p>Kh&aacute;ch h&agrave;ng thanh to&aacute;n nhanh 95% được nhận lu&ocirc;n cam kết chia lợi nhuận từ CĐT: kh&aacute;ch h&agrave;ng được hưởng lợi nhuận 12%/năm v&agrave; được thanh to&aacute;n 6 th&aacute;ng một lần trong thời hạn 05 năm. Sau 05 năm nếu hai b&ecirc;n c&ugrave;ng c&oacute; nhu cầu sẽ thỏa thuận v&agrave; k&yacute; hợp đồng tiếp theo để x&aacute;c định thời gian thực hiện HĐ v&agrave; mức lợi nhuận được hưởng.</p>\r\n\r\n<p><strong>2. Thanh to&aacute;n theo tiến độ th&ocirc;ng thường:</strong></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Tiến độ thanh toán beverly hills hạ long\" src=\"/public/upload/images/beverly-hills-halong/beverly%20hills-thanhtoan.JPG\" style=\"height:415px; width:1027px\" /></p>', NULL, NULL, '<p style=\"text-align:justify\"><strong>1. Ưu đ&atilde;i d&agrave;nh cho KH mua đợt đầu</strong></p>\r\n\r\n<p style=\"text-align:justify\">15 kh&aacute;ch h&agrave;ng đầu ti&ecirc;n mua Chung cư v&agrave; 15 kh&aacute;ch h&agrave;ng đầu ti&ecirc;n mua Hometel được chiết khấu 2%/tổng gi&aacute; trị căn hộ.</p>\r\n\r\n<p style=\"text-align:justify\"><strong>2. Ưu đ&atilde;i d&agrave;nh cho kh&aacute;ch h&agrave;ng mua Hometel:</strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Kh&aacute;ch h&agrave;ng được cam kết mua lại 100% gi&aacute; trị căn hộ sau 05 năm</li>\r\n	<li style=\"text-align:justify\">&nbsp;Kh&aacute;ch h&agrave;ng được tặng 10 đ&ecirc;m nghỉ dưỡng/năm tại c&aacute;c căn hộ tương đương trong dự &aacute;n</li>\r\n	<li style=\"text-align:justify\">Kh&aacute;ch h&agrave;ng được nhận cam kết lợi nhuận 12%/năm v&agrave; được thanh to&aacute;n 6 th&aacute;ng một lần trong thời hạn 05 năm kể từ khi thanh to&aacute;n đủ 95% gi&aacute; trị căn hộ.</li>\r\n</ul>', 'TẠI SAO NÊN CHỌN BEVERLY HILLS HẠ LONG?', 'TỔNG QUAN DỰ ÁN BEVERLY HILLS HẠ LONG', 'VỊ TRÍ ĐỘC TÔN - NGỰ ĐỈNH KỲ QUAN CỦA BEVERLY HILLS', 'THIẾT KẾ, MẶT BẰNG DỰ ÁN BEVERLY HILLS HẠ LONG', 'TIỆN ÍCH HẠNG SANG - NÂNG TẦM CUỘC SỐNG TẠI BEVERLY HILLS HẠ LONG', 'TIẾN ĐỘ THANH TOÁN', NULL, NULL, 'CHÍNH SÁCH BÁN HÀNG', 'Beverly Hills Hạ Long - Hometel Đầu Tư Tốt Nhất Bãi Cháy', '02/28/2020', NULL, 'beverly-hills-ha-long', 'Beverly Hilla nổi lên như một ngôi sao sáng, với tầm nhìn cao và thoáng rộng, 2 tòa nhà cao 19 tâng trở nên nổi trội giữa vùng trời Hạ Long, thể hiện khát khao và tầm ầng', NULL, 'index, follow'),
(133, '69', 55, 'Admin', 'Louis City Hoàng Mai', 'Tân Mai', 1000, 'louis-city-hoang-mai-v18.jpg', NULL, NULL, NULL, 'true', '<ul>\r\n	<li style=\"text-align:justify\">T&ecirc;n Dự &Aacute;n: Louis City Ho&agrave;ng Mai</li>\r\n	<li style=\"text-align:justify\">Vị tr&iacute;: Đường T&acirc;n Mai, quận Ho&agrave;ng Mai, TP. H&agrave; Nội</li>\r\n	<li style=\"text-align:justify\">Chủ Đầu Tư: Cty Cổ Phần đầu tư ph&aacute;t triển đ&ocirc; thị Ho&agrave;ng Mai</li>\r\n	<li style=\"text-align:justify\">Tổng diện t&iacute;ch: 28,000 m2</li>\r\n	<li style=\"text-align:justify\">Diện t&iacute;ch thấp tầng: 23,500 m2</li>\r\n	<li style=\"text-align:justify\">Quy M&ocirc;: 754 căn liền kề, biệt thự, nh&agrave; phố shophouse cao 3.5 &ndash; 5 tầng.</li>\r\n	<li style=\"text-align:justify\">Diện t&iacute;ch đất: 90m2 &ndash; 300m2.</li>\r\n	<li style=\"text-align:justify\">Diện t&iacute;ch s&agrave;n: 350m &ndash; 650m.</li>\r\n	<li style=\"text-align:justify\">H&igrave;nh thức sở hữu: Sở hữu l&acirc;u d&agrave;i</li>\r\n</ul>', 'Tổng quan', 'Vị trí', 'Thiết kế', 'Tiện ích', 'Mặt bằng', 'Thanh toán', NULL, NULL, 'Chính sách', '<p style=\"text-align:justify\">Khởi c&ocirc;ng đầu năm 2020, dự &aacute;n Louis City Ho&agrave;ng Mai nhanh ch&oacute;ng tạo n&ecirc;n cơn sốt tr&ecirc;n thị trường bất động sản H&agrave; Nội. Khu đ&ocirc; thị mới Louis City mang đến cho kh&aacute;ch h&agrave;ng những căn biệt thự sang trọng, liền kề, shophouse thương mại được thiết kế t&acirc;n cổ điển theo phong c&aacute;ch ch&acirc;u &Acirc;u. Được thiết kế, x&acirc;y dựng bởi c&aacute;c kiến tr&uacute;c sư, kỹ sư h&agrave;ng đầu thế giới, dự &aacute;n được chăm ch&uacute;t cầu kỳ đến từng chi tiết nhỏ nhất với mục đ&iacute;ch l&agrave;m to&aacute;t l&ecirc;n vẻ sang trọng, lộng lẫy v&agrave; khiến chủ sở hữu tự h&agrave;o.</p>\r\n\r\n<p style=\"text-align:justify\">Dự &aacute;n l&agrave; một khu đ&ocirc; thị kh&eacute;p k&iacute;n v&agrave; sang trọng, một h&igrave;nh mẫu kh&ocirc;ng gian kiến tr&uacute;c l&yacute; tưởng của tương lai. Ngo&agrave;i ra, kiến tr&uacute;c dự &aacute;n c&ograve;n ch&uacute; trọng đến sự tiện nghi, đặc biệt l&agrave; sự h&ograve;a hợp c&ugrave;ng thi&ecirc;n nhi&ecirc;n trong l&agrave;nh. Kh&ocirc;ng gian xanh tr&agrave;n ngập trong mỗi căn biệt thự, shophouse tại đ&acirc;y. Ngo&agrave;i ra, dự &aacute;n cũng được kỳ vọng l&agrave; sản phẩm đầu tư tiềm năng nhờ sở hữu nhiều ưu thế vượt trội:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Vị tr&iacute; đẹp, hữu hạn v&agrave; kỳ vọng tăng gi&aacute;</li>\r\n	<li style=\"text-align:justify\">Thiết kế quy hoạch đồng bộ</li>\r\n	<li style=\"text-align:justify\">Cơ sở hạ tầng ho&agrave;n hảo</li>\r\n	<li style=\"text-align:justify\">Tiện &iacute;ch cao cấp</li>\r\n	<li style=\"text-align:justify\">Chủ đầu tư uy t&iacute;n</li>\r\n	<li style=\"text-align:justify\">Gi&aacute; b&aacute;n hợp l&yacute;</li>\r\n</ul>', '<p style=\"text-align:justify\">Dự &aacute;n Louis City tọa lạc ngay tại mặt đường T&acirc;n Mai rộng 40m kết nối giữa đường Giải Ph&oacute;ng v&agrave; đường Tam Chinh. Ph&iacute;a Nam gi&aacute;p với trục đường 30m kết nối Trung t&acirc;m h&agrave;nh ch&iacute;nh quận Ho&agrave;ng Mai tới đường Tam Chinh. B&ecirc;n cạnh đ&oacute;, dự &aacute;n c&ograve;n tiếp gi&aacute;p với hồ điều h&ograve;a Y&ecirc;n Sở l&agrave; một trong những hồ lớn nhất H&agrave; Nội tạo ra kh&ocirc;ng gian sống trong l&agrave;nh.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Vị tr&iacute; của dự &aacute;n l&agrave; khu vực tập trung đ&ocirc;ng d&acirc;n cư, cửa ng&otilde; ph&iacute;a Nam H&agrave; Nội, thừa hưởng to&agrave;n bộ hệ thống giao th&ocirc;ng hạ tầng tiện &iacute;ch, gi&uacute;p cho cư d&acirc;n c&oacute; thể dễ d&agrave;ng di chuyển đến c&aacute;c khu trung t&acirc;m, vui chơi: Hồ Gươm, Time City, Aeon Mall, Đại học B&aacute;ch Khoa, Đại học Kinh tế Quốc d&acirc;n, Bệnh viện Bạch Mai, Bến xe Gi&aacute;p B&aacute;t&hellip; tạo n&ecirc;n quần thể dự &aacute;n cao cấp hội tụ mọi yếu tố vượt trội.</p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>Li&ecirc;n kết v&ugrave;ng dự &aacute;n</strong></h3>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Ph&iacute;a Đ&ocirc;ng Bắc: gi&aacute;p chợ đầu mối ph&iacute;a Nam</li>\r\n	<li style=\"text-align:justify\">Ph&aacute;i T&acirc;y Bắc: gi&aacute;p - đối diện KĐt Đền Lừ v&agrave; Hồ Đền Lừ</li>\r\n	<li style=\"text-align:justify\">Ph&iacute;a T&acirc;y Nam: gi&aacute;p KĐT Thịnh Liệt</li>\r\n	<li style=\"text-align:justify\">Ph&iacute;a Đ&ocirc;ng Nam: gi&aacute;p Hồ điều h&ograve;a Y&ecirc;n Sở</li>\r\n	<li style=\"text-align:justify\">Gần c&aacute;c khu đ&ocirc; thị: Đền Lừ, KĐT K35 T&acirc;n Mai, KĐT Time City, KĐT Vinhomes Riverside</li>\r\n	<li style=\"text-align:justify\">C&aacute;ch chợ đầu mối ph&iacute;a Nam 20m, si&ecirc;u thị điện m&aacute;y HC Center 300m, si&ecirc;u thị Metro - Big C Ho&agrave;ng Mai 500m, Aeon Mall Long Bi&ecirc;n 5km</li>\r\n	<li style=\"text-align:justify\">Gần c&aacute;c trường mầm non, tiểu học, THCS Đền Lừ, Thịnh Liệt, THPT Trương Định, ĐH Kinh tế Quốc d&acirc;n, ĐH B&aacute;ch Khoa, ĐH Y, ĐH Phương Đ&ocirc;ng&hellip;</li>\r\n	<li style=\"text-align:justify\">Gần c&aacute;c bệnh viện Bạch Mai, BV Thanh Nh&agrave;n, BV Tai Mũi Họng, BV Vinmec, BV Mắt Trung ương&hellip;</li>\r\n</ul>', '<p style=\"text-align:justify\">Mỗi căn shophouse, biệt thự liền kề tại dự &aacute;n Louis City sở hữu chiều cao 5 tầng c&ugrave;ng thiết kế t&acirc;n cổ điển, sang trọng ph&ugrave; hợp với mọi h&igrave;nh thức kinh doanh. Gia chủ được t&ugrave;y &yacute; s&aacute;ng tạo v&agrave; khai th&aacute;c tối đa c&ocirc;ng năng sử dụng của từng căn. Đặc biệt, thiết kế kh&ocirc;ng liền tường giữa c&aacute;c căn gi&uacute;p c&aacute;ch &acirc;m, c&aacute;ch nhiệt, đảm bảo ri&ecirc;ng tư cho gia chủ. Những căn biệt thự mang đậm hướng thiết kế t&acirc;n cổ điển, kết hợp s&acirc;n vườn v&agrave; c&ocirc;ng vi&ecirc;n tạo n&ecirc;n một kh&ocirc;ng gian sống tươi xanh, gần gũi với thi&ecirc;n nhi&ecirc;n.</p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>NH&Agrave; LIỀN KỀ</strong></h3>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Loại nh&agrave;: Liền kề phố &ndash; liền kề vườn</li>\r\n	<li style=\"text-align:justify\">Ph&ugrave; hợp: Mục ti&ecirc;u văn ph&ograve;ng kết hợp nh&agrave; ở</li>\r\n	<li style=\"text-align:justify\">Thiết kế: Cửa sổ k&iacute;nh kh&ocirc;ng qu&aacute; rộng, giảm diện t&iacute;ch ban c&ocirc;ng.</li>\r\n	<li style=\"text-align:justify\">Mặt bằng : Thiết kế cầu thang ở giữa, 2 ph&ograve;ng hai b&ecirc;n. C&oacute; thể lắp đặt thang m&aacute;y.</li>\r\n	<li style=\"text-align:justify\">Số tầng: Hỗn hợp 5 tầng</li>\r\n	<li style=\"text-align:justify\">Tổng diện t&iacute;ch s&agrave;n: 375 &ndash; 400 m</li>\r\n	<li style=\"text-align:justify\">Chiều cao tối đa nh&agrave; 5 tầng: 17.5m</li>\r\n	<li style=\"text-align:justify\">Mật độ x&acirc;y dựng: 70%</li>\r\n</ul>\r\n\r\n<h3 style=\"text-align:justify\"><strong>SHOPHOUSE KẾT HỢP Ở</strong></h3>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Loại nh&agrave;: Nh&agrave; phố thương mại</li>\r\n	<li style=\"text-align:justify\">Vị tr&iacute;: C&aacute;c trục đường ch&iacute;nh trong dự &aacute;n</li>\r\n	<li style=\"text-align:justify\">Ph&ugrave; hợp: Mục ti&ecirc;u kinh doanh tầng 1, nh&agrave; ở&nbsp; tầng 2 trở l&ecirc;n</li>\r\n	<li style=\"text-align:justify\">Thiết kế: Tầng 1 cửa rộng ph&ugrave; hợp cho cửa h&agrave;ng. Diện t&iacute;ch s&agrave;n tầng 1 được bố tr&iacute; từ 70 &ndash; 100 m ph&ugrave; hợp cho kinh doanh cửa h&agrave;ng.</li>\r\n	<li style=\"text-align:justify\">Số tầng: Hỗn hợp 5 tầng</li>\r\n	<li style=\"text-align:justify\">Tổng diện t&iacute;ch s&agrave;n: 400 &ndash; 500 m</li>\r\n	<li style=\"text-align:justify\">Mật độ x&acirc;y dựng: 70%</li>\r\n</ul>\r\n\r\n<h3 style=\"text-align:justify\"><strong>BIỆT THỰ VƯỜN</strong></h3>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Vị tr&iacute;: Trục c&acirc;y xanh giữa dự &aacute;n.</li>\r\n	<li style=\"text-align:justify\">Ph&ugrave; hợp: Mục ti&ecirc;u nh&agrave; ở</li>\r\n	<li style=\"text-align:justify\">Thiết kế: Thiết kế biệt thự đơn lập hoặc song lập, c&oacute; s&acirc;n vườn bao quanh, đặc biệt ph&iacute;a sau l&agrave; khoảng c&ocirc;ng vi&ecirc;n c&acirc;y xanh rộng r&atilde;i được thiết kế hiện đại, đẹp mắt.</li>\r\n	<li style=\"text-align:justify\">Số tầng: 4 tầng</li>\r\n	<li style=\"text-align:justify\">Tổng diện t&iacute;ch s&agrave;n: 450 &ndash; 500 m</li>\r\n	<li style=\"text-align:justify\">Mật độ x&acirc;y dựng: 70%</li>\r\n</ul>\r\n\r\n<h3 style=\"text-align:justify\"><strong>KH&Ocirc;NG GIAN XANH ĐƯỢC CH&Uacute; TRỌNG</strong></h3>\r\n\r\n<p style=\"text-align:justify\">Đan xen những căn nh&agrave; phố thương mại hiện đại, đồng bộ, Louis City sở hữu một kh&ocirc;ng gian xanh với tỷ lệ đất c&ocirc;ng vi&ecirc;n chiếm đến 40% dự &aacute;n. B&ecirc;n cạnh đ&oacute;, dự &aacute;n nằm s&aacute;t hồ điều h&ograve;a Y&ecirc;n Sở - một trong những hồ lớn nhất H&agrave; Nội - tạo ra một kh&ocirc;ng gian sống trong l&agrave;nh.</p>', '<p style=\"text-align:justify\">Nằm tại trung t&acirc;m quận Ho&agrave;ng Mai, dự &aacute;n Louis City thừa hưởng hạ tầng tiện &iacute;ch, giao th&ocirc;ng sẵn c&oacute;, lại sở hữu cho m&igrave;nh vị tr&iacute; đắc địa phong thủy thuận h&ograve;a: Cận đ&ocirc; thị phồn hoa, gi&aacute;p hồ điều h&ograve;a, c&ocirc;ng vi&ecirc;n xanh m&aacute;t, sinh kh&iacute; vẹn to&agrave;n, gi&uacute;p gia chủ an cư, hưng thịnh.</p>\r\n\r\n<p style=\"text-align:justify\">Ngo&agrave;i ra, dự &aacute;n c&ograve;n cung cấp c&aacute;c dịch vụ tiện &iacute;ch đồng bộ chất lượng cao, mang đến cho cư d&acirc;n một trải nghiệm sống ho&agrave;n hảo, h&ograve;a quyện với thi&ecirc;n nhi&ecirc;n, tận hưởng v&agrave; trải nghiệm cuộc sống chuẩn Ch&acirc;u &Acirc;u:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Hệ thống trung t&acirc;m thương mại</li>\r\n	<li style=\"text-align:justify\">Trường li&ecirc;n cấp</li>\r\n	<li style=\"text-align:justify\">Hệ thống bệnh viện lớn</li>\r\n	<li style=\"text-align:justify\">Trung t&acirc;m ẩm thực sang trọng</li>\r\n	<li style=\"text-align:justify\">C&aacute;c cơ quan h&agrave;nh ch&iacute;nh - kinh tế</li>\r\n	<li style=\"text-align:justify\">Kh&ocirc;ng gian rừng cọ xanh, c&ocirc;ng vi&ecirc;n c&acirc;y xanh vườn hoa</li>\r\n	<li style=\"text-align:justify\">Bể bơi ngo&agrave;i trời</li>\r\n	<li style=\"text-align:justify\">Khu vui chơi d&agrave;nh cho trẻ em</li>\r\n	<li style=\"text-align:justify\">Nh&agrave; sinh hoạt cộng đồng</li>\r\n	<li style=\"text-align:justify\">Đ&agrave;i phun nước đa điểm</li>\r\n	<li style=\"text-align:justify\">Vườn tuổi thơ</li>\r\n	<li style=\"text-align:justify\">Ph&ograve;ng tập Gym, Yoga, Spa l&agrave;m đẹp</li>\r\n	<li style=\"text-align:justify\">Nh&agrave; h&agrave;ng c&agrave; ph&ecirc;</li>\r\n</ul>', '<p style=\"text-align: center;\"><img alt=\"Mặt bằng phối cảnh tổng thể dự án\" src=\"/public/upload/files/louis-city-hoang-mai/matbang.jpg\" /></p>', '<p style=\"text-align:center\"><img alt=\"Tiến độ thanh toán Louis City\" src=\"/public/upload/images/louis-city-hoangmai/56.JPG\" style=\"height:383px; width:662px\" /></p>', NULL, NULL, NULL, 'TỔNG QUAN DỰ ÁN LOUIS CITY HOÀNG MAI', 'VỊ TRÍ HOÀN HẢO CỦA LOUIS CITY', 'THIẾT KẾ DỰ ÁN LOUIS HOÀNG MAI', 'TIỆN ÍCH 5 SAO DỰ ÁN LOUIS CITY HOÀNG MAI', 'Mặt bằng phối cảnh tổng thể dự án', 'TIẾN ĐỘ THANH TOÁN LOUIS CITY', NULL, NULL, NULL, 'Louis City Hoàng Mai - Thông Tin HOT Nhất Trực Tiếp Từ CĐT', '03/26/2020', NULL, 'louis-city-hoang-mai', 'Khu đô thị mới Louis City mang đến cho khách hàng những căn biệt thự sang trọng, liền kề, shophouse thương mại được thiết kế tân cổ điển theo phong cách châu Âu.', NULL, 'index, follow');
INSERT INTO `tbl_product` (`id`, `cat_id`, `dis_id`, `user`, `name`, `address`, `hits`, `img`, `price`, `hot`, `tag`, `status`, `detail`, `tab1`, `tab2`, `tab3`, `tab4`, `tab5`, `tab6`, `tab7`, `tab8`, `tab9`, `content1`, `content2`, `content3`, `content4`, `content5`, `content6`, `content7`, `content8`, `content9`, `heading1`, `heading2`, `heading3`, `heading4`, `heading5`, `heading6`, `heading7`, `heading8`, `heading9`, `title`, `date`, `date_up`, `slug`, `description`, `keywords`, `robot`) VALUES
(135, '69', 56, 'Admin', 'The Terra An Hưng', 'KĐT An Hưng', 1687, 'the-terra-an-hung.jpg', NULL, 'false', NULL, 'true', '<ul>\r\n	<li style=\"text-align:justify\">T&ecirc;n dự &aacute;n: The Terra An Hưng</li>\r\n	<li style=\"text-align:justify\">Vị tr&iacute;: KĐT An Hưng, La Kh&ecirc;, H&agrave; Đ&ocirc;ng, H&agrave; Nội</li>\r\n	<li style=\"text-align:justify\">Loại h&igrave;nh sản phẩm: Tổ hợp thương mại dịch vụ, liền kề, shophouse v&agrave; căn hộ chung cư</li>\r\n	<li style=\"text-align:justify\">CĐT: C&ocirc;ng ty Cổ phần Đầu tư Văn Ph&uacute; - Invest</li>\r\n	<li style=\"text-align:justify\">Quy m&ocirc; dự &aacute;n: 35.380 m2</li>\r\n	<li style=\"text-align:justify\">Diện t&iacute;ch x&acirc;y dựng: 16.499 m2</li>\r\n	<li style=\"text-align:justify\">Khối thấp tầng: Gồm 166 căn liền kề, Shophouse cao&nbsp; 5 - 7 tầng, mặt tiền rộng tr&ecirc;n 6,5m v&agrave; diện t&iacute;ch hợp l&yacute;</li>\r\n	<li style=\"text-align:justify\">Khối cao tầng: Gồm 3 t&ograve;a chung cư cao 45 tầng với 1328 căn hộ</li>\r\n	<li style=\"text-align:justify\">Dự kiến b&agrave;n giao khối thấp tầng: Qu&yacute; IV/2019</li>\r\n	<li style=\"text-align:justify\">Dự kiến b&agrave;n giao khối cao tầng: Qu&yacute; IV/2021</li>\r\n</ul>', 'Tổng quan', 'Vị trí', 'Tiện ích', 'Mặt bằng', 'Tiến độ thanh toán', 'Căn Dual Key', NULL, NULL, 'Chính sách', '<p style=\"text-align:justify\"><a href=\"https://maivietland.vn/du-an-bds/the-terra-an-hung.html\"><strong>The Terra An Hưng</strong></a> l&agrave; dự &aacute;n tổ hợp thương mại dịch vụ v&agrave; căn hộ cao cấp được chủ đầu tư Văn Ph&uacute; x&acirc;y dựng với đầy đủ kh&ocirc;ng gian chức năng c&ugrave;ng với c&aacute;c đ&ocirc; thị ph&iacute;a T&acirc;y H&agrave; Nội tạo n&ecirc;n một quần thể đ&ocirc; thị hiện đại, thống nhất. Với những điểm nhấn hiện đại c&ugrave;ng hệ thống tiện &iacute;ch đa dạng nằm trong khu vực c&oacute; hạ tầng đồng bộ, dự &aacute;n hứa hẹn sẽ tạo n&ecirc;n sức h&uacute;t mạnh mẽ tr&ecirc;n thị trường.</p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>Tại sao c&aacute;c gia đ&igrave;nh n&ecirc;n lựa chọn The Terra An Hưng l&agrave; nơi an cư?</strong></h3>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Tọa lạc tại vị tr&iacute; thuận lợi, kết nối v&ugrave;ng ho&agrave;n hảo</li>\r\n	<li style=\"text-align:justify\">Thiết kế căn hộ nổi bật với vẻ đẹp sang trọng, lịch l&atilde;m c&ugrave;ng đường lối kiến tr&uacute;c ph&oacute;ng kho&aacute;ng, hiện đại</li>\r\n	<li style=\"text-align:justify\">Vừa c&oacute; thể sử dụng gia đ&igrave;nh vừa c&oacute; thể cho thu&ecirc; thu lời</li>\r\n	<li style=\"text-align:justify\">Tiện &iacute;ch cao cấp ngay ngưỡng cửa, đ&aacute;p ứng mọi nhu cầu cho cư d&acirc;n</li>\r\n	<li style=\"text-align:justify\">Ưu đ&atilde;i t&agrave;i ch&iacute;nh cực hấp dẫn, gia đ&igrave;nh trẻ kinh tế chưa mạnh vẫn c&oacute; thể mua</li>\r\n</ul>', '<p style=\"text-align:justify\">The <a href=\"https://maivietland.vn/du-an-bds/the-terra-an-hung.html\"><strong>Terra An Hưng</strong></a> tọa lạc ngay tại cửa ng&otilde; của KĐT An Hưng, mặt đường Tố Hữu, La Kh&ecirc;, H&agrave; Đ&ocirc;ng. Từ dự &aacute;n, cư d&acirc;n c&oacute; thể dễ d&agrave;ng di chuyển tới trung t&acirc;m th&agrave;nh phố nhờ tiếp cận trạm xe bus BRT v&agrave; tuyến đường sắt tr&ecirc;n cao.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"vị trí the terra an hưng\" src=\"/public/upload/files/the-terra-anhung/vitri.jpg\" /></p>\r\n\r\n<p style=\"text-align:justify\">Với hạ tầng đồng bộ, vị tr&iacute; đắc địa, cư d&acirc;n đi tới ga t&agrave;u điện La Kh&ecirc; chỉ 6 ph&uacute;t, tới bến xe Y&ecirc;n Nghĩa chỉ 8 ph&uacute;t, tới Trung t&acirc;m Hội nghị Quốc gia 15 ph&uacute;t&hellip; v&agrave; nhiều vị tr&iacute; trọng yếu kh&aacute;c trong th&agrave;nh phố. Đồng thời, từ dự &aacute;n cư dẫn dễ d&agrave;ng di chuyển v&agrave;o khu vực trung t&acirc;m nội đ&ocirc; qua c&aacute;c tuyến đường Tố Hữu - L&ecirc; Văn Lương, Đại lộ Thăng Long v&agrave; Nguyễn Tr&atilde;i cũng như kết nối v&ocirc; c&ugrave;ng thuận tiện tới c&aacute;c tỉnh th&agrave;nh ph&iacute;a Bắc.</p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>Li&ecirc;n kết v&ugrave;ng Terra An Hưng H&agrave; Đ&ocirc;ng</strong></h3>\r\n\r\n<table border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:100%\">\r\n	<tbody>\r\n		<tr>\r\n			<td>Ga T&agrave;u Điện La Kh&ecirc;: 6 Ph&uacute;t</td>\r\n			<td>Aeon Mall H&agrave; Đ&ocirc;ng: 7 Ph&uacute;t</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Thi&ecirc;n Đường Bảo Sơn: 9 ph&uacute;t</td>\r\n			<td>Bến Xe Y&ecirc;n Nghĩa: 10 ph&uacute;t</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Bệnh Viện Qu&acirc;n Y 103: 13 Ph&uacute;t</td>\r\n			<td>ĐH H&agrave; Nội: 14 Ph&uacute;t</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Trung T&acirc;m Hội Nghị Quốc Gia: 14 Ph&uacute;t</td>\r\n			<td>T&ograve;a Th&aacute;p Văn Ph&ograve;ng Keangnam: 16 ph&uacute;t</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Royal City: 20 ph&uacute;t</td>\r\n			<td>Hồ Ho&agrave;n Kiếm: 40 ph&uacute;t</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Liên kết vùng the terra an hưng\" src=\"/public/upload/files/the-terra-anhung/lien-ket-vung-the-terra-an-hung.jpg\" /></p>', '<p><strong>Tiện &iacute;ch Ngoại khu:</strong>&nbsp;Tọa lạc tại Thủ phủ T&acirc;y Nam - khu vực đang trỗi dậy với tiềm năng kinh tế lớn mạnh v&agrave; sự xuất hiện của h&agrave;ng loạt c&aacute;c khu đ&ocirc; thị chuẩn quốc tế, điểm đến của cộng đồng cư d&acirc;n nước ngo&agrave;i: Singapore, H&agrave;n Quốc, Hong Kong&hellip;, The Terra Văn Ph&uacute;&nbsp;sở hữu nhiều ưu thế vượt trội về y tế, gi&aacute;o dục, văn h&oacute;a, thương mại, vui chơi giải tr&iacute;&hellip;</p>\r\n\r\n<p><strong>Về Gi&aacute;o dục:</strong> xung quanh dự &aacute;n l&agrave; h&agrave;ng loạt c&aacute;c trường học danh tiếng như: Trường quốc tế Nhật Bản, Trường Olympia, Trường quốc tế Việt Nam, Đại học Kiến tr&uacute;c, Học viện Bưu ch&iacute;nh viễn th&ocirc;ng&hellip;</p>\r\n\r\n<p><strong>Về y tế:</strong> Sống tại Terra An Hưng sức khỏe của bạn v&agrave; gia đ&igrave;nh sẽ lu&ocirc;n được đảm bảo bởi xung quanh dự &aacute;n c&oacute; rất nhiều bệnh viện hiện đại chất lượng cao: Bệnh viện Qu&acirc;n đội 103, Bệnh viện đa khoa H&agrave; Đ&ocirc;ng&hellip;</p>\r\n\r\n<p><strong>Khu mua sắm:</strong> Hệ thống si&ecirc;u thị, trung t&acirc;m thương mại lớn: BigC Thăng Long, BigC Hồ Gươm, Aeon Mall H&agrave; Đ&ocirc;ng&hellip;</p>\r\n\r\n<p><strong>Tiện &iacute;ch nội khu đẳng cấp: </strong>Với kh&aacute;t vọng kiến tạo cuộc sống chuẩn hiện đại cho cư d&acirc;n tương lai, ngay trong khu&ocirc;n vi&ecirc;n của dự &aacute;n Chủ đầu tư đ&atilde; mạnh tay trong việc đầu tư hệ thống 32 tiện &iacute;ch nội khu sang trọng, hiện đại. The Terra An Hưng sở hữu m&ocirc; h&igrave;nh tiện &iacute;ch &ldquo;All in one&rdquo; ngay trong nội khu dự &aacute;n, hội tụ đầy đủ những tiện &iacute;ch đẳng cấp phục vụ mọi nhu cầu của cư d&acirc;n:</p>\r\n\r\n<table border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:100%\">\r\n	<tbody>\r\n		<tr>\r\n			<td>Quảng trường nghệ thuật</td>\r\n			<td>Nh&agrave; trẻ (Kindergarten)</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Ốc đảo xanh (Kid island)</td>\r\n			<td>Khu vực l&agrave;m đẹp, thời trang</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Khu vực văn ph&ograve;ng, dịch vụ</td>\r\n			<td>Bể bơi 4 m&ugrave;a</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Ph&ograve;ng tập Yoga</td>\r\n			<td>Đồ ăn nhanh, nh&agrave; h&agrave;ng</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Ph&ograve;ng tập Gym &amp; Fitness</td>\r\n			<td>Khu vực BBQ</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Cầu treo</td>\r\n			<td>Khu vực sinh hoạt cộng đồng</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Vườn tr&ecirc;n m&aacute;i</td>\r\n			<td>S&acirc;n chơi ngo&agrave;i trời</td>\r\n		</tr>\r\n		<tr>\r\n			<td>2 tầng hầm v&agrave; 3 tầng nổi để xe</td>\r\n			<td>Gian l&aacute;nh nạn đảm bảo an to&agrave;n PCCC</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', '<p style=\"text-align:justify\"><strong><a href=\"https://maivietland.vn/du-an-bds/the-terra-an-hung.html\">Chung cư The Terra</a> An Hưng </strong>l&agrave; sự kết hợp của những kh&ocirc;ng gian hiện đại với thiết kế tinh tế ấn tượng trong từng đường n&eacute;t. Ba t&ograve;a th&aacute;p chung cư The Terra An Hưng nằm ch&iacute;nh giữa của dự &aacute;n v&agrave; được bao quanh bởi khu nh&agrave; thấp tầng. L&agrave; sự giao h&ograve;a độc đ&aacute;o của n&eacute;t kiến tr&uacute;c hiện đại mang đậm chất Ch&acirc;u &Acirc;u trong l&ograve;ng H&agrave; Nội.</p>\r\n\r\n<p style=\"text-align:justify\">Dưới b&agrave;n tay t&agrave;i hoa của đội ngũ kiến tr&uacute;c sư h&agrave;ng đầu, mỗi căn hộ đều được bố tr&iacute; v&agrave; thiết kế hợp l&yacute;, tối ưu h&oacute;a diện t&iacute;ch v&agrave; c&ocirc;ng năng sử dụng đảm bảo c&oacute; đủ cả kh&ocirc;ng gian sinh hoạt chung v&agrave; kh&ocirc;ng gian ri&ecirc;ng tư cho mỗi th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh.</p>\r\n\r\n<p style=\"text-align:justify\">Cả 3 t&ograve;a chung cư đều sở hữu mật độ l&yacute; tưởng:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">11 - 12 căn/ 7 thang m&aacute;y/ tầng</li>\r\n	<li style=\"text-align:justify\">Diện t&iacute;ch linh hoạt từ 68,7 &ndash; 140,8m2 bố tr&iacute; 2 &ndash; 4 PN mang đến nhiều lựa chọn cho kh&aacute;ch h&agrave;ng.</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">Đặc biệt, Chung Cư The Terra - An Hưng c&ograve;n được chủ đầu tư cho ra mắt d&ograve;ng sản phẩm căn hộ Dual Key ( mẫu căn hộ 2 ch&igrave;a kh&oacute;a) được thiết kế một mặt s&agrave;n chia th&agrave;nh 2 căn hộ sử dụng 2 cửa kh&aacute;c nhau, mẫu căn hộ n&agrave;y đặc biệt ph&ugrave; hợp với gia đ&igrave;nh đa thế hệ.</p>\r\n\r\n<p style=\"text-align:justify\">Với việc sắp xếp c&aacute;c khoảng kh&ocirc;ng gian v&ocirc; c&ugrave;ng hợp l&yacute; c&aacute;c căn hộ Dual Key b&ecirc;n cạnh chức năng để ở c&ograve;n c&oacute; thể kết hợp vừa l&agrave;m văn ph&ograve;ng, vừa kinh doanh,... rất thuận tiện cho gia chủ.</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Shophouse The Terra An Hưng</strong> được thiết kế với những d&atilde;y phố c&oacute; kh&ocirc;ng gian mở, kết nối linh hoạt đường Tố Hữu - Khu đ&ocirc; thị An Hưng - Khu đ&ocirc; thị Dương Nội Nam Cường. Với mặt tiền rộng tr&ecirc;n 6.5m, chiều cao 7 tầng ( 6 tầng 1 tum) v&agrave; c&aacute;c loại diện t&iacute;ch, cư d&acirc;n The Terra c&oacute; thể lựa chọn linh hoạt c&aacute;c mẫu Shophouse từ 65m2 đến 138m2.</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Liền kề The Terra An Hưng</strong> với sự bao bọc ph&iacute;a tr&ecirc;n l&agrave; c&aacute;c mặt shophouse v&agrave; ph&iacute;a dưới với 3 t&ograve;a chung cư cao 45 tầng thiết kế với chiều cao 5 tầng 1 tum. Với số lượng kh&aacute; lớn kh&aacute; ph&ugrave; hợp để ở tr&aacute;nh đi kh&oacute;i bụi ồn &agrave;o của mặt đường v&agrave; khả năng kinh doanh phục vụ cho cư d&acirc;n chung cư của dự &aacute;n cũng như phục vụ người d&acirc;n c&aacute;c khu l&acirc;n cận.</p>', '<p style=\"text-align:center\"><img alt=\"tiến độ thanh toán\" src=\"/public/upload/files/the-terra-anhung/tiendo.png\" /></p>', '<ul>\r\n	<li>Với thiết kế th&ocirc;ng minh c&ugrave;ng diện t&iacute;ch l&ecirc;n tới 140m&sup2;, d&ograve;ng căn hộ Dual Key tại The Terra - An Hưng sở hữu tới 2 lối đi ri&ecirc;ng biệt, chia đ&ocirc;i kh&ocirc;ng gian b&ecirc;n trong v&agrave; mang tới sự ri&ecirc;ng tư hơn d&ograve;ng căn hộ truyền thống. Từ đ&oacute;, sản phẩm ch&iacute;nh l&agrave; ch&igrave;a kh&oacute;a mở ra những giải ph&aacute;p tuyệt vời:</li>\r\n	<li>Tối ưu chi ph&iacute;: đ&acirc;y l&agrave; giải ph&aacute;p &ldquo;mua 1 được 2&rdquo;, một căn hộ Dual Key tương đương với 2 &ldquo;căn hộ&rdquo; nhỏ gi&uacute;p cư d&acirc;n tận dụng tối đa kh&ocirc;ng gian để ở hoặc cho thu&ecirc; sinh lời.</li>\r\n	<li>Gắn kết gia đ&igrave;nh: một b&ecirc;n d&agrave;nh cho bố mẹ lớn tuổi, một b&ecirc;n cho gia đ&igrave;nh trẻ, căn hộ mang tới cơ hội để cả nh&agrave; gần nhau hơn m&agrave; vẫn giữ sự ri&ecirc;ng tư cần thiết. Những vấn đề như &ldquo;đi sớm về muộn&rdquo;, kh&aacute;c biệt trong giờ giấc sinh hoạt v&agrave; mong muốn b&agrave;i tr&iacute; kh&ocirc;ng gian kh&aacute;c nhau của 2 thế hệ đều dễ d&agrave;ng giải quyết tại căn hộ Dual Key.</li>\r\n	<li>Đầu tư linh hoạt: cư d&acirc;n c&oacute; thể ở một phần v&agrave; cho thu&ecirc; phần c&ograve;n lại để sinh lời thụ động, dễ d&agrave;ng quản l&yacute;. Hoặc cả 2 phần c&oacute; thể trở th&agrave;nh 2 t&agrave;i sản sinh lời ri&ecirc;ng biệt, c&oacute; phong c&aacute;ch trang tr&iacute; v&agrave; đ&aacute;p ứng đa dạng nhu cầu của kh&aacute;ch thu&ecirc;.</li>\r\n	<li>Lựa chọn căn hộ Dual Key, cư d&acirc;n sẽ sở hữu một phong c&aacute;ch sống - đầu tư th&ocirc;ng minh, thời thượng với mức chi ph&iacute; hợp l&yacute; hiếm c&oacute;.</li>\r\n</ul>\r\n\r\n<p style=\"text-align:center\"><img alt=\"căn hộ Dual Key tại The Terra\" src=\"/public/upload/files/the-terra-anhung/123.jpg\" /></p>', NULL, NULL, '<p style=\"text-align:justify\">Thanh to&aacute;n 10% GTCH k&yacute; ngay Hợp đồng Mua b&aacute;n</p>\r\n\r\n<p style=\"text-align:justify\">Hỗ trợ vay 65% GTCH l&atilde;i suất 0% trong 24 th&aacute;ng</p>\r\n\r\n<p style=\"text-align:justify\">Qu&agrave; t&acirc;n gia 15 triệu (căn 2PN) v&agrave; 20 triệu (căn 3PN)</p>\r\n\r\n<p>Căn 2PN chỉ từ 1,7 TỶ</p>\r\n\r\n<p>Căn 3PN chỉ từ 2 TỶ</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>', 'TỔNG QUAN DỰ ÁN THE TERRA AN HƯNG', 'VỊ TRÍ TRUNG TÂM - KẾT NỐI HOÀN HẢO CỦA TERRA AN HƯNG', 'TIỆN ÍCH HOÀN HẢO CHUNG CƯ TERRA AN HƯNG', 'MẶT BẰNG THIẾT KẾ THE TERRA AN HƯNG', 'TIẾN ĐỘ THANH TOÁN THE TERRA AN HƯNG', 'CHIA HAI KHÔNG GIAN, NHÂN BA LỢI ÍCH', NULL, NULL, 'CHÍNH SÁCH BÁN HÀNG', 'The Terra An Hưng -  Bảng Giá Và Thông Tin Mới Nhất Từ CĐT', '06/26/2020', NULL, 'the-terra-an-hung', 'The Terra An Hưng là dự án tổ hợp thương mại dịch vụ và căn hộ cao cấp được chủ đầu tư Văn Phú xây dựng với đầy đủ không gian chức năng cùng với các đô thị phía Tây HN', NULL, 'index, follow'),
(136, '69', 49, 'Admin', 'Sonasea Vân Đồn Harbor City', 'Xã Hạ Long, huyện Vân Đồn', 1131, 'tong_the_sonasea_van_don.jpg', NULL, 'false', NULL, 'true', '<ul>\r\n	<li style=\"text-align:justify\">T&ecirc;n dự &aacute;n:&nbsp;Singapore Shoptel&nbsp; Sonasea V&acirc;n Đồn Harbor City</li>\r\n	<li style=\"text-align:justify\">Vị tr&iacute;: Ph&acirc;n khu Complex, dự &aacute;n Sonasea V&acirc;n Đồn Harbor City, x&atilde; Hạ Long, H. V&acirc;n Đồn, T. Quảng Ninh</li>\r\n	<li style=\"text-align:justify\">Chủ đầu tư: CEO Group</li>\r\n	<li style=\"text-align:justify\">Tư vấn thiết kế kiến tr&uacute;c v&agrave; cảnh quan: Humphreys &amp; Partners Architects (Mỹ)</li>\r\n	<li style=\"text-align:justify\">Số lượng: 192 căn</li>\r\n	<li style=\"text-align:justify\">Số tầng: 5 tầng nổi, 1 tầng hầm</li>\r\n	<li style=\"text-align:justify\">Số vốn đầu tư: 1200 tỷ VNĐ</li>\r\n</ul>', 'Tổng quan', 'Lợi thế', 'Vị trí', 'Mặt bằng', 'Tiện ích', 'Đầu tư', NULL, NULL, 'Chính sách', '<p style=\"text-align:justify\">Vịnh B&aacute;i Tử Long sở hữu hệ thống đảo đ&aacute; đa dạng, c&aacute;c h&ograve;n đ&aacute; cao thấp nhấp nh&ocirc;, nổi tr&ecirc;n tr&ecirc;n mặt nước xanh biếc h&ograve;a c&ugrave;ng m&acirc;y n&uacute;i bạt ng&agrave;n tạo n&ecirc;n cảnh sắc n&ecirc;n thơ, tuyệt mỹ, đủ sức m&ecirc; hoặc mọi du kh&aacute;ch mỗi lần gh&eacute; thăm.</p>\r\n\r\n<p style=\"text-align:justify\">Với địa thế &ldquo;tọa sơn hướng hải&rdquo; mở rộng về ph&iacute;a vịnh B&aacute;i Tử Long, Tổ hợp du lịch nghỉ dưỡng Sonasea V&acirc;n Đồn Harbor City sở hữu 02 km đường bờ biển hoang sơ tuyệt mỹ c&ugrave;ng địa h&igrave;nh cao dần, thuận lợi cho ph&aacute;t triển du lịch nghỉ dưỡng.</p>\r\n\r\n<p style=\"text-align:justify\">Trong giai đoạn đầu của dự &aacute;n, CEO Group cho ra mắt khu phố thương mại Singapore Shoptel. Ph&aacute;t triển theo m&ocirc; h&igrave;nh shoptel th&agrave;nh c&ocirc;ng bậc nhất tại Quốc đảo sư tử, Singapore Shoptel mở ra khung cảnh những d&atilde;y cửa hiệu sang trọng, tuyến phố đi bộ sầm uất với c&aacute;c hoạt động văn h&oacute;a giải tr&iacute; s&ocirc;i động diễn ra suốt bốn m&ugrave;a trong năm.</p>\r\n\r\n<p style=\"text-align:justify\">Với thiết kế ấn tượng mang đặc trưng ri&ecirc;ng , phản &aacute;nh sự ph&aacute;t triển nhanh ch&oacute;ng của v&ugrave;ng đặc khu kinh tế V&acirc;n Đồn , khu nh&agrave; phố thương mại gồm 4 d&atilde;y nh&agrave;, mỗi d&atilde;y nh&agrave; được x&acirc;y dựng gồm 1 tầng hầm v&agrave; 5 tầng nổi, c&oacute; kết cấu chặt chẽ v&agrave; được kết nối li&ecirc;n ho&agrave;n với nhau trong 1 tổng thể nh&agrave; phố thương mại s&ocirc;i động. Đ&acirc;y chắc chắn sẽ l&agrave; điểm nhấn l&agrave;m gia tăng trải nghiệm mua sắm, ẩm thực của du kh&aacute;ch khi đến với Sonasea V&acirc;n Đồn Harbor City.</p>', '<ol>\r\n	<li><strong>Nằm trong khu kinh tế ven biển:</strong> Với cơ chế ch&iacute;nh s&aacute;ch đột ph&aacute; c&ugrave;ng nhiều ưu đ&atilde;i về thuế, đất đai... V&acirc;n Đồn mang đến cơ hội đầu tư vượt trội d&agrave;nh cho kh&aacute;ch h&agrave;ng.</li>\r\n	<li><strong>Kết nối v&ugrave;ng linh hoạt:</strong>&nbsp;Sở hữu vị tr&iacute; đắc địa b&ecirc;n tỉnh lộ 334, Singapore Shoptel dễ d&agrave;ng tiếp cận với c&aacute;c thị trường du lịch lớn như H&agrave; Nội, Trung Quốc, Asean &amp; Thế giới nhờ s&acirc;n bay V&acirc;n Đồn, cao tốc H&agrave; Nội - M&oacute;ng C&aacute;i v&agrave; cảng t&agrave;u kh&aacute;ch quốc tế.</li>\r\n	<li><strong>Thu h&uacute;t lượng du kh&aacute;ch lớn:</strong>&nbsp;Tọa lạc tại trung t&acirc;m b&atilde;i D&agrave;i, Singapore Shoptel l&agrave; điểm đến giải tr&iacute; thuộc tổ hợp Sonasea V&acirc;n Đồn Harbor City. Liền kề khu nghỉ dưỡng quy m&ocirc; 1000 ph&ograve;ng Sonasea V&acirc;n Đồn Complex, Singapore Shoptel đ&aacute;p ứng nhu cầu mua sắm của du kh&aacute;ch.</li>\r\n	<li><strong>Tiện &iacute;ch - dịch vụ đa dạng:</strong>&nbsp;Thừa hưởng hệ thống tiện &iacute;ch - khu vui chơi cao cấp trong nội khu Sonasea V&acirc;n Đồn Harbor City c&ugrave;ng hệ thống trục cảnh quan, phố đi bộ đặc sắc, Singapore Shoptel mang đến kh&ocirc;ng gian trải nghiệm mới lạ.</li>\r\n	<li><strong>Kiến tr&uacute;c shoptel ưu việt:&nbsp;</strong>Được thiết kế bởi Humphrey &amp; Partners Architects (Mỹ), 100% c&aacute;c căn nh&agrave; phố Singapore Shoptel sở hữu &iacute;t nhất 2 mặt tiền v&agrave; 6 tầng nổi - 1 tầng hầm, tối đa kh&ocirc;ng gian d&agrave;nh cho hoạt động kinh doanh.</li>\r\n	<li><strong>Sở hữu l&acirc;u d&agrave;i:</strong>&nbsp;Nh&agrave; phố Singapore Shoptel l&agrave; một trong số hữu hạn c&aacute;c dự &aacute;n bất động sản được x&acirc;y dựng tr&ecirc;n đất ở tại vị tr&iacute; đắt gi&aacute; ven biển. Với đặc quyền sở hữu l&acirc;u d&agrave;i, Singapore Shoptel l&agrave; lựa chọn đầu tư bền vững tại V&acirc;n Đồn.</li>\r\n	<li><strong>H&igrave;nh thức kinh doanh linh hoạt:</strong>&nbsp;Với diện t&iacute;ch sử dụng lớn, Singapore Shoptel ph&ugrave; hợp cho c&aacute;c hoạt động kinh doanh kết hợp (như tầng 1 mở cửa h&agrave;ng v&agrave; c&aacute;c tầng tr&ecirc;n kinh doanh lưu tr&uacute;), tối đa nguồn doanh thu từ sản phẩm bất động sản.</li>\r\n	<li><strong>Tầng hầm kết nối hiện đại:</strong>&nbsp;Singapore Shoptel sở hữu đường hầm để xe hiện đại, kết nối trực tiếp với tầng để xe của từng nh&agrave;, thuận tiện để lưu giữ phương tiện cũng như tối đa diện t&iacute;ch sử dụng.</li>\r\n	<li><strong>Sản phẩm đầu tư sinh lời bền vững:</strong>&nbsp;Sở hữu vị tr&iacute; trung t&acirc;m khu kinh tế ven biển, nằm trong Tổ hợp Sonasea V&acirc;n Đồn Harbor City, nh&agrave; phố thương mại Singapore Shoptel l&agrave; sản phẩm bất động sản c&oacute; tiềm năng tăng gi&aacute; cao v&agrave; thanh khoản tốt d&agrave;nh nh&agrave; đầu tư.</li>\r\n	<li><strong>Chủ đầu tư uy t&iacute;n:&nbsp;</strong>Tập đo&agrave;n CEO - TOP 10 Tập đo&agrave;n uy t&iacute;n ng&agrave;nh bất động sản, đặc biệt th&agrave;nh c&ocirc;ng với c&aacute;c khu nghỉ dưỡng 5 sao quốc tế như Novotel Phu Quoc Resort, Novotel Villas, Best Western Premier Sonasea Phu Quoc...</li>\r\n</ol>', '<p style=\"text-align:justify\">Nằm b&ecirc;n tỉnh lộ 334 (3km đường qua dự &aacute;n dự kiến sẽ được mở rộng l&ecirc;n 44m v&agrave;o năm 2020), Sonasea Singapore shoptel kết nối linh hoạt đến c&aacute;c điểm du lịch nổi tiếng tại Quảng Ninh cũng như c&aacute;c tỉnh/ th&agrave;nh phố khu vực ph&iacute;a Bắc v&agrave; Trung Quốc nhờ tuyến cao tốc trọng điểm L&agrave;o Cai &ndash; H&agrave; Nội &ndash; Hải Ph&ograve;ng &ndash; Hạ Long &ndash; V&acirc;n Đồn &ndash; M&oacute;ng C&aacute;i. Đồng thời, li&ecirc;n kết dễ d&agrave;ng với s&acirc;n bay quốc tế V&acirc;n Đồn v&agrave; cảng h&agrave;nh kh&aacute;ch quốc tế Hạ Long, Singapore shoptel hứa hẹn trở th&agrave;nh điểm đến tiện lợi kh&ocirc;ng thể bỏ qua d&agrave;nh cho du kh&aacute;ch trong nước v&agrave; quốc tế.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"vị trí\" src=\"/public/upload/files/sonasea-van-don/map-location-sonasea-vandon.png\" /></p>\r\n\r\n<p style=\"text-align:justify\">Li&ecirc;n kết v&ugrave;ng Singapore Shoptel CEO V&acirc;n Đồn:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Nằm b&ecirc;n tỉnh lộ 334 &ndash; tuyến giao th&ocirc;ng huyết mạch tại V&acirc;n Đồn.</li>\r\n	<li style=\"text-align:justify\">C&aacute;ch tuyến cao tốc L&agrave;o Cai &ndash; H&agrave; Nội &ndash; Hải Ph&ograve;ng &ndash; Hạ Long V&acirc;n Đồn &ndash; M&oacute;ng C&aacute;i 5 ph&uacute;t di chuyển.</li>\r\n	<li style=\"text-align:justify\">C&aacute;ch s&acirc;n bay quốc tế V&acirc;n Đồn 18 ph&uacute;t di chuyển.</li>\r\n	<li style=\"text-align:justify\">C&aacute;ch cảng h&agrave;nh kh&aacute;ch quốc tế Hạ Long 1 giờ di chuyển.</li>\r\n	<li style=\"text-align:justify\">C&aacute;ch trung t&acirc;m th&agrave;nh phố Hạ Long 50 ph&uacute;t di chuyển.</li>\r\n	<li style=\"text-align:justify\">C&aacute;ch trung t&acirc;m th&agrave;nh phố M&oacute;ng C&aacute;i 50 ph&uacute;t di chuyển.</li>\r\n	<li style=\"text-align:justify\">C&aacute;ch trung t&acirc;m thủ đ&ocirc; H&agrave; Nội 2,5 giờ di chuyển.</li>\r\n	<li style=\"text-align:justify\">C&aacute;ch trung t&acirc;m th&agrave;nh phố Hải Ph&ograve;ng 1,5 giờ di chuyển.</li>\r\n	<li style=\"text-align:justify\">C&aacute;ch Ch&ugrave;a C&aacute;i Bầu 5 ph&uacute;t di chuyển.</li>\r\n	<li style=\"text-align:justify\">Kết nối thuận tiện tới c&aacute;c điểm du lịch, giải tr&iacute; như vịnh B&aacute;i Tử Long, s&acirc;n golf quốc tế, khu Casino, đảo Quan Lạn&hellip;</li>\r\n</ul>', '<p style=\"text-align:justify\">Singapore Shoptel l&agrave; dự &aacute;n bao gồm 24 block nh&agrave; phố được quy hoạch ph&aacute;t triển th&agrave;nh 4 ph&acirc;n khu độc đ&aacute;o: văn h&oacute;a - nghệ thuật, gia đ&igrave;nh &ndash; trẻ em, ẩm thực, &acirc;m nhạc &ndash; giải tr&iacute;. Ưu thế nổi bật tại Singapore Shoptel ch&iacute;nh l&agrave; hệ thống đường hầm th&ocirc;ng minh kết nối trực tiếp với tầng để xe của từng căn nh&agrave;, mang đến kh&ocirc;ng gian lưu giữ phương tiện giao th&ocirc;ng rộng r&atilde;i cũng như k&eacute;o d&agrave;i thời gian lưu tr&uacute; của du kh&aacute;ch tại V&acirc;n Đồn.</p>\r\n\r\n<p style=\"text-align:justify\">Kiến tr&uacute;c shoptel mang phong c&aacute;ch Singapore, được quy hoạch bởi Humphreys &amp; Partners Architecture. Singapore shoptel mang đến sự thịnh vượng d&agrave;nh ri&ecirc;ng cho chủ nh&acirc;n xứng tầm từ kiến tr&uacute;c Singapore đột ph&aacute;.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Được x&acirc;y dựng 6 tầng nổi v&agrave; 1 tầng hầm, shoptel tối đa kh&ocirc;ng gian d&agrave;nh cho kinh doanh với 6 đến 20 ph&ograve;ng kh&aacute;ch sạn, 100% c&aacute;c căn c&oacute; 2 mặt tiền, ph&ugrave; hợp cho việc ph&aacute;t triển đa h&igrave;nh thức kinh doanh như để an cư, đầu tư, cho thu&ecirc;. Đặc biệt c&aacute;c căn shotel đều c&oacute; tầm nh&igrave;n tho&aacute;ng rộng ra Vịnh B&aacute;i Tử Long tuyệt đẹp, gi&uacute;p cư d&acirc;n v&agrave; du kh&aacute;ch c&oacute; thể chi&ecirc;m ngưỡng cảnh sắc thi&ecirc;n nhi&ecirc;n bao la, hữu t&igrave;nh.</p>\r\n\r\n<table border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:100%\">\r\n	<tbody>\r\n		<tr>\r\n			<td><strong>Mẫu shoptel DH1</strong></td>\r\n			<td><strong>Mẫu Shoptel DH2</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Tổng diện t&iacute;ch l&ocirc; đất: 88 &ndash; 124m2</td>\r\n			<td>Diện t&iacute;ch l&ocirc; đất: 122 &ndash; 137m2</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Tổng diện t&iacute;ch s&agrave;n: 489m2 &ndash; 543m2</td>\r\n			<td>Tổng diện t&iacute;ch s&agrave;n: 683 &ndash; 710m2</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Số tầng: 1 tầng hầm v&agrave; 6 tầng nổi</td>\r\n			<td>Số tầng: 1 tầng hầm v&agrave; 6 tầng nổi</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Số ph&ograve;ng ngủ: 6 &ndash; 8 ph&ograve;ng</td>\r\n			<td>Số ph&ograve;ng ngủ: 12 &ndash; 16 ph&ograve;ng</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:100%\">\r\n	<tbody>\r\n		<tr>\r\n			<td><strong>Mẫu shoptel DH3</strong></td>\r\n			<td><strong>Mẫu shoptel DH4</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Diện t&iacute;ch l&ocirc; đất: 190 &ndash; 256m2</td>\r\n			<td>Diện t&iacute;ch l&ocirc; đất: 255 &ndash; 263m2</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Tổng diện t&iacute;ch s&agrave;n: 738 &ndash; 961m2</td>\r\n			<td>Tổng diện t&iacute;ch s&agrave;n: 1132 &ndash; 1200m2</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Số tầng: 1 tầng hầm v&agrave; 6 tầng nổi</td>\r\n			<td>Số tầng: 1 tầng hầm v&agrave; 6 tầng nổi</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Số ph&ograve;ng ngủ: 11 &ndash; 19 ph&ograve;ng</td>\r\n			<td>Số ph&ograve;ng ngủ: 11 &ndash; 20 ph&ograve;ng</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', '<p style=\"text-align:justify\">Kh&ocirc;ng chỉ sở hữu ri&ecirc;ng cho m&igrave;nh những tiện &iacute;ch như phố đi bộ hoa vi&ecirc;n, đ&agrave;i phun nước độc đ&aacute;o, trung t&acirc;m vui chơi giải tr&iacute; trong nh&agrave; v&agrave; ngo&agrave;i trời&hellip; Đến Sonasea Singapore Shoptel, du kh&aacute;ch cũng c&oacute; thể dễ d&agrave;ng t&igrave;m thấy c&aacute;c điểm ăn uống đường phố, coffee shop sang trọng hay qu&aacute;n bar ngo&agrave;i trời lu&ocirc;n rực rỡ &aacute;nh đ&egrave;n c&ugrave;ng &acirc;m nhạc s&ocirc;i động.</p>\r\n\r\n<p style=\"text-align:justify\">B&ecirc;n cạnh đ&oacute; Singapore shoptel sẽ l&agrave; nơi tổ chức c&aacute;c lễ hội, hoạt động biểu diễn đặc sắc, s&ocirc;i động hứa hẹn sẽ thu h&uacute;t du kh&aacute;ch mọi nơi tr&ecirc;n thế giới. Một kh&ocirc;ng gian n&aacute;o nhiệt của c&aacute;c chương tr&igrave;nh giải tr&iacute;, những lễ hội văn h&oacute;a đặc sắc mang m&agrave;u sắc miền biển, c&aacute;c điểm đến vui chơi đa sắc m&agrave;u d&agrave;nh cho mọi lứa tuổi&hellip; mở ra th&agrave;nh phố kh&ocirc;ng ngừng chuyển động c&ugrave;ng thời gian.</p>', '<p style=\"text-align:justify\">C&aacute;ch H&agrave; nội 2,5 giờ l&aacute;i xe &ocirc; t&ocirc;, Singapore shoptel hứa hẹn l&agrave; một trong những điểm đến đ&aacute;ng gi&aacute; tại khu vực ph&iacute;a Bắc, mở ra cơ hội đầu tư sinh lời hấp dẫn. Đặc biệt, nằm liền kề khu nghỉ dưỡng Sonasea V&acirc;n Đồn Complex quy m&ocirc; hơn 1000 ph&ograve;ng, Singapore shoptel th&uacute;c đẩy giao thương song h&agrave;nh c&ugrave;ng dịch vụ du lịch, tạo ra những gi&aacute; trị kh&ocirc;ng ngừng gia tăng theo thời gian d&agrave;nh cho chủ sở hữu.</p>\r\n\r\n<p style=\"text-align:justify\">Mặt kh&aacute;c, t&ecirc;n tuổi đơn vị thiết kế h&agrave;ng đầu n&agrave;y kết hợp với danh tiếng của chủ đầu tư Tập đo&agrave;n CEO - Top 11 nh&agrave; ph&aacute;t triển bất động sản uy t&iacute;n nhất Việt Nam (Giải thưởng Bất động sản quốc gia 2018) ch&iacute;nh l&agrave; bảo chứng cho chất lượng, sự vượt trội về đẳng cấp, kh&aacute;c biệt của c&ocirc;ng tr&igrave;nh Singapore shoptel Sonasea V&acirc;n Đ&ocirc;̀n Harbor City đang được h&igrave;nh th&agrave;nh tại v&ugrave;ng đất Rồng.</p>', NULL, NULL, NULL, 'Tổng quan dự án Sonasea Vân Đồn Harbor City', '10 LỢI THẾ ĐẦU TƯ SINGAPORE SHOPTEL', 'VỊ TRÍ NỔI BẬT GIỮA TRUNG TÂM KINH TẾ VÂN ĐỒN', 'MẶT BẰNG THIẾT KẾ SINGAPORE SHOPTEL', 'TIỆN ÍCH SINGAPORE SHOPTEL SONASEA VÂN ĐỒN HARBOR CITY', 'CƠ HỘI ĐẦU TƯ SINGAPORE SHOPTEL KHÔNG THỂ BỎ LỠ', NULL, NULL, NULL, 'Singapore Shoptel - Sonasea Vân Đồn Harbor City', '06/26/2020', NULL, 'sonasea-van-don-harbor-city', 'Phát triển theo mô hình shoptel thành công bậc nhất tại Quốc đảo sư tử, Singapore Shoptel mở ra khung cảnh những dãy cửa hiệu sang trọng, tuyến phố đi bộ sầm uất', NULL, 'index, follow'),
(137, '69', 49, 'Admin', 'Harbor Bay Hạ Long', 'Bán đảo 2 Khu đô thị Hạ Long Marina', 861, 'sua.jpg', NULL, NULL, NULL, 'true', '<ul>\r\n	<li style=\"text-align:justify\">T&ecirc;n dự &aacute;n: Harbor Bay</li>\r\n	<li style=\"text-align:justify\">Chủ đầu tư: BIM Group</li>\r\n	<li style=\"text-align:justify\">Diện t&iacute;ch l&ocirc; đất: 13,7ha</li>\r\n	<li style=\"text-align:justify\">Số lượng căn: 375 căn</li>\r\n	<li style=\"text-align:justify\">Mật độ x&acirc;y dựng: 23%</li>\r\n	<li style=\"text-align:justify\">Số tầng: 5</li>\r\n	<li style=\"text-align:justify\">Tổng diện t&iacute;ch s&agrave;n x&acirc;y dựng: 309m2 - 893m2</li>\r\n	<li style=\"text-align:justify\">K&iacute;ch thước l&ocirc; đất: 74m2 - 237m2</li>\r\n	<li style=\"text-align:justify\">K&iacute;ch thước mặt tiền trung b&igrave;nh: 6,5m</li>\r\n</ul>', 'Tổng quan', 'Lợi thế', 'Vị trí', 'Mặt bằng', 'Tiện ích', 'Thanh toán', NULL, NULL, 'Chính sách', '<p style=\"text-align:justify\">Harbor Bay l&agrave; dự &aacute;n nh&agrave; phố thương mại của BIM Group c&oacute; vị tr&iacute; tại b&aacute;n đảo 2 Hạ Long Marina, được x&aacute;c định l&agrave; khu vực trung t&acirc;m thương mại, vui chơi giải tr&iacute;, mua sắm. Dự &aacute;n hứa hẹn mang đến trải nghiệm sống đẳng cấp, trẻ trung v&agrave; ho&agrave;n to&agrave;n mới lạ.&nbsp;<br />\r\nLấy cảm hứng về một Địa Trung Hải rực nắng tươi đẹp, Harbor Bay tạo n&ecirc;n một điểm nhấn kiến tr&uacute;c kh&aacute;c lạ với những d&atilde;y phố uốn lượn rợp b&oacute;ng c&acirc;y, bao quanh bởi đường ven biển, cận kề bến cảng sầm uất. Vừa đủ kh&eacute;p k&iacute;n để an cư, nghỉ dưỡng, lại đủ ph&oacute;ng kho&aacute;ng, s&ocirc;i động để kinh doanh, Harbor Bay tượng trưng cho một điểm đến &ldquo;phồn thịnh&rdquo; b&ecirc;n bờ Vịnh. B&ecirc;n cạnh đ&oacute;, dự &aacute;n cũng sở hữu những lợi thế về vị tr&iacute;, tiện &iacute;ch, hứa hẹn sẽ l&agrave; dự &aacute;n c&oacute; tiềm năng ph&aacute;t triển đột ph&aacute; trong tương lai kh&ocirc;ng xa.<br />\r\n&nbsp;</p>', '<p style=\"text-align:justify\">Harbor bay c&oacute; nhiều lợi thế của một sản phẩm đầu tư an to&agrave;n, sinh lời vượt trội:</p>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\">Vị tr&iacute; đắc địa, phong thủy vượng ph&aacute;t</li>\r\n	<li style=\"text-align:justify\">Lợi &iacute;ch 2 in 1: Thỏa m&atilde;n nhu cầu để ở v&agrave; cho thu&ecirc; sinh lời</li>\r\n	<li style=\"text-align:justify\">Ph&aacute;p l&yacute; đầy đủ - Sổ đỏ l&acirc;u d&agrave;i</li>\r\n	<li style=\"text-align:justify\">Chủ đầu tư uy t&iacute;n - T&agrave;i ch&iacute;nh vững mạnh</li>\r\n	<li style=\"text-align:justify\">T&agrave;i sản an to&agrave;n - Sinh lời ổn định</li>\r\n</ol>\r\n\r\n<p style=\"text-align:justify\">C&aacute;c căn nh&agrave; phố Harbor Bay nhận được những đ&aacute;nh gi&aacute; rất t&iacute;ch cực nhờ t&iacute;nh đặc th&ugrave; cao bởi mang t&iacute;nh chất của một dự &aacute;n mở với nhiều tuyến đường huyết mạch li&ecirc;n khu đi qua n&ecirc;n nhiều căn nh&agrave; phố ở đ&acirc;y nằm tr&ecirc;n mặt đường lớn 17m, 25m&hellip;</p>\r\n\r\n<p style=\"text-align:justify\">Kh&ocirc;ng những vậy c&ograve;n c&oacute; c&aacute;c căn nh&agrave; vừa nằm tr&ecirc;n mặt đường lớn vừa c&oacute; tầm nh&igrave;n hướng khu quảng trường lớn, hướng hồ, mặt biển, mặt bến du thuyền &hellip;n&ecirc;n mang tiềm năng gia tăng gi&aacute; trị trong tương lai rất lớn ch&iacute;nh v&igrave; vậy đ&atilde; thu h&uacute;t rất nhiều kh&aacute;ch h&agrave;ng mong muốn l&agrave; chủ sở hữu của c&aacute;c bất động sản đặc biệt n&agrave;y.</p>', '<p>Dự &aacute;n Harbor Bay tọa lạc tại b&aacute;n đảo 2, b&aacute;n đảo trung t&acirc;m của khu đ&ocirc; thị Hạ Long Marina với tổng diện t&iacute;ch l&ecirc;n tới 248ha, trải d&agrave;i tr&ecirc;n 3.8km đường biển. Đ&acirc;y được xem l&agrave; tr&aacute;i tim của khu đ&ocirc; thị khi sở hữu vị tr&iacute; đất đắc địa bậc nhất tại Hạ Long. Dự &aacute;n hội tụ đầy đủ những tinh hoa nhất của một khu nghỉ dưỡng, kinh doanh sầm uất bậc nhất. Với c&aacute;c tiện &iacute;ch như: khu thương mại mua sắm, resort 5 sao InterContinental, Phố đi bộ, Quảng trường biển, b&atilde;i tắm, Marina Square</p>\r\n\r\n<table align=\"center\" border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:100%\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"text-align:justify\">50m &ndash; B&atilde;i biển Marina Bay&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</td>\r\n			<td style=\"text-align:justify\">100m &ndash; Tổ hợp thương mại &amp; giải tr&iacute; Marine Plaza</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:justify\">300m &ndash; Rạp chiếu phim CGV</td>\r\n			<td style=\"text-align:justify\">200m &ndash; Khu ẩm thực</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:justify\">300m &ndash; Chợ đ&ecirc;m Hạ Long</td>\r\n			<td style=\"text-align:justify\">3km &ndash; Bến xe B&atilde;i Ch&aacute;y</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:justify\">3km &ndash; Khu vui chơi giải tr&iacute; quốc tế Tuần Ch&acirc;u</td>\r\n			<td style=\"text-align:justify\">3,5km &ndash; C&ocirc;ng vi&ecirc;n Sunworld</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><br />\r\nB&ecirc;n cạnh đ&oacute;, c&aacute;c hạ tầng đường cao tốc li&ecirc;n kết đến Hạ Long đ&atilde; ho&agrave;n th&agrave;nh như : Cao tốc H&agrave; nội - Hải Ph&ograve;ng - Hạ Long, Cao Tốc Nội B&agrave;i V&acirc;n Đồn. Đặc biệt, s&acirc;n bay Quốc Tế V&acirc;n Đồn đ&atilde; ho&agrave;n th&agrave;nh v&agrave; đi v&agrave;o hoạt động sẽ mang đến những lượng kh&aacute;ch lớn từ c&aacute;c tỉnh Việt Nam v&agrave; Quốc tế. Qua đ&oacute; mang lại cơ hội đầu tư tiềm năng v&agrave; sinh lời bền vững cho nh&agrave; đầu tư lựa chọn Harbor Bay l&agrave; điểm đến đầu tư.<br />\r\n&nbsp;</p>', '<p style=\"text-align:justify\">Lấy cảm hứng kiến tr&uacute;c từ những th&agrave;nh phố cảng tại ch&acirc;u &Acirc;u v&agrave; Địa Trung Hải, Harbor Bay gồm 375 căn nh&agrave; phố 5 tầng được thiết kế duy&ecirc;n d&aacute;ng theo phong c&aacute;ch kiến tr&uacute;c Địa Trung Hải ph&oacute;ng kho&aacute;ng, s&ocirc;i động. Mỗi căn nh&agrave; phố được x&acirc;y dựng với 2 mặt tiền th&ocirc;ng nhau linh hoạt. Từng cụm nh&agrave; với 8 t&ocirc;ng m&agrave;u kh&aacute;c nhau uốn lượn theo h&igrave;nh c&aacute;nh cung tạo n&ecirc;n một điểm nhấn kiến tr&uacute;c kh&aacute;c lạ. Với những d&atilde;y phố rợp b&oacute;ng c&acirc;y, bao quanh bởi đường ven biển, cận kề bến cảng vừa đủ kh&eacute;p m&igrave;nh để an cư nghỉ dưỡng, lại đủ ph&oacute;ng kho&aacute;ng, s&ocirc;i động để kinh doanh.</p>\r\n\r\n<p style=\"text-align:justify\">Harbor Bay mang đậm tinh thần của một th&agrave;nh phố biển được thiết kế cực kỳ ph&oacute;ng kho&aacute;ng, mang hơi thở kiến tr&uacute;c Địa Trung Hải. Từng cụm nh&agrave; phố được sắp xếp theo h&igrave;nh c&aacute;nh cung hướng Vịnh &ndash; hướng hồ &ndash; hướng quảng trường biển Marina Bay.</p>', '<p style=\"text-align:justify\">Harbor Bay mang đến cho kh&aacute;ch h&agrave;ng một cuộc sống trọn vẹn với khung cảnh đẹp ngỡ ng&agrave;ng của thi&ecirc;n nhi&ecirc;n v&agrave; hệ thống tiện &iacute;ch cận kề phong ph&uacute;, hiện đại với:</p>\r\n\r\n<table align=\"center\" border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:100%\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"text-align:justify\">Đ&agrave;i phun nước &amp; Quảng trường</td>\r\n			<td style=\"text-align:justify\">Khu vui chơi trẻ em</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:justify\">Khu hoạt động thể thao</td>\r\n			<td style=\"text-align:justify\">Nh&agrave; h&agrave;ng tr&ecirc;n biển</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:justify\">Hệ thống bể bơi</td>\r\n			<td style=\"text-align:justify\">Khu ăn uống ngo&agrave;i trời</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:justify\">B&atilde;i biển Marina Bay</td>\r\n			<td style=\"text-align:justify\">Quảng trường biển</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:justify\">Bến du thuyền</td>\r\n			<td style=\"text-align:justify\">Tuyến phố đi bộ Marina Square</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style=\"text-align:justify\">B&ecirc;n cạnh đ&oacute;, L&agrave; một trong số &iacute;t c&aacute;c dự &aacute;n hưởng lợi từ việc gi&aacute;p cả hồ cảnh quan, bờ biển Vịnh Hạ Long cộng với mật độ x&acirc;y dựng chỉ 23% điều n&agrave;y đồng nghĩa với việc cư d&acirc;n tương của Harbor Bay sẽ được sống trong m&ocirc;i trường tr&agrave;n ngập sắc xanh của cỏ c&acirc;y hoa l&aacute;, tận hưởng khung cảnh n&ecirc;n thơ hữu t&igrave;nh của hồ nước, đặc biệt hơn thế nữa l&agrave; kh&ocirc;ng gian rộng lớn của biển cả.</p>', '<p style=\"text-align:center\"><img alt=\"TIẾN ĐỘ THANH TOÁN LINH HOẠT CỦA HARBOR BAY\" src=\"/public/upload/images/harbor-bay-halong/tt1.JPG\" style=\"height:261px; width:1127px\" /></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"TIẾN ĐỘ THANH TOÁN LINH HOẠT CỦA HARBOR BAY\" src=\"/public/upload/images/harbor-bay-halong/tt2.JPG\" style=\"height:251px; width:988px\" /></p>', NULL, NULL, '<ul>\r\n	<li>Hỗ trợ L&atilde;i suất 0%, &acirc;n hạn nợ gốc 12 th&aacute;ng cho 70% GTHĐ</li>\r\n	<li>Tặng Voucher mua xe &ocirc; t&ocirc; Vinfast Fadil (d&agrave;nh cho kh&aacute;ch h&agrave;ng kh&ocirc;ng vay)</li>\r\n	<li>Chiết khấu 6% cho KH kh&ocirc;ng vay + kh&ocirc;ng nhận Voucher Vinfast</li>\r\n</ul>', 'TỔNG QUAN DỰ ÁN HARBOR BAY HẠ LONG', 'HARBOR BAY - GIÁ TRỊ THỰC, TÍCH LŨY THỰC', 'VỊ TRÍ KIM CƯƠNG - TIỀM NĂNG DẪN LỐI CỦA HARBOR BAY', 'MẶT BẰNG THIẾT KẾ HARBOR BAY HẠ LONG', 'TIỆN ÍCH ĐẲNG CẤP CỦA HARBOR BAY HẠ LONG', 'TIẾN ĐỘ THANH TOÁN LINH HOẠT CỦA HARBOR BAY', NULL, NULL, 'CHÍNH SÁCH HARBOR BAY ĐANG TỐT NHẤT THỊ TRƯỜNG', 'Harbor Bay Hạ Long - Nhà phố mặt Vịnh Hạ Long đầu tư siêu lời', '07/01/2020', NULL, 'harbor-bay-ha-long', 'Harbor Bay là dự án nhà phố thương mại của BIM Group có vị trí tại bán đảo 2 Hạ Long Marina, được xác định là khu vực trung tâm thương mại, vui chơi giải trí, mua sắm', NULL, 'index, follow');
INSERT INTO `tbl_product` (`id`, `cat_id`, `dis_id`, `user`, `name`, `address`, `hits`, `img`, `price`, `hot`, `tag`, `status`, `detail`, `tab1`, `tab2`, `tab3`, `tab4`, `tab5`, `tab6`, `tab7`, `tab8`, `tab9`, `content1`, `content2`, `content3`, `content4`, `content5`, `content6`, `content7`, `content8`, `content9`, `heading1`, `heading2`, `heading3`, `heading4`, `heading5`, `heading6`, `heading7`, `heading8`, `heading9`, `title`, `date`, `date_up`, `slug`, `description`, `keywords`, `robot`) VALUES
(140, '69', 60, 'Admin', 'NovaWorld Phan Thiet', 'Xã Tiến Thành', 1604, 'Phối cảnh 6.jpg', NULL, 'true', NULL, 'true', '<ul>\r\n	<li>T&ecirc;n dự &aacute;n: Novaworld Phan Thiet</li>\r\n	<li>Quy m&ocirc;: gần 1000 ha</li>\r\n	<li>Vị tr&iacute;: X&atilde; Tiến Th&agrave;nh, Th&agrave;nh phố Phan Thiết, tỉnh B&igrave;nh Thuận</li>\r\n	<li>Chủ đầu tư: Novaland Group</li>\r\n	<li>Loại h&igrave;nh: Biệt thự, nh&agrave; phố, nh&agrave; phố thương mại</li>\r\n</ul>', 'Tổng quan', 'Vị trí', 'Mặt bằng', 'Tiện ích', NULL, NULL, NULL, NULL, 'Chính sách', '<p style=\"text-align:justify\">Dự &aacute;n NovaWorld Phan Thiết l&agrave; một quần thể nghỉ dưỡng du lịch t&iacute;ch hợp giữa đ&ocirc; thị, du lịch v&agrave; giải tr&iacute; được tập đo&agrave;n bất động sản danh tiếng Novaland ph&aacute;t triển. Novaland c&oacute; tham vọng sẽ ph&aacute;t triển đ&acirc;y l&agrave; điểm đến h&agrave;ng đầu cho du kh&aacute;ch nội địa v&agrave; nước ngo&agrave;i mỗi khi đến với Phan Thiết - B&igrave;nh Thuận.</p>\r\n\r\n<p style=\"text-align:justify\">Tổ hợp du lịch nghỉ dưỡng giải tr&iacute; NovaWorld Phan Thiết c&oacute; quy m&ocirc; gần 1000ha. Sở hữu địa thế eo biển tuyệt đẹp v&agrave; b&atilde;i biển trong xanh bất tận với kh&iacute; hậu quanh năm chan h&ograve;a &aacute;nh nắng. Dự &aacute;n quy tụ chuỗi tiện &iacute;ch thể thao giải tr&iacute; nghỉ dưỡng t&iacute;ch hợp đa dạng v&agrave; độc đ&aacute;o.</p>\r\n\r\n<p style=\"text-align:justify\">NovaWorld Phan Thiet ph&aacute;t triển đa dạng c&aacute;c sản phẩm như nh&agrave; phố, biệt thự nghỉ dưỡng (second-home) v&agrave; nh&agrave; phố thương mại (shophouse) với tầm nh&igrave;n hướng biển. Dự &aacute;n sở hữu nhiều lợi thế vượt trội:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Sự ủng hộ từ chủ trương của Ch&iacute;nh Phủ v&agrave; Tỉnh B&igrave;nh Thuận</li>\r\n	<li style=\"text-align:justify\">Đ&oacute;n đầu những đột ph&aacute; của hạ tầng giao th&ocirc;ng</li>\r\n	<li style=\"text-align:justify\">Vị tr&iacute; l&agrave; trung t&acirc;m của c&aacute;c kết nối</li>\r\n	<li style=\"text-align:justify\">Địa thế Sơn - Thủy v&agrave; Hưng Vượng bắt trọn tầm nh&igrave;n</li>\r\n	<li style=\"text-align:justify\">Dự &aacute;n lớn nhất tỉnh B&igrave;nh Thuận với quy m&ocirc; gần 1000ha</li>\r\n	<li style=\"text-align:justify\">Tiện &iacute;ch c&ocirc;ng vi&ecirc;n biển lớn nhất Việt Nam</li>\r\n	<li style=\"text-align:justify\">Dự &aacute;n c&oacute; s&acirc;n Golf quốc tế lớn nhất Miền Nam &amp; Sport Complex</li>\r\n	<li style=\"text-align:justify\">Uy t&iacute;n từ Novaland v&agrave; c&aacute;c đơn vị vận h&agrave;nh quốc tế</li>\r\n</ul>', '<p style=\"text-align:justify\">Tọa lạc ấn tượng tr&ecirc;n cung đường biển Lạc Long Qu&acirc;n, với vị thế tựa sơn hướng biển, NovaWorld Phan Thiet kết hợp vẻ đẹp của bờ biển với trục cảnh quan k&eacute;o từ b&atilde;i biển l&ecirc;n triền đồi với c&aacute;c cao độ kh&aacute;c nhau tạo cảm gi&aacute;c kết nối v&agrave; tầm nh&igrave;n trải rộng, lưu giữ to&agrave;n bộ những n&eacute;t đẹp tự nhi&ecirc;n. Đ&acirc;y l&agrave; vị thế phong thủy mang đến t&agrave;i lộc thịnh vượng cho gia chủ.</p>\r\n\r\n<p style=\"text-align:justify\">NovaWorld Phan Thiet sở hữu ri&ecirc;ng đến 7km đường bờ biển Phan Thiết ngập tr&agrave;n c&aacute;t trắng v&agrave; biển xanh - nơi đ&atilde; l&agrave;m say đắm biết bao tr&aacute;i tim du kh&aacute;ch v&agrave; được b&igrave;nh chọn l&agrave;:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Top 2 b&atilde;i biển đẹp nhất Đ&ocirc;ng Nam &Aacute;</li>\r\n	<li style=\"text-align:justify\">Top 6 điểm đến hấp dẫn nhất Việt Nam</li>\r\n	<li style=\"text-align:justify\">Top 10 b&atilde;i biển đẹp nhất Ch&acirc;u &Aacute; Th&aacute;i B&igrave;nh Dương</li>\r\n	<li style=\"text-align:justify\">B&atilde;i biển lướt s&oacute;ng thể thao duy nhất Việt Nam</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">B&ecirc;n cạnh đ&oacute;, dự &aacute;n c&oacute; li&ecirc;n kết v&ugrave;ng thuận lợi, dễ d&agrave;ng di chuyển nhanh ch&oacute;ng đến c&aacute;c tuyến đường giao th&ocirc;ng huyết mạch, c&aacute;c danh lam thắng cảnh, địa điểm du lịch nổi tiếng của Phan Thiết.</p>\r\n\r\n<table border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:100%\">\r\n	<tbody>\r\n		<tr>\r\n			<td>7 ph&uacute;t: Cao tốc Long Th&agrave;nh - Dầu Gi&acirc;y Phan Thiết</td>\r\n			<td>10 ph&uacute;t: Trung t&acirc;m TP. Phan Thiết &amp; S&acirc;n bay Phan Thiết</td>\r\n		</tr>\r\n		<tr>\r\n			<td>15 ph&uacute;t: B&atilde;i biển Vịnh H&ograve;n Rơm &amp; Suối c&ocirc; Ti&ecirc;n R&atilde;nh đ&aacute; đỏ</td>\r\n			<td>20 ph&uacute;t: Đồi C&aacute;t V&agrave;ng &amp; L&agrave;ng ch&agrave;i Mũi N&eacute;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>25 ph&uacute;t: Đồi c&aacute;t trắng</td>\r\n			<td>3 tiếng: Th&agrave;nh phố Hồ Ch&iacute; Minh</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style=\"text-align:justify\">Với li&ecirc;n kết v&ugrave;ng thuận lợi v&agrave; vị tr&iacute; tựa sơn hướng thủy, t&iacute;nh thanh khoản v&agrave; khả năng khai th&aacute;c sinh lời từ dự &aacute;n l&agrave; điều kh&ocirc;ng thể phủ nhận.</p>', '<p style=\"text-align:justify\">The Florida - Th&agrave;nh phố Đại dương đầy sức sống.</p>\r\n\r\n<p style=\"text-align:justify\">Những ng&ocirc;i nh&agrave; đầy m&agrave;u sắc, tại NovaWorld Phan Thiet được thiết kế v&agrave; lấy cảm hứng từ kiến tr&uacute;c Florida, thừa hưởng những đặc trưng của miền T&acirc;y nước Mỹ với những b&atilde;i biển ngập nắng, s&ocirc;i động đầy cuốn h&uacute;t. Ph&acirc;n kh&uacute;c được ph&aacute;t triển đồng bộ với c&aacute;c sản phẩm nh&agrave; phố, biệt thự, khu thương mại mang đến trải nghiệm đa dạng đầy phong c&aacute;ch.</p>', '<p style=\"text-align:justify\">Khi nhắc đến Novaworld Phan Thiết người ta sẽ nhắc đến đ&acirc;y l&agrave; một đại đ&ocirc; thị th&ocirc;ng minh với hơn 1000 tiện&nbsp; &iacute;ch đẳng cấp quốc tế được chủ đầu tư Novaland x&acirc;y dựng th&agrave;nh một khu đ&ocirc; thị kiểu mẫu, thiết kế hiện đại.</p>\r\n\r\n<p style=\"text-align:justify\">Ch&iacute;nh bởi hội tụ quy m&ocirc; tiện&nbsp; &iacute;ch khổng lồ, dự &aacute;n l&agrave; khu phức hợp du lịch t&iacute;ch - hợp nhiều năng đ&aacute;p ứng tất cả mọi nhu cầu an sinh, giải tr&iacute;, du lịch v&agrave; nghỉ dưỡng cho qu&yacute; kh&aacute;ch. Với hệ thống hơn 1000 tiện&nbsp; - &iacute;ch cao cấp v&agrave; nhiều dịch vụ chu đ&aacute;o, NovaWorld Phan Thiết hứa hẹn sẽ sẽ tạo n&ecirc;n một thi&ecirc;n đường nghỉ dưỡng bậc nhất B&igrave;nh Thuận.</p>\r\n\r\n<p style=\"text-align:justify\"><strong>TỔ HỢP TR&Ograve; CHƠI GIẢI TR&Iacute;</strong></p>\r\n\r\n<p style=\"text-align:justify\">Novaworld Phan Thiết được đầu tư v&agrave; vận h&agrave;nh c&aacute;c hoạt động vui chơi Giải tr&iacute; đa dạng như:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Khu tr&ograve; chơi con trẻ &amp; gia đ&igrave;nh</li>\r\n	<li style=\"text-align:justify\">C&ocirc;ng vi&ecirc;n nước</li>\r\n	<li style=\"text-align:justify\">CLB thể thao biển (beach Club)</li>\r\n	<li style=\"text-align:justify\">Quảng trường</li>\r\n	<li style=\"text-align:justify\">Nh&agrave; h&aacute;t/biểu diễn show quốc tế để l&ocirc;i cuốn v&agrave; k&eacute;o d&agrave;i thời k&igrave; tạm tr&uacute; du kh&aacute;ch trong v&agrave; ngo&agrave;i nước.</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><strong>TỔ HỢP THƯƠNG MẠI &ndash; ẨM THỰC QUỐC TẾ</strong></p>\r\n\r\n<p style=\"text-align:justify\">Novaworld Phan Thiết được đầu tư v&agrave; x&acirc;y dựng c&aacute;c trọng điểm THƯƠNG MẠI để vận h&agrave;nh c&aacute;c hoạt động mua sắm cho du kh&aacute;ch ngay b&ecirc;n trong c&aacute;c tổ hợp du lịch của Novaland. hiệp t&aacute;c li&ecirc;n minh với c&aacute;c khối b&aacute;n lẻ hoặc hiệp t&aacute;c với c&aacute;c thương hiệu b&aacute;n sỉ lớn v&agrave; kết li&ecirc;n với địa phương giới thiệu c&aacute;c sản phẩm truyền thống (đặc sản, l&agrave;ng nghề).</p>\r\n\r\n<p style=\"text-align:justify\">Ph&aacute;t triển c&aacute;c loại nh&agrave; h&agrave;ng, qu&aacute;n cafe tr&agrave;, pub, beach club, lounge&hellip;.nằm b&ecirc;n trong tổ hợp du lịch của Novaland g&oacute;p phần mang lại những trải nghiệm nghỉ dưỡng nhẵn cho du kh&aacute;ch.</p>\r\n\r\n<p style=\"text-align:justify\"><strong>QUY HOẠCH VƯỜN TH&Uacute; (SAFARI)</strong></p>\r\n\r\n<p style=\"text-align:justify\">Novaworld Phan Thiết được đầu tư v&agrave; x&acirc;y dựng c&aacute;c khu vườn th&uacute; hoang dại (safari) với đa dạng c&aacute;c loại chừng như:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Mini safari</li>\r\n	<li style=\"text-align:justify\">Vườn th&uacute; ng&agrave;y (day safari)</li>\r\n	<li style=\"text-align:justify\">Vườn th&uacute; đ&ecirc;m (night safari)</li>\r\n	<li style=\"text-align:justify\">Vườn th&uacute; trẻ n&iacute;t (Kid zoo)</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><strong>C&Ocirc;NG VI&Ecirc;N CHỦ ĐỀ, C&Ocirc;NG VI&Ecirc;N BIỂN CHUỖI TIỆN &Iacute;CH BIỂN ĐA DẠNG</strong></p>\r\n\r\n<p style=\"text-align:justify\">Novaworld Phan Thiết được thiết kế cảnh quan đặc biệt với cụm c&aacute;c c&ocirc;ng vi&ecirc;n chủ đề như:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">C&ocirc;ng vi&ecirc;n sinh vật biển (Aquarium)</li>\r\n	<li style=\"text-align:justify\">Zen garden/maze garden</li>\r\n	<li style=\"text-align:justify\">Tree house/rock climbing</li>\r\n	<li style=\"text-align:justify\">Khu bắn s&uacute;ng sơn&hellip;</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">Với nhiều hoạt động thể thao giải tr&iacute; tr&ecirc;n biển v&agrave; dưới biển c&ugrave;ng chuỗi tiện &iacute;ch lần đầu ti&ecirc;n xuất hiện như: Ocean Lagoon, CLB thể thao biển, Bến du thuyền, c&ocirc;ng vi&ecirc;n chủ đề, khu mua sắm ẩm thực biển, khu vui chơi trẻ em, khu cafe, nh&agrave; h&agrave;ng, kh&aacute;ch sạn.</p>\r\n\r\n<p style=\"text-align:justify\"><strong>KHU PHỨC HỢP THỂ THAO S&Acirc;N GOLF 36 LỖ</strong></p>\r\n\r\n<p style=\"text-align:justify\">Khu phức hợp thể thao 220ha đạt chuẩn quốc tế vận h&agrave;nh bởi IMG</p>\r\n\r\n<p style=\"text-align:justify\">Nổi bật nhất ch&iacute;nh l&agrave; cụm s&acirc;n Golf 36 lỗ được thiết kế bởi huyền thoại Greg Norman, đủ điều kiện đăng k&yacute; tổ chức thi đấu c&aacute;c giải PGA Tour.</p>\r\n\r\n<p style=\"text-align:justify\"><strong>L&Agrave;NG THẾ GIỚI - MỘT BƯỚC CH&Acirc;N CHẠM TINH HOA KIẾN TR&Uacute;C THẾ GIỚI</strong></p>\r\n\r\n<p style=\"text-align:justify\">Khu đ&ocirc; thị theo chủ đề L&agrave;ng thế giới, nơi hội tụ tinh hoa kiến tr&uacute;c của c&aacute;c quốc gia &Yacute;, Ph&aacute;p, Nga, Mỹ, Nhật Bản, H&agrave;n Quốc, Việt Nam&hellip;</p>\r\n\r\n<p style=\"text-align:justify\">Một cụm đ&ocirc; thị c&oacute; khu tiện &iacute;ch nội khu (c&ocirc;ng vi&ecirc;n, hồ bơi, khu mua sắm&hellip;)</p>\r\n\r\n<p style=\"text-align:justify\"><strong>CON ĐƯỜNG DI SẢN</strong></p>\r\n\r\n<p style=\"text-align:justify\">Con đường di sản hội tụ c&aacute;c gian h&agrave;ng sản phẩm truyền thống, tinh hoa ẩm thực v&agrave; chợ n&ocirc;ng sản của B&igrave;nh Thuận, L&acirc;m Đồng, Kh&aacute;nh H&ograve;a; ngo&agrave;i ra mỗi tuần c&ograve;n c&oacute; c&aacute;c chương tr&igrave;nh diễn lễ hội, thời trang, văn h&oacute;a bản địa đặc sắc.</p>', NULL, NULL, NULL, NULL, '<p>Chương tr&igrave;nh thanh to&aacute;n đặc biệt chỉ 15% đến khi nhận nh&agrave;</p>\r\n\r\n<p>Hỗ trợ l&atilde;i suất 0% trong 3 năm</p>\r\n\r\n<p>Tặng 36 đ&ecirc;m nghỉ dưỡng tại NovaHills Mui Ne Resorts &amp; Villas</p>', 'TỔNG QUAN DỰ ÁN NOVAWORLD PHAN THIẾT', 'VỊ THẾ TỰA SƠN HƯỚNG THỦY - TẦM NHÌN HƯỚNG BIỂN', 'MẶT BẰNG THIẾT KẾ NOVAWORLD PHAN THIET', 'TIỆN ÍCH  ĐẲNG CẤP CỦA DỰ ÁN NOVAWORLD PHAN THIET', NULL, NULL, NULL, NULL, 'CHÍNH SÁCH BÁN HÀNG HẤP DẪN', 'NovaWorld Phan Thiết - Cam kết sinh lời 2.3 tỷ - Cam kết mua lại', '09/28/2020', NULL, 'novaworld-phan-thiet', 'Dự án NovaWorld Phan Thiết là một quần thể nghỉ dưỡng du lịch tích hợp giữa đô thị, du lịch và giải trí được tập đoàn bất động sản danh tiếng Novaland phát triển.', NULL, 'index, follow'),
(141, '69', 57, 'Admin', 'Hà Đô Charm Villas', 'gần Đại Lộ Thăng Long, thuộc địa bàn 2 xã An Thượng và Song Phương', 1127, 'anh 4.jpg', NULL, NULL, NULL, 'true', '<ul>\r\n	<li>T&ecirc;n dự &aacute;n: H&agrave; Đ&ocirc; Charm Villas.&nbsp;</li>\r\n	<li>Chủ đầu tư: C&ocirc;ng ty cổ phần đầu tư H&agrave; Đ&ocirc;.&nbsp;</li>\r\n	<li>Vị tr&iacute; dự &aacute;n: gần Đại Lộ Thăng Long, thuộc địa b&agrave;n 2 x&atilde; An Thượng v&agrave; Song Phương, huyện Ho&agrave;i Đức, H&agrave; Nội.&nbsp;</li>\r\n	<li>Đơn vị thiết kế: Subana International Consultants Pte,Ltd.&nbsp;</li>\r\n	<li>Quy m&ocirc; dự &aacute;n: 30ha, 528 biệt thự liền kề đơn lập, song lập</li>\r\n	<li>H&igrave;nh thức sở hữu: SỔ HỒNG L&Acirc;U D&Agrave;I.&nbsp;</li>\r\n	<li>Thời gian b&agrave;n giao: Qu&yacute; IV/2020.</li>\r\n</ul>', 'Tổng quan', 'Vị trí', 'Mặt bằng', 'Tiện ích', 'Thanh toán', NULL, NULL, NULL, 'Chính sách', '<p style=\"text-align:justify\">T&igrave;m về kh&ocirc;ng gian sống y&ecirc;n b&igrave;nh, khoảng xanh rộng lớn l&agrave; những g&igrave; m&agrave; c&aacute;c gia đ&igrave;nh đang sinh sống tại thủ đ&ocirc; H&agrave; Nội mong muốn. V&agrave; để hiện thực h&oacute;a ước mơ đ&oacute;, tập đo&agrave;n H&agrave; Đ&ocirc; đ&atilde; mang đến dự &aacute;n Hado Charm Villas. Tọa lạc tr&ecirc;n trục đại lộ Thăng Long, nơi đ&acirc;y kh&ocirc;ng chỉ c&oacute; vị tr&iacute; đẹp m&agrave; c&ograve;n tạo dựng một cuộc sống xanh đ&uacute;ng nghĩa ngay giữa l&ograve;ng th&agrave;nh phố.</p>\r\n\r\n<p style=\"text-align:justify\">Dự &aacute;n H&agrave; Đ&ocirc; Charm được triển khai tr&ecirc;n diện t&iacute;ch 30ha với quy m&ocirc; hơn 528 l&ocirc; biệt thự đơn lập, song lập. Đ&acirc;y l&agrave; dự &aacute;n nh&agrave; liền kề b&agrave;n giao x&acirc;y th&ocirc; ho&agrave;n thiện mặt ngo&agrave;i, đảm bảo gi&aacute; hợp l&yacute; để gia chủ mua ở hoặc đầu tư.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Được thiết kế v&agrave; quy hoạch bởi SIC. Ltd của Singapore, Khu biệt thự H&agrave; Đ&ocirc; Charm được bố tr&iacute; h&agrave;i h&ograve;a với cảnh quan thi&ecirc;n nhi&ecirc;n, c&oacute; đầy đủ dịch vụ tiện &iacute;ch đẳng cấp gi&uacute;p n&acirc;ng tầm chất lượng cuộc sống v&agrave; khẳng định vị thế cho cư d&acirc;n khu đ&ocirc; thị.</p>', '<p style=\"text-align:justify\">H&agrave; Đ&ocirc; Charm Villas c&oacute; vị tr&iacute; đắc địa Nằm tr&ecirc;n trục đại lộ Thăng Long rộng 140m nối trung t&acirc;m ph&iacute;a t&acirc;y H&agrave; Nội với khu đ&ocirc; thị Vệ Tinh H&ograve;a Lạc, Ngo&agrave;i ra dự &aacute;n c&ograve;n tiếp gi&aacute;p với đường li&ecirc;n khu 8 nối Ho&agrave;i Đức với Đan Phượng đang được x&acirc;y dựng.</p>\r\n\r\n<p style=\"text-align:justify\">Xung quanh dự &aacute;n l&agrave; c&aacute;c khu đ&ocirc; thị lớn đ&atilde; ho&agrave;n th&agrave;nh như Khu đ&ocirc; thị Spendora Bắc An Kh&aacute;nh, Vinhomes Thăng Long, Khu đ&ocirc; thị Vinhomes Smart City, Geleximco, Sudico rộng h&agrave;ng trăm ha v&agrave; mỗi căn biệt thự c&oacute; gi&aacute; h&agrave;ng chục tỷ đồng. Cư d&acirc;n H&agrave; Đ&ocirc; Charm Villas sẽ được thừa hưởng nhiều tiện &iacute;ch cao cấp v&agrave; cơ hội đầu tư lớn khi mức gi&aacute; ở đ&acirc;y rất hợp l&yacute; chỉ từ 7 đến 10 tỷ đ&atilde; c&oacute; thể sở hữu căn biệt thự tại Hado Charm.</p>\r\n\r\n<p style=\"text-align:justify\">Đặc điểm vị tr&iacute; của dự &aacute;n:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Cửa ng&otilde; ph&iacute;a t&acirc;y của thủ đ&ocirc; H&agrave; Nội</li>\r\n	<li style=\"text-align:justify\">20 ph&uacute;t di chuyển để tới c&aacute;c quận trung t&acirc;m th&agrave;nh phố</li>\r\n	<li style=\"text-align:justify\">15 ph&uacute;t để di chuyển tới trung t&acirc;m hội nghị quốc gia</li>\r\n	<li style=\"text-align:justify\">35 ph&uacute;t để di chuyển ra s&acirc;n bay quốc tế Nội B&agrave;i</li>\r\n	<li style=\"text-align:justify\">20 ph&uacute;t để tới khu c&ocirc;ng nghệ cao L&aacute;ng Ho&agrave; Lạc</li>\r\n</ul>', '<p style=\"text-align:justify\">Được quy hoạch v&agrave; thiết kế bởi Surbana International Consultants Pte. Ltd, c&ocirc;ng ty thiết kế nổi tiếng ở Singapore. V&igrave; vậy, những hạng mục nh&agrave; ở, tiện &iacute;ch tại H&agrave; Đ&ocirc; Charm Villas đều đạt ti&ecirc;u chuẩn khu đ&ocirc; thị th&ocirc;ng minh đẳng cấp.</p>\r\n\r\n<p style=\"text-align:justify\">Những sản phẩm biệt thự c&oacute; kiến tr&uacute;c đồng bộ, hiện đại, theo phong thủy v&agrave; bố tr&iacute; h&agrave;i h&ograve;a với thi&ecirc;n nhi&ecirc;n. Biệt thự H&agrave; Đ&ocirc; Charm c&oacute; diện t&iacute;ch từ 200m2 &ndash; 350 m2, X&acirc;y dưng 3 tầng, được bao quanh bởi kh&ocirc;ng gian xanh. Biệt thự được x&acirc;y dựng với nhiều ph&ograve;ng chức năng. Thiết kế mở gi&uacute;p căn nh&agrave; đ&oacute;n nhiều &aacute;nh s&aacute;ng tự nhi&ecirc;n v&agrave; kh&iacute; trời.</p>\r\n\r\n<p style=\"text-align:justify\">Khu nh&agrave; liền kề H&agrave; Đ&ocirc; Charm c&oacute; diện t&iacute;ch từ 95m2 &ndash; 110m2 X&acirc;y dựng 4 tầng với 260m2 s&agrave;n sử dụng rộng r&atilde;i. To&agrave;n bộ hạ tầng kỹ thuật được thiết kế v&agrave; x&acirc;y dựng ngầm, th&ocirc;ng tho&aacute;ng v&agrave; an to&agrave;n, c&oacute; Gara để xe hơi, tiện nghi đầy đủ.</p>\r\n\r\n<p style=\"text-align:justify\">Với thế đất đẹp, vượng phong thủy, Tập đo&agrave;n H&agrave; Đ&ocirc; đ&atilde; ph&aacute;t triển dự &aacute;n biệt thự sang trọng, đẳng cấp. Được thiết kế kiến tr&uacute;c, cảnh quan bởi c&aacute;c c&ocirc;ng ty danh tiếng như Architype, quản l&yacute; bởi Savills,&hellip;Chắc chắn Hado Charm Villas sẽ trở th&agrave;nh khu đ&ocirc; thị xanh đ&aacute;ng sống bậc nhất ph&iacute;a t&acirc;y thủ đ&ocirc;.</p>', '<p style=\"text-align:justify\">Hado Charm Villas hội tụ 33 tiện &iacute;ch nội khu đẳng cấp phục vụ ri&ecirc;ng cho cư d&acirc;n trong dự &aacute;n, nổi bật trong đ&oacute; l&agrave; hệ thống an ninh 3 lớp bảo vệ 24/24, hệ thống trường học li&ecirc;n cấp quốc tế Newton; Khu ClubHouse 1 đ&atilde; ho&agrave;n thiện với hồ bơi ngo&agrave;i trời kết hợp khu vực tập gym, spa, cafe, si&ecirc;u thị; Khu Club House 2 đang x&acirc;y dựng với hồ bơi bốn m&ugrave;a đạt chuẩn Olympic, khu thể thao trong nh&agrave;.</p>\r\n\r\n<p style=\"text-align:justify\">L&agrave; khu biệt thự kh&eacute;p k&iacute;n, biệt lập với 2 cổng v&agrave;o ch&iacute;nh được trang bị bốt an ninh với c&ocirc;ng nghệ camera cảm nhiệt 24/24 c&ugrave;ng hệ thống intercom giữa bảo vệ v&agrave; chủ nh&agrave;, c&aacute;c chủ nh&acirc;n H&agrave; Đ&ocirc; Charm Villas c&oacute; thể ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m về sự an to&agrave;n v&agrave; ri&ecirc;ng tư tuyệt đối khi sống ở đ&acirc;y.</p>\r\n\r\n<p style=\"text-align:justify\">Ngo&agrave;i ra, dự &aacute;n c&ograve;n c&oacute; s&acirc;n tennis, cầu l&ocirc;ng, gym ngo&agrave;i trời, vườn yoga, vườn dưỡng sinh, vườn dạo bộ, vườn tuổi thơ v&agrave; khu vườn đọc s&aacute;ch thư gi&atilde;n&hellip; phục vụ tối đa nhu cầu của cư d&acirc;n ở mọi lứa tuổi.</p>\r\n\r\n<p style=\"text-align:justify\">B&ecirc;n cạnh việc hợp t&aacute;c x&acirc;y dựng trường quốc tế li&ecirc;n cấp, H&agrave; đ&ocirc; đ&atilde; triển khai bổ sung th&ecirc;m c&aacute;c tiện &iacute;ch nội khu với 20 c&ocirc;ng vi&ecirc;n &ndash; vườn xanh, khu cắm trại picnic, khu phức hợp bể bơi trong nh&agrave; v&agrave; ngo&agrave;i trời ti&ecirc;u chuẩn quốc tế, tổ hợp gym &amp; spa sang trọng, hướng tới một khu đ&ocirc; thị sinh th&aacute;i chuẩn Singapore.</p>\r\n\r\n<p style=\"text-align:justify\">C&aacute;c tiện &iacute;ch nội khu được bố tr&iacute; trong dự &aacute;n Hado Charm Villas:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">C&ocirc;ng vi&ecirc;n</li>\r\n	<li style=\"text-align:justify\">Trường học li&ecirc;n cấp, trường học quốc tế</li>\r\n	<li style=\"text-align:justify\">Quảng trường</li>\r\n	<li style=\"text-align:justify\">Vườn hoa</li>\r\n	<li style=\"text-align:justify\">S&acirc;n tập golf</li>\r\n	<li style=\"text-align:justify\">S&acirc;n b&oacute;ng đ&aacute;, cầu l&ocirc;ng, tennis, b&oacute;ng rổ</li>\r\n	<li style=\"text-align:justify\">Gym, spa, bể bơi</li>\r\n	<li style=\"text-align:justify\">Trung t&acirc;m chăm s&oacute;c sức khỏe</li>\r\n	<li style=\"text-align:justify\">S&acirc;n chơi trẻ em</li>\r\n	<li style=\"text-align:justify\">An ninh 24/24</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">Ngo&agrave;i ra, dự &aacute;n c&ograve;n thừa hưởng c&aacute;c tiện &iacute;ch ngoại khu như trung t&acirc;m thương mại AEON Mall H&agrave; Đ&ocirc;ng, khu vui chơi giải tr&iacute; Thi&ecirc;n đường Bảo Sơn, C&aacute;c trung t&acirc;m thương mại của Vincom, Bệnh viện Quốc tế H&agrave; Đ&ocirc;ng&hellip;sẽ n&acirc;ng cao chất lượng sống v&agrave; tăng gi&aacute; trị bất động sản tại đ&acirc;y.</p>', '<p style=\"text-align:justify\">Tiến độ thanh to&aacute;n dự &aacute;n</p>\r\n\r\n<p style=\"text-align:justify\">Đợt 1: Thanh to&aacute;n 30% khi k&yacute; HĐMB</p>\r\n\r\n<p style=\"text-align:justify\">Đợt 2: Thanh to&aacute;n 15% khi x&acirc;y xong phần khung</p>\r\n\r\n<p style=\"text-align:justify\">Đợt 3: Thanh to&aacute;n 15% khi ho&agrave;n thiện xong phần tường</p>\r\n\r\n<p style=\"text-align:justify\">Đợt 4: Thanh to&aacute;n 10% Khi ho&agrave;n thiện hạ tầng điện nước</p>\r\n\r\n<p style=\"text-align:justify\">Đợt 5: Thanh to&aacute;n 25% khi c&oacute; th&ocirc;ng b&aacute;o b&agrave;n giao nh&agrave;</p>\r\n\r\n<p style=\"text-align:justify\">Đợt 6: Thanh to&aacute;n 5% cấp Sổ đỏ</p>', NULL, NULL, NULL, NULL, 'TỔNG QUAN DỰ ÁN HÀ ĐÔ CHARM VILLAS', 'VỊ TRÍ THUẬN TIỆN - LIÊN KẾT VÙNG HOÀN HẢO CỦA HÀ ĐÔ CHARM VILLAS', 'MẶT BẰNG VÀ THIẾT KẾ DỰ ÁN HÀ ĐÔ CHARM VILLAS', 'TIỆN ÍCH HOÀN HẢO CỦA DỰ ÁN HÀ ĐÔ CHARM VILLAS', 'TIẾN ĐỘ THANH TOÁN DỰ ÁN', NULL, NULL, NULL, NULL, 'Hà Đô Charm Villas - Bảng Hàng + Giá Mới Nhất Từ Chủ Đầu Tư', '11/10/2020', NULL, 'ha-do-charm-villas', 'Dự án Hà Đô Charm được triển khai trên diện tích 30ha với quy mô hơn 528 lô biệt thự đơn lập, song lập. Đây là dự án nhà liền kề bàn giao xây thô hoàn thiện mặt ngoài', NULL, 'index, follow'),
(142, '69', 62, 'Admin', 'Xanh Villas', 'KĐT Xanh Villas, Tiến Xuân', 1024, 'Slide-Đào-tạo-Khu-A-B-DA-Xanh-Villas_update-02.jpg', NULL, NULL, NULL, 'true', '<ul>\r\n	<li style=\"text-align:justify\">Chủ đầu tư: C&ocirc;ng ty Cổ phần Đầu tư Xu&acirc;n Cầu.</li>\r\n	<li style=\"text-align:justify\">T&ecirc;n dự &aacute;n (ph&aacute;p l&yacute;): Khu biệt thự nh&agrave; vườn v&agrave; thể thao giải tr&iacute; tại x&atilde; Tiến Xu&acirc;n.</li>\r\n	<li style=\"text-align:justify\">Địa chỉ dự &aacute;n: Tiến Xu&acirc;n, Thạch Thất, H&agrave; Nội.</li>\r\n	<li style=\"text-align:justify\">Tổng diện t&iacute;ch đất: ~ 45ha</li>\r\n	<li style=\"text-align:justify\">Quy m&ocirc; D&acirc;n số: Hơn 2,000 người</li>\r\n	<li style=\"text-align:justify\">Mật độ x&acirc;y dựng:</li>\r\n	<li style=\"text-align:justify\">Diện t&iacute;ch đất nh&agrave; ở: ~ 228,700 m2</li>\r\n	<li style=\"text-align:justify\">Diện t&iacute;ch đất c&acirc;y xanh, cảnh quan: ~ 72,600 m2</li>\r\n</ul>', 'Tổng quan', 'Vị trí', 'Tiện ích', 'Thiết kế', NULL, NULL, NULL, NULL, 'Chính sách', '<p style=\"text-align:justify\">Xanh Villas l&agrave; khu đ&ocirc; thị sinh th&aacute;i đẳng cấp tại ph&iacute;a T&acirc;y Nam của Đ&ocirc; thị Ho&agrave; Lạc, H&agrave; Nội. Huyết mạch của Xanh Villas l&agrave; d&ograve;ng suối Ngọc &ndash; Vua B&agrave; tr&agrave;n đầy vượng kh&iacute; c&ugrave;ng 6,000 c&acirc;y xanh được ươm trồng hơn 10 năm qua. To&agrave;n khu đ&ocirc; thị được t&aacute;ch xa khỏi nỗi lo bụi bặm của th&agrave;nh phố, mang đến cho cư d&acirc;n t&acirc;m tr&iacute; thư gi&atilde;n, sức khoẻ c&acirc;n bằng, m&ocirc;i trường y&ecirc;n tĩnh biệt lập.</p>\r\n\r\n<p style=\"text-align:justify\">Từ d&ograve;ng suối thi&ecirc;n nhi&ecirc;n trong l&agrave;nh chảy dọc thung lũng c&ugrave;ng c&acirc;y cối v&agrave; đồi n&uacute;i xanh m&aacute;t bao quanh, kiến tr&uacute;c của Xanh Villas dựa tr&ecirc;n sự t&ocirc;n trọng v&agrave; h&ograve;a hợp với thi&ecirc;n nhi&ecirc;n. C&aacute;c villas được đặt b&ecirc;n d&ograve;ng suối chảy r&oacute;c r&aacute;ch quanh năm, ẩn hiện dưới những tầng c&acirc;y, t&aacute;n l&aacute;.</p>', '<p>Vị tr&iacute; đắc địa của dự &aacute;n Xanh Villas tọa lạc ngay trung t&acirc;m của KĐT H&ograve;a Lạc, kế b&ecirc;n Đại Lộ Thăng Long, Thạch Thất, H&agrave; Nội. Nằm cạnh c&aacute;c c&ocirc;ng tr&igrave;nh trọng điểm quốc gia như:</p>\r\n\r\n<ul>\r\n	<li>Khu c&ocirc;ng nghệ cao H&ograve;a Lạc</li>\r\n	<li>L&agrave;ng văn h&oacute;a c&aacute;c d&acirc;n tộc Việt Nam</li>\r\n	<li>Trường Đại học Quốc gia</li>\r\n	<li>S&acirc;n golf Đồng M&ocirc;</li>\r\n	<li>S&acirc;n golf Phoenix</li>\r\n	<li>Th&agrave;nh cổ Sơn T&acirc;y</li>\r\n	<li>Suối kho&aacute;ng Kim B&ocirc;i</li>\r\n	<li>Khu du lịch Ba V&igrave;</li>\r\n</ul>\r\n\r\n<p>Vị tr&iacute; thuận lợi về giao th&ocirc;ng đ&atilde; gi&uacute;p cho đ&ocirc; thị vệ tinh lớn nhất H&agrave; Nội c&aacute;ch khoảng nửa tiếng chạy xe đến trung t&acirc;m thủ đ&ocirc;. Kh&ocirc;ng gặp &ugrave;n tắc hay chậm trễ về giao th&ocirc;ng, l&agrave;n đường xe rộng, thuận lợi đi đến S&acirc;n bay Nội B&agrave;i khoảng 45 ph&uacute;t.</p>\r\n\r\n<p>Nằm trong một vị tr&iacute; hội tụ tất cả yếu tố thi&ecirc;n thời địa lợi, gi&uacute;p cho dự &aacute;n trở th&agrave;nh bất động sản được đ&aacute;nh gi&aacute; cao v&agrave; thu h&uacute;t sự quan t&acirc;m từ ph&iacute;a kh&aacute;ch h&agrave;ng.</p>', '<p style=\"text-align:justify\">Tiện &iacute;ch đa dạng v&agrave; dịch vụ nội khu chất lượng resort cao cấp dẫn bước chủ nh&acirc;n đến với phong c&aacute;ch sống wellness: Bể bơi, khu tập gym b&ecirc;n suối tự nhi&ecirc;n; Hệ thống x&ocirc;ng hơi phong c&aacute;ch H&agrave;n Quốc Jjim Jil Bang quy m&ocirc; 800 m2 v&agrave; c&aacute;c dịch vụ trị liệu chuy&ecirc;n s&acirc;u phục hồi sức khoẻ tại May Spa, g&oacute;p phần tạo n&ecirc;n sự kh&aacute;c biệt cho to&agrave;n bộ dự &aacute;n.</p>\r\n\r\n<p style=\"text-align:justify\">Xanh Villas được quy hoạch theo m&ocirc; h&igrave;nh khu đ&ocirc; thị đ&oacute;ng với hệ thống quản l&yacute; chất lượng resort cao cấp, đồng bộ về hạ tầng tiện &iacute;ch, tiện nghi, kiến tạo n&ecirc;n khu đ&ocirc; thị hiện đại, đẳng cấp, m&ocirc;i trường sống văn minh, trong l&agrave;nh cho cộng đồng cư d&acirc;n.</p>\r\n\r\n<p>Tiện &iacute;ch ngoại khu</p>\r\n\r\n<ul>\r\n	<li>Khu c&ocirc;ng nghệ cao H&ograve;a Lạc</li>\r\n	<li>L&agrave;ng văn h&oacute;a c&aacute;c d&acirc;n tộc Việt Nam</li>\r\n	<li>Trường Đại học Quốc gia</li>\r\n	<li>S&acirc;n golf Đồng M&ocirc;</li>\r\n	<li>S&acirc;n golf Phoenix</li>\r\n	<li>Th&agrave;nh cổ Sơn T&acirc;y</li>\r\n	<li>Suối kho&aacute;ng Kim B&ocirc;i</li>\r\n	<li>Khu du lịch Ba V&igrave;</li>\r\n</ul>', '<p style=\"text-align:justify\">Thiết kế Khu đ&ocirc; thị Xanh Villas về một nơi ở chuẩn mực, đẳng cấp, đem con người lại gần với thi&ecirc;n nhi&ecirc;n hơn. Từ sự nỗ lực v&agrave; s&aacute;ng tạo kh&ocirc;ng ngừng nghỉ của nh&agrave; đầu tư đ&atilde; v&agrave; đang mang đến một kh&ocirc;ng gian xanh đ&aacute;ng sống nhất giữa l&ograve;ng thủ đ&ocirc; H&agrave; Nội.</p>\r\n\r\n<p style=\"text-align:justify\">Được coi l&agrave; bản giao hưởng chan h&ograve;a về cảnh quan, quy hoạch tổng thể v&agrave; kiến tr&uacute;c của từng căn nh&agrave; ở. Điểm nhấn đặc biệt ở đ&acirc;y l&agrave; sản phẩm duy nhất m&agrave; dự &aacute;n cung cấp chỉ c&oacute; biệt thự.</p>\r\n\r\n<p style=\"text-align:justify\">Mỗi một mẫu biệt thự được s&aacute;ng tạo d&agrave;nh ri&ecirc;ng cho chủ nh&acirc;n theo phong c&aacute;ch truyền thống. Được thiết kế từ c&aacute;c kiến tr&uacute;c sư người Malaysia nổi tiếng, chuy&ecirc;n nghiệp. Mỗi một kh&ocirc;ng gian nh&agrave; ở mang n&eacute;t hiện đại, ph&oacute;ng kho&aacute;ng, c&oacute; ban c&ocirc;ng v&agrave; nội thất sang trọng, tinh tế.</p>\r\n\r\n<p style=\"text-align:justify\">Điểm nhấn nổi bật của dự &aacute;n c&oacute; tới 6 mẫu biệt thự Xanh Villas:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Creative House: diện t&iacute;ch khu&ocirc;n vi&ecirc;n 380m2 &ndash; 580m2</li>\r\n	<li style=\"text-align:justify\">Elegant House: diện t&iacute;ch khu&ocirc;n vi&ecirc;n 450m2 &ndash;700m2</li>\r\n	<li style=\"text-align:justify\">Gallant House: diện t&iacute;ch khu&ocirc;n vi&ecirc;n 570m2 &ndash;600m2</li>\r\n	<li style=\"text-align:justify\">Tropical Villa: diện t&iacute;ch khu&ocirc;n vi&ecirc;n 800m2 &ndash;1000m2</li>\r\n	<li style=\"text-align:justify\">Honour Villa: diện t&iacute;ch khu&ocirc;n vi&ecirc;n 800m2 &ndash;1400m2</li>\r\n	<li style=\"text-align:justify\">Biệt thự song lập Glamour: diện t&iacute;ch khu&ocirc;n vi&ecirc;n 200m2 &ndash; 300m2</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">Thiết kế cảnh quan c&oacute; thể nh&igrave;n thẳng ra hồ bơi trong khu&ocirc;n vi&ecirc;n biệt thự từ mọi g&oacute;c nh&igrave;n. C&aacute;c gi&agrave;n hoa pergola được t&ocirc; điểm tạo n&ecirc;n sự thanh tho&aacute;t, tinh tế nhưng kh&ocirc;ng k&eacute;m phần sang trọng.</p>\r\n\r\n<p style=\"text-align:justify\">Mặt bằng tổng thể khu đ&ocirc; thị</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Diện t&iacute;ch quy hoạch: 50 ha</li>\r\n	<li style=\"text-align:justify\">Thiết kế được chia l&agrave;m 3 khu: A, B, C</li>\r\n	<li style=\"text-align:justify\">Tổng số lượng: 500 căn biệt thự</li>\r\n	<li style=\"text-align:justify\">Diện t&iacute;ch đất c&ocirc;ng tr&igrave;nh giao th&ocirc;ng: 10,8 ha</li>\r\n	<li style=\"text-align:justify\">Diện t&iacute;ch mặt nước: 4,2 ha</li>\r\n	<li style=\"text-align:justify\">Diện t&iacute;ch c&acirc;y xanh cảnh quan: 5ha</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">Tiện &iacute;ch được sắp xếp v&agrave; bố tr&iacute; h&agrave;i h&ograve;a, cảnh quan tự nhi&ecirc;n nổi bật. Diện t&iacute;ch c&aacute;c khu bao gồm:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Diện t&iacute;ch Khu A: 22 ha</li>\r\n	<li style=\"text-align:justify\">Tổng số căn biệt thự: 100 căn</li>\r\n	<li style=\"text-align:justify\">Diện t&iacute;ch s&agrave;n: 350m2 &ndash; 1.250m2</li>\r\n	<li style=\"text-align:justify\">Diện t&iacute;ch Khu B: 10 ha</li>\r\n	<li style=\"text-align:justify\">Tổng số căn biệt thự: 200 căn (Chia l&agrave;m 108 căn biệt thự đơn lập v&agrave; 92 căn biệt thự song lập)</li>\r\n	<li style=\"text-align:justify\">Diện t&iacute;ch s&agrave;n biệt thự đơn lập: 186m2 &ndash; 398m2</li>\r\n	<li style=\"text-align:justify\">Diện t&iacute;ch s&agrave;n biệt thự song lập: 296m2 &ndash; 769m2</li>\r\n	<li style=\"text-align:justify\">Diện t&iacute;ch Khu C: 15 ha</li>\r\n	<li style=\"text-align:justify\">Tổng số căn biệt thự: 100 căn</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">Được x&acirc;y dựng hướng mặt bờ suối v&agrave; nằm thoải m&aacute;i theo triền đồi m&agrave; địa h&igrave;nh khu B bằng phẳng hơn địa h&igrave;nh khu A. Một số quả đồi thấp c&oacute; view nh&igrave;n ra suối kho&aacute;ng đạt, tho&aacute;ng đ&atilde;ng. Thiết kế kh&ocirc;ng gian mở, rộng r&atilde;i, tr&agrave;n ngập &aacute;nh s&aacute;ng tự nhi&ecirc;n.</p>', NULL, NULL, NULL, NULL, '<ul>\r\n	<li>Miễn ph&iacute; 2 năm dịch vụ quản l&yacute;&nbsp;</li>\r\n	<li>Ng&acirc;n h&agrave;ng hỗ trợ 70% L&atilde;i 0% trong 24 th&aacute;ng</li>\r\n	<li>Kh&ocirc;ng Vay chiết khấu 4,5%</li>\r\n	<li>Chiết khấu thanh to&aacute;n sớm l&ecirc;n tới 11%</li>\r\n</ul>', 'TỔNG QUAN DỰ ÁN XANH VILLAS', 'VỊ TRÍ', 'TIỆN ÍCH XANH VILLAS', 'THIẾT KẾ KĐT XANH VILLAS', NULL, NULL, NULL, NULL, 'CHÍNH SÁCH BÁN HÀNG', 'Xanh Villas - Resort Trong Lòng Đô Thị - Thông Tin Mới Nhất Từ CĐT', '12/03/2020', NULL, 'xanh-villas', 'Xanh Villas là khu đô thị sinh thái đẳng cấp tại phía Tây Nam của Đô thị Hoà Lạc, Hà Nội. Huyết mạch của Xanh Villas là dòng suối Ngọc – Vua Bà', NULL, 'index, follow'),
(143, '69', 55, 'Admin', 'Feliz Homes', 'P. Hoàng Văn Thụ', 1033, 'HỒ BƠI ỐC ĐẢO.png', NULL, NULL, NULL, 'true', '<ul>\r\n	<li style=\"text-align:justify\">Chủ đầu tư: C&ocirc;ng ty Cổ Phần KLB</li>\r\n	<li style=\"text-align:justify\">Vị tr&iacute;: P. Ho&agrave;ng Văn Thụ, Q. Ho&agrave;ng Mai, H&agrave; Nội</li>\r\n	<li style=\"text-align:justify\">Sản phẩm:</li>\r\n	<li style=\"text-align:justify\">Căn hộ chung cư cao cấp sổ hồng l&acirc;u d&agrave;i</li>\r\n	<li style=\"text-align:justify\">Quy m&ocirc;:</li>\r\n	<li style=\"text-align:justify\">03 t&ograve;a th&aacute;p chung cư</li>\r\n	<li style=\"text-align:justify\">C&ocirc;ng vi&ecirc;n c&acirc;y xanh v&agrave; tiện &iacute;ch</li>\r\n	<li style=\"text-align:justify\">Trường mầm non</li>\r\n	<li style=\"text-align:justify\">Nh&agrave; đỗ xe th&ocirc;ng minh</li>\r\n	<li style=\"text-align:justify\">Tổng số căn hộ: 1.248 căn</li>\r\n	<li style=\"text-align:justify\">Thời gian ho&agrave;n th&agrave;nh: Dự kiến Qu&yacute; III/2022</li>\r\n</ul>', 'Tổng quan', 'Vị trí', 'Mặt bằng', 'Tiện ích', NULL, NULL, NULL, NULL, 'Chính sách', '<p style=\"text-align:justify\">Chung cư Feliz Homes tọa lạc tại trung t&acirc;m khu vực Ho&agrave;ng Văn Thụ, Ho&agrave;ng Mai, H&agrave; Nội gồm 3 t&ograve;a căn hộ chung cư cao c&acirc;́p c&oacute; diện t&iacute;ch từ 44m2 đến 120m2, thiết kế sang trọng v&agrave; hiện đại từ 1-4 ph&ograve;ng ngủ, đầy đủ tiện nghi v&agrave; tiện &iacute;ch vượt trội. Khu căn hộ đẳng cấp v&agrave; sang trọng trong m&ocirc;i trường sống gắn liền với kh&ocirc;ng gian xanh rộng lớn c&ugrave;ng c&aacute;c quần thể dịch vụ đa dạng. C&aacute;c t&ograve;a nh&agrave; ở đ&acirc;y đều sử dụng giải ph&aacute;p năng lượng th&acirc;n thiện với m&ocirc;i trường, tiết kiệm năng lượng v&agrave; giảm thiểu &ocirc; nhiễm.</p>\r\n\r\n<p style=\"text-align:justify\">Dự án Feliz Homes Ho&agrave;ng Văn Thụ được x&acirc;y dựng với định hướng mang đến sự tiện &iacute;ch, sang trọng v&agrave; đẳng cấp cho kh&aacute;ch h&agrave;ng. C&aacute;c căn hộ đảm bảo được t&iacute;nh th&ocirc;ng tho&aacute;ng, hiện đại nhưng vẫn tối ưu được c&ocirc;ng năng sử dụng v&agrave; tất cả c&aacute;c căn hộ đều tr&agrave;n ngập &aacute;nh s&aacute;ng tự nhi&ecirc;n, Panorama Ho&agrave;ng Văn Thụ sẽ mang lại cho cư d&acirc;n sự thoải m&aacute;i sau những giờ l&agrave;m việc căng thẳng, sẽ đảm bảo được &ldquo;nơi chốn an cư lạc nghiệp&rdquo; cho cư d&acirc;n sinh sống nơi đ&acirc;y.</p>', '<p style=\"text-align:justify\">Tọa lạc tại &ldquo;tr&aacute;i tim&rdquo; quận Ho&agrave;ng Mai, được bao quanh bởi 6 con đường sầm uất, Feliz Homes kiến tạo một &ldquo;thị tứ&rdquo; phồn hoa, một &ldquo;v&ograve;ng xuyến giao th&ocirc;ng&rdquo; của khu vực, nơi giao th&ocirc;ng thuận lợi, giao thương thuận tiện.</p>\r\n\r\n<p style=\"text-align:justify\">Li&ecirc;n kết dự &aacute;n Feliz Homes</p>\r\n\r\n<table border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:100%\">\r\n	<tbody>\r\n		<tr>\r\n			<td><strong>5 ph&uacute;t:</strong> Hồ Đền Lừ, Chợ đầu mối</td>\r\n			<td><strong>15 ph&uacute;t</strong>: C&ocirc;ng vi&ecirc;n Y&ecirc;n Sở, Ga H&agrave; Nội, Nh&agrave; h&aacute;t lớn, Hồ Ho&agrave;n Kiếm</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>10 ph&uacute;t:</strong> Bến xe Gi&aacute;p B&aacute;t, Đại học B&aacute;ch khoa, Đại học Kinh tế quốc d&acirc;n,</p>\r\n\r\n			<p>Đại học x&acirc;y dựng, Bệnh viện Bạch Mai, Bệnh viện Thanh Nh&agrave;n</p>\r\n			</td>\r\n			<td><strong>40 ph&uacute;t:</strong> S&acirc;n bay Nội B&agrave;i</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>', '<p style=\"text-align:justify\">Chung cư Feliz Home Ho&agrave;ng Mai thiết kế theo phong c&aacute;ch phong c&aacute;ch hiện đại. Căn hộ h&agrave;i h&ograve;a, đảm bảo tiện nghi cho cuộc sống ho&agrave;n hảo của cư d&acirc;n. C&aacute;c gian ph&ograve;ng được ph&acirc;n bố căn ke hợp l&yacute;, kh&ocirc;ng gian đ&oacute;n &aacute;nh s&aacute;ng, nắng gi&oacute; thuận tiện. Hệ thống c&ocirc;ng vi&ecirc;n v&agrave; hồ sinh th&aacute;i b&ecirc;n trong dự &aacute;n, to&agrave;n bộ sẽ đem đến cho cư d&acirc;n những gi&aacute; trị sống ho&agrave;n mỹ v&agrave; ri&ecirc;ng biệt.</p>\r\n\r\n<p style=\"text-align:justify\">Với cảm hứng từ những chi tiết nhỏ nhất, c&aacute;c t&ograve;a th&aacute;p tại Feliz Homes được c&aacute;ch điệu như l&agrave; những c&aacute;nh mai &oacute;ng &aacute;nh giữa đất trời. H&atilde;y thử tưởng tượng &aacute;nh nắng chiếu v&agrave;o v&agrave; to&aacute;t l&ecirc;n một m&agrave;u v&agrave;ng &oacute;ng &aacute;nh mang lại sự thịnh vượng cho cả một khu đ&ocirc; thị sầm uất. Du an chung cu Feliz home hoang mai c&oacute; tổng số căn hộ 1.248 căn. (Block CT1A. Block CT1B, CT2).</p>\r\n\r\n<p style=\"text-align:justify\"><strong>T&ograve;a CT1B &ndash; Tropical Tower:</strong></p>\r\n\r\n<p style=\"text-align:justify\">T&acirc;̀ng 1: trung tâm thương mại</p>\r\n\r\n<p style=\"text-align:justify\">T&acirc;̀ng 2-25: 13 căn hộ trên 1 sàn &ndash; 312 căn</p>\r\n\r\n<p style=\"text-align:justify\">Diện tích:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">2PN: 107 &ndash; 110 &ndash; 119 m2</li>\r\n	<li style=\"text-align:justify\">3PN: 64 &ndash; 75 m2</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><strong>T&ograve;a CT1A &ndash; Fantasy Tower:</strong></p>\r\n\r\n<p style=\"text-align:justify\">T&acirc;̀ng 1: trung tâm thương mại</p>\r\n\r\n<p style=\"text-align:justify\">T&acirc;̀ng 2-27: 16 căn hộ trên 1 sàn.</p>\r\n\r\n<p style=\"text-align:justify\">Diện tích:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">2PN: 75m2</li>\r\n	<li style=\"text-align:justify\">3PN: 95 &ndash; 108 m2</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><strong>T&ograve;a CT2 &ndash; Zen Tower:</strong></p>\r\n\r\n<p style=\"text-align:justify\">T&acirc;̀ng 1: trung tâm thương mại</p>\r\n\r\n<p style=\"text-align:justify\">T&acirc;̀ng 2-27: 20 căn hộ trên 1 sàn.</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">1PN:45m2</li>\r\n	<li style=\"text-align:justify\">2PN: 64 &ndash; 72 m2</li>\r\n	<li style=\"text-align:justify\">3PN: 90 &ndash;&nbsp; 113 m2</li>\r\n</ul>', '<h2 style=\"text-align:justify\">Xanh M&aacute;t Nghỉ Dưỡng</h2>\r\n\r\n<p style=\"text-align:justify\">Một dự &aacute;n hiếm hoi giữa l&ograve;ng thủ đ&ocirc; mang đến kh&ocirc;ng gian thanh khiết với bạt ng&agrave;n c&acirc;y xanh v&agrave; mặt nước. Bước v&agrave;o Feliz Homes, cư d&acirc;n kh&ocirc;ng chỉ c&oacute; cảm gi&aacute;c như đang lạc bước trong một khu vườn nhiệt đới m&agrave; c&ograve;n cảm thấy như đang bước ch&acirc;n v&agrave;o một th&agrave;nh phố xanh thu nhỏ. Ở đ&oacute;, tiếng c&ograve;i xe inh ỏi được thay thế bởi những thanh &acirc;m tuyệt diệu: Tiếng nước chảy r&oacute;c r&aacute;ch, tiếng chim k&ecirc;u r&iacute;u r&iacute;t, tiếng gi&oacute; đuổi nhau x&agrave;o xạc trong những t&aacute;n c&acirc;y. Mọi lo toan, mỏi mệt nhường chỗ cho sự an nhi&ecirc;n với hương hoa thoang thoảng, m&ugrave;i cỏ non quyện trong vị nắng.</p>\r\n\r\n<h2 style=\"text-align:justify\">Trọn vẹn tiện nghi</h2>\r\n\r\n<p style=\"text-align:justify\">Sở hữu kh&ocirc;ng gian sống xanh v&agrave; độc đ&aacute;o như resort nhiệt đới c&ugrave;ng h&agrave;ng loạt tiện &iacute;ch từ chăm s&oacute;c sức khoẻ đến vui chơi cho trẻ nhỏ, tại Feliz Homes, mỗi cư d&acirc;n đều t&igrave;m thấy một kh&ocirc;ng gian được thiết kế v&agrave; &ldquo;may đo&rdquo; cho ri&ecirc;ng m&igrave;nh, nơi tận hưởng mỗi ng&agrave;y đều tr&agrave;n đầy cảm hứng.</p>\r\n\r\n<h2 style=\"text-align:justify\">Hơn 40 tiện &iacute;ch nội khu đẳng cấp</h2>\r\n\r\n<p style=\"text-align:justify\">Hồ bơi điện ph&acirc;n đồng giữa c&ocirc;ng vi&ecirc;n</p>\r\n\r\n<p style=\"text-align:justify\">3 vườn ch&acirc;n m&acirc;y tr&ecirc;n đỉnh 3 to&agrave; th&aacute;p</p>\r\n\r\n<p style=\"text-align:justify\">Vườn tr&aacute;i c&acirc;y nhiệt đới, vườn thiền</p>\r\n\r\n<p style=\"text-align:justify\">Tổ hợp trung t&acirc;m thương mại</p>\r\n\r\n<p style=\"text-align:justify\">Vườn v&agrave; khu vui chơi thần ti&ecirc;n cho trẻ nhỏ</p>\r\n\r\n<p style=\"text-align:justify\">&hellip;&hellip;&hellip;&hellip;&hellip;...</p>', NULL, NULL, NULL, NULL, '<p style=\"text-align:justify\">Hỗ trợ LS 0% trong 18 th&aacute;ng</p>\r\n\r\n<p style=\"text-align:justify\">Thanh to&aacute;n sớm 95% chiết khấu 5.5% GTCH</p>\r\n\r\n<p style=\"text-align:justify\">Qu&agrave; tặng early bird:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Căn 2PN: Ban c&ocirc;ng nhiệt đới trị gi&aacute; 22.000.000 VNĐ</li>\r\n	<li style=\"text-align:justify\">Căn 3PN v&agrave; 4PN: G&oacute;i Smart Home trị gi&aacute; 33.000.000 VNĐ</li>\r\n</ul>', 'TỔNG QUAN DỰ ÁN FELIZ HOMES', 'VỊ TRÍ “TÂM ĐIỂM PHỒN HOA - GIAO THƯƠNG THUẬN TIỆN”', 'MẶT BẰNG THIẾT KẾ CĂN HỘ', 'TIỆN ÍCH SỐNG NHƯ RESORT', NULL, NULL, NULL, NULL, 'CHÍNH SÁCH BÁN HÀNG', 'Feliz Homes - Tải Xuống Bảng Giá Mới Nhất Từ CĐT', '12/03/2020', NULL, 'feliz-homes', 'Dự án Feliz Homes Hoàng Văn Thụ được xây dựng với định hướng mang đến sự tiện ích, sang trọng và đẳng cấp cho khách hàng.', NULL, 'index, follow'),
(144, '69', 63, 'Admin', 'Khu dân cư, đô thị - dịch vụ thương mại tại đầu cầu Bà Mai', 'mặt đường Trần Nhân Tông – xã Hải Xuân – phường Hoà Lạc', 1013, 'Móng Cái final.jpg', NULL, NULL, NULL, 'true', '<ul>\r\n	<li>T&ecirc;n dự &aacute;n: Khu d&acirc;n cư, đ&ocirc; thị - dịch vụ thương mại tại đầu cầu B&agrave; Mai</li>\r\n	<li>Chủ đầu tư: C&ocirc;ng tư cổ phần đầu tư ASC</li>\r\n	<li>Vị tr&iacute;: mặt đường Trần Nh&acirc;n T&ocirc;ng &ndash; x&atilde; Hải Xu&acirc;n &ndash; phường Ho&agrave; Lạc &ndash; TP. M&oacute;ng C&aacute;i</li>\r\n	<li>Tổng diện t&iacute;ch dự &aacute;n rộng 14,8ha</li>\r\n	<li><strong>Diện t&iacute;ch:</strong></li>\r\n	<li>&Ocirc; thường: 100 &ndash; 120m2</li>\r\n	<li>&Ocirc; g&oacute;c 200 &ndash; 250m2</li>\r\n	<li><strong>Hạ tầng: </strong>Hạ tầng ho&agrave;n thiện</li>\r\n	<li><strong>Ph&aacute;p l&yacute;: </strong>Ph&aacute;p l&yacute; đầy đủ, r&otilde; r&agrave;ng , c&oacute; sổ hồng</li>\r\n</ul>', 'Tổng quan', 'Vị trí', 'Đất nền', 'Mặt bằng', 'Đầu tư', NULL, NULL, NULL, 'Chính sách', '<p style=\"text-align:justify\">Khu d&acirc;n cư, đ&ocirc; thị - dịch vụ thương mại tại đầu cầu B&agrave; Mai l&agrave; dự &aacute;n hiếm hoi nằm tại vị tr&iacute; cực k&igrave; đắc địa tại n&uacute;t giao v&ograve;ng xuyến Cao tốc V&acirc;n Đồn &ndash; M&oacute;ng C&aacute;i. Hiện tại dự &aacute;n đang cho ra mắt quỹ h&agrave;ng đất nền được quy hoạch b&agrave;i bản, hạ tầng ho&agrave;n thiện v&agrave;&nbsp; đ&atilde; c&oacute; sổ hồng từng l&ocirc; rất th&iacute;ch hợp để đầu tư trong trung v&agrave; d&agrave;i hạn.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Đất nền ASC Móng Cái\" src=\"/public/upload/files/2020/ef36583c4390b1cee881_1619485524_1.jpg\" style=\"height:422px; width:738px\" /></p>', '<p style=\"text-align:justify\">Đất nền dự &aacute;n Khu d&acirc;n cư, đ&ocirc; thị - dịch vụ thương mại tại đầu cầu B&agrave; Mai sở hữu vị tr&iacute; độc đạo khi nằm tr&ecirc;n n&uacute;t giao v&ograve;ng xuyến Cao tốc V&acirc;n Đồn &ndash; M&oacute;ng C&aacute;i v&agrave; đường l&ecirc;n cầu Bắc Lu&acirc;n 2 kết nối giao thương hai nước Việt &ndash; Trung.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Dự &aacute;n cũng kết nối trực tiếp đến trung t&acirc;m th&agrave;nh phố M&oacute;ng C&aacute;i v&agrave; Khu du lịch Tr&agrave; Cổ. C&oacute; thể n&oacute;i dự &aacute;n nằm tại &ldquo;ng&atilde; 4 kim cương&rdquo; mang tầm chiến lược trong ph&aacute;t triển của th&agrave;nh phố M&oacute;ng C&aacute;i. Tại đ&acirc;y sẽ h&igrave;nh th&agrave;nh khu đ&ocirc; thị sầm uất nhất của v&ugrave;ng kinh tế cửa khẩu v&agrave; mang lại nhiều tiện &iacute;ch cho cư d&acirc;n.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Khu dân cư, đô thị - dịch vụ thương mại tại đầu cầu Bà Mai\" src=\"/public/upload/files/2020/M%C3%B3ng%20C%C3%A1i%20final(1).jpg\" style=\"height:562px; width:1000px\" /><em>Đất nền ASC M&oacute;ng C&aacute;i&nbsp;tọa lạc tại &ldquo;ng&atilde; 4 kim cương&rdquo; đắc địa nhất TP M&oacute;ng C&aacute;i</em></p>\r\n\r\n<p style=\"text-align:justify\">B&ecirc;n cạnh đ&oacute;, đất nền ASC M&oacute;ng C&aacute;i cũng tiếp gi&aacute;p h&agrave;ng loạt si&ecirc;u dự &aacute;n sắp triển khai của c&aacute;c &ocirc;ng lớn như Sun Group, Vingroup, CEO, T&amp;T n&ecirc;n tiềm năng tăng gi&aacute; l&agrave; rất cao.</p>\r\n\r\n<p style=\"text-align:justify\">Li&ecirc;n kết v&ugrave;ng dự &aacute;n Khu d&acirc;n cư, đ&ocirc; thị - dịch vụ thương mại tại đầu cầu B&agrave; Mai - Đất nền ASC M&oacute;ng C&aacute;i</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Nằm tr&ecirc;n trục đường huyết mặt của trung t&acirc;m Tp. M&oacute;ng C&aacute;i</li>\r\n	<li style=\"text-align:justify\">C&aacute;ch Trung t&acirc;m TP. M&oacute;ng C&aacute;i 2km</li>\r\n	<li style=\"text-align:justify\">Gần trường tiểu học Hải Xu&acirc;n, trường tiểu học Hải Ho&agrave;, THCS Hải Ho&agrave; ( b&aacute;n k&iacute;nh 1km)</li>\r\n	<li style=\"text-align:justify\">Gần khu vực h&agrave;nh ch&iacute;nh c&ocirc;ng, c&ocirc;ng an phường, trạm y tế ( b&aacute;n k&iacute;nh 1km)</li>\r\n	<li style=\"text-align:justify\">Gần TTTM Vincom Plaza M&oacute;ng C&aacute;i, phố đi bộ Nguyễn Huệ, chợ M&oacute;ng C&aacute;i ( 5-7p đi xe, 3km)</li>\r\n	<li style=\"text-align:justify\">Gần khu kh&aacute;ch sạn v&agrave; casino Hồng Vận ( 10p đi xe)</li>\r\n	<li style=\"text-align:justify\">C&aacute;ch b&atilde;i tắm Tr&agrave; Cổ, s&acirc;n Golf Quốc Tế khoảng 6km</li>\r\n	<li style=\"text-align:justify\">Nằm vị tr&iacute; trọng t&acirc;m giữa 2 cửa khẩu lớn nhất cả nước: Cửa khẩu M&oacute;ng c&aacute;i v&agrave; Cửa khẩu Bắc Lu&acirc;n 2</li>\r\n</ul>', '<p style=\"text-align:justify\">Thời điểm hiện tại, dự &aacute;n Khu d&acirc;n cư, đ&ocirc; thị - dịch vụ thương mại tại đầu cầu B&agrave; Mai đang mang đến cơ hội đầu tư hấp dẫn cho nh&agrave; đầu tư khi cho ra mắt quỹ h&agrave;ng đất nền c&oacute; diện t&iacute;ch 100m2 &ndash; 120m2 với hạ tầng ho&agrave;n thiện, sổ hồng trao tay v&agrave; kh&ocirc;ng phải x&acirc;y dựng.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Khu dân cư, đô thị - dịch vụ thương mại tại đầu cầu Bà Mai tọa lạc tại “ngã 4 kim cương” đắc địa nhất TP Móng Cái\" src=\"/public/upload/files/2020/LK05-12_1(1).jpg\" style=\"height:695px; width:1000px\" /><em>Đất nền dự &aacute;n Khu d&acirc;n cư, đ&ocirc; thị - dịch vụ thương mại tại đầu cầu B&agrave; Mai đ&atilde; c&oacute; ph&aacute;p l&yacute; cao nhất &ndash; Sổ hồng trao tay.</em></p>\r\n\r\n<p style=\"text-align:justify\">Với đ&agrave; tăng trưởng nhanh ch&oacute;ng của thị trường, c&ugrave;ng sự bứt tốc của hạ tầng v&agrave; d&ograve;ng vốn đầu tư v&agrave;o thị trường khu kinh tế cửa khẩu M&oacute;ng C&aacute;i đang kh&ocirc;ng ngừng tăng, th&igrave; gi&aacute; đất nền tại khu vực n&agrave;y sẽ tăng l&agrave; điều tất yếu. Ngo&agrave;i ra, dự &aacute;n c&oacute; ph&aacute;p l&yacute; ho&agrave;n thiện, an to&agrave;n, ch&iacute;nh l&agrave; &ldquo;bảo chứng v&agrave;ng&rdquo; cho cơ hội đầu tư tăng gi&aacute; phi m&atilde;.</p>', '<p style=\"text-align: justify;\">Khu d&acirc;n cư, đ&ocirc; thị - dịch vụ thương mại tại đầu cầu B&agrave; Mai đ&atilde; c&oacute; hạ tầng ho&agrave;n thiện,&nbsp;được trải đường nhựa, tho&aacute;ng rộng, vỉa h&egrave; l&aacute;t gạch được bố tr&iacute; cảnh quan c&acirc;y xanh, mạng lưới đ&egrave;n đường, hệ thống cấp tho&aacute;t nước&hellip;đều đ&atilde; ho&agrave;n thiện, khang trang.</p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Mặt bằng ASC Móng Cái\" src=\"/public/upload/files/2020/mat%20bang.PNG\" style=\"height:669px; width:542px\" /></p>\r\n\r\n<p style=\"text-align: justify;\">Khu đ&ocirc; thị ASC M&oacute;ng C&aacute;i được chia th&agrave;nh c&aacute;c khu chức năng bao gồm: khu nh&agrave; liền kề, biệt thự, nh&agrave; ở cao tầng, trung t&acirc;m thương mại, si&ecirc;u thị&hellip; hứa hẹn sẽ l&agrave; một kh&ocirc;ng gian sống l&yacute; tưởng cho cộng đồng cư d&acirc;n.</p>', '<ol>\r\n	<li>Chỉ từ 1,5 tỷ/l&ocirc; diện t&iacute;ch 100 - 120m2</li>\r\n	<li>Đ&oacute;n s&oacute;ng Cao tốc V&acirc;n Đồn - M&oacute;ng C&aacute;i sắp th&ocirc;ng xe cuối năm 2021 - Gi&aacute; đất sẽ tăng nhanh ch&oacute;ng, tăng X2 X3 chỉ sau 1 năm</li>\r\n	<li>Giao th&ocirc;ng kết nối trực tiếp với cửa khẩu Bắc Lu&acirc;n 2</li>\r\n	<li>Thuận tiện di chuyển đến trung t&acirc;m TP M&oacute;ng C&aacute;i</li>\r\n	<li>S&aacute;t ngay c&aacute;c si&ecirc;u dự &aacute;n của Vin, Sun. CEO</li>\r\n	<li>Hạ tầng đ&atilde; ho&agrave;n thiện 100%</li>\r\n	<li>Ph&aacute;p l&yacute; r&otilde; r&agrave;ng, sổ hồng từng l&ocirc;</li>\r\n	<li>Khả năng sinh lời vượt trội, thanh khoản tốt</li>\r\n</ol>', NULL, NULL, NULL, NULL, 'Tổng quan dự án', 'Vị trí kim cương của đất nền ASC Móng Cái', 'Ra mắt quỹ hàng đất nền Sổ hồng từng lô', 'Mặt bằng dự án ASC Móng Cái Cầu Bà Ma', 'Các lý do nên đầu tư đất nền ASC Móng Cái', NULL, NULL, NULL, NULL, 'Đất nền dự án ASC Móng Cái - Sổ Hồng Từng Lô - Chỉ Từ 1,5 Tỷ/Lô', '05/14/2021', NULL, 'khu-dan-cu-do-thi-dich-vu-thuong-mai-tai-dau-cau-ba-mai', 'Đất nền ASC Móng Cái  là dự án hiếm hoi nằm tại vị trí cực kì đắc địa tại nút giao vòng xuyến Cao tốc Vân Đồn – Móng Cái, có diện tích 100 - 120m2, Sổ Hồng từng lô', NULL, 'index, follow');
